			out.WriteString(ufmt.Sprintf("• **%s** (*%s*) - [✓ Bought It](%s:buy-%s) | [Remove](%s:removeshop-%s)\n", 
				item.Name, item.Quantity, realmPath, urlEncode(item.Name), realmPath, urlEncode(item.Name)))
		}
		out.WriteString("\n")
	}

	out.WriteString("---\n\n")

	// Quick add section
	out.WriteString("## ➕ Quick Add Items\n\n")
	out.WriteString("**Add common items:**\n\n")

	quickItems := []struct{ name, quantity string }{
		{"Milk", "1-gallon"},
		{"Bread", "1-loaf"},
		{"Eggs", "12-count"},
		{"Coffee", "1-bag"},
		{"Chicken", "2-lbs"},
		{"Rice", "2-lbs"},
	}

	for i, item := range quickItems {
		out.WriteString(ufmt.Sprintf("[+ %s](%s:add-%s-%s)", item.name, realmPath, urlEncode(item.name), urlEncode(item.quantity)))
		if i < len(quickItems)-1 {
			out.WriteString(" | ")
		}
	}
	out.WriteString("\n\n")

	out.WriteString("---\n\n")
	out.WriteString("*💡 Tip: Click 'Use It' on pantry items to move them to shopping list. Click 'Bought It' to move items back to pantry.*\n")

	return out.String()
}

