		if selected != "" {
			selectedCount = len(strings.Split(selected, ","))
		}
		out.WriteString(ufmt.Sprintf("**Selected: %s items**\n\n", intToString(selectedCount)))

		for _, item := range requiredItems {
			itemKey := urlEncode(item.Name) + "-" + urlEncode(item.Quantity)
			newSelection := toggleItem(selected, itemKey)

			if contains(selected, itemKey) {
