			{"Bread", "1-loaf"},
			{"Eggs", "12-count"},
			{"Butter", "1-stick"},
			{"Cheese", "8-oz"},
			{"Rice", "2-lbs"},
			{"Pasta", "1-box"},
			{"Chicken", "2-lbs"},
			{"Beef", "1-lb"},
			{"Tomatoes", "4-count"},
			{"Onions", "3-count"},
			{"Potatoes", "5-lbs"},
			{"Carrots", "1-bag"},
			{"Lettuce", "1-head"},
			{"Apples", "6-count"},
			{"Bananas", "1-bunch"},
			{"Juice", "64-oz"},
			{"Coffee", "1-bag"},
			{"Sugar", "2-lbs"},
			{"Flour", "5-lbs"},
		}

		for i, item := range commonItems {
			itemKey := urlEncode(item.name) + "-" + urlEncode(item.quantity)
			out.WriteString(ufmt.Sprintf("[+ %s](%s:select-req-%s)", item.name, realmPath, itemKey))
			if (i+1)%5 == 0 {
				out.WriteString("\n\n")
			} else if i < len(commonItems)-1 {
				out.WriteString(" | ")
			}
		}
		out.WriteString("\n\n")

		out.WriteString("---\n\n")
		out.WriteString(ufmt.Sprintf("[Continue →](%s:step2)", realmPath))
		return out.String()
	}

