package pv12

package pv12

import (
	"chain/runtime"
	"strings"
	"gno.land/p/nt/ufmt"
)

// Helper functions
func intToString(n int) string {
	if n == 0 {
		return "0"
	}
	negative := n < 0
	if negative {
		n = -n
	}
	result := ""
	for n > 0 {
		result = string(rune('0' + n%10)) + result
		n /= 10
	}
	if negative {
		result = "-" + result
	}
	return result
}

func urlEncode(s string) string {
	return strings.ReplaceAll(s, " ", "_")
}

func urlDecode(s string) string {
	return strings.ReplaceAll(s, "_", " ")
}

func contains(list string, item string) bool {
	if list == "" {
		return false
	}
	items := strings.Split(list, ",")
	for _, i := range items {
		if i == item {
			return true
		}
	}
	return false
}

func toggleItem(list string, item string) string {
	if contains(list, item) {
		items := strings.Split(list, ",")
		newList := []string{}
		for _, i := range items {
			if i != item {
				newList = append(newList, i)
			}
		}
		return strings.Join(newList, ",")
	}
	if list == "" {
		return item
	}
	return list + "," + item
}

// Parse URL to extract required items and pantry stock
func parseURL(path string) (userID string, requiredItems []string, pantryStock []string) {
	if path == "" {
		return "", []string{}, []string{}
	}

	parts := strings.Split(path, "/")
	if len(parts) == 0 {
		return "", []string{}, []string{}
	}

	userID = parts[0]

	for i := 1; i < len(parts); i++ {
		part := parts[i]

		if strings.HasPrefix(part, "req-") {
			itemsStr := strings.TrimPrefix(part, "req-")
			if itemsStr != "" {
				requiredItems = strings.Split(itemsStr, ",")
			}
		}

		if strings.HasPrefix(part, "stock-") {
			itemsStr := strings.TrimPrefix(part, "stock-")
			if itemsStr != "" {
				pantryStock = strings.Split(itemsStr, ",")
			}
		}
	}

	return userID, requiredItems, pantryStock
}

// Build URL from state
func buildURL(userID string, requiredItems string, pantryStock string) string {
	url := userID
	if requiredItems != "" {
		url += "/req-" + requiredItems
	}
	if pantryStock != "" {
		url += "/stock-" + pantryStock
	}
	return url
}

// Get all items string
func getAllItemsString() string {
	commonItems := []string{
		"Milk", "Bread", "Eggs", "Butter", "Cheese",
		"Rice", "Pasta", "Chicken", "Beef", "Tomatoes",
		"Onions", "Potatoes", "Carrots", "Lettuce", "Apples",
		"Bananas", "Juice", "Coffee", "Sugar", "Flour",
	}
	return strings.Join(commonItems, ",")
}

// Render function
func Render(path string) string {
	realmPath := strings.TrimPrefix(runtime.CurrentRealm().PkgPath(), runtime.ChainDomain())
	var out strings.Builder

	out.WriteString("# 🏘️ Pantry Manager (URL-Based)\n\n")
	out.WriteString("*All state stored in URL - no blockchain transactions*\n\n")
	out.WriteString("---\n\n")

	// Entry screen - pick user ID
	if path == "" {
		out.WriteString("## 👤 Choose Your Pantry ID\n\n")
		out.WriteString("Pick any 3-digit number:\n\n")
		out.WriteString(ufmt.Sprintf("[001](%s:001) | [002](%s:002) | [123](%s:123) | [456](%s:456) | [789](%s:789)\n\n",
			realmPath, realmPath, realmPath, realmPath, realmPath))
		return out.String()
	}

	userID, requiredItems, pantryStock := parseURL(path)

	// Step 1: Select required items
	if len(requiredItems) == 0 {
		out.WriteString(ufmt.Sprintf("## Pantry #%s - Step 1\n\n", userID))
		out.WriteString("**Select items you want to track:**\n\n")

		// Selection in progress?
		if strings.Contains(path, "/selecting-") {
			selected := ""
			parts := strings.Split(path, "/")
			for _, part := range parts {
				if strings.HasPrefix(part, "selecting-") {
					selected = strings.TrimPrefix(part, "selecting-")
				}
			}

			selectedCount := 0
			if selected != "" {
				selectedCount = len(strings.Split(selected, ","))
			}
			out.WriteString(ufmt.Sprintf("**Selected: %s items**\n\n", intToString(selectedCount)))

			allItems := getAllItemsString()
			if selected == allItems {
				out.WriteString(ufmt.Sprintf("[Clear All](%s:%s/selecting-)\n\n", realmPath, userID))
			} else {
				out.WriteString(ufmt.Sprintf("[✓ Select All (20)](%s:%s/selecting-%s)\n\n", realmPath, userID, allItems))
			}

			// Show items as clickable
			items := strings.Split(allItems, ",")
			for i, item := range items {
				newSelection := toggleItem(selected, item)

				if contains(selected, item) {
					out.WriteString(ufmt.Sprintf("[✓ %s](%s:%s/selecting-%s)", item, realmPath, userID, newSelection))
				} else {
					out.WriteString(ufmt.Sprintf("[+ %s](%s:%s/selecting-%s)", item, realmPath, userID, newSelection))
				}

				if (i+1)%5 == 0 {
					out.WriteString("\n\n")
				} else if i < len(items)-1 {
					out.WriteString(" | ")
				}
			}
			out.WriteString("\n\n---\n\n")

			if selectedCount > 0 {
				out.WriteString(ufmt.Sprintf("[Continue with %s items →](%s:%s/req-%s)\n", intToString(selectedCount), realmPath, userID, selected))
			}
		} else {
			// Start selection
			allItems := getAllItemsString()
			out.WriteString(ufmt.Sprintf("[✓ Quick Start - Select All 20 Items](%s:%s/req-%s)\n\n", realmPath, userID, allItems))
			out.WriteString(ufmt.Sprintf("[Custom Selection →](%s:%s/selecting-)\n", realmPath, userID))
		}

		return out.String()
	}

	// Step 2: Stock pantry
	if len(pantryStock) == 0 && !strings.Contains(path, "/stocking-") {
		out.WriteString(ufmt.Sprintf("## Pantry #%s - Step 2\n\n", userID))
		out.WriteString("**What do you currently have?**\n\n")

		reqStr := strings.Join(requiredItems, ",")
		out.WriteString(ufmt.Sprintf("[✓ Select All](%s:%s/req-%s/stock-%s) | [Skip - Empty](%s:%s/req-%s/stock-empty)\n\n", 
			realmPath, userID, reqStr, reqStr, realmPath, userID, reqStr))
		out.WriteString(ufmt.Sprintf("[Custom Selection →](%s:%s/req-%s/stocking-)\n", realmPath, userID, reqStr))
		return out.String()
	}

	// Stocking in progress
	if strings.Contains(path, "/stocking-") {
		selected := ""
		parts := strings.Split(path, "/")
		for _, part := range parts {
			if strings.HasPrefix(part, "stocking-") {
				selected = strings.TrimPrefix(part, "stocking-")
			}
		}

		selectedCount := 0
		if selected != "" && selected != "empty" {
			selectedCount = len(strings.Split(selected, ","))
		}

		out.WriteString(ufmt.Sprintf("## Pantry #%s - Stock Selection\n\n", userID))
		out.WriteString(ufmt.Sprintf("**Selected: %s items**\n\n", intToString(selectedCount)))

		reqStr := strings.Join(requiredItems, ",")

		if selected == reqStr {
			out.WriteString(ufmt.Sprintf("[Clear All](%s:%s/req-%s/stocking-)\n\n", realmPath, userID, reqStr))
		} else {
			out.WriteString(ufmt.Sprintf("[✓ Select All](%s:%s/req-%s/stocking-%s)\n\n", realmPath, userID, reqStr, reqStr))
		}

		for i, item := range requiredItems {
			newSelection := toggleItem(selected, item)

			if contains(selected, item) {
				out.WriteString(ufmt.Sprintf("[✓ %s](%s:%s/req-%s/stocking-%s)", item, realmPath, userID, reqStr, newSelection))
			} else {
				out.WriteString(ufmt.Sprintf("[+ %s](%s:%s/req-%s/stocking-%s)", item, realmPath, userID, reqStr, newSelection))
			}

			if (i+1)%5 == 0 {
				out.WriteString("\n\n")
			} else if i < len(requiredItems)-1 {
				out.WriteString(" | ")
			}
		}
		out.WriteString("\n\n---\n\n")

		if selectedCount > 0 {
			out.WriteString(ufmt.Sprintf("[Done - Add %s items →](%s:%s/req-%s/stock-%s)\n", intToString(selectedCount), realmPath, userID, reqStr, selected))
		} else {
			out.WriteString(ufmt.Sprintf("[Skip](%s:%s/req-%s/stock-empty)\n", realmPath, userID, reqStr))
		}

		return out.String()
	}

	// Main view
	reqStr := strings.Join(requiredItems, ",")
	stockStr := strings.Join(pantryStock, ",")
	if stockStr == "empty" {
		stockStr = ""
		pantryStock = []string{}
	}

	// Calculate shopping list
	shoppingList := []string{}
	for _, item := range requiredItems {
		found := false
		for _, stock := range pantryStock {
			if item == stock {
				found = true
				break
			}
		}
		if !found {
			shoppingList = append(shoppingList, item)
		}
	}

	out.WriteString(ufmt.Sprintf("## 🏠 Pantry #%s\n\n", userID))

	out.WriteString("### 📋 Required Items (" + intToString(len(requiredItems)) + ")\n\n")
	for _, item := range requiredItems {
		inStock := false
		for _, stock := range pantryStock {
			if item == stock {
				inStock = true
				break
			}
		}
		if inStock {
			out.WriteString(ufmt.Sprintf("• ✅ %s\n", item))
		} else {
			out.WriteString(ufmt.Sprintf("• ⚠️ %s\n", item))
		}
	}

	out.WriteString("\n---\n\n")

	out.WriteString("### 🥫 Pantry Stock (" + intToString(len(pantryStock)) + ")\n\n")
	if len(pantryStock) == 0 {
		out.WriteString("*Empty*\n\n")
	} else {
		for _, item := range pantryStock {
			// Remove this item from stock
			newStock := []string{}
			for _, s := range pantryStock {
				if s != item {
					newStock = append(newStock, s)
				}
			}
			newStockStr := strings.Join(newStock, ",")
			if newStockStr == "" {
				newStockStr = "empty"
			}

			out.WriteString(ufmt.Sprintf("• %s - [Used It](%s:%s/req-%s/stock-%s)\n",
				item, realmPath, userID, reqStr, newStockStr))
		}
	}

	out.WriteString("\n---\n\n")

	out.WriteString("### 🛒 Shopping List (" + intToString(len(shoppingList)) + ")\n\n")
	if len(shoppingList) == 0 {
		out.WriteString("✅ **All stocked!**\n\n")
	} else {
		for _, item := range shoppingList {
			// Add this item to stock
			newStock := append(pantryStock, item)
			newStockStr := strings.Join(newStock, ",")

			out.WriteString(ufmt.Sprintf("• %s - [✓ Bought](%s:%s/req-%s/stock-%s)\n",
				item, realmPath, userID, reqStr, newStockStr))
		}
	}

	out.WriteString("\n---\n\n")
	out.WriteString(ufmt.Sprintf("[🔄 Reset](%s) | [📝 Edit Required Items](%s:%s/selecting-%s)\n", 
		realmPath, realmPath, userID, reqStr))

	return out.String()
}

