// Helper functions
func intToString(n int) string {
	if n == 0 {
		return "0"
	}
	negative := n < 0
	if negative {
		n = -n
	}
	result := ""
	for n > 0 {
		result = string(rune('0' + n%10)) + result
		n /= 10
	}
	if negative {
		result = "-" + result
	}
	return result
}

func contains(list string, item string) bool {
	if list == "" {
		return false
	}
	items := strings.Split(list, ",")
	for _, i := range items {
		if i == item {
			return true
		}
	}
	return false
}

func toggleItem(list string, item string) string {
	if contains(list, item) {
		items := strings.Split(list, ",")
		newList := []string{}
		for _, i := range items {
			if i != item {
				newList = append(newList, i)
			}
		}
		return strings.Join(newList, ",")
	}
	if list == "" {
		return item
	}
	return list + "," + item
}

// Parse URL to extract required items and pantry stock
func parseURL(path string) (requiredItems []string, pantryStock []string) {
	if path == "" {
		return []string{}, []string{}
	}

	parts := strings.Split(path, "/")

	for i := 0; i < len(parts); i++ {
		part := parts[i]

		if strings.HasPrefix(part, "req-") {
			itemsStr := strings.TrimPrefix(part, "req-")
			if itemsStr != "" {
				requiredItems = strings.Split(itemsStr, ",")
			}
		}

		if strings.HasPrefix(part, "stock-") {
			itemsStr := strings.TrimPrefix(part, "stock-")
			if itemsStr != "" {
				pantryStock = strings.Split(itemsStr, ",")
			}
		}
	}

	return requiredItems, pantryStock
}

