	// Handle adding items via path (format: add-ItemName-Quantity)
	if strings.HasPrefix(path, "add-") {
		parts := strings.Split(strings.TrimPrefix(path, "add-"), "-")
		if len(parts) >= 2 {
			name := urlDecode(parts[0])
			quantity := urlDecode(strings.Join(parts[1:], "-"))
			AddToPantry(name, quantity)
