// Community pantry - what we currently have
var communityPantry []PantryItem

// Required items - what we should always have
var requiredItems []RequiredItem

type PantryItem struct {
	Name     string
	Quantity string
}

type RequiredItem struct {
	Name     string
	Quantity string
}

// Add item to required items list
func AddRequiredItem(name, quantity string) {
	requiredItems = append(requiredItems, RequiredItem{
		Name:     name,
		Quantity: quantity,
	})
}

// Add multiple items to required list at once
func AddRequiredItems(items string) {
	// items format: "Milk-1-gallon,Bread-1-loaf,Eggs-12-count"
	if items == "" {
		return
	}

	itemList := strings.Split(items, ",")
	for _, item := range itemList {
		parts := strings.Split(item, "-")
		if len(parts) >= 2 {
			name := urlDecode(parts[0])
			quantity := urlDecode(strings.Join(parts[1:], "-"))
			AddRequiredItem(name, quantity)
		}
	}
}

// Add multiple items to pantry at once
func AddItemsToPantry(items string) {
	// items format: "Milk-1-gallon,Bread-1-loaf,Eggs-12-count"
	if items == "" {
		return
	}

	itemList := strings.Split(items, ",")
	for _, item := range itemList {
		parts := strings.Split(item, "-")
		if len(parts) >= 2 {
			name := urlDecode(parts[0])
			quantity := urlDecode(strings.Join(parts[1:], "-"))
			AddToPantry(name, quantity)
		}
	}
}

// Remove from required items
func RemoveRequiredItem(name string) {
	newRequired := []RequiredItem{}
	for _, item := range requiredItems {
		if item.Name != name {
			newRequired = append(newRequired, item)
		}
	}
	requiredItems = newRequired
}

// Add item to pantry
func AddToPantry(name, quantity string) {
	communityPantry = append(communityPantry, PantryItem{
		Name:     name,
		Quantity: quantity,
	})
}

// Use/remove item from pantry
func UseFromPantry(name string) {
	newPantry := []PantryItem{}
	for _, item := range communityPantry {
		if item.Name != name {
			newPantry = append(newPantry, item)
		}
	}
	communityPantry = newPantry
}

// Mark item as bought - adds to pantry
func MarkAsBought(name string) {
	// Find the item in required items to get quantity
	var quantity string
	for _, item := range requiredItems {
		if item.Name == name {
			quantity = item.Quantity
			break
		}
	}

	if quantity != "" {
		AddToPantry(name, quantity)
	}
}

// Check if item is in pantry
func isInPantry(name string) bool {
	for _, item := range communityPantry {
		if item.Name == name {
			return true
		}
	}
	return false
}

// Generate shopping list based on required items not in pantry
func getShoppingList() []RequiredItem {
	shopping := []RequiredItem{}
	for _, required := range requiredItems {
		if !isInPantry(required.Name) {
			shopping = append(shopping, required)
		}
	}
	return shopping
}

// Clear everything
func ClearAll() {
	communityPantry = []PantryItem{}
	requiredItems = []RequiredItem{}
}

// Helper to convert int to string
func intToString(n int) string {
	if n == 0 {
		return "0"
	}

	negative := n < 0
	if negative {
		n = -n
	}

	result := ""
	for n > 0 {
		result = string(rune('0' + n%10)) + result
		n /= 10
	}

	if negative {
		result = "-" + result
	}

	return result
}

// Helper to replace spaces with underscores for URLs
func urlEncode(s string) string {
	return strings.ReplaceAll(s, " ", "_")
}

// Helper to decode underscores back to spaces
func urlDecode(s string) string {
	return strings.ReplaceAll(s, "_", " ")
}

// Check if item is in list
func contains(list string, item string) bool {
	if list == "" {
		return false
	}
	items := strings.Split(list, ",")
	for _, i := range items {
		if i == item {
			return true
		}
	}
	return false
}

// Add or remove item from selection list
func toggleItem(list string, item string) string {
	if contains(list, item) {
		// Remove it
		items := strings.Split(list, ",")
		newList := []string{}
		for _, i := range items {
			if i != item {
				newList = append(newList, i)
			}
		}
		return strings.Join(newList, ",")
	} else {
		// Add it
		if list == "" {
			return item
		}
		return list + "," + item
	}
}

// Render function with interactive UI
func Render(path string) string {
	realmPath := strings.TrimPrefix(runtime.CurrentRealm().PkgPath(), runtime.ChainDomain())

	var out strings.Builder

	out.WriteString("# 🏘️ Community Pantry & Shopping List\n\n")
	out.WriteString("*A shared pantry managed by the community*\n\n")
	out.WriteString("---\n\n")

	// Handle buying item (moving to pantry)
	if strings.HasPrefix(path, "buy-") {
		itemName := urlDecode(strings.TrimPrefix(path, "buy-"))
		MarkAsBought(itemName)
		out.WriteString("✅ Bought **" + itemName + "** - added to pantry!\n\n")
		out.WriteString(ufmt.Sprintf("[← Back to Pantry](%s)", realmPath))
		return out.String()
	}

	// Handle using item from pantry
	if strings.HasPrefix(path, "use-") {
		itemName := urlDecode(strings.TrimPrefix(path, "use-"))
		UseFromPantry(itemName)
		out.WriteString("✅ Used **" + itemName + "** - removed from pantry!\n\n")
		out.WriteString(ufmt.Sprintf("[← Back to Pantry](%s)", realmPath))
		return out.String()
	}

	// Handle removing from required list
	if strings.HasPrefix(path, "remreq-") {
		itemName := urlDecode(strings.TrimPrefix(path, "remreq-"))
		RemoveRequiredItem(itemName)
		out.WriteString("✅ Removed **" + itemName + "** from required items!\n\n")
		out.WriteString(ufmt.Sprintf("[← Back to Pantry](%s)", realmPath))
		return out.String()
	}

	// Handle committing required items
	if strings.HasPrefix(path, "commit-req-") {
		selected := strings.TrimPrefix(path, "commit-req-")
		AddRequiredItems(selected)
		out.WriteString("## ✅ Required Items Added!\n\n")
		out.WriteString(ufmt.Sprintf("Added %s items to your required list.\n\n", intToString(len(requiredItems))))
		out.WriteString(ufmt.Sprintf("[Next: Stock Your Pantry →](%s:step2)", realmPath))
		return out.String()
	}

	// Handle committing pantry stock
	if strings.HasPrefix(path, "commit-stock-") {
		selected := strings.TrimPrefix(path, "commit-stock-")
		AddItemsToPantry(selected)
		out.WriteString("## ✅ Pantry Stocked!\n\n")
		out.WriteString(ufmt.Sprintf("Added %s items to your pantry.\n\n", intToString(len(communityPantry))))
		out.WriteString(ufmt.Sprintf("[View Your Pantry & Shopping List →](%s)", realmPath))
		return out.String()
	}

	// Handle selecting required items (building the list)
	if strings.HasPrefix(path, "select-req-") {
		selected := strings.TrimPrefix(path, "select-req-")

		out.WriteString("## 🎯 Step 1: Select Required Items\n\n")

		// Show selected count
		selectedCount := 0
		if selected != "" {
			selectedCount = len(strings.Split(selected, ","))
		}
		out.WriteString(ufmt.Sprintf("**Selected: %s items**\n\n", intToString(selectedCount)))

		commonItems := []struct{ name, quantity string }{
			{"Milk", "1-gallon"},
			{"Bread", "1-loaf"},
			{"Eggs", "12-count"},
			{"Butter", "1-stick"},
			{"Cheese", "8-oz"},
			{"Rice", "2-lbs"},
			{"Pasta", "1-box"},
			{"Chicken", "2-lbs"},
			{"Beef", "1-lb"},
			{"Tomatoes", "4-count"},
			{"Onions", "3-count"},
			{"Potatoes", "5-lbs"},
			{"Carrots", "1-bag"},
			{"Lettuce", "1-head"},
			{"Apples", "6-count"},
			{"Bananas", "1-bunch"},
			{"Juice", "64-oz"},
			{"Coffee", "1-bag"},
			{"Sugar", "2-lbs"},
			{"Flour", "5-lbs"},
		}

		for i, item := range commonItems {
			itemKey := urlEncode(item.name) + "-" + urlEncode(item.quantity)
			newSelection := toggleItem(selected, itemKey)

			if contains(selected, itemKey) {
				out.WriteString(ufmt.Sprintf("[✓ %s](%s:select-req-%s)", item.name, realmPath, newSelection))
			} else {
				out.WriteString(ufmt.Sprintf("[+ %s](%s:select-req-%s)", item.name, realmPath, newSelection))
			}

			if (i+1)%5 == 0 {
				out.WriteString("\n\n")
			} else if i < len(commonItems)-1 {
				out.WriteString(" | ")
			}
		}
		out.WriteString("\n\n")

		out.WriteString("---\n\n")
		if selectedCount > 0 {
			out.WriteString(ufmt.Sprintf("[Continue → Add %s Items](%s:commit-req-%s)", intToString(selectedCount), realmPath, selected))
		} else {
			out.WriteString("*Select at least one item to continue*")
		}
		return out.String()
	}

	// Handle selecting items to stock
	if strings.HasPrefix(path, "select-stock-") {
		selected := strings.TrimPrefix(path, "select-stock-")

		out.WriteString("## 🥫 Step 2: Stock Your Initial Pantry\n\n")

		selectedCount := 0
		if selected != "" {
			selectedCount = len(strings.Split(selected, ","))
		}
		out.WriteString(ufmt.Sprintf("**Selected: %s items**\n\n", intToString(selectedCount)))

		for _, item := range requiredItems {
			itemKey := urlEncode(item.Name) + "-" + urlEncode(item.Quantity)
			newSelection := toggleItem(selected, itemKey)

			if contains(selected, itemKey) {
				out.WriteString(ufmt.Sprintf("[✓ %s](%s:select-stock-%s)", item.Name, realmPath, newSelection))
			} else {
				out.WriteString(ufmt.Sprintf("[+ %s](%s:select-stock-%s)", item.Name, realmPath, newSelection))
			}
			out.WriteString(" | ")
		}
		out.WriteString("\n\n---\n\n")

		if selectedCount > 0 {
			out.WriteString(ufmt.Sprintf("[Done - Add %s Items to Pantry](%s:commit-stock-%s)", intToString(selectedCount), realmPath, selected))
		} else {
			out.WriteString(ufmt.Sprintf("[Skip - Start With Empty Pantry](%s)", realmPath))
		}
		return out.String()
	}

	// STEP 2: Stock initial pantry (only show if required items exist but pantry is empty AND not coming from another action)
	if path == "step2" && len(requiredItems) > 0 && len(communityPantry) == 0 {
		out.WriteString("## 🥫 Step 2: Stock Your Initial Pantry\n\n")
		out.WriteString("Click the items you CURRENTLY HAVE at home:\n\n")

		out.WriteString("**Your Required Items:**\n\n")
		for _, item := range requiredItems {
			out.WriteString(ufmt.Sprintf("[+ %s](%s:select-stock-%s-%s)", 
				item.Name, realmPath, urlEncode(item.Name), urlEncode(item.Quantity)))
			out.WriteString(" | ")
		}
		out.WriteString("\n\n---\n\n")
		out.WriteString(ufmt.Sprintf("[Skip - Start With Empty Pantry](%s)", realmPath))
		return out.String()
	}

	// STEP 1: If no required items (initial setup)
	if len(requiredItems) == 0 {
		out.WriteString("## 🎯 Step 1: Select Required Items\n\n")
		out.WriteString("Click multiple items you want to always have stocked, then click 'Continue'.\n\n")

		commonItems := []struct{ name, quantity string }{
			{"Milk", "1-gallon"},
			{"Bread", "1-loaf"},
			{"Eggs", "12-count"},
			{"Butter", "1-stick"},
			{"Cheese", "8-oz"},
			{"Rice", "2-lbs"},
			{"Pasta", "1-box"},
			{"Chicken", "2-lbs"},
			{"Beef", "1-lb"},
			{"Tomatoes", "4-count"},
			{"Onions", "3-count"},
			{"Potatoes", "5-lbs"},
			{"Carrots", "1-bag"},
			{"Lettuce", "1-head"},
			{"Apples", "6-count"},
			{"Bananas", "1-bunch"},
			{"Juice", "64-oz"},
			{"Coffee", "1-bag"},
			{"Sugar", "2-lbs"},
			{"Flour", "5-lbs"},
		}

		for i, item := range commonItems {
			itemKey := urlEncode(item.name) + "-" + urlEncode(item.quantity)
			out.WriteString(ufmt.Sprintf("[+ %s](%s:select-req-%s)", item.name, realmPath, itemKey))
			if (i+1)%5 == 0 {
				out.WriteString("\n\n")
			} else if i < len(commonItems)-1 {
				out.WriteString(" | ")
			}
		}
		out.WriteString("\n\n")

		out.WriteString("---\n\n")
		out.WriteString(ufmt.Sprintf("[Continue →](%s:step2)", realmPath))
		return out.String()
	}

	// MAIN VIEW - Show pantry and shopping list (this is the persistent view)
	shoppingList := getShoppingList()

	out.WriteString("## 📋 Required Items\n\n")
	out.WriteString("**Items that should always be stocked:** (" + intToString(len(requiredItems)) + " items)\n\n")

	for _, item := range requiredItems {
		inPantry := isInPantry(item.Name)
		if inPantry {
			out.WriteString(ufmt.Sprintf("• ✅ **%s** (*%s*)\n", item.Name, item.Quantity))
		} else {
			out.WriteString(ufmt.Sprintf("• ⚠️  **%s** (*%s*)\n", item.Name, item.Quantity))
		}
	}
	out.WriteString("\n")

	out.WriteString("---\n\n")

	// Pantry section
	out.WriteString("## 🥫 Current Pantry Stock\n\n")

	if len(communityPantry) == 0 {
		out.WriteString("*Pantry is empty. Shop for items below!*\n\n")
	} else {
		out.WriteString("**In Stock:** (" + intToString(len(communityPantry)) + " items)\n\n")
		for _, item := range communityPantry {
			out.WriteString(ufmt.Sprintf("• **%s** (*%s*) - [Used It](%s:use-%s)\n", 
				item.Name, item.Quantity, realmPath, urlEncode(item.Name)))
		}
		out.WriteString("\n")
	}

	out.WriteString("---\n\n")

	// Shopping List section
	out.WriteString("## 🛒 Shopping List\n\n")

	if len(shoppingList) == 0 {
		out.WriteString("✅ **All stocked up!**\n\n")
	} else {
		out.WriteString("**Need to Buy:** (" + intToString(len(shoppingList)) + " items)\n\n")
		for _, item := range shoppingList {
			out.WriteString(ufmt.Sprintf("• **%s** (*%s*) - [✓ Bought](%s:buy-%s)\n", 
				item.Name, item.Quantity, realmPath, urlEncode(item.Name)))
		}
		out.WriteString("\n")
	}

	out.WriteString("---\n\n")
	out.WriteString("*💡 Click 'Used It' to remove from pantry. Click 'Bought' to add to pantry.*\n")
	out.WriteString("\n\n")
	out.WriteString(ufmt.Sprintf("[🔄 Reset Everything](%s:reset)", realmPath))

	return out.String()
}

