// Helper to convert int to string
func intToString(n int) string {
	if n == 0 {
		return "0"
	}

	negative := n < 0
	if negative {
		n = -n
	}

	result := ""
	for n > 0 {
		result = string(rune('0' + n%10)) + result
		n /= 10
	}

	if negative {
		result = "-" + result
	}

	return result
}

// Helper to replace spaces with underscores for URLs
func urlEncode(s string) string {
	return strings.ReplaceAll(s, " ", "_")
}

// Helper to decode underscores back to spaces
func urlDecode(s string) string {
	return strings.ReplaceAll(s, "_", " ")
}

// Check if item is in list
func contains(list string, item string) bool {
	if list == "" {
		return false
	}
	items := strings.Split(list, ",")
	for _, i := range items {
		if i == item {
			return true
		}
	}
	return false
}

// Add or remove item from selection list
func toggleItem(list string, item string) string {
	if contains(list, item) {
		items := strings.Split(list, ",")
		newList := []string{}
		for _, i := range items {
			if i != item {
				newList = append(newList, i)
			}
		}
		return strings.Join(newList, ",")
	} else {
		if list == "" {
			return item
		}
		return list + "," + item
	}
}

