			for _, item := range shoppingList {
				out.WriteString(ufmt.Sprintf("• **%s** (*%s*) - [✓ Bought](%s:buy-%s)\n", 
					item.Name, item.Quantity, realmPath, urlEncode(item.Name)))
			}
			out.WriteString("\n")
		}

		out.WriteString("---\n\n")
		out.WriteString("*💡 Click 'Used It' to remove from pantry. Click 'Bought' to add to pantry.*\n\n")
		out.WriteString(ufmt.Sprintf("[🔄 Reset Everything](%s:reset)", realmPath))

		return out.String()
