// Render function
func Render(path string) string {
	realmPath := strings.TrimPrefix(runtime.CurrentRealm().PkgPath(), runtime.ChainDomain())
	var out strings.Builder

	out.WriteString(createHeader())
	out.WriteString("\n\n")
	out.WriteString("---\n\n")

	requiredItems, pantryStock := parseURL(path)

	// Step 1: Select required items
	if len(requiredItems) == 0 {
		out.WriteString(createStepBox("1", "Select Items to Track", ""))
		out.WriteString("\n\n")

		// Selection in progress?
		if strings.HasPrefix(path, "selecting-") {
			selected := strings.TrimPrefix(path, "selecting-")

			selectedCount := 0
			if selected != "" {
				selectedCount = len(strings.Split(selected, ","))
			}

			out.WriteString(createCountBox(intToString(selectedCount), "20"))
			out.WriteString("\n\n")

			allItems := getAllItemsString()

			// Select All button above items
			if selected == allItems {
				out.WriteString(ufmt.Sprintf("[Clear All](%s:selecting-)", realmPath))
			} else {
				out.WriteString(ufmt.Sprintf("[✓ Select All](%s:selecting-%s)", realmPath, allItems))
			}
			out.WriteString("\n\n")

			out.WriteString(createSelectionBox("Click items to select:"))
			out.WriteString("\n\n")

			// Show items as clickable - 7 per line
			items := strings.Split(allItems, ",")
			out.WriteString("<gno-columns>\n\n")
			for i, item := range items {
				newSelection := toggleItem(selected, item)

				if contains(selected, item) {
					out.WriteString(ufmt.Sprintf("[✓ %s](%s:selecting-%s)", item, realmPath, newSelection))
				} else {
					out.WriteString(ufmt.Sprintf("[+ %s](%s:selecting-%s)", item, realmPath, newSelection))
				}

				if (i+1)%7 == 0 {
					out.WriteString("\n\n</gno-columns>\n\n<gno-columns>\n\n")
				} else if i < len(items)-1 {
					out.WriteString(" | ")
				}
			}
			out.WriteString("\n\n</gno-columns>\n\n")

			out.WriteString("---\n\n")

			if selectedCount > 0 {
				out.WriteString(ufmt.Sprintf("[Continue with %s items →](%s:req-%s)\n", intToString(selectedCount), realmPath, selected))
			} else {
				out.WriteString("*Select at least one item to continue*\n")
			}
		} else {
			// Start selection
			allItems := getAllItemsString()
			out.WriteString("<gno-columns>\n\n")
			out.WriteString(ufmt.Sprintf("[✓ Quick Start - All 20 Items](%s:req-%s)", realmPath, allItems))
			out.WriteString(" | ")
			out.WriteString(ufmt.Sprintf("[Custom Selection →](%s:selecting-)", realmPath))
			out.WriteString("\n\n</gno-columns>\n\n")
		}

		return out.String()
	}

	// Step 2: Stock pantry (show if no stock and not in stocking mode)
	if len(pantryStock) == 0 && !strings.Contains(path, "/stocking-") && !strings.Contains(path, "/stock-") {
		out.WriteString(createStepBox("2", "Stock Your Pantry", ""))
		out.WriteString("\n\n")

		reqStr := strings.Join(requiredItems, ",")

		out.WriteString("<gno-columns>\n\n")
		out.WriteString(ufmt.Sprintf("[✓ Select All](%s:req-%s/stock-%s)", realmPath, reqStr, reqStr))
		out.WriteString(" | ")
		out.WriteString(ufmt.Sprintf("[Skip - Empty](%s:req-%s/stock-empty)", realmPath, reqStr))
		out.WriteString(" | ")
		out.WriteString(ufmt.Sprintf("[Custom Selection](%s:req-%s/stocking-)", realmPath, reqStr))
		out.WriteString("\n\n</gno-columns>\n\n")

		return out.String()
	}

	// Stocking in progress
	if strings.Contains(path, "/stocking-") {
		selected := ""
		parts := strings.Split(path, "/")
		for _, part := range parts {
			if strings.HasPrefix(part, "stocking-") {
				selected = strings.TrimPrefix(part, "stocking-")
			}
		}

		selectedCount := 0
		if selected != "" && selected != "empty" {
			selectedCount = len(strings.Split(selected, ","))
		}

		out.WriteString(createStepBox("2", "Stock Your Pantry", ""))
		out.WriteString("\n\n")

		out.WriteString(createCountBox(intToString(selectedCount), intToString(len(requiredItems))))
		out.WriteString("\n\n")

		reqStr := strings.Join(requiredItems, ",")

		// Select All button above items
		if selected == reqStr {
			out.WriteString(ufmt.Sprintf("[Clear All](%s:req-%s/stocking-)", realmPath, reqStr))
		} else {
			out.WriteString(ufmt.Sprintf("[✓ Select All](%s:req-%s/stocking-%s)", realmPath, reqStr, reqStr))
		}
		out.WriteString("\n\n")

		out.WriteString(createSelectionBox("Click items you have:"))
		out.WriteString("\n\n")

		out.WriteString("<gno-columns>\n\n")
		for i, item := range requiredItems {
			newSelection := toggleItem(selected, item)

			if contains(selected, item) {
				out.WriteString(ufmt.Sprintf("[✓ %s](%s:req-%s/stocking-%s)", item, realmPath, reqStr, newSelection))
			} else {
				out.WriteString(ufmt.Sprintf("[+ %s](%s:req-%s/stocking-%s)", item, realmPath, reqStr, newSelection))
			}

			if (i+1)%7 == 0 {
				out.WriteString("\n\n</gno-columns>\n\n<gno-columns>\n\n")
			} else if i < len(requiredItems)-1 {
				out.WriteString(" | ")
			}
		}
		out.WriteString("\n\n</gno-columns>\n\n")

		out.WriteString("---\n\n")

		if selectedCount > 0 {
			out.WriteString(ufmt.Sprintf("[Done - Add %s items →](%s:req-%s/stock-%s)\n", intToString(selectedCount), realmPath, reqStr, selected))
		} else {
			out.WriteString(ufmt.Sprintf("[Skip - Empty Pantry](%s:req-%s/stock-empty)\n", realmPath, reqStr))
		}

		return out.String()
	}

	// Main view
	reqStr := strings.Join(requiredItems, ",")
	stockStr := strings.Join(pantryStock, ",")
	if stockStr == "empty" {
		stockStr = ""
		pantryStock = []string{}
	}

	// Calculate shopping list
	shoppingList := []string{}
	for _, item := range requiredItems {
		found := false
		for _, stock := range pantryStock {
			if item == stock {
				found = true
				break
			}
		}
		if !found {
			shoppingList = append(shoppingList, item)
		}
	}

	out.WriteString(createSectionBox("📋 Required Items", intToString(len(requiredItems))))
	out.WriteString("\n\n")

	out.WriteString("> ")
	for i, item := range requiredItems {
		inStock := false
		for _, stock := range pantryStock {
			if item == stock {
				inStock = true
				break
			}
		}
		if inStock {
			out.WriteString(ufmt.Sprintf("✅ **%s**", item))
		} else {
			out.WriteString(ufmt.Sprintf("⚠️ %s", item))
		}
		if i < len(requiredItems)-1 {
			out.WriteString("  \n> ")
		}
	}
	out.WriteString("\n\n")

	out.WriteString("\n\n---\n\n")

	out.WriteString(createSectionBox("🥫 Pantry Stock", intToString(len(pantryStock))))
	out.WriteString("\n\n")

	if len(pantryStock) == 0 {
		out.WriteString("> *Empty - shop for items below!*\n\n")
	} else {
		out.WriteString("> ")
		for i, item := range pantryStock {
			// Remove this item from stock
			newStock := []string{}
			for _, s := range pantryStock {
				if s != item {
					newStock = append(newStock, s)
				}
			}
			newStockStr := strings.Join(newStock, ",")
			if newStockStr == "" {
				newStockStr = "empty"
			}

			out.WriteString(ufmt.Sprintf("**%s** - [Used It](%s:req-%s/stock-%s)",
				item, realmPath, reqStr, newStockStr))
			if i < len(pantryStock)-1 {
				out.WriteString("  \n> ")
			}
		}
		out.WriteString("\n\n")
	}

	out.WriteString("\n---\n\n")

	out.WriteString(createSectionBox("🛒 Shopping List", intToString(len(shoppingList))))
	out.WriteString("\n\n")

	if len(shoppingList) == 0 {
		out.WriteString(createSuccessBox("All stocked up!"))
		out.WriteString("\n\n")
	} else {
		out.WriteString("> ")
		for i, item := range shoppingList {
			// Add this item to stock
			newStock := append(pantryStock, item)
			newStockStr := strings.Join(newStock, ",")

			out.WriteString(ufmt.Sprintf("%s - [✓ Bought](%s:req-%s/stock-%s)",
				item, realmPath, reqStr, newStockStr))
			if i < len(shoppingList)-1 {
				out.WriteString("  \n> ")
			}
		}
		out.WriteString("\n\n")
	}

	out.WriteString("---\n\n")
	out.WriteString(ufmt.Sprintf("[🔄 Start Over](%s) | [📝 Edit Required Items](%s:selecting-%s)\n", 
		realmPath, realmPath, reqStr))

	return out.String()
}

