package grocerylist

package grocerylist

import (
	"strings"
	"gno.land/p/nt/ufmt"
)

// Store pantries by user address using built-in map
var pantries = make(map[string]*UserPantry)

type UserPantry struct {
	Owner         string
	RequiredItems []Item
	PantryStock   []Item
}

type Item struct {
	Name     string
	Quantity string
}

// Get or create pantry for address
func getOrCreatePantry(addrStr string) *UserPantry {
	pantry, exists := pantries[addrStr]
	if exists {
		return pantry
	}

	// Create new pantry
	pantry = &UserPantry{
		Owner:         addrStr,
		RequiredItems: []Item{},
		PantryStock:   []Item{},
	}
	pantries[addrStr] = pantry
	return pantry
}

// BATCH OPERATIONS - Submit multiple changes in one transaction
// Note: Without std package, caller must pass their address as first parameter

// Set required items (replaces entire list)
// Format: address, "Milk:1-gallon,Bread:1-loaf,Eggs:12-count"
func SetRequiredItems(caller string, itemsStr string) {
	pantry := getOrCreatePantry(caller)

	pantry.RequiredItems = []Item{}

	if itemsStr == "" {
		return
	}

	items := strings.Split(itemsStr, ",")
	for _, itemStr := range items {
		parts := strings.Split(itemStr, ":")
		if len(parts) == 2 {
			pantry.RequiredItems = append(pantry.RequiredItems, Item{
				Name:     parts[0],
				Quantity: parts[1],
			})
		}
	}
}

// Set pantry stock (replaces entire list)
// Format: address, "Milk:1-gallon,Bread:1-loaf"
func SetPantryStock(caller string, itemsStr string) {
	pantry := getOrCreatePantry(caller)

	pantry.PantryStock = []Item{}

	if itemsStr == "" {
		return
	}

	items := strings.Split(itemsStr, ",")
	for _, itemStr := range items {
		parts := strings.Split(itemStr, ":")
		if len(parts) == 2 {
			pantry.PantryStock = append(pantry.PantryStock, Item{
				Name:     parts[0],
				Quantity: parts[1],
			})
		}
	}
}

// Add items to required list (batch)
func AddRequiredItems(caller string, itemsStr string) {
	pantry := getOrCreatePantry(caller)

	if itemsStr == "" {
		return
	}

	items := strings.Split(itemsStr, ",")
	for _, itemStr := range items {
		parts := strings.Split(itemStr, ":")
		if len(parts) == 2 {
			pantry.RequiredItems = append(pantry.RequiredItems, Item{
				Name:     parts[0],
				Quantity: parts[1],
			})
		}
	}
}

// Add items to pantry stock (batch)
func AddToPantryStock(caller string, itemsStr string) {
	pantry := getOrCreatePantry(caller)

	if itemsStr == "" {
		return
	}

	items := strings.Split(itemsStr, ",")
	for _, itemStr := range items {
		parts := strings.Split(itemStr, ":")
		if len(parts) == 2 {
			pantry.PantryStock = append(pantry.PantryStock, Item{
				Name:     parts[0],
				Quantity: parts[1],
			})
		}
	}
}

// Remove items from pantry stock by name (batch)
// Format: address, "Milk,Bread,Eggs"
func RemoveFromPantryStock(caller string, itemNamesStr string) {
	pantry := getOrCreatePantry(caller)

	if itemNamesStr == "" {
		return
	}

	namesToRemove := strings.Split(itemNamesStr, ",")
	newStock := []Item{}

	for _, item := range pantry.PantryStock {
		shouldKeep := true
		for _, name := range namesToRemove {
			if item.Name == name {
				shouldKeep = false
				break
			}
		}
		if shouldKeep {
			newStock = append(newStock, item)
		}
	}

	pantry.PantryStock = newStock
}

// Buy items - moves from shopping list to pantry (batch)
// Format: address, "Milk,Bread,Eggs"
func BuyItems(caller string, itemNamesStr string) {
	pantry := getOrCreatePantry(caller)

	if itemNamesStr == "" {
		return
	}

	namesToBuy := strings.Split(itemNamesStr, ",")

	for _, name := range namesToBuy {
		// Find item in required list
		for _, required := range pantry.RequiredItems {
			if required.Name == name {
				// Add to pantry stock
				pantry.PantryStock = append(pantry.PantryStock, Item{
					Name:     required.Name,
					Quantity: required.Quantity,
				})
				break
			}
		}
	}
}

// Clear entire pantry
func ClearPantry(caller string) {
	delete(pantries, caller)
}

// READ-ONLY FUNCTIONS (for displaying data)

// Get user's pantry data by address string
func GetPantry(addr string) (requiredItems []Item, pantryStock []Item) {
	pantry, exists := pantries[addr]
	if !exists {
		return []Item{}, []Item{}
	}

	return pantry.RequiredItems, pantry.PantryStock
}

// Get shopping list (required items not in stock)
func GetShoppingList(addr string) []Item {
	pantry, exists := pantries[addr]
	if !exists {
		return []Item{}
	}

	shoppingList := []Item{}

	for _, required := range pantry.RequiredItems {
		found := false
		for _, stock := range pantry.PantryStock {
			if required.Name == stock.Name {
				found = true
				break
			}
		}
		if !found {
			shoppingList = append(shoppingList, required)
		}
	}

	return shoppingList
}

// Render for viewing in browser
// URL format: realm:address or just realm: for help
func Render(path string) string {
	var out strings.Builder
	out.WriteString("# 🏠 Pantry Manager (On-Chain)\n\n")

	if path == "" {
		out.WriteString("*Manage your grocery list on the blockchain*\n\n")
		out.WriteString("---\n\n")
		out.WriteString("## How to Use\n\n")
		out.WriteString("Visit: `realm:YOUR_ADDRESS` to see your pantry\n\n")
		out.WriteString("**Functions:**\n")
		out.WriteString("- `SetRequiredItems(addr, items)` - Set tracked items\n")
		out.WriteString("- `SetPantryStock(addr, items)` - Set current stock\n")
		out.WriteString("- `BuyItems(addr, items)` - Mark items as purchased\n")
		out.WriteString("- `RemoveFromPantryStock(addr, items)` - Mark items as used\n\n")
		return out.String()
	}

	// Clean up path to get address
	addr := strings.TrimPrefix(path, "/")

	requiredItems, pantryStock := GetPantry(addr)
	shoppingList := GetShoppingList(addr)

	out.WriteString(ufmt.Sprintf("**Address:** `%s`\n\n", addr))
	out.WriteString("---\n\n")

	out.WriteString("## 📋 Required Items (" + intToString(len(requiredItems)) + ")\n\n")
	if len(requiredItems) == 0 {
		out.WriteString("*No required items set. Use function calls to add items.*\n\n")
	} else {
		for _, item := range requiredItems {
			out.WriteString(ufmt.Sprintf("• **%s** - %s\n", item.Name, item.Quantity))
		}
		out.WriteString("\n")
	}

	out.WriteString("---\n\n")

	out.WriteString("## 🥫 Pantry Stock (" + intToString(len(pantryStock)) + ")\n\n")
	if len(pantryStock) == 0 {
		out.WriteString("*Empty - time to go shopping!*\n\n")
	} else {
		for _, item := range pantryStock {
			out.WriteString(ufmt.Sprintf("• **%s** - %s\n", item.Name, item.Quantity))
		}
		out.WriteString("\n")
	}

	out.WriteString("---\n\n")

	out.WriteString("## 🛒 Shopping List (" + intToString(len(shoppingList)) + ")\n\n")
	if len(shoppingList) == 0 {
		out.WriteString("✅ **All stocked up!**\n\n")
	} else {
		for _, item := range shoppingList {
			out.WriteString(ufmt.Sprintf("• %s - %s\n", item.Name, item.Quantity))
		}
		out.WriteString("\n")
	}

	return out.String()
}

// Helper function
func intToString(n int) string {
	if n == 0 {
		return "0"
	}
	negative := n < 0
	if negative {
		n = -n
	}
	result := ""
	for n > 0 {
		result = string(rune('0' + n%10)) + result
		n /= 10
	}
	if negative {
		result = "-" + result
	}
	return result
}

