	out.WriteString("## 🥫 Pantry Stock (" + intToString(len(pantryStock)) + ")\n\n")
	if len(pantryStock) == 0 {
		out.WriteString("*Empty - time to go shopping!*\n\n")
	} else {
		for _, item := range pantryStock {
			out.WriteString(ufmt.Sprintf("• **%s** - %s\n", item.Name, item.Quantity))
		}
		out.WriteString("\n")
	}

	out.WriteString("---\n\n")

	out.WriteString("## 🛒 Shopping List (" + intToString(len(shoppingList)) + ")\n\n")
	if len(shoppingList) == 0 {
		out.WriteString("✅ **All stocked up!**\n\n")
	} else {
		for _, item := range shoppingList {
			out.WriteString(ufmt.Sprintf("• %s - %s\n", item.Name, item.Quantity))
		}
		out.WriteString("\n")
	}

	return out.String()
}

// Helper function
func intToString(n int) string {
	if n == 0 {
		return "0"
	}
	negative := n < 0
	if negative {
		n = -n
	}
	result := ""
	for n > 0 {
		result = string(rune('0' + n%10)) + result
		n /= 10
	}
	if negative {
		result = "-" + result
	}
	return result
}

