func hashString(addr string) []int {
    values := make([]int, 12)
    for i := 0; i < len(addr); i++ {
        c := int(addr[i])
        values[i%12] += c
        values[(i+3)%12] += c * (i + 1)
        values[(i+7)%12] += c * c
    }
    return values
}

func capitalize(s string) string {
    if len(s) == 0 {
        return s
    }
    first := s[0]
    if first >= 'a' && first <= 'z' {
        first = first - 32
    }
    return string(first) + s[1:]
}

func generateFirstName(hash []int) string {
    firstName := ""
    prefixIdx := hash[0] % len(prefixes)
    firstName += prefixes[prefixIdx]
    structure := hash[1] % 8
    if structure == 0 {
        idx1 := hash[2] % len(syllables)
        idx2 := hash[3] % len(syllables)
        firstName += syllables[idx1] + syllables[idx2]
    } else if structure == 1 {
        idx1 := hash[2] % len(syllables)
        idx2 := hash[3] % len(letters)
        idx3 := hash[4] % len(syllables)
        firstName += syllables[idx1] + letters[idx2] + syllables[idx3]
    } else if structure == 2 {
        idx1 := hash[2] % len(letters)
        idx2 := hash[3] % len(syllables)
        idx3 := hash[4] % len(syllables)
        firstName += letters[idx1] + syllables[idx2] + syllables[idx3]
    } else if structure == 3 {
        idx1 := hash[2] % len(syllables)
        idx2 := hash[3] % len(syllables)
        idx3 := hash[4] % len(letters)
        firstName += syllables[idx1] + syllables[idx2] + letters[idx3]
    } else if structure == 4 {
        idx1 := hash[2] % len(letters)
        idx2 := hash[3] % len(syllables)
        idx3 := hash[4] % len(letters)
        idx4 := hash[5] % len(syllables)
        firstName += letters[idx1] + syllables[idx2] + letters[idx3] + syllables[idx4]
    } else if structure == 5 {
        idx1 := hash[2] % len(syllables)
        idx2 := hash[3] % len(letters)
        idx3 := hash[4] % len(letters)
        idx4 := hash[5] % len(syllables)
        firstName += syllables[idx1] + letters[idx2] + letters[idx3] + syllables[idx4]
    } else if structure == 6 {
        idx1 := hash[2] % len(letters)
        idx2 := hash[3] % len(letters)
        idx3 := hash[4] % len(syllables)
        idx4 := hash[5] % len(syllables)
        firstName += letters[idx1] + letters[idx2] + syllables[idx3] + syllables[idx4]
    } else {
        idx1 := hash[2] % len(syllables)
        idx2 := hash[3] % len(letters)
        idx3 := hash[4] % len(syllables)
        idx4 := hash[5] % len(letters)
        firstName += syllables[idx1] + letters[idx2] + syllables[idx3] + letters[idx4]
    }
    suffixIdx := hash[6] % len(suffixes)
    firstName += suffixes[suffixIdx]
    return capitalize(firstName)
}

func generateLastName(hash []int) string {
    adjIdx := hash[8] % len(adjectives)
    nounIdx := hash[9] % len(nouns)
    lastName := adjectives[adjIdx] + nouns[nounIdx]
    return capitalize(lastName)
}

func GetGnomeName(addr string) string {
    hash := hashString(addr)
    firstName := generateFirstName(hash)
    lastName := generateLastName(hash)
    return "Welcome to Gnoland, " + firstName + " " + lastName + "! May your beard grow long and your mushrooms plentiful."
}

func Render(path string) string {
    if path != "" {
        hash := hashString(path)
        firstName := generateFirstName(hash)
        lastName := generateLastName(hash)
        return "# Welcome to Gnoland, " + firstName + " " + lastName + "!\n\n*May your beard grow long and your mushrooms plentiful.*\n\nYou are now part of the Gnome community."
    }
    return "# Gnome Name Registry\n\nEvery address has a unique gnome name.\n\nAdd your address to the URL to discover yours:\n\n`/r/demo/gnomes:youraddress`"
}

