func intToStr(n int) string {
    if n == 0 {
        return "0"
    }
    if n < 0 {
        n = -n
    }
    result := ""
    for n > 0 {
        result = string(rune('0'+n%10)) + result
        n = n / 10
    }
    return result
}

func toHex(n int) string {
