func generateName(addr string) string {
    hash := hashString(addr)
    firstName := generateFirstName(hash)
    lastName := generateLastName(hash)
    return firstName + " " + lastName
}

// Mint SANS argument - gno.land envoie le caller automatiquement
func Mint() string {
    return "Use MintFor(addr) to mint"
}

// MintFor avec 1 argument pour le frontend
func MintFor(addr string) string {
    if addr == "" {
        return "Error: address required"
    }
    if names[addr] != "" {
        num := numbers[addr]
        total := len(owners)
        return "Already minted: " + names[addr] + " #" + intToStr(num) + "/" + intToStr(total)
    }

    name := generateName(addr)
    svg := generateSVG(addr)
    num := len(owners) + 1

    names[addr] = name
    gnomes[addr] = svg
    numbers[addr] = num
    owners = append(owners, addr)

    return "Minted: " + name + " #" + intToStr(num) + "/" + intToStr(num)
}

func GetName(addr string) string {
    if names[addr] != "" {
        return names[addr]
    }
    return generateName(addr)
}

func GetSVG(addr string) string {
    if gnomes[addr] != "" {
        return gnomes[addr]
    }
    return generateSVG(addr)
}

func GetNumber(addr string) string {
    if numbers[addr] != 0 {
        return intToStr(numbers[addr]) + "/" + intToStr(len(owners))
    }
    return "Not minted"
}

func TotalSupply() int {
    return len(owners)
}

func Render(path string) string {
    if path != "" {
        name := generateName(path)
        svg := generateSVG(path)

        if names[path] != "" {
            num := numbers[path]
            total := len(owners)
            return "# " + name + "\n\n**#" + intToStr(num) + "/" + intToStr(total) + "**\n\n" + svg
        }
        return "# " + name + "\n\n**Not minted yet**\n\n" + svg + "\n\nCall Mint to claim!"
    }
    return "# Gnome Registry\n\nTotal minted: " + intToStr(len(owners)) + "\n\nAdd your address to see your Gnome!"
}

