    return result
}

func hashString(addr string) []int {
    values := make([]int, 16)
    for i := 0; i < len(addr); i++ {
        c := int(addr[i])
        values[i%16] += c
        values[(i+3)%16] += c * (i + 1)
        values[(i+7)%16] += c * c
        values[(i+11)%16] += c * 31
    }
    for i := 0; i < 16; i++ {
        if values[i] < 0 {
            values[i] = -values[i]
        }
    }
    return values
}

