// Couleurs cyberpunk: neon sur fond sombre
func makeCyberColor(h1, h2, h3, variant int) string {
    // Palettes neon: cyan, magenta, vert fluo, orange, violet
    palettes := [][]int{
        {0, 255, 255},    // Cyan
        {255, 0, 255},    // Magenta
        {0, 255, 128},    // Vert neon
        {255, 128, 0},    // Orange
        {128, 0, 255},    // Violet
        {255, 255, 0},    // Jaune
        {255, 0, 128},    // Rose
        {0, 128, 255},    // Bleu
    }
    idx := (h1 + variant) % len(palettes)
    base := palettes[idx]

    // Variation légère
    r := (base[0] + (h2 % 60) - 30)
    g := (base[1] + (h3 % 60) - 30)
    b := (base[2] + (h1 % 60) - 30)

    // Clamp values
    if r < 0 { r = 0 }
    if r > 255 { r = 255 }
    if g < 0 { g = 0 }
    if g > 255 { g = 255 }
    if b < 0 { b = 0 }
    if b > 255 { b = 255 }

    return "#" + toHex(r) + toHex(g) + toHex(b)
}

