func intToStr(n int) string {
    if n == 0 {
        return "0"
    }
    if n < 0 {
        n = -n
    }
    result := ""
    for n > 0 {
        result = string(rune('0'+n%10)) + result
        n = n / 10
    }
    return result
}

func hashString(addr string) []int {
    values := make([]int, 32)
    for i := 0; i < len(addr); i++ {
        c := int(addr[i])
        values[i%32] += c
        values[(i+3)%32] += c * (i + 1)
        values[(i+7)%32] += c * c
        values[(i+11)%32] += c * 31
        values[(i+13)%32] += c * 17
        values[(i+19)%32] += c * (c % 7)
    }
    for i := 0; i < 32; i++ {
        if values[i] < 0 {
            values[i] = -values[i]
        }
    }
    return values
}

func capitalize(s string) string {
    if len(s) == 0 {
        return s
    }
    first := s[0]
    if first >= 'a' && first <= 'z' {
        first = first - 32
    }
    return string(first) + s[1:]
}

func generateFirstName(hash []int) string {
    firstName := ""
    prefixIdx := hash[0] % len(prefixes)
    firstName += prefixes[prefixIdx]
    structure := hash[1] % 8
    if structure == 0 {
        firstName += syllables[hash[2]%len(syllables)] + syllables[hash[3]%len(syllables)]
    } else if structure == 1 {
        firstName += syllables[hash[2]%len(syllables)] + letters[hash[3]%len(letters)] + syllables[hash[4]%len(syllables)]
    } else if structure == 2 {
        firstName += letters[hash[2]%len(letters)] + syllables[hash[3]%len(syllables)] + syllables[hash[4]%len(syllables)]
    } else if structure == 3 {
        firstName += syllables[hash[2]%len(syllables)] + syllables[hash[3]%len(syllables)] + letters[hash[4]%len(letters)]
    } else if structure == 4 {
        firstName += letters[hash[2]%len(letters)] + syllables[hash[3]%len(syllables)] + letters[hash[4]%len(letters)] + syllables[hash[5]%len(syllables)]
    } else if structure == 5 {
        firstName += syllables[hash[2]%len(syllables)] + letters[hash[3]%len(letters)] + letters[hash[4]%len(letters)] + syllables[hash[5]%len(syllables)]
    } else if structure == 6 {
        firstName += letters[hash[2]%len(letters)] + letters[hash[3]%len(letters)] + syllables[hash[4]%len(syllables)] + syllables[hash[5]%len(syllables)]
    } else {
        firstName += syllables[hash[2]%len(syllables)] + letters[hash[3]%len(letters)] + syllables[hash[4]%len(syllables)] + letters[hash[5]%len(letters)]
    }
    firstName += suffixes[hash[6]%len(suffixes)]
    return capitalize(firstName)
}

func generateLastName(hash []int) string {
    adjIdx := hash[8] % len(adjectives)
    nounIdx := hash[9] % len(nouns)
    return capitalize(adjectives[adjIdx] + nouns[nounIdx])
}

func toHex(n int) string {
    return hexchar[(n/16)%16] + hexchar[n%16]
}

func clamp(val, min, max int) int {
    if val < min {
        return min
    }
    if val > max {
        return max
    }
    return val
}

func generateSVG(addr string) string {
    h := hashString(addr)

    // === FOND ===
    bgType := h[0] % 6
    var bgR, bgG, bgB int
    if bgType == 0 {
        bgR, bgG, bgB = 5+(h[1]%10), 5+(h[2]%8), 15+(h[3]%15)
    } else if bgType == 1 {
        bgR, bgG, bgB = 10+(h[1]%12), 5+(h[2]%8), 20+(h[3]%20)
    } else if bgType == 2 {
        bgR, bgG, bgB = 5+(h[1]%8), 12+(h[2]%15), 12+(h[3]%15)
    } else if bgType == 3 {
        bgR, bgG, bgB = 15+(h[1]%15), 5+(h[2]%10), 15+(h[3]%15)
    } else if bgType == 4 {
        bgR, bgG, bgB = 8+(h[1]%10), 8+(h[2]%10), 8+(h[3]%10)
    } else {
        bgR, bgG, bgB = 12+(h[1]%12), 8+(h[2]%10), 5+(h[3]%8)
    }
    bgColor := "#" + toHex(bgR) + toHex(bgG) + toHex(bgB)

    // === COULEURS NEON (12 palettes) ===
    neonType := h[4] % 12
    var n1R, n1G, n1B, n2R, n2G, n2B, n3R, n3G, n3B int
    if neonType == 0 {
        n1R, n1G, n1B = 0, 255, 255
        n2R, n2G, n2B = 255, 0, 255
        n3R, n3G, n3B = 0, 255, 128
    } else if neonType == 1 {
        n1R, n1G, n1B = 255, 0, 255
        n2R, n2G, n2B = 0, 255, 255
        n3R, n3G, n3B = 255, 255, 0
    } else if neonType == 2 {
        n1R, n1G, n1B = 0, 255, 128
        n2R, n2G, n2B = 255, 128, 0
        n3R, n3G, n3B = 0, 191, 255
    } else if neonType == 3 {
        n1R, n1G, n1B = 255, 128, 0
        n2R, n2G, n2B = 255, 0, 128
        n3R, n3G, n3B = 255, 255, 0
    } else if neonType == 4 {
        n1R, n1G, n1B = 128, 0, 255
        n2R, n2G, n2B = 0, 255, 255
        n3R, n3G, n3B = 255, 0, 128
    } else if neonType == 5 {
        n1R, n1G, n1B = 255, 255, 0
        n2R, n2G, n2B = 255, 128, 0
        n3R, n3G, n3B = 255, 64, 64
    } else if neonType == 6 {
        n1R, n1G, n1B = 255, 0, 128
        n2R, n2G, n2B = 128, 0, 255
        n3R, n3G, n3B = 255, 0, 255
    } else if neonType == 7 {
        n1R, n1G, n1B = 0, 191, 255
        n2R, n2G, n2B = 0, 255, 191
        n3R, n3G, n3B = 0, 255, 255
    } else if neonType == 8 {
        n1R, n1G, n1B = 255, 64, 64
        n2R, n2G, n2B = 255, 128, 0
        n3R, n3G, n3B = 255, 0, 128
    } else if neonType == 9 {
        n1R, n1G, n1B = 0, 255, 191
        n2R, n2G, n2B = 0, 255, 128
        n3R, n3G, n3B = 128, 255, 128
    } else if neonType == 10 {
        n1R, n1G, n1B = 255, 191, 0
        n2R, n2G, n2B = 255, 128, 64
        n3R, n3G, n3B = 255, 64, 0
    } else {
        n1R, n1G, n1B = 191, 0, 255
        n2R, n2G, n2B = 128, 0, 191
        n3R, n3G, n3B = 255, 0, 191
    }
    neonPrimary := "#" + toHex(n1R) + toHex(n1G) + toHex(n1B)
    neonSecondary := "#" + toHex(n2R) + toHex(n2G) + toHex(n2B)
    neonAccent := "#" + toHex(n3R) + toHex(n3G) + toHex(n3B)

    // === PEAU (8 types) ===
    skinType := h[5] % 8
    var skinR, skinG, skinB int
    if skinType == 0 {
        skinR, skinG, skinB = 70+(h[6]%25), 75+(h[6]%25), 90+(h[6]%25)
    } else if skinType == 1 {
        skinR, skinG, skinB = 95+(h[6]%25), 85+(h[6]%20), 80+(h[6]%15)
    } else if skinType == 2 {
        skinR, skinG, skinB = 60+(h[6]%20), 80+(h[6]%30), 110+(h[6]%30)
    } else if skinType == 3 {
        skinR, skinG, skinB = 70+(h[6]%25), 100+(h[6]%30), 80+(h[6]%20)
    } else if skinType == 4 {
        skinR, skinG, skinB = 100+(h[6]%30), 80+(h[6]%25), 110+(h[6]%30)
    } else if skinType == 5 {
        skinR, skinG, skinB = 110+(h[6]%30), 100+(h[6]%25), 70+(h[6]%20)
    } else if skinType == 6 {
        skinR, skinG, skinB = 90+(h[6]%30), 90+(h[6]%30), 90+(h[6]%30)
    } else {
        skinR, skinG, skinB = 70+(h[6]%20), 90+(h[6]%25), 100+(h[6]%30)
    }
    skinColor := "#" + toHex(skinR) + toHex(skinG) + toHex(skinB)
    skinDark := "#" + toHex(clamp(skinR-20, 0, 255)) + toHex(clamp(skinG-15, 0, 255)) + toHex(clamp(skinB-10, 0, 255))

    // === BARBE (6 couleurs) ===
    beardType := h[7] % 6
    var beardR, beardG, beardB int
    if beardType == 0 {
        beardR, beardG, beardB = 25+(h[8]%15), 25+(h[8]%15), 30+(h[8]%15)
    } else if beardType == 1 {
        beardR, beardG, beardB = 35+(h[8]%20), 30+(h[8]%15), 25+(h[8]%10)
    } else if beardType == 2 {
        beardR, beardG, beardB = 20+(h[8]%10), 25+(h[8]%15), 35+(h[8]%20)
    } else if beardType == 3 {
        beardR, beardG, beardB = 40+(h[8]%20), 40+(h[8]%20), 40+(h[8]%20)
    } else if beardType == 4 {
        beardR, beardG, beardB = 30+(h[8]%15), 20+(h[8]%10), 35+(h[8]%20)
    } else {
        beardR, beardG, beardB = 25+(h[8]%15), 30+(h[8]%18), 28+(h[8]%15)
    }
    beardColor := "#" + toHex(beardR) + toHex(beardG) + toHex(beardB)

    // === VETEMENTS ===
    shirtType := h[9] % 5
    var shirtR, shirtG, shirtB int
    if shirtType == 0 {
        shirtR, shirtG, shirtB = 15+(h[10]%15), 15+(h[10]%12), 25+(h[10]%20)
    } else if shirtType == 1 {
        shirtR, shirtG, shirtB = 25+(h[10]%20), 15+(h[10]%12), 15+(h[10]%12)
    } else if shirtType == 2 {
        shirtR, shirtG, shirtB = 15+(h[10]%12), 25+(h[10]%18), 20+(h[10]%15)
    } else if shirtType == 3 {
        shirtR, shirtG, shirtB = 20+(h[10]%15), 20+(h[10]%15), 20+(h[10]%15)
    } else {
        shirtR, shirtG, shirtB = 18+(h[10]%15), 15+(h[10]%12), 28+(h[10]%20)
    }
    shirtColor := "#" + toHex(shirtR) + toHex(shirtG) + toHex(shirtB)
    pantsColor := "#" + toHex(shirtR-5) + toHex(shirtG-5) + toHex(shirtB-5)

    // === TRAITS VARIABLES ===
    hatHeight := 28 + (h[11] % 40)
    hatStyle := h[12] % 6
    beardLen := 18 + (h[13] % 50)
    beardStyle := h[14] % 5
    visorStyle := h[15] % 8
    bgPattern := h[16] % 8
    earStyle := h[17] % 4
    accessory1 := h[18] % 6
    accessory2 := h[19] % 5
    shirtDetail := h[20] % 5
    hatDetail := h[21] % 5
    beardDetail := h[22] % 4
    noseSize := 5 + (h[23] % 7)

    hatTop := intToStr(95 - hatHeight)
    beardEnd := intToStr(130 + beardLen)
    beardTip := intToStr(135 + beardLen)

    svg := "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 200 200'>"

    // Defs
