    svg := "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 200 200'>"

    // Defs
    svg += "<defs>"
    svg += "<filter id='g'><feGaussianBlur stdDeviation='2' result='b'/><feMerge><feMergeNode in='b'/><feMergeNode in='SourceGraphic'/></feMerge></filter>"
    svg += "<filter id='g2'><feGaussianBlur stdDeviation='3' result='b'/><feMerge><feMergeNode in='b'/><feMergeNode in='SourceGraphic'/></feMerge></filter>"
    svg += "</defs>"

    // Fond
    svg += "<rect width='200' height='200' fill='" + bgColor + "'/>"

    // Pattern fond (8 types)
    if bgPattern == 0 {
        svg += "<path d='M0 0L0 200M20 0L20 200M40 0L40 200M60 0L60 200M80 0L80 200M100 0L100 200M120 0L120 200M140 0L140 200M160 0L160 200M180 0L180 200' stroke='" + neonPrimary + "' stroke-width='.3' opacity='.12'/>"
