    } else if bgPattern == 1 {
        svg += "<path d='M0 180L40 180L50 170L90 170L100 180L200 180' stroke='" + neonPrimary + "' stroke-width='1' fill='none' opacity='.2'/>"
        svg += "<path d='M0 20L60 20L70 30L130 30L140 20L200 20' stroke='" + neonSecondary + "' stroke-width='1' fill='none' opacity='.2'/>"
        svg += "<circle cx='50' cy='170' r='3' fill='" + neonPrimary + "' opacity='.3'/>"
        svg += "<circle cx='140' cy='20' r='3' fill='" + neonSecondary + "' opacity='.3'/>"
    } else if bgPattern == 2 {
        svg += "<text x='5' y='15' font-size='8' fill='" + neonPrimary + "' opacity='.15' font-family='monospace'>01101001</text>"
