func generateName(addr string) string {
    hash := hashString(addr)
    firstName := generateFirstName(hash)
    lastName := generateLastName(hash)
    return firstName + " " + lastName
}

func Mint(_ realm, addr string) string {
    if addr == "" {
        return "Error: address required"
    }
    if names[addr] != "" {
        num := numbers[addr]
        return "Already minted: " + names[addr] + " #" + intToStr(num) + "/" + intToStr(maxSupply)
    }
    if len(owners) >= maxSupply {
        return "Error: Max supply of " + intToStr(maxSupply) + " reached"
    }

    name := generateName(addr)
    svg := generateSVG(addr)
    num := len(owners) + 1

    names[addr] = name
    gnomes[addr] = svg
    numbers[addr] = num
    owners = append(owners, addr)

    return "Minted: " + name + " #" + intToStr(num) + "/" + intToStr(maxSupply)
}

func GetName(addr string) string {
    if names[addr] != "" {
        return names[addr]
    }
    return generateName(addr)
}

func GetSVG(addr string) string {
    if gnomes[addr] != "" {
        return gnomes[addr]
    }
    return generateSVG(addr)
}

func GetNumber(addr string) string {
    if numbers[addr] != 0 {
        return intToStr(numbers[addr]) + "/" + intToStr(maxSupply)
    }
    return "Not minted"
}

func TotalSupply() int {
    return len(owners)
}

func MaxSupply() int {
    return maxSupply
}

// GetOwners returns all minted addresses separated by commas
func GetOwners() string {
    result := ""
    for i, addr := range owners {
        if i > 0 {
            result += ","
        }
        result += addr
    }
    return result
}

// GetOwnerByNumber returns the owner address by mint number (1-indexed)
func GetOwnerByNumber(num int) string {
    if num < 1 || num > len(owners) {
        return ""
    }
    return owners[num-1]
}

func Render(path string) string {
    if path != "" {
        name := generateName(path)
        svg := generateSVG(path)

        if names[path] != "" {
            num := numbers[path]
            return "# " + name + "\n\n**#" + intToStr(num) + "/" + intToStr(maxSupply) + "**\n\n" + svg
        }
        return "# " + name + "\n\n**Not minted yet**\n\n" + svg + "\n\nCall Mint to claim!"
    }
    return "# CypherGnome Registry\n\n**" + intToStr(len(owners)) + "/" + intToStr(maxSupply) + " minted**\n\nAdd your address to see your CypherGnome!"
}

