package gnobounty

import (
	"chain/runtime"
	"time"

	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

// ApplyForBounty allows a user to apply for a bounty with their merged PR link
// This creates an application with a private DAO for 3 random validators to vote
func ApplyForBounty(_ realm, bountyID uint64, prLink string) uint64 {
	caller := runtime.OriginCaller()

	// Validate inputs
	if prLink == "" {
		panic("PR link cannot be empty")
	}

	// Check bounty exists and is not claimed
	bounty := GetBounty(bountyID)
	if bounty == nil {
		panic("bounty not found")
	}

	if bounty.IsClaimed {
		panic("bounty already claimed")
	}

	// Check if there are enough validators
	activeValidatorCount := GetActiveValidatorCount()
	if activeValidatorCount < validatorsPerDAO {
		panic(ufmt.Sprintf("not enough validators: need %d, have %d", validatorsPerDAO, activeValidatorCount))
	}

	// Check if user already has a pending application for this bounty
	applications.Iterate("", "", func(key string, value interface{}) bool {
		app := value.(*Application)
		if app.BountyID == bountyID && app.Applicant == caller && app.Status == StatusPending {
			panic("you already have a pending application for this bounty")
		}
		return false
	})

	// Select 3 random validators for this application
	selectedValidators := selectRandomValidators(applicationCount+1, validatorsPerDAO)

	// Create the DAO for this application
	applicationCount++
	daoName := ufmt.Sprintf("Bounty #%d Application #%d Review DAO", bountyID, applicationCount)
	daoDesc := ufmt.Sprintf("Private DAO to review application for bounty #%d", bountyID)

	// Create DAO with selected validators as members
	// DisableVotingDeadlineCheck allows immediate execution after votes
	dao := commondao.New(
		commondao.WithID(applicationCount),
		commondao.WithName(daoName),
		commondao.WithDescription(daoDesc),
		commondao.DisableVotingDeadlineCheck(),
	)

	// Add selected validators as members
	for _, validatorAddr := range selectedValidators {
		dao.Members().Add(validatorAddr)
	}

	// Create the application
	application := &Application{
		ID:         applicationCount,
		BountyID:   bountyID,
		Applicant:  caller,
		PRLink:     prLink,
		AppliedAt:  time.Now(),
		Status:     StatusPending,
		DAO:        dao,
		ProposalID: 0, // Will be set when proposal is created
	}

	// Create a proposal in the DAO to approve this application
	proposalDef := NewClaimBountyProposal(bountyID, applicationCount, caller, prLink)

	proposal := dao.MustPropose(caller, proposalDef)
	application.ProposalID = proposal.ID()

	// Store application
	applications.Set(ufmt.Sprintf("%d", applicationCount), application)

	return applicationCount
}

// Vote allows a selected validator to vote on an application via the DAO
func Vote(_ realm, applicationID uint64, vote string) {
	caller := runtime.OriginCaller()

	// Check if caller is a validator
	if !IsValidator(caller) {
		panic("only validators can vote")
	}

	// Get application
	app := GetApplication(applicationID)
	if app == nil {
		panic("application not found")
	}

	if app.Status != StatusPending {
		panic("application is no longer pending")
	}

	// Check if caller is a member of this application's DAO
	if !app.DAO.Members().Has(caller) {
		panic("you are not a validator for this application")
	}

	// Get the proposal
	proposal := app.DAO.ActiveProposals().Get(app.ProposalID)
	if proposal == nil {
		panic("proposal not found")
	}

	// Convert string vote to VoteChoice
	var voteChoice commondao.VoteChoice
	switch vote {
	case "yes", "true", "approve":
		voteChoice = commondao.ChoiceYes
	case "no", "false", "reject":
		voteChoice = commondao.ChoiceNo
	case "abstain":
		voteChoice = commondao.ChoiceAbstain
	default:
		panic("invalid vote: must be 'yes', 'no', or 'abstain'")
	}

	// Vote on the proposal using CommonDAO's Vote method
	// This method validates membership, deadline, and vote choice
	err := app.DAO.Vote(caller, app.ProposalID, voteChoice, "")
	if err != nil {
		panic(err)
	}

	// Record vote locally
	app.Votes = append(app.Votes, VoteRecord{
		Voter:  caller,
		Choice: voteChoice,
	})

	// Try to execute the proposal
	// Tally() will return true when all validators have voted
	// Execute() will handle both approval and rejection
	proposal = app.DAO.ActiveProposals().Get(app.ProposalID)
	if proposal != nil {
		totalMembers := app.DAO.Members().Size()
		totalVotes := proposal.VotingRecord().Size()

		// If all validators have voted, execute to finalize
		if totalVotes == totalMembers {
			err = app.DAO.Execute(app.ProposalID)
			// Ignore errors - proposal might already be executed or failed
			// The status will be updated in Execute() regardless
		}
	}
}

// GetApplicationsForBounty returns all applications for a specific bounty
func GetApplicationsForBounty(bountyID uint64) []*Application {
	apps := make([]*Application, 0)

	applications.Iterate("", "", func(key string, value interface{}) bool {
		app := value.(*Application)
		if app.BountyID == bountyID {
			apps = append(apps, app)
		}
		return false
	})

	return apps
}

// GetApplicationsForValidator returns all applications assigned to a validator
func GetApplicationsForValidator(validatorAddr address) []*Application {
	apps := make([]*Application, 0)

	applications.Iterate("", "", func(key string, value interface{}) bool {
		app := value.(*Application)
		// Check if validator is a member of the application's DAO
		if app.DAO != nil && app.Status == StatusPending {
			if app.DAO.Members().Has(validatorAddr) {
				apps = append(apps, app)
			}
		}
		return false
	})

	return apps
}

package gnobounty

import (
	"testing"

	"gno.land/p/nt/ownable"
	"gno.land/p/nt/testutils"
)

func TestAddValidator(t *testing.T) {
	// Setup owner
	adminAddr := testutils.TestAddress("admin")
	testing.SetOriginCaller(adminAddr)
	ownership = ownable.New()

	// Add a validator
	validator1 := testutils.TestAddress("validator1")
	AddValidator(cross, validator1)

	if !IsValidator(validator1) {
		t.Error("Validator should be active")
	}

	count := GetActiveValidatorCount()
	if count < 1 {
		t.Error("Expected at least 1 active validator")
	}
}

func TestValidatorList(t *testing.T) {
	// Setup owner
	adminAddr := testutils.TestAddress("admin")
	testing.SetOriginCaller(adminAddr)
	ownership = ownable.New()

	// Add more validators
	validator2 := testutils.TestAddress("validator2")

	// Check if already exists before adding
	if !IsValidator(validator2) {
		AddValidator(cross, validator2)
	}

	if !IsValidator(validator2) {
		t.Error("Validator2 should be active")
	}

	count := GetActiveValidatorCount()
	if count < 2 {
		t.Errorf("Expected at least 2 active validators, got %d", count)
	}
}

func TestListValidators(t *testing.T) {
	list := ListValidators()
	if list == "" {
		t.Error("Expected non-empty validators list")
	}
}

func TestIsValidatorCheck(t *testing.T) {
	// Test a non-validator address
	nonValidator := testutils.TestAddress("nonvalidator")
	if IsValidator(nonValidator) {
		t.Error("Non-validator should not be recognized as validator")
	}

	// Test existing validator
	validator1 := testutils.TestAddress("validator1")
	if !IsValidator(validator1) {
		t.Error("validator1 should be recognized as validator")
	}
}

func TestGetValidator(t *testing.T) {
	validator1 := testutils.TestAddress("validator1")
	v := GetValidator(validator1)

	if v == nil {
		t.Fatal("Expected to find validator1")
	}

	if v.Address != validator1 {
		t.Error("Validator address mismatch")
	}

	if !v.Active {
		t.Error("Validator should be active")
	}
}

package gnobounty

import (
	"chain"
	"testing"

	"gno.land/p/nt/testutils"
)

func TestCreateBounty(t *testing.T) {
	// Setup test environment
	testing.SetOriginCaller(testutils.TestAddress("alice"))
	testing.SetOriginSend(chain.Coins{chain.Coin{Denom: "ugnot", Amount: 5000000}})

	// Create a bounty
	bountyID := CreateBounty(
		cross,
		"Fix Rendering Bug",
		"https://github.com/gnolang/gno/issues/1234",
		"Fix the rendering issue in the GRC20 token display",
	)

	if bountyID != 1 {
		t.Errorf("Expected bounty ID 1, got %d", bountyID)
	}

	// Check bounty was created
	bounty := GetBounty(bountyID)
	if bounty == nil {
		t.Fatal("Bounty not found")
	}

	if bounty.Amount != 5000000 {
		t.Errorf("Expected amount 5000000, got %d", bounty.Amount)
	}

	if bounty.IsClaimed {
		t.Error("Bounty should not be claimed yet")
	}

	if bounty.Title != "Fix Rendering Bug" {
		t.Errorf("Expected title 'Fix Rendering Bug', got %s", bounty.Title)
	}
}

func TestGetBountyCount(t *testing.T) {
	count := GetBountyCount()
	if count < 1 {
		t.Error("Expected at least 1 bounty from previous test")
	}
}

func TestListBounties(t *testing.T) {
	list := ListBounties()
	if list == "" {
		t.Error("Expected non-empty bounties list")
	}
	if list == "No bounties available" {
		t.Error("Expected bounties to be listed")
	}
}

func TestGetBountyDetails(t *testing.T) {
	// Test getting details for existing bounty
	details := GetBountyDetails(1)
	if details == "" {
		t.Error("Expected non-empty bounty details")
	}
	if details == "Bounty not found" {
		t.Error("Bounty #1 should exist")
	}
}

func TestGetBountyDetailsNotFound(t *testing.T) {
	// Test getting details for non-existent bounty
	details := GetBountyDetails(99999)
	if details != "Bounty not found" {
		t.Error("Expected 'Bounty not found' for non-existent bounty")
	}
}

package gnobounty

import (
	"gno.land/p/nt/avl"
	"gno.land/p/nt/ufmt"
)

// LeaderboardEntry holds the stats for a user
type LeaderboardEntry struct {
	Address              address
	BountiesCreated      int
	BountiesApplied      int
	ValidationsPerformed int
	Score                int
}

// GetLeaderboard aggregates stats for all users
func GetLeaderboard() []LeaderboardEntry {
	var stats avl.Tree // address -> *LeaderboardEntry

	// Count bounties created (10 points)
	bounties.Iterate("", "", func(key string, value interface{}) bool {
		if value == nil {
			return false
		}
		bounty, ok := value.(*Bounty)
		if !ok || bounty == nil {
			return false
		}
		entry := getEntry(&stats, bounty.Creator)
		entry.BountiesCreated++
		entry.Score += 10
		return false
	})

	// Count applications and validations
	applications.Iterate("", "", func(key string, value interface{}) bool {
		if value == nil {
			return false
		}
		app, ok := value.(*Application)
		if !ok || app == nil {
			return false
		}

		// Only count approved applications (20 points)
		if app.Status == StatusApproved {
			entry := getEntry(&stats, app.Applicant)
			entry.BountiesApplied++
			entry.Score += 20
		}

		// Count validations from local record (5 points)
		for _, vote := range app.Votes {
			entry := getEntry(&stats, vote.Voter)
			entry.ValidationsPerformed++
			entry.Score += 5
		}

		return false
	})

	// Convert tree to slice
	var result []LeaderboardEntry
	stats.Iterate("", "", func(key string, value interface{}) bool {
		entry := value.(*LeaderboardEntry)
		result = append(result, *entry)
		return false
	})

	// Sort by Score descending
	// Manual bubble sort since sort.Slice is not available
	for i := 0; i < len(result)-1; i++ {
		for j := 0; j < len(result)-i-1; j++ {
			if result[j].Score < result[j+1].Score {
				// Swap
				result[j], result[j+1] = result[j+1], result[j]
			}
		}
	}

	return result
}

// RenderLeaderboard renders the leaderboard page
func RenderLeaderboard() string {
	entries := GetLeaderboard()

	output := "# üèÜ Community Leaderboard\n\n"
	output += "Top contributors to the GnoBounty ecosystem.\n\n"
	output += "**Scoring:** Bounty Creation (10pts) | Approved Application (20pts) | Validation (5pts)\n\n"

	output += "| Rank | User | Score | üí∞ Bounties | üìù Applications | ‚öñÔ∏è Validations |\n"
	output += "|---|---|---|---|---|---|\n"

	for i, entry := range entries {
		rank := ""
		switch i {
		case 0:
			rank = "ü•á"
		case 1:
			rank = "ü•à"
		case 2:
			rank = "ü•â"
		default:
			rank = ufmt.Sprintf("%d", i+1)
		}

		output += ufmt.Sprintf("| %s | %s | **%d** | %d | %d | %d |\n",
			rank,
			entry.Address,
			entry.Score,
			entry.BountiesCreated,
			entry.BountiesApplied,
			entry.ValidationsPerformed,
		)
	}

	if len(entries) == 0 {
		output += "\nNo activity recorded yet."
	}

	return output
}

func getEntry(stats *avl.Tree, addr address) *LeaderboardEntry {
	key := string(addr)
	val, ok := stats.Get(key)
	if !ok {
		entry := &LeaderboardEntry{Address: addr}
		stats.Set(key, entry)
		return entry
	}
	return val.(*LeaderboardEntry)
}

package gnobounty

import (
	"chain"
	"chain/banker"
	"chain/runtime"
	"time"

	"gno.land/p/nt/ufmt"
)

// CreateBounty creates a new bounty post
// title: short title for the bounty
// issueURL: link to the GitHub issue or other issue tracker
// description: description of what needs to be done
// Caller must send GNOT tokens with this transaction
func CreateBounty(_ realm, title, issueURL, description string) uint64 {
	// Validate inputs
	if title == "" {
		panic("title is required")
	}
	if issueURL == "" {
		panic("issue URL is required")
	}
	if description == "" {
		panic("description is required")
	}

	// Get the sent coins
	sent := banker.OriginSend()
	if len(sent) == 0 {
		panic("no coins sent with bounty")
	}

	// Find GNOT amount
	var gnotAmount int64
	for _, coin := range sent {
		if coin.Denom == "ugnot" {
			gnotAmount = coin.Amount
			break
		}
	}

	if gnotAmount < minimumBounty {
		panic(ufmt.Sprintf("bounty amount must be at least %d ugnot", minimumBounty))
	}

	// Create bounty
	bountyCount++
	bounty := &Bounty{
		ID:          bountyCount,
		Title:       title,
		IssueURL:    issueURL,
		Description: description,
		Amount:      gnotAmount,
		Creator:     runtime.OriginCaller(),
		CreatedAt:   time.Now(),
		IsClaimed:   false,
	}

	// Store bounty
	bounties.Set(ufmt.Sprintf("%d", bountyCount), bounty)

	return bountyCount
}

// ClaimBounty allows someone to claim a bounty
// Only the creator can approve and transfer funds
func ClaimBounty(_ realm, id uint64, claimer address) {
	caller := runtime.OriginCaller()

	bounty := GetBounty(id)
	if bounty == nil {
		panic("bounty not found")
	}

	if bounty.IsClaimed {
		panic("bounty already claimed")
	}

	if caller != bounty.Creator {
		panic("only bounty creator can approve claims")
	}

	if claimer == "" {
		panic("claimer address cannot be empty")
	}

	// Mark as claimed
	bounty.IsClaimed = true
	bounty.Claimer = claimer
	bounty.ClaimedAt = time.Now()

	// Transfer bounty amount to claimer
	bnk := banker.NewBanker(banker.BankerTypeRealmSend)
	send := chain.Coins{chain.Coin{Denom: "ugnot", Amount: bounty.Amount}}
	pkgAddr := chain.PackageAddress("gno.land/r/greg007/gnobounty")
	bnk.SendCoins(pkgAddr, claimer, send)
}

// CancelBounty allows creator to cancel and get refund
func CancelBounty(_ realm, id uint64) {
	caller := runtime.OriginCaller()

	bounty := GetBounty(id)
	if bounty == nil {
		panic("bounty not found")
	}

	if bounty.IsClaimed {
		panic("cannot cancel claimed bounty")
	}

	if caller != bounty.Creator {
		panic("only bounty creator can cancel")
	}

	// Remove bounty first (Checks-Effects-Interactions)
	bounties.Remove(ufmt.Sprintf("%d", id))

	// Refund the creator
	bnk := banker.NewBanker(banker.BankerTypeRealmSend)
	send := chain.Coins{chain.Coin{Denom: "ugnot", Amount: bounty.Amount}}
	pkgAddr := chain.PackageAddress("gno.land/r/greg007/gnobounty")
	bnk.SendCoins(pkgAddr, bounty.Creator, send)
}

package gnobounty

import (
	"chain"
	"chain/banker"
	"time"

	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

// ClaimBountyProposal implements commondao.ProposalDefinition for bounty claim proposals
type ClaimBountyProposal struct {
	BountyID      uint64
	ApplicationID uint64
	Applicant     address
	PRLink        string
	title         string
	description   string
}

// NewClaimBountyProposal creates a new bounty claim proposal definition
func NewClaimBountyProposal(bountyID, applicationID uint64, applicant address, prLink string) *ClaimBountyProposal {
	return &ClaimBountyProposal{
		BountyID:      bountyID,
		ApplicationID: applicationID,
		Applicant:     applicant,
		PRLink:        prLink,
		title:         ufmt.Sprintf("Approve Application #%d for Bounty #%d", applicationID, bountyID),
		description:   ufmt.Sprintf("Review PR: %s\nApplicant: %s", prLink, applicant),
	}
}

// Title returns proposal title
func (p *ClaimBountyProposal) Title() string {
	return p.title
}

// Body returns proposal's body
func (p *ClaimBountyProposal) Body() string {
	return p.description
}

// VotingPeriod returns the period where votes are allowed after proposal creation
func (p *ClaimBountyProposal) VotingPeriod() time.Duration {
	// 7 days voting period
	return 7 * 24 * time.Hour
}

// Tally counts votes and determines if proposal is ready to execute
// Returns true when all validators have voted (regardless of outcome)
func (p *ClaimBountyProposal) Tally(ctx commondao.VotingContext) (bool, error) {
	totalMembers := ctx.Members.Size()
	if totalMembers == 0 {
		return false, ufmt.Errorf("no members in DAO")
	}

	totalVotes := ctx.VotingRecord.Size()

	// Return true when all validators have voted
	// This allows Execute to be called and handle approval/rejection
	if totalVotes == totalMembers {
		return true, nil
	}

	// Not ready yet - waiting for more votes
	return false, nil
}

// Execute executes the proposal after all votes are in
// Determines approval or rejection based on vote counts
func (p *ClaimBountyProposal) Execute(_ realm) error {
	app := GetApplication(p.ApplicationID)
	if app == nil {
		return ufmt.Errorf("application not found")
	}

	bounty := GetBounty(p.BountyID)
	if bounty == nil {
		return ufmt.Errorf("bounty not found")
	}

	if bounty.IsClaimed {
		return ufmt.Errorf("bounty already claimed")
	}

	// Get vote counts from the DAO proposal
	proposal := app.DAO.ActiveProposals().Get(app.ProposalID)
	if proposal == nil {
		return ufmt.Errorf("proposal not found")
	}

	yesCount := proposal.VotingRecord().VoteCount(commondao.ChoiceYes)
	noCount := proposal.VotingRecord().VoteCount(commondao.ChoiceNo)
	totalMembers := app.DAO.Members().Size()

	// Check if unanimous approval (all YES, no NO)
	if yesCount == totalMembers && noCount == 0 {
		// Approve and transfer funds
		app.Status = StatusApproved

		bounty.IsClaimed = true
		bounty.Claimer = p.Applicant
		bounty.ClaimedAt = time.Now()

		// Calculate reward split: 5% to validators, 95% to applicant
		validatorReward := (bounty.Amount * validatorRewardPercent) / 100
		applicantReward := bounty.Amount - validatorReward

		// Split validator reward equally among all validators who voted
		rewardPerValidator := validatorReward / int64(totalMembers)

		// Transfer rewards
		bnk := banker.NewBanker(banker.BankerTypeRealmSend)
		pkgAddr := chain.PackageAddress("gno.land/r/greg007/gnobounty")

		// Pay each validator their share
		app.DAO.Members().IterateByOffset(0, totalMembers, func(member address) bool {
			validatorPayout := chain.Coins{chain.Coin{Denom: "ugnot", Amount: rewardPerValidator}}
			bnk.SendCoins(pkgAddr, member, validatorPayout)
			return false
		})

		// Pay the applicant the remaining 95%
		applicantPayout := chain.Coins{chain.Coin{Denom: "ugnot", Amount: applicantReward}}
		bnk.SendCoins(pkgAddr, p.Applicant, applicantPayout)
	} else {
		// Reject - not unanimous
		app.Status = StatusRejected
	}

	return nil
}

package gnobounty

import (
	"strconv"

	"gno.land/p/moul/md"
	"gno.land/p/moul/txlink"
	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

// ListBounties returns all active bounties
func ListBounties() string {
	minSend := ufmt.Sprintf("%dugnot", minimumBounty)
	createBountyLink := txlink.NewLink("CreateBounty").
		AddArgs(
			"title", "",
			"issueURL", "",
			"description", "",
		).
		SetSend(minSend).
		URL()

	output := "# GnoBounty - Decentralized Bounty System\n\n"

	output += "## üéØ Quick Actions\n\n"
	output += md.Link("üí∞ Create a new bounty", createBountyLink)
	output += ufmt.Sprintf(" _(minimum reward: %s)_", minSend)
	output += " | "
	output += md.Link("üìã View All Validators", "/r/greg007/gnobounty:validators")
	output += " | "
	output += md.Link("üèÜ Leaderboard", "/r/greg007/gnobounty:leaderboard")
	output += "\n\n"

	// Add validator stats
	activeValidatorCount := GetActiveValidatorCount()
	output += ufmt.Sprintf("**Active Validators:** %d | **Total Bounties:** %d\n\n", activeValidatorCount, bountyCount)

	output += "---\n\n"
	output += "## üíé Active Bounties\n\n"
	output += "**How to apply:** To apply to a bounty, you need to give your **MERGED PR** (important) URL in the transaction. You also need to write your Gno address in the description of your PR to verify your identity. If no address is written, the bounty can be refused.\n\n"

	if bountyCount == 0 {
		output += "No bounties available"
		return output
	}

	bounties.Iterate("", "", func(key string, value interface{}) bool {
		bounty := value.(*Bounty)
		if !bounty.IsClaimed {
			output += ufmt.Sprintf("## Bounty #%d: %s\n", bounty.ID, bounty.Title)
			output += ufmt.Sprintf("**Issue:** %s\n", bounty.IssueURL)
			output += ufmt.Sprintf("**Description:** %s\n", bounty.Description)
			output += ufmt.Sprintf("**Reward:** %d ugnot (%.2f GNOT)\n", bounty.Amount, float64(bounty.Amount)/1000000.0)
			output += ufmt.Sprintf("**Creator:** %s\n", bounty.Creator)
			output += ufmt.Sprintf("**Created:** %s\n\n", bounty.CreatedAt.Format("2006-01-02 15:04:05"))

			// Add apply button for this bounty
			applyLink := txlink.NewLink("ApplyForBounty").
				AddArgs(
					"bountyID", ufmt.Sprintf("%d", bounty.ID),
					"prLink", "",
				).
				URL()
			output += md.Link("üìù Apply for this bounty", applyLink)
			output += " | "

			// Add view details link
			detailsPath := ufmt.Sprintf("/r/greg007/gnobounty:%d", bounty.ID)
			output += md.Link("üîç View details", detailsPath)
			output += "\n\n"
		}
		return false
	})

	return output
}

// GetBountyDetails returns detailed information about a specific bounty
func GetBountyDetails(id uint64) string {
	bounty := GetBounty(id)
	if bounty == nil {
		return "Bounty not found"
	}

	output := ufmt.Sprintf("# Bounty #%d: %s\n\n", bounty.ID, bounty.Title)
	output += ufmt.Sprintf("**Issue URL:** %s\n", bounty.IssueURL)
	output += ufmt.Sprintf("**Description:** %s\n", bounty.Description)
	output += ufmt.Sprintf("**Reward:** %d ugnot (%.2f GNOT)\n", bounty.Amount, float64(bounty.Amount)/1000000.0)
	output += ufmt.Sprintf("**Creator:** %s\n", bounty.Creator)
	output += ufmt.Sprintf("**Created:** %s\n", bounty.CreatedAt.Format("2006-01-02 15:04:05"))

	if bounty.IsClaimed {
		output += ufmt.Sprintf("**Status:** CLAIMED\n")
		output += ufmt.Sprintf("**Claimed by:** %s\n", bounty.Claimer)
		output += ufmt.Sprintf("**Claimed at:** %s\n", bounty.ClaimedAt.Format("2006-01-02 15:04:05"))
	} else {
		output += ufmt.Sprintf("**Status:** OPEN\n\n")
		output += "**How to apply:** To apply to a bounty, you need to give your **MERGED PR** (important) URL in the transaction. You also need to write your Gno address in the description of your PR to verify your identity. If no address is written, the bounty can be refused.\n\n"

		// Add apply button
		applyLink := txlink.NewLink("ApplyForBounty").
			AddArgs(
				"bountyID", ufmt.Sprintf("%d", id),
				"prLink", "",
			).
			URL()
		output += md.Link("Apply for this bounty", applyLink) + "\n\n"

		// Show existing applications
		apps := GetApplicationsForBounty(id)
		if len(apps) > 0 {
			output += "### Applications\n\n"
			for _, app := range apps {
				output += renderApplication(app)
			}
		}
	}

	return output
}

// renderApplication renders a single application
func renderApplication(app *Application) string {
	output := ufmt.Sprintf("**Application #%d**\n", app.ID)
	output += ufmt.Sprintf("- Applicant: %s\n", app.Applicant)
	output += ufmt.Sprintf("- PR Link: %s\n", app.PRLink)
	output += ufmt.Sprintf("- Applied: %s\n", app.AppliedAt.Format("2006-01-02 15:04:05"))

	statusStr := "PENDING"
	if app.Status == StatusApproved {
		statusStr = "APPROVED"
	} else if app.Status == StatusRejected {
		statusStr = "REJECTED"
	}
	output += ufmt.Sprintf("- Status: %s\n", statusStr)

	// Show DAO voting information if DAO exists
	if app.DAO != nil {
		output += ufmt.Sprintf("- Proposal ID: %d\n", app.ProposalID)

		// Get proposal and show detailed validator voting status
		proposal := app.DAO.ActiveProposals().Get(app.ProposalID)
		if proposal != nil {
			// Get vote counts
			yesCount := proposal.VotingRecord().VoteCount(commondao.ChoiceYes)
			noCount := proposal.VotingRecord().VoteCount(commondao.ChoiceNo)
			abstainCount := proposal.VotingRecord().VoteCount(commondao.ChoiceAbstain)
			totalVotes := proposal.VotingRecord().Size()
			totalMembers := app.DAO.Members().Size()

			output += ufmt.Sprintf("\n**Voting Progress:** %d/%d validators have voted\n", totalVotes, totalMembers)
			output += ufmt.Sprintf("- ‚úÖ YES: %d\n", yesCount)
			output += ufmt.Sprintf("- ‚ùå NO: %d\n", noCount)
			output += ufmt.Sprintf("- ü§∑ ABSTAIN: %d\n", abstainCount)
			output += ufmt.Sprintf("- ‚è≥ PENDING: %d\n", totalMembers-totalVotes)
		}
	}

	// Add vote buttons for validators if application is still pending
	if app.Status == StatusPending && app.DAO != nil {
		output += "\n**Vote on this application:**\n"

		// Add vote buttons (yes/no/abstain)
		yesLink := txlink.NewLink("Vote").
			AddArgs(
				"applicationID", ufmt.Sprintf("%d", app.ID),
				"vote", "yes",
			).
			URL()
		noLink := txlink.NewLink("Vote").
			AddArgs(
				"applicationID", ufmt.Sprintf("%d", app.ID),
				"vote", "no",
			).
			URL()
		abstainLink := txlink.NewLink("Vote").
			AddArgs(
				"applicationID", ufmt.Sprintf("%d", app.ID),
				"vote", "abstain",
			).
			URL()

		output += md.Link("‚úÖ Approve", yesLink) + " | " + md.Link("‚ùå Reject", noLink) + " | " + md.Link("ü§∑ Abstain", abstainLink) + "\n"
		output += "\n_Note: Only assigned validators can vote on this application._\n"
	}
	output += "\n"

	return output
}

// RenderMyVotes shows pending votes for a validator
func RenderMyVotes(validatorAddr address) string {
	if !IsValidator(validatorAddr) {
		return "You are not a registered validator"
	}

	output := "# My Pending Votes\n\n"

	apps := GetApplicationsForValidator(validatorAddr)
	pendingCount := 0

	for _, app := range apps {
		if app.Status == StatusPending {
			bounty := GetBounty(app.BountyID)
			if bounty == nil {
				continue
			}

			pendingCount++
			output += ufmt.Sprintf("## Application #%d for Bounty #%d\n", app.ID, app.BountyID)
			output += ufmt.Sprintf("**Bounty:** %s\n", bounty.Description)
			output += ufmt.Sprintf("**Bounty Reward:** %d ugnot (%.2f GNOT)\n", bounty.Amount, float64(bounty.Amount)/1000000.0)
			output += ufmt.Sprintf("**Applicant:** %s\n", app.Applicant)
			output += ufmt.Sprintf("**PR Link:** %s\n", app.PRLink)

			// Show DAO proposal details
			if app.DAO != nil {
				output += ufmt.Sprintf("**Proposal ID:** %d\n", app.ProposalID)

				// Get proposal and show vote count
				proposal := app.DAO.ActiveProposals().Get(app.ProposalID)
				if proposal != nil {
					voteCount := 0
					proposal.VotingRecord().Iterate(0, 0, false, func(v commondao.Vote) bool {
						voteCount++
						return false
					})
					output += ufmt.Sprintf("**Votes Submitted:** %d/%d\n", voteCount, app.DAO.Members().Size())
				}
			}
			output += "\n"

			// Add vote buttons (yes/no/abstain)
			yesLink := txlink.NewLink("Vote").
				AddArgs(
					"applicationID", ufmt.Sprintf("%d", app.ID),
					"vote", "yes",
				).
				URL()
			noLink := txlink.NewLink("Vote").
				AddArgs(
					"applicationID", ufmt.Sprintf("%d", app.ID),
					"vote", "no",
				).
				URL()
			abstainLink := txlink.NewLink("Vote").
				AddArgs(
					"applicationID", ufmt.Sprintf("%d", app.ID),
					"vote", "abstain",
				).
				URL()

			output += md.Link("Approve (Yes)", yesLink) + " | " + md.Link("Reject (No)", noLink) + " | " + md.Link("Abstain", abstainLink) + "\n\n"
			output += "---\n\n"
		}
	}

	if pendingCount == 0 {
		output += "No pending votes"
	}

	return output
}

// RenderValidators shows all validators
func RenderValidators() string {
	return ListValidators()
}

// Render implements the Render() method for the realm
func Render(path string) string {
	if path == "" {
		return ListBounties()
	}

	// Check for special paths
	if path == "validators" {
		return RenderValidators()
	}

	if path == "leaderboard" {
		return RenderLeaderboard()
	}

	// Parse bounty ID from path
	id, err := strconv.Atoi(path)
	if err != nil {
		return "Invalid path. Use /r/greg007/gnobounty for bounties or /r/greg007/gnobounty:validators for validators list"
	}

	return GetBountyDetails(uint64(id))
}

package gnobounty

import (
	"chain/runtime"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ownable"
	"gno.land/p/nt/ufmt"
)

var (
	bounties      avl.Tree // uint64 -> *Bounty
	bountyCount   uint64
	minimumBounty int64 = 1000000 // 1 GNOT minimum

	applications     avl.Tree // uint64 -> *Application
	applicationCount uint64

	validators       avl.Tree      // address -> *Validator
	validatorList    []address     // List of validator addresses for random selection
	validatorsPerDAO int       = 3 // Number of validators per DAO vote
	requiredVotes    int       = 3 // Number of approvals needed (3/3)

	// Validator reward percentage (5% of bounty split among validators)
	validatorRewardPercent int64 = 5 // 5% of bounty goes to validators

	ownership *ownable.Ownable
)

func init() {
	ownership = ownable.New()
}

// GetBounty returns a bounty by ID
func GetBounty(id uint64) *Bounty {
	bountyInterface, exists := bounties.Get(ufmt.Sprintf("%d", id))
	if !exists {
		return nil
	}
	return bountyInterface.(*Bounty)
}

// GetBountyCount returns the total number of bounties created
func GetBountyCount() uint64 {
	return bountyCount
}

// SetMinimumBounty allows setting minimum bounty amount (admin function)
func SetMinimumBounty(amount int64) {
	if ownership.Owner() != runtime.OriginCaller() {
		panic("unauthorized: only owner can set minimum bounty")
	}
	minimumBounty = amount
}

// GetApplication returns an application by ID
func GetApplication(id uint64) *Application {
	appInterface, exists := applications.Get(ufmt.Sprintf("%d", id))
	if !exists {
		return nil
	}
	return appInterface.(*Application)
}

// GetApplicationCount returns the total number of applications
func GetApplicationCount() uint64 {
	return applicationCount
}

// IsValidator checks if an address is a validator
func IsValidator(addr address) bool {
	validatorInterface, exists := validators.Get(string(addr))
	if !exists {
		return false
	}
	validator := validatorInterface.(*Validator)
	return validator.Active
}

// GetValidator returns a validator by address
func GetValidator(addr address) *Validator {
	validatorInterface, exists := validators.Get(string(addr))
	if !exists {
		return nil
	}
	return validatorInterface.(*Validator)
}

// GetActiveValidatorCount returns the number of active validators
func GetActiveValidatorCount() int {
	count := 0
	validators.Iterate("", "", func(key string, value interface{}) bool {
		validator := value.(*Validator)
		if validator.Active {
			count++
		}
		return false
	})
	return count
}

