		apps := GetApplicationsForBounty(id)
		if len(apps) > 0 {
			output += "### Applications\n\n"
			for _, app := range apps {
				output += renderApplication(app)
			}
		}
	}

	return output
}

// renderApplication renders a single application
func renderApplication(app *Application) string {
	output := ufmt.Sprintf("**Application #%d**\n", app.ID)
	output += ufmt.Sprintf("- Applicant: %s\n", app.Applicant)
	output += ufmt.Sprintf("- PR Link: %s\n", app.PRLink)
	output += ufmt.Sprintf("- Applied: %s\n", app.AppliedAt.Format("2006-01-02 15:04:05"))

	statusStr := "PENDING"
	if app.Status == StatusApproved {
		statusStr = "APPROVED"
	} else if app.Status == StatusRejected {
		statusStr = "REJECTED"
	}
	output += ufmt.Sprintf("- Status: %s\n", statusStr)

	// Show DAO voting information if DAO exists
	if app.DAO != nil {
		output += ufmt.Sprintf("- Proposal ID: %d\n", app.ProposalID)

		// Get proposal and show detailed validator voting status
		proposal := app.DAO.ActiveProposals().Get(app.ProposalID)
		if proposal != nil {
			// Get vote counts
			yesCount := proposal.VotingRecord().VoteCount(commondao.ChoiceYes)
			noCount := proposal.VotingRecord().VoteCount(commondao.ChoiceNo)
			abstainCount := proposal.VotingRecord().VoteCount(commondao.ChoiceAbstain)
			totalVotes := proposal.VotingRecord().Size()
			totalMembers := app.DAO.Members().Size()

			output += ufmt.Sprintf("\n**Voting Progress:** %d/%d validators have voted\n", totalVotes, totalMembers)
			output += ufmt.Sprintf("- ‚úÖ YES: %d\n", yesCount)
			output += ufmt.Sprintf("- ‚ùå NO: %d\n", noCount)
			output += ufmt.Sprintf("- ü§∑ ABSTAIN: %d\n", abstainCount)
			output += ufmt.Sprintf("- ‚è≥ PENDING: %d\n", totalMembers-totalVotes)
		}
	}

	// Add vote buttons for validators if application is still pending
	if app.Status == StatusPending && app.DAO != nil {
		output += "\n**Vote on this application:**\n"

		// Add vote buttons (yes/no/abstain)
		yesLink := txlink.NewLink("Vote").
			AddArgs(
				"applicationID", ufmt.Sprintf("%d", app.ID),
				"vote", "yes",
			).
			URL()
		noLink := txlink.NewLink("Vote").
			AddArgs(
				"applicationID", ufmt.Sprintf("%d", app.ID),
				"vote", "no",
			).
			URL()
		abstainLink := txlink.NewLink("Vote").
			AddArgs(
				"applicationID", ufmt.Sprintf("%d", app.ID),
				"vote", "abstain",
			).
			URL()

		output += md.Link("‚úÖ Approve", yesLink) + " | " + md.Link("‚ùå Reject", noLink) + " | " + md.Link("ü§∑ Abstain", abstainLink) + "\n"
		output += "\n_Note: Only assigned validators can vote on this application._\n"
	}
	output += "\n"

	return output
}

// RenderMyVotes shows pending votes for a validator
func RenderMyVotes(validatorAddr address) string {
	if !IsValidator(validatorAddr) {
		return "You are not a registered validator"
	}

	output := "# My Pending Votes\n\n"

	apps := GetApplicationsForValidator(validatorAddr)
	pendingCount := 0

	for _, app := range apps {
		if app.Status == StatusPending {
			bounty := GetBounty(app.BountyID)
			if bounty == nil {
				continue
			}

			pendingCount++
			output += ufmt.Sprintf("## Application #%d for Bounty #%d\n", app.ID, app.BountyID)
			output += ufmt.Sprintf("**Bounty:** %s\n", bounty.Description)
			output += ufmt.Sprintf("**Bounty Reward:** %d ugnot (%.2f GNOT)\n", bounty.Amount, float64(bounty.Amount)/1000000.0)
			output += ufmt.Sprintf("**Applicant:** %s\n", app.Applicant)
			output += ufmt.Sprintf("**PR Link:** %s\n", app.PRLink)

			// Show DAO proposal details
			if app.DAO != nil {
				output += ufmt.Sprintf("**Proposal ID:** %d\n", app.ProposalID)

				// Get proposal and show vote count
				proposal := app.DAO.ActiveProposals().Get(app.ProposalID)
				if proposal != nil {
					voteCount := 0
					proposal.VotingRecord().Iterate(0, 0, false, func(v commondao.Vote) bool {
						voteCount++
						return false
					})
					output += ufmt.Sprintf("**Votes Submitted:** %d/%d\n", voteCount, app.DAO.Members().Size())
				}
			}
			output += "\n"

			// Add vote buttons (yes/no/abstain)
			yesLink := txlink.NewLink("Vote").
				AddArgs(
					"applicationID", ufmt.Sprintf("%d", app.ID),
					"vote", "yes",
				).
				URL()
			noLink := txlink.NewLink("Vote").
				AddArgs(
					"applicationID", ufmt.Sprintf("%d", app.ID),
					"vote", "no",
				).
				URL()
			abstainLink := txlink.NewLink("Vote").
				AddArgs(
					"applicationID", ufmt.Sprintf("%d", app.ID),
					"vote", "abstain",
				).
				URL()

			output += md.Link("Approve (Yes)", yesLink) + " | " + md.Link("Reject (No)", noLink) + " | " + md.Link("Abstain", abstainLink) + "\n\n"
			output += "---\n\n"
		}
	}

	if pendingCount == 0 {
		output += "No pending votes"
	}

	return output
}

// RenderValidators shows all validators
func RenderValidators() string {
	return ListValidators()
}

// Render implements the Render() method for the realm
func Render(path string) string {
	if path == "" {
		return ListBounties()
	}

	// Check for special paths
	if path == "validators" {
		return RenderValidators()
	}

	if path == "leaderboard" {
		return RenderLeaderboard()
	}

	// Parse bounty ID from path
	id, err := strconv.Atoi(path)
	if err != nil {
		return ufmt.Sprintf("Invalid path. Use /%s for bounties or /%s:validators for validators list", pkgPath, pkgPath)
	}

	return GetBountyDetails(uint64(id))
}

package gnobounty

import (
	"chain"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ownable"
	"gno.land/p/nt/ufmt"
)

var (
	bounties      avl.Tree // uint64 -> *Bounty
	bountyCount   uint64
	minimumBounty int64 = 1000000 // 1 GNOT minimum

	applications     avl.Tree // uint64 -> *Application
	applicationCount uint64

	validators       avl.Tree      // address -> *Validator
	validatorList    []address     // List of validator addresses for random selection
	validatorsPerDAO int       = 3 // Number of validators per DAO vote
	requiredVotes    int       = 3 // Number of approvals needed (3/3)

	// Validator reward percentage (5% of bounty split among validators)
	validatorRewardPercent int64 = 5 // 5% of bounty goes to validators

	ownership *ownable.Ownable

	// Package address - stored for easy redeployment
	pkgAddr address

	// Package path - used in URLs for rendering
	pkgPath string = "gno.land/r/greg007/gnobounty"
)

func init() {
	ownership = ownable.NewWithOrigin()
	pkgAddr = chain.PackageAddress(pkgPath)
}

// GetBounty returns a bounty by ID
func GetBounty(id uint64) *Bounty {
	bountyInterface, exists := bounties.Get(ufmt.Sprintf("%d", id))
	if !exists {
		return nil
	}
	return bountyInterface.(*Bounty)
}

// GetBountyCount returns the total number of bounties created
func GetBountyCount() uint64 {
	return bountyCount
}

// SetMinimumBounty allows setting minimum bounty amount (admin function)
func SetMinimumBounty(amount int64) {
	ownership.AssertOwnedByPrevious()
	minimumBounty = amount
}

// SetPackagePath allows setting the package path for URLs (admin function)
func SetPackagePath(path string) {
	ownership.AssertOwnedByPrevious()
	if path == "" {
		panic("package path cannot be empty")
	}
	pkgPath = path
	pkgAddr = chain.PackageAddress(path)
}

// GetApplication returns an application by ID
func GetApplication(id uint64) *Application {
	appInterface, exists := applications.Get(ufmt.Sprintf("%d", id))
	if !exists {
		return nil
	}
	return appInterface.(*Application)
}

// GetApplicationCount returns the total number of applications
func GetApplicationCount() uint64 {
	return applicationCount
}

// IsValidator checks if an address is a validator
func IsValidator(addr address) bool {
	validatorInterface, exists := validators.Get(string(addr))
	if !exists {
		return false
	}
	validator := validatorInterface.(*Validator)
	return validator.Active
}

// GetValidator returns a validator by address
func GetValidator(addr address) *Validator {
	validatorInterface, exists := validators.Get(string(addr))
	if !exists {
		return nil
	}
	return validatorInterface.(*Validator)
}

// GetActiveValidatorCount returns the number of active validators
func GetActiveValidatorCount() int {
	count := 0
	validators.Iterate("", "", func(key string, value interface{}) bool {
		validator := value.(*Validator)
		if validator.Active {
			count++
		}
		return false
	})
	return count
}

// GetOwner returns the owner address (helper function for debugging)
func GetOwner() address {
	return ownership.Owner()
}

// GetPackagePath returns the current package path
func GetPackagePath() string {
	return pkgPath
}

