// LeaderboardEntry holds the stats for a user
type LeaderboardEntry struct {
	Address              address
	BountiesCreated      int
	BountiesApplied      int
	ValidationsPerformed int
	Score                int
}

// GetLeaderboard aggregates stats for all users
func GetLeaderboard() []LeaderboardEntry {
	var stats avl.Tree // address -> *LeaderboardEntry

	// Count bounties created (10 points)
	bounties.Iterate("", "", func(key string, value interface{}) bool {
		if value == nil {
			return false
		}
		bounty, ok := value.(*Bounty)
		if !ok || bounty == nil {
			return false
		}
		entry := getEntry(&stats, bounty.Creator)
		entry.BountiesCreated++
		entry.Score += 10
		return false
	})

	// Count applications and validations
	applications.Iterate("", "", func(key string, value interface{}) bool {
		if value == nil {
			return false
		}
		app, ok := value.(*Application)
		if !ok || app == nil {
			return false
		}

		// Only count approved applications (20 points)
		if app.Status == StatusApproved {
			entry := getEntry(&stats, app.Applicant)
			entry.BountiesApplied++
			entry.Score += 20
		}

		// Count validations from local record (5 points)
		for _, vote := range app.Votes {
			entry := getEntry(&stats, vote.Voter)
			entry.ValidationsPerformed++
			entry.Score += 5
		}

		return false
	})

	// Convert tree to slice
	var result []LeaderboardEntry
	stats.Iterate("", "", func(key string, value interface{}) bool {
		entry := value.(*LeaderboardEntry)
		result = append(result, *entry)
		return false
	})

	// Sort by Score descending
	// Manual bubble sort since sort.Slice is not available
	for i := 0; i < len(result)-1; i++ {
		for j := 0; j < len(result)-i-1; j++ {
			if result[j].Score < result[j+1].Score {
				// Swap
				result[j], result[j+1] = result[j+1], result[j]
			}
		}
	}

	return result
}

// RenderLeaderboard renders the leaderboard page
func RenderLeaderboard() string {
	entries := GetLeaderboard()

	output := md.H1("üèÜ Community Leaderboard")
	output += "Top contributors to the GnoBounty ecosystem.\n\n"
	output += md.Bold("Scoring:") + " Bounty Creation (10pts) | Approved Application (20pts) | Validation (5pts)\n\n"

	if len(entries) == 0 {
		output += "No activity recorded yet."
		return output
	}

	// Table header
	output += "| Rank | User | Score | üí∞ Bounties | üìù Applications | ‚öñÔ∏è Validations |\n"
	output += "|---|---|---|---|---|---|\n"

	// Table rows
	for i, entry := range entries {
		rank := getRankDisplay(i)
		output += tableRow(
			rank,
			string(entry.Address),
			md.Bold(ufmt.Sprintf("%d", entry.Score)),
			ufmt.Sprintf("%d", entry.BountiesCreated),
			ufmt.Sprintf("%d", entry.BountiesApplied),
			ufmt.Sprintf("%d", entry.ValidationsPerformed),
		)
	}

	return output
}

// getRankDisplay returns the rank display (medal for top 3, number otherwise)
func getRankDisplay(position int) string {
	switch position {
	case 0:
		return "ü•á"
	case 1:
		return "ü•à"
	case 2:
		return "ü•â"
	default:
		return ufmt.Sprintf("%d", position+1)
	}
}

// tableRow creates a markdown table row
func tableRow(cells ...string) string {
	output := "|"
	for _, cell := range cells {
		output += " " + cell + " |"
	}
	return output + "\n"
}

func getEntry(stats *avl.Tree, addr address) *LeaderboardEntry {
	key := string(addr)
	val, ok := stats.Get(key)
	if !ok {
		entry := &LeaderboardEntry{Address: addr}
		stats.Set(key, entry)
		return entry
	}
	return val.(*LeaderboardEntry)
}

package gnobounty

import (
	"chain"
	"chain/banker"
	"chain/runtime"
	"time"

	"gno.land/p/nt/ufmt"
)

// CreateBounty creates a new bounty post
// title: short title for the bounty
// issueURL: link to the GitHub issue or other issue tracker
// description: description of what needs to be done
// Caller must send GNOT tokens with this transaction
func CreateBounty(_ realm, title, issueURL, description string) uint64 {
	// Validate inputs
	if title == "" {
		panic("title is required")
	}
	if issueURL == "" {
		panic("issue URL is required")
	}
	if description == "" {
		panic("description is required")
	}

	// Get the sent coins
	sent := banker.OriginSend()
	if len(sent) == 0 {
		panic("no coins sent with bounty")
	}

	// Find GNOT amount
	var gnotAmount int64
	for _, coin := range sent {
		if coin.Denom == "ugnot" {
			gnotAmount = coin.Amount
			break
		}
	}

	if gnotAmount < minimumBounty {
		panic(ufmt.Sprintf("bounty amount must be at least %d ugnot", minimumBounty))
	}

	// Create bounty
	bountyCount++
	bounty := &Bounty{
		ID:          bountyCount,
		Title:       title,
		IssueURL:    issueURL,
		Description: description,
		Amount:      gnotAmount,
		Creator:     runtime.OriginCaller(),
		CreatedAt:   time.Now(),
		IsClaimed:   false,
	}

	// Store bounty
	bounties.Set(ufmt.Sprintf("%d", bountyCount), bounty)

	return bountyCount
}

// ClaimBounty allows someone to claim a bounty
// Only the creator can approve and transfer funds
func ClaimBounty(_ realm, id uint64, claimer address) {
	caller := runtime.OriginCaller()

	bounty := GetBounty(id)
	if bounty == nil {
		panic("bounty not found")
	}

	if bounty.IsClaimed {
		panic("bounty already claimed")
	}

	if caller != bounty.Creator {
		panic("only bounty creator can approve claims")
	}

	if claimer == "" {
		panic("claimer address cannot be empty")
	}

	// Mark as claimed
	bounty.IsClaimed = true
	bounty.Claimer = claimer
	bounty.ClaimedAt = time.Now()

	// Transfer bounty amount to claimer
	bnk := banker.NewBanker(banker.BankerTypeRealmSend)
	send := chain.Coins{chain.Coin{Denom: "ugnot", Amount: bounty.Amount}}
	bnk.SendCoins(pkgAddr, claimer, send)
}

// CancelBounty allows creator to cancel and get refund
func CancelBounty(_ realm, id uint64) {
	caller := runtime.OriginCaller()

	bounty := GetBounty(id)
	if bounty == nil {
		panic("bounty not found")
	}

	if bounty.IsClaimed {
		panic("cannot cancel claimed bounty")
	}

	if caller != bounty.Creator {
		panic("only bounty creator can cancel")
	}

	// Remove bounty first (Checks-Effects-Interactions)
	bounties.Remove(ufmt.Sprintf("%d", id))

	// Refund the creator
	bnk := banker.NewBanker(banker.BankerTypeRealmSend)
	send := chain.Coins{chain.Coin{Denom: "ugnot", Amount: bounty.Amount}}
	bnk.SendCoins(pkgAddr, bounty.Creator, send)
}

package gnobounty

import (
	"chain"
	"chain/banker"
	"time"

	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

// ClaimBountyProposal implements commondao.ProposalDefinition for bounty claim proposals
type ClaimBountyProposal struct {
	BountyID      uint64
	ApplicationID uint64
	Applicant     address
	PRLink        string
	title         string
	description   string
}

// NewClaimBountyProposal creates a new bounty claim proposal definition
func NewClaimBountyProposal(bountyID, applicationID uint64, applicant address, prLink string) *ClaimBountyProposal {
	return &ClaimBountyProposal{
		BountyID:      bountyID,
		ApplicationID: applicationID,
		Applicant:     applicant,
		PRLink:        prLink,
		title:         ufmt.Sprintf("Approve Application #%d for Bounty #%d", applicationID, bountyID),
		description:   ufmt.Sprintf("Review PR: %s\nApplicant: %s", prLink, applicant),
	}
}

// Title returns proposal title
func (p *ClaimBountyProposal) Title() string {
	return p.title
}

// Body returns proposal's body
func (p *ClaimBountyProposal) Body() string {
	return p.description
}

// VotingPeriod returns the period where votes are allowed after proposal creation
func (p *ClaimBountyProposal) VotingPeriod() time.Duration {
	// 7 days voting period
	return 7 * 24 * time.Hour
}

// Tally counts votes and determines if proposal is ready to execute
// Returns true when all validators have voted (regardless of outcome)
func (p *ClaimBountyProposal) Tally(ctx commondao.VotingContext) (bool, error) {
	totalMembers := ctx.Members.Size()
	if totalMembers == 0 {
		return false, ufmt.Errorf("no members in DAO")
	}

	totalVotes := ctx.VotingRecord.Size()

	// Return true when all validators have voted
	// This allows Execute to be called and handle approval/rejection
	if totalVotes == totalMembers {
		return true, nil
	}

	// Not ready yet - waiting for more votes
	return false, nil
}

// Execute executes the proposal after all votes are in
// Determines approval or rejection based on vote counts
func (p *ClaimBountyProposal) Execute(_ realm) error {
	app := GetApplication(p.ApplicationID)
	if app == nil {
		return ufmt.Errorf("application not found")
	}

	bounty := GetBounty(p.BountyID)
	if bounty == nil {
		return ufmt.Errorf("bounty not found")
	}

	if bounty.IsClaimed {
		return ufmt.Errorf("bounty already claimed")
	}

	// Get vote counts from the DAO proposal
	proposal := app.DAO.ActiveProposals().Get(app.ProposalID)
	if proposal == nil {
		return ufmt.Errorf("proposal not found")
	}

	yesCount := proposal.VotingRecord().VoteCount(commondao.ChoiceYes)
	noCount := proposal.VotingRecord().VoteCount(commondao.ChoiceNo)
	totalMembers := app.DAO.Members().Size()

	// Check if unanimous approval (all YES, no NO)
	if yesCount == totalMembers && noCount == 0 {
		// Approve and transfer funds
		app.Status = StatusApproved

		bounty.IsClaimed = true
		bounty.Claimer = p.Applicant
		bounty.ClaimedAt = time.Now()

		// Calculate reward split: 5% to validators, 95% to applicant
		validatorReward := (bounty.Amount * validatorRewardPercent) / 100
		applicantReward := bounty.Amount - validatorReward

		// Split validator reward equally among all validators who voted
		rewardPerValidator := validatorReward / int64(totalMembers)

		// Transfer rewards
		bnk := banker.NewBanker(banker.BankerTypeRealmSend)

		// Pay each validator their share
		app.DAO.Members().IterateByOffset(0, totalMembers, func(member address) bool {
			validatorPayout := chain.Coins{chain.Coin{Denom: "ugnot", Amount: rewardPerValidator}}
			bnk.SendCoins(pkgAddr, member, validatorPayout)
			return false
		})

		// Pay the applicant the remaining 95%
		applicantPayout := chain.Coins{chain.Coin{Denom: "ugnot", Amount: applicantReward}}
		bnk.SendCoins(pkgAddr, p.Applicant, applicantPayout)
	} else {
		// Reject - not unanimous
		app.Status = StatusRejected
	}

	return nil
}

