func renderArchivedProposalPreview(p *commondao.Proposal) string {
	output := ufmt.Sprintf("## Proposal #%d - %s\n\n", p.ID(), string(p.Status()))
	output += ufmt.Sprintf("**%s**\n\n", p.Definition().Title())

	// Count final votes
	yesVotes := 0
	noVotes := 0
	p.VotingRecord().Iterate(0, p.VotingRecord().Size(), false, func(v commondao.Vote) bool {
		if string(v.Choice) == "yes" || string(v.Choice) == "YES" {
			yesVotes++
		} else if string(v.Choice) == "no" || string(v.Choice) == "NO" {
			noVotes++
		}
		return false
	})

	output += ufmt.Sprintf("**Final Result:** Yes: %d | No: %d\n\n", yesVotes, noVotes)

	statusLabel := "PASSED"
	if p.Status() == "failed" {
		statusLabel = "FAILED"
	}
	output += ufmt.Sprintf("**Status:** %s\n\n", statusLabel)

	if p.StatusReason() != "" {
		output += ufmt.Sprintf("**Reason:** %s\n\n", p.StatusReason())
	}

	output += ufmt.Sprintf("[View details](/r/pierre115/daoregistry:archived/%d)\n\n", p.ID())
	output += "---\n\n"

	return output
}

func renderArchivedProposal(proposalID uint64) string {
	proposal := registryDAO.FinishedProposals().Get(proposalID)
	if proposal == nil {
		return "# Archived proposal not found"
	}

	output := ufmt.Sprintf("# Proposal #%d - %s\n\n", proposalID, string(proposal.Status()))
	output += ufmt.Sprintf("## %s\n\n", proposal.Definition().Title())
	output += ufmt.Sprintf("%s\n\n", proposal.Definition().Body())

	output += "---\n\n"

	// Final vote counts
	yesVotes := 0
	noVotes := 0
	totalVotes := 0

	proposal.VotingRecord().Iterate(0, proposal.VotingRecord().Size(), false, func(v commondao.Vote) bool {
		if string(v.Choice) == "yes" || string(v.Choice) == "YES" {
			yesVotes++
		} else if string(v.Choice) == "no" || string(v.Choice) == "NO" {
			noVotes++
		}
		totalVotes++
		return false
	})

	output += "## Final Results\n\n"
	output += ufmt.Sprintf("- **Yes votes:** %d\n", yesVotes)
	output += ufmt.Sprintf("- **No votes:** %d\n", noVotes)
	output += ufmt.Sprintf("- **Total votes:** %d\n", totalVotes)

	totalMembers := GetTotalRegistryMembers()
	quorumRequired := int(float64(totalMembers) * REGISTRY_QUORUM)
	output += ufmt.Sprintf("- **Quorum required:** %d/%d votes\n\n", totalVotes, quorumRequired)

	statusLabel := "PASSED"
	if proposal.Status() == "failed" {
		statusLabel = "FAILED"
	}
	output += ufmt.Sprintf("**Final Status:** %s\n\n", statusLabel)

	if proposal.StatusReason() != "" {
		output += ufmt.Sprintf("**Status Reason:** %s\n\n", proposal.StatusReason())
	}

	output += ufmt.Sprintf("**Voting Ended:** %s\n\n", proposal.VotingDeadline().Format("2006-01-02 15:04:05"))

	output += "---\n\n"
	output += "[Back to archive](/r/pierre115/daoregistry:archive) | [Home](/r/pierre115/daoregistry)\n"

	return output
}

package daoregistry2

import (
	"chain/runtime"

	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

// ProposeVerifyCollection - Create proposal to verify a collection
func ProposeVerifyCollection(_ realm, collectionAddr address, collectionName string, reason string) uint64 {
	caller := runtime.PreviousRealm().Address()

	if !IsRegistryDAOMember(caller) {
		panic("only Registry DAO members can create proposals")
	}

	info := GetCollection(collectionAddr)
	if info == nil {
		panic("collection not registered")
	}

	if info.Verified {
		panic("collection already verified")
	}

	propDef := NewVerifyCollectionProposal(collectionAddr, collectionName, reason)

	proposal, err := registryDAO.Propose(caller, propDef)
	if err != nil {
		panic("failed to create proposal: " + err.Error())
	}

	return proposal.ID()
}

// ProposeUnverifyCollection - Create proposal to unverify a collection
func ProposeUnverifyCollection(_ realm, collectionAddr address, collectionName string, reason string) uint64 {
	caller := runtime.PreviousRealm().Address()

	if !IsRegistryDAOMember(caller) {
		panic("only Registry DAO members can create proposals")
	}

	info := GetCollection(collectionAddr)
	if info == nil {
		panic("collection not registered")
	}

	if !info.Verified {
		panic("collection not verified")
	}

	propDef := NewUnverifyCollectionProposal(collectionAddr, collectionName, reason)

	proposal, err := registryDAO.Propose(caller, propDef)
	if err != nil {
		panic("failed to create proposal: " + err.Error())
	}

	return proposal.ID()
}

// VoteRegistry - Vote on a registry proposal
func VoteRegistry(_ realm, proposalID uint64, choice string) string {
	caller := runtime.PreviousRealm().Address()

	if choice != "yes" && choice != "no" {
		panic("choice must be 'yes' or 'no'")
	}

	// Use DAO's Vote method - handles all validations automatically
	err := registryDAO.Vote(caller, proposalID, commondao.VoteChoice(choice), "")
	if err != nil {
		panic("vote failed: " + err.Error())
	}

	return "Vote recorded: " + choice
}

// TallyRegistryProposal - Execute a registry proposal
func TallyRegistryProposal(_ realm, proposalID uint64) string {
	err := registryDAO.Execute(proposalID)
	if err != nil {
		panic("execution failed: " + err.Error())
	}

	return ufmt.Sprintf("Proposal %d executed successfully", proposalID)
}

