package daoregistry3

import (
	"errors"
	"time"

	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

const (
	PROPOSAL_TYPE_VERIFY_COLLECTION   = "verify_collection"
	PROPOSAL_TYPE_UNVERIFY_COLLECTION = "unverify_collection"

	REGISTRY_VOTING_PERIOD = 5 * time.Minute
	REGISTRY_QUORUM        = commondao.QuorumOneThird // 33%
)

// VerifyCollectionProposal - Proposal to verify a collection
type VerifyCollectionProposal struct {
	collectionAddr address
	collectionName string
	reason         string
	approved       bool
	executed       bool
}

func NewVerifyCollectionProposal(addr address, name string, reason string) *VerifyCollectionProposal {
	return &VerifyCollectionProposal{
		collectionAddr: addr,
		collectionName: name,
		reason:         reason,
		approved:       false,
		executed:       false,
	}
}

func (p *VerifyCollectionProposal) Title() string {
	return "Verify Collection: " + p.collectionName
}

func (p *VerifyCollectionProposal) Body() string {
	return ufmt.Sprintf(
		"Collection: %s\nAddress: %s\nReason: %s\n\nVote YES to verify, NO to reject",
		p.collectionName,
		p.collectionAddr.String(),
		p.reason,
	)
}

func (p *VerifyCollectionProposal) VotingPeriod() time.Duration {
	return REGISTRY_VOTING_PERIOD
}

func (p *VerifyCollectionProposal) Tally(ctx commondao.VotingContext) (bool, error) {
	votes := ctx.VotingRecord
	members := ctx.Members

	yesVotes := 0
	noVotes := 0

	votes.Iterate(0, votes.Size(), false, func(v commondao.Vote) bool {
		if v.Choice == commondao.ChoiceYes {
			yesVotes++
		} else if v.Choice == commondao.ChoiceNo {
			noVotes++
		}
		return false
	})

	// Check quorum
	if !commondao.IsQuorumReached(REGISTRY_QUORUM, votes, members) {
		p.approved = false
		return false, commondao.ErrNoQuorum
	}

	// Simple majority
	p.approved = yesVotes > noVotes
	return p.approved, nil
}

func (p *VerifyCollectionProposal) Execute(realm) error {
	info := GetCollection(p.collectionAddr)
	if info == nil {
		return errors.New("collection not found")
	}

	if info.Verified {
		return errors.New("collection already verified")
	}

	info.Verified = true
	collections.Set(p.collectionAddr.String(), info)

	p.executed = true
	return nil
}

// UnverifyCollectionProposal - Proposal to unverify a collection
type UnverifyCollectionProposal struct {
	collectionAddr address
	collectionName string
	reason         string
	approved       bool
	executed       bool
}

func NewUnverifyCollectionProposal(addr address, name string, reason string) *UnverifyCollectionProposal {
	return &UnverifyCollectionProposal{
		collectionAddr: addr,
		collectionName: name,
		reason:         reason,
		approved:       false,
		executed:       false,
	}
}

func (p *UnverifyCollectionProposal) Title() string {
	return "Unverify Collection: " + p.collectionName
}

func (p *UnverifyCollectionProposal) Body() string {
	return ufmt.Sprintf(
		"Collection: %s\nAddress: %s\nReason: %s\n\nVote YES to unverify, NO to reject",
		p.collectionName,
		p.collectionAddr.String(),
		p.reason,
	)
}

func (p *UnverifyCollectionProposal) VotingPeriod() time.Duration {
	return REGISTRY_VOTING_PERIOD
}

func (p *UnverifyCollectionProposal) Tally(ctx commondao.VotingContext) (bool, error) {
	votes := ctx.VotingRecord
	members := ctx.Members

	yesVotes := 0
	noVotes := 0

	votes.Iterate(0, votes.Size(), false, func(v commondao.Vote) bool {
		if v.Choice == commondao.ChoiceYes {
			yesVotes++
		} else if v.Choice == commondao.ChoiceNo {
			noVotes++
		}
		return false
	})

	// Check quorum
	if !commondao.IsQuorumReached(REGISTRY_QUORUM, votes, members) {
		p.approved = false
		return false, commondao.ErrNoQuorum
	}

	// Simple majority
	p.approved = yesVotes > noVotes
	return p.approved, nil
}

func (p *UnverifyCollectionProposal) Execute(realm) error {
	info := GetCollection(p.collectionAddr)
	if info == nil {
		return errors.New("collection not found")
	}

	if !info.Verified {
		return errors.New("collection not verified")
	}

	info.Verified = false
	collections.Set(p.collectionAddr.String(), info)

	p.executed = true
	return nil
}

package daoregistry3

import (
	"chain/runtime"

	"gno.land/p/demo/tokens/grc721"
	"gno.land/p/nt/avl"
)

// CollectionInfo - Extended metadata stored in the registry
type CollectionInfo struct {
	Address          address
	Name             string
	Symbol           string
	Creator          address
	RegisteredAt     int64
	Verified         bool
	Category         string
	Description      string
	ExternalURL      string
	NFTGetter        grc721.NFTGetter
	SupportsMetadata bool
}

var (
	collections     avl.Tree
	categoriesIndex avl.Tree
	registrationFee int64 = 1000000
)

func init() {
	initRegistryDAO()
}

// RegisterCollection - Register a new NFT collection
func RegisterCollection(
	realmAddr address,
	name string,
	symbol string,
	category string,
	description string,
	externalURL string,
	supportsMetadata bool,
	getter grc721.NFTGetter,
) {
	creator := runtime.OriginCaller()

	// Check if collection already registered
	if collections.Has(realmAddr.String()) {
		panic("Collection already registered")
	}

	if getter == nil {
		panic("NFT getter function is required")
	}

	if name == "" || symbol == "" {
		panic("Name and symbol cannot be empty")
	}

	info := &CollectionInfo{
		Address:          realmAddr,
		Creator:          creator,
		Name:             name,
		Symbol:           symbol,
		RegisteredAt:     runtime.ChainHeight(),
		Verified:         false,
		Category:         category,
		Description:      description,
		ExternalURL:      externalURL,
		NFTGetter:        getter,
		SupportsMetadata: supportsMetadata,
	}

	collections.Set(realmAddr.String(), info)
}

// IsRegistered - Check if a collection is registered
func IsRegistered(collectionAddr address) bool {
	return collections.Has(collectionAddr.String())
}

// GetTotalCollections - Total number of registered collections
func GetTotalCollections() int {
	count := 0
	collections.Iterate("", "", func(key string, value interface{}) bool {
		count++
		return false
	})
	return count
}

// GetVerifiedCount - Count of verified collections
func GetVerifiedCount() int {
	count := 0
	collections.Iterate("", "", func(key string, value interface{}) bool {
		info := value.(*CollectionInfo)
		if info.Verified {
			count++
		}
		return false
	})
	return count
}

// GetCollection - Retrieve collection info by address
func GetCollection(collectionAddr address) *CollectionInfo {
	val, exists := collections.Get(collectionAddr.String())
	if !exists {
		return nil
	}
	return val.(*CollectionInfo)
}

// GetNFTGetter - Retrieve the NFTGetter function for a collection
func GetNFTGetter(collectionAddr address) (grc721.NFTGetter, bool) {
	info := GetCollection(collectionAddr)
	if info == nil {
		return nil, false
	}
	return info.NFTGetter, true
}

// GetTokenMetadata - Retrieve onchain metadata for a token
func GetTokenMetadata(collectionAddr address, tokenId grc721.TokenID) (grc721.Metadata, error) {
	info := GetCollection(collectionAddr)
	if info == nil {
		panic("Collection not registered")
	}

	if !info.SupportsMetadata {
		panic("Collection does not support onchain metadata")
	}

	nftInstance := info.NFTGetter()
	metadataCollection := nftInstance.(grc721.IGRC721MetadataOnchain)

	return metadataCollection.TokenMetadata(tokenId)
}

// UpdateCollectionInfo - Update collection metadata
func UpdateCollectionInfo(category, description, externalURL string) {
	caller := runtime.PreviousRealm().Address()

	info := GetCollection(caller)
	if info == nil {
		panic("Collection not registered")
	}

	info.Category = category
	info.Description = description
	info.ExternalURL = externalURL
	collections.Set(caller.String(), info)
}

package daoregistry3

import (
	"strconv"
	"strings"

	"gno.land/p/leon/svgbtn"
	"gno.land/p/moul/txlink"
	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

func Render(path string) string {
	if path == "" {
		return renderHome()
	}

	if path == "proposals" {
		return renderProposals()
	}

	if path == "archive" {
		return renderArchive()
	}

	if strings.HasPrefix(path, "proposal/") {
		idStr := strings.TrimPrefix(path, "proposal/")
		id, _ := strconv.Atoi(idStr)
		return renderProposal(uint64(id))
	}

	if strings.HasPrefix(path, "archived/") {
		idStr := strings.TrimPrefix(path, "archived/")
		id, _ := strconv.Atoi(idStr)
		return renderArchivedProposal(uint64(id))
	}

	if strings.HasPrefix(path, "collection/") {
		addrStr := strings.TrimPrefix(path, "collection/")
		addr := address(addrStr)
		return renderCollection(addr)
	}

	return "Page not found"
}

func renderHome() string {
	output := "# NFT Registry\n\n"
	output += "Decentralized NFT Collection Registry with DAO Governance\n\n"
	output += "---\n\n"

	output += "## Statistics\n\n"
	output += ufmt.Sprintf("- Total registered collections: %d\n", GetTotalCollections())
	output += ufmt.Sprintf("- Verified collections: %d\n\n", GetVerifiedCount())

	output += "---\n\n"

	// DAO Section
	output += "## DAO Governance\n\n"
	output += ufmt.Sprintf("**Total DAO Members:** %d\n\n", GetTotalRegistryMembers())

	activeProposals := registryDAO.ActiveProposals()
	if activeProposals.Size() > 0 {
		output += ufmt.Sprintf("**Active Proposals:** %d\n\n", activeProposals.Size())
		output += "[View all proposals](/r/pierre115/daoregistry:proposals)\n\n"
	} else {
		output += "**Active Proposals:** 0\n\n"
		output += "[View proposals](/r/pierre115/daoregistry:proposals) | "
	}

	finishedProposals := registryDAO.FinishedProposals()
	output += ufmt.Sprintf("[View archive (%d)](/r/pierre115/daoregistry:archive)\n\n", finishedProposals.Size())
	output += "---\n\n"

	// Vote Join button
	linkjoin := txlink.NewLink("JoinRegistryDAO").
		URL()
	output += svgbtn.SuccessButton(100, 30, "Join DAO", linkjoin) + "\n\n"
	output += "---\n\n"

	// Collections list
	output += "## Registered Collections\n\n"

	hasCollections := false
	collections.Iterate("", "", func(key string, value interface{}) bool {
		hasCollections = true
		info := value.(*CollectionInfo)
		output += renderCollectionPreview(info)
		return false
	})

	if !hasCollections {
		output += "No collections registered yet.\n\n"
	}

	return output
}

func renderCollectionPreview(info *CollectionInfo) string {
	output := ufmt.Sprintf("### %s (%s)\n\n", info.Name, info.Symbol)

	if info.Verified {
		output += "Status: VERIFIED\n\n"
	} else {
		output += "Status: Not verified\n\n"
	}

	output += ufmt.Sprintf("Category: %s\n\n", info.Category)
	output += ufmt.Sprintf("[View details](/r/pierre115/daoregistry:collection/%s)\n\n", info.Address.String())
	output += "---\n\n"

	return output
}

func renderCollection(addr address) string {
	info := GetCollection(addr)
	if info == nil {
		return "# Collection not found"
	}

	output := ufmt.Sprintf("# %s\n\n", info.Name)

	if info.Verified {
		output += "**Status:** VERIFIED\n\n"
	} else {
		output += "**Status:** Not verified\n\n"
	}

	output += "## Details\n\n"
	output += ufmt.Sprintf("**Symbol:** %s\n\n", info.Symbol)
	output += ufmt.Sprintf("**Address:** %s\n\n", info.Address.String())
	output += ufmt.Sprintf("**Creator:** %s\n\n", info.Creator.String())
	output += ufmt.Sprintf("**Category:** %s\n\n", info.Category)
	output += ufmt.Sprintf("**Description:** %s\n\n", info.Description)

	if info.ExternalURL != "" {
		output += ufmt.Sprintf("**External URL:** %s\n\n", info.ExternalURL)
	}

	output += ufmt.Sprintf("**Registered at block:** %d\n\n", info.RegisteredAt)
	output += ufmt.Sprintf("**Supports onchain metadata:** %t\n\n", info.SupportsMetadata)

	output += "[Back to registry](/r/pierre115/daoregistry)\n"

	return output
}

func renderProposals() string {
	output := "# Active Registry Proposals\n\n"

	proposals := registryDAO.ActiveProposals()

	if proposals.Size() == 0 {
		output += "No active proposals at the moment.\n\n"
		output += "DAO members can create proposals to:\n"
		output += "- Verify collections\n"
		output += "- Unverify collections\n\n"
		output += "[Back](/r/pierre115/daoregistry)\n"
		return output
	}

	proposals.Iterate(0, proposals.Size(), false, func(p *commondao.Proposal) bool {
		output += renderProposalPreview(p)
		return false
	})

	output += "\n[View archive](/r/pierre115/daoregistry:archive) | [Back](/r/pierre115/daoregistry)\n"

	return output
}

func renderProposalPreview(p *commondao.Proposal) string {
	output := ufmt.Sprintf("## Proposal #%d\n\n", p.ID())
	output += ufmt.Sprintf("**%s**\n\n", p.Definition().Title())

	// Count votes
	yesVotes := 0
	noVotes := 0
	p.VotingRecord().Iterate(0, p.VotingRecord().Size(), false, func(v commondao.Vote) bool {
		if string(v.Choice) == "yes" || string(v.Choice) == "YES" {
			yesVotes++
		} else if string(v.Choice) == "no" || string(v.Choice) == "NO" {
			noVotes++
		}
		return false
	})

	output += ufmt.Sprintf("**Yes:** %d | **No:** %d | **Total:** %d\n\n", yesVotes, noVotes, yesVotes+noVotes)

	if p.HasVotingDeadlinePassed() {
		output += "Status: Voting ended\n\n"
	} else {
		output += "Status: Voting open\n\n"
	}

	output += ufmt.Sprintf("[View and Vote](/r/pierre115/daoregistry:proposal/%d)\n\n", p.ID())
	output += "---\n\n"

	return output
}

func renderProposal(proposalID uint64) string {
	proposal := registryDAO.ActiveProposals().Get(proposalID)
	if proposal == nil {
		return "# Proposal not found"
	}

	output := ufmt.Sprintf("# Proposal #%d\n\n", proposalID)
	output += ufmt.Sprintf("## %s\n\n", proposal.Definition().Title())
	output += ufmt.Sprintf("%s\n\n", proposal.Definition().Body())

	output += "---\n\n"

	// Vote counts
	yesVotes := 0
	noVotes := 0
	totalVotes := 0

	proposal.VotingRecord().Iterate(0, proposal.VotingRecord().Size(), false, func(v commondao.Vote) bool {
		if string(v.Choice) == "yes" || string(v.Choice) == "YES" {
			yesVotes++
		} else if string(v.Choice) == "no" || string(v.Choice) == "NO" {
			noVotes++
		}
		totalVotes++
		return false
	})

	output += "## Current Results\n\n"
	output += ufmt.Sprintf("- **Yes votes:** %d\n", yesVotes)
	output += ufmt.Sprintf("- **No votes:** %d\n", noVotes)
	output += ufmt.Sprintf("- **Total votes:** %d\n", totalVotes)

	totalMembers := GetTotalRegistryMembers()
	quorumRequired := int(float64(totalMembers) * REGISTRY_QUORUM)
	output += ufmt.Sprintf("- **Quorum required:** %d/%d votes\n\n", totalVotes, quorumRequired)

	if proposal.HasVotingDeadlinePassed() {
		output += "**Voting period has ended**\n\n"
	} else {
		output += "**Voting is open**\n\n"

		output += "---\n\n"
		output += "## Cast Your Vote\n\n"

		// Vote YES button
		linkyes := txlink.NewLink("VoteRegistry").
			AddArgs("proposalID", ufmt.Sprintf("%d", proposalID)).
			AddArgs("choice", "yes").
			URL()
		output += svgbtn.SuccessButton(100, 30, "YES", linkyes) + "\n\n"

		// Vote NO button
		linkno := txlink.NewLink("VoteRegistry").
			AddArgs("proposalID", ufmt.Sprintf("%d", proposalID)).
			AddArgs("choice", "no").
			URL()
		output += svgbtn.DangerButton(100, 30, "NO", linkno) + "\n\n"
	}

	output += "---\n\n"
	output += "[Back to proposals](/r/pierre115/daoregistry:proposals) | [Home](/r/pierre115/daoregistry)\n"

	return output
}

func renderArchive() string {
	output := "# Proposal Archive\n\n"

	finishedProposals := registryDAO.FinishedProposals()

	if finishedProposals.Size() == 0 {
		output += "No finished proposals yet.\n\n"
		output += "[Back](/r/pierre115/daoregistry)\n"
		return output
	}

	output += ufmt.Sprintf("**Total archived proposals:** %d\n\n", finishedProposals.Size())
	output += "---\n\n"

	finishedProposals.Iterate(0, finishedProposals.Size(), false, func(p *commondao.Proposal) bool {
		output += renderArchivedProposalPreview(p)
		return false
	})

	output += "\n[Back](/r/pierre115/daoregistry)\n"

	return output
}

func renderArchivedProposalPreview(p *commondao.Proposal) string {
	output := ufmt.Sprintf("## Proposal #%d - %s\n\n", p.ID(), string(p.Status()))
	output += ufmt.Sprintf("**%s**\n\n", p.Definition().Title())

	// Count final votes
	yesVotes := 0
	noVotes := 0
	p.VotingRecord().Iterate(0, p.VotingRecord().Size(), false, func(v commondao.Vote) bool {
		if string(v.Choice) == "yes" || string(v.Choice) == "YES" {
			yesVotes++
		} else if string(v.Choice) == "no" || string(v.Choice) == "NO" {
			noVotes++
		}
		return false
	})

	output += ufmt.Sprintf("**Final Result:** Yes: %d | No: %d\n\n", yesVotes, noVotes)

	statusLabel := "PASSED"
	if p.Status() == "failed" {
		statusLabel = "FAILED"
	}
	output += ufmt.Sprintf("**Status:** %s\n\n", statusLabel)

	if p.StatusReason() != "" {
		output += ufmt.Sprintf("**Reason:** %s\n\n", p.StatusReason())
	}

	output += ufmt.Sprintf("[View details](/r/pierre115/daoregistry:archived/%d)\n\n", p.ID())
	output += "---\n\n"

	return output
}

func renderArchivedProposal(proposalID uint64) string {
	proposal := registryDAO.FinishedProposals().Get(proposalID)
	if proposal == nil {
		return "# Archived proposal not found"
	}

	output := ufmt.Sprintf("# Proposal #%d - %s\n\n", proposalID, string(proposal.Status()))
	output += ufmt.Sprintf("## %s\n\n", proposal.Definition().Title())
	output += ufmt.Sprintf("%s\n\n", proposal.Definition().Body())

	output += "---\n\n"

	// Final vote counts
	yesVotes := 0
	noVotes := 0
	totalVotes := 0

	proposal.VotingRecord().Iterate(0, proposal.VotingRecord().Size(), false, func(v commondao.Vote) bool {
		if string(v.Choice) == "yes" || string(v.Choice) == "YES" {
			yesVotes++
		} else if string(v.Choice) == "no" || string(v.Choice) == "NO" {
			noVotes++
		}
		totalVotes++
		return false
	})

	output += "## Final Results\n\n"
	output += ufmt.Sprintf("- **Yes votes:** %d\n", yesVotes)
	output += ufmt.Sprintf("- **No votes:** %d\n", noVotes)
	output += ufmt.Sprintf("- **Total votes:** %d\n", totalVotes)

	totalMembers := GetTotalRegistryMembers()
	quorumRequired := int(float64(totalMembers) * REGISTRY_QUORUM)
	output += ufmt.Sprintf("- **Quorum required:** %d/%d votes\n\n", totalVotes, quorumRequired)

	statusLabel := "PASSED"
	if proposal.Status() == "failed" {
		statusLabel = "FAILED"
	}
	output += ufmt.Sprintf("**Final Status:** %s\n\n", statusLabel)

	if proposal.StatusReason() != "" {
		output += ufmt.Sprintf("**Status Reason:** %s\n\n", proposal.StatusReason())
	}

	output += ufmt.Sprintf("**Voting Ended:** %s\n\n", proposal.VotingDeadline().Format("2006-01-02 15:04:05"))

	output += "---\n\n"
	output += "[Back to archive](/r/pierre115/daoregistry:archive) | [Home](/r/pierre115/daoregistry)\n"

	return output
}

package daoregistry3

import (
	"chain/runtime"

	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

// ProposeVerifyCollection - Create proposal to verify a collection
func ProposeVerifyCollection(_ realm, collectionAddr address, collectionName string, reason string) uint64 {
	caller := runtime.PreviousRealm().Address()

	if !IsRegistryDAOMember(caller) {
		panic("only Registry DAO members can create proposals")
	}

	info := GetCollection(collectionAddr)
	if info == nil {
		panic("collection not registered")
	}

	if info.Verified {
		panic("collection already verified")
	}

	propDef := NewVerifyCollectionProposal(collectionAddr, collectionName, reason)

	proposal, err := registryDAO.Propose(caller, propDef)
	if err != nil {
		panic("failed to create proposal: " + err.Error())
	}

	return proposal.ID()
}

// ProposeUnverifyCollection - Create proposal to unverify a collection
func ProposeUnverifyCollection(_ realm, collectionAddr address, collectionName string, reason string) uint64 {
	caller := runtime.PreviousRealm().Address()

	if !IsRegistryDAOMember(caller) {
		panic("only Registry DAO members can create proposals")
	}

	info := GetCollection(collectionAddr)
	if info == nil {
		panic("collection not registered")
	}

	if !info.Verified {
		panic("collection not verified")
	}

	propDef := NewUnverifyCollectionProposal(collectionAddr, collectionName, reason)

	proposal, err := registryDAO.Propose(caller, propDef)
	if err != nil {
		panic("failed to create proposal: " + err.Error())
	}

	return proposal.ID()
}

// VoteRegistry - Vote on a registry proposal
func VoteRegistry(_ realm, proposalID uint64, choice string) string {
	caller := runtime.PreviousRealm().Address()

	if choice != "yes" && choice != "no" {
		panic("choice must be 'yes' or 'no'")
	}

	// Use DAO's Vote method - handles all validations automatically
	err := registryDAO.Vote(caller, proposalID, commondao.VoteChoice(choice), "")
	if err != nil {
		panic("vote failed: " + err.Error())
	}

	return "Vote recorded: " + choice
}

// TallyRegistryProposal - Execute a registry proposal
func TallyRegistryProposal(_ realm, proposalID uint64) string {
	err := registryDAO.Execute(proposalID)
	if err != nil {
		panic("execution failed: " + err.Error())
	}

	return ufmt.Sprintf("Proposal %d executed successfully", proposalID)
}

