package daoregistry4

import (
	"errors"
	"time"

	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

const (
	PROPOSAL_TYPE_VERIFY_COLLECTION   = "verify_collection"
	PROPOSAL_TYPE_UNVERIFY_COLLECTION = "unverify_collection"

	REGISTRY_VOTING_PERIOD = 5 * time.Minute
	REGISTRY_QUORUM        = commondao.QuorumOneThird // 33%
)

// VerifyCollectionProposal - Proposal to verify a collection
type VerifyCollectionProposal struct {
	collectionAddr address
	collectionName string
	reason         string
	approved       bool
	executed       bool
}

func NewVerifyCollectionProposal(addr address, name string, reason string) *VerifyCollectionProposal {
	return &VerifyCollectionProposal{
		collectionAddr: addr,
		collectionName: name,
		reason:         reason,
		approved:       false,
		executed:       false,
	}
}

func (p *VerifyCollectionProposal) Title() string {
	return "Verify Collection: " + p.collectionName
}

func (p *VerifyCollectionProposal) Body() string {
	return ufmt.Sprintf(
		"Collection: %s\nAddress: %s\nReason: %s\n\nVote YES to verify, NO to reject",
		p.collectionName,
		p.collectionAddr.String(),
		p.reason,
	)
}

func (p *VerifyCollectionProposal) VotingPeriod() time.Duration {
	return REGISTRY_VOTING_PERIOD
}

func (p *VerifyCollectionProposal) Tally(ctx commondao.VotingContext) (bool, error) {
	votes := ctx.VotingRecord
	members := ctx.Members

	yesVotes := 0
	noVotes := 0

	votes.Iterate(0, votes.Size(), false, func(v commondao.Vote) bool {
		if v.Choice == commondao.ChoiceYes {
			yesVotes++
		} else if v.Choice == commondao.ChoiceNo {
			noVotes++
		}
		return false
	})

	// Check quorum
	if !commondao.IsQuorumReached(REGISTRY_QUORUM, votes, members) {
		p.approved = false
		return false, commondao.ErrNoQuorum
	}

	// Simple majority
	p.approved = yesVotes > noVotes
	return p.approved, nil
}

func (p *VerifyCollectionProposal) Execute(realm) error {
	info := GetCollection(p.collectionAddr)
	if info == nil {
		return errors.New("collection not found")
	}

	if info.Verified {
		return errors.New("collection already verified")
	}

	info.Verified = true
	collections.Set(p.collectionAddr.String(), info)

	p.executed = true
	return nil
}

// UnverifyCollectionProposal - Proposal to unverify a collection
type UnverifyCollectionProposal struct {
	collectionAddr address
	collectionName string
	reason         string
	approved       bool
	executed       bool
}

func NewUnverifyCollectionProposal(addr address, name string, reason string) *UnverifyCollectionProposal {
	return &UnverifyCollectionProposal{
		collectionAddr: addr,
		collectionName: name,
		reason:         reason,
		approved:       false,
		executed:       false,
	}
}

func (p *UnverifyCollectionProposal) Title() string {
	return "Unverify Collection: " + p.collectionName
}

func (p *UnverifyCollectionProposal) Body() string {
	return ufmt.Sprintf(
		"Collection: %s\nAddress: %s\nReason: %s\n\nVote YES to unverify, NO to reject",
		p.collectionName,
		p.collectionAddr.String(),
		p.reason,
	)
}

func (p *UnverifyCollectionProposal) VotingPeriod() time.Duration {
	return REGISTRY_VOTING_PERIOD
}

func (p *UnverifyCollectionProposal) Tally(ctx commondao.VotingContext) (bool, error) {
	votes := ctx.VotingRecord
	members := ctx.Members

	yesVotes := 0
	noVotes := 0

	votes.Iterate(0, votes.Size(), false, func(v commondao.Vote) bool {
		if v.Choice == commondao.ChoiceYes {
			yesVotes++
		} else if v.Choice == commondao.ChoiceNo {
			noVotes++
		}
		return false
	})

	// Check quorum
	if !commondao.IsQuorumReached(REGISTRY_QUORUM, votes, members) {
		p.approved = false
		return false, commondao.ErrNoQuorum
	}

	// Simple majority
	p.approved = yesVotes > noVotes
	return p.approved, nil
}

func (p *UnverifyCollectionProposal) Execute(realm) error {
	info := GetCollection(p.collectionAddr)
	if info == nil {
		return errors.New("collection not found")
	}

	if !info.Verified {
		return errors.New("collection not verified")
	}

	info.Verified = false
	collections.Set(p.collectionAddr.String(), info)

	p.executed = true
	return nil
}

package daoregistry4

import (
	"chain/runtime"

	"gno.land/p/demo/tokens/grc721"
	"gno.land/p/nt/avl"
)

// CollectionInfo - Extended metadata stored in the registry
type CollectionInfo struct {
	Address          address
	Name             string
	Symbol           string
	Creator          address
	RegisteredAt     int64
	Verified         bool
	Category         string
	Description      string
	ExternalURL      string
	NFTGetter        grc721.NFTGetter
	SupportsMetadata bool
}

var (
	collections     avl.Tree
	categoriesIndex avl.Tree
	registrationFee int64 = 1000000
)

func init() {
	initRegistryDAO()
}

// RegisterCollection - Register a new NFT collection
func RegisterCollection(
	realmAddr address,
	name string,
	symbol string,
	category string,
	description string,
	externalURL string,
	supportsMetadata bool,
	getter grc721.NFTGetter,
) {
	creator := runtime.OriginCaller()

	// Check if collection already registered
	if collections.Has(realmAddr.String()) {
		panic("Collection already registered")
	}

	if getter == nil {
		panic("NFT getter function is required")
	}

	if name == "" || symbol == "" {
		panic("Name and symbol cannot be empty")
	}

	info := &CollectionInfo{
		Address:          realmAddr,
		Creator:          creator,
		Name:             name,
		Symbol:           symbol,
		RegisteredAt:     runtime.ChainHeight(),
		Verified:         false,
		Category:         category,
		Description:      description,
		ExternalURL:      externalURL,
		NFTGetter:        getter,
		SupportsMetadata: supportsMetadata,
	}

	collections.Set(realmAddr.String(), info)
}

// IsRegistered - Check if a collection is registered
func IsRegistered(collectionAddr address) bool {
	return collections.Has(collectionAddr.String())
}

// GetTotalCollections - Total number of registered collections
func GetTotalCollections() int {
	count := 0
	collections.Iterate("", "", func(key string, value interface{}) bool {
		count++
		return false
	})
	return count
}

// GetVerifiedCount - Count of verified collections
func GetVerifiedCount() int {
	count := 0
	collections.Iterate("", "", func(key string, value interface{}) bool {
		info := value.(*CollectionInfo)
		if info.Verified {
			count++
		}
		return false
	})
	return count
}

// GetCollection - Retrieve collection info by address
func GetCollection(collectionAddr address) *CollectionInfo {
	val, exists := collections.Get(collectionAddr.String())
	if !exists {
		return nil
	}
	return val.(*CollectionInfo)
}

// GetNFTGetter - Retrieve the NFTGetter function for a collection
func GetNFTGetter(collectionAddr address) (grc721.NFTGetter, bool) {
	info := GetCollection(collectionAddr)
	if info == nil {
		return nil, false
	}
	return info.NFTGetter, true
}

// GetTokenMetadata - Retrieve onchain metadata for a token
func GetTokenMetadata(collectionAddr address, tokenId grc721.TokenID) (grc721.Metadata, error) {
	info := GetCollection(collectionAddr)
	if info == nil {
		panic("Collection not registered")
	}

	if !info.SupportsMetadata {
		panic("Collection does not support onchain metadata")
	}

	nftInstance := info.NFTGetter()
	metadataCollection := nftInstance.(grc721.IGRC721MetadataOnchain)

	return metadataCollection.TokenMetadata(tokenId)
}

// UpdateCollectionInfo - Update collection metadata
func UpdateCollectionInfo(category, description, externalURL string) {
	caller := runtime.PreviousRealm().Address()

	info := GetCollection(caller)
	if info == nil {
		panic("Collection not registered")
	}

	info.Category = category
	info.Description = description
	info.ExternalURL = externalURL
	collections.Set(caller.String(), info)
}

package daoregistry4

import (
	"strconv"
	"strings"

	"gno.land/p/leon/svgbtn"
	"gno.land/p/moul/txlink"
	"gno.land/p/nt/commondao"
	"gno.land/p/nt/ufmt"
)

func Render(path string) string {
	if path == "" {
		return renderHome()
	}

	if path == "proposals" {
		return renderProposals()
	}

	if path == "archive" {
		return renderArchive()
	}

	if strings.HasPrefix(path, "proposal/") {
		idStr := strings.TrimPrefix(path, "proposal/")
		id, _ := strconv.Atoi(idStr)
		return renderProposal(uint64(id))
	}

	if strings.HasPrefix(path, "archived/") {
		idStr := strings.TrimPrefix(path, "archived/")
		id, _ := strconv.Atoi(idStr)
		return renderArchivedProposal(uint64(id))
	}

	if strings.HasPrefix(path, "collection/") {
		addrStr := strings.TrimPrefix(path, "collection/")
		addr := address(addrStr)
		return renderCollection(addr)
	}

	return "Page not found"
}

func renderHome() string {
	output := "# NFT Registry\n\n"
	output += "Decentralized NFT Collection Registry with DAO Governance\n\n"
	output += "---\n\n"

	output += "## Statistics\n\n"
	output += ufmt.Sprintf("- Total registered collections: %d\n", GetTotalCollections())
	output += ufmt.Sprintf("- Verified collections: %d\n\n", GetVerifiedCount())

	output += "---\n\n"

	// DAO Section
	output += "## DAO Governance\n\n"
	output += ufmt.Sprintf("**Total DAO Members:** %d\n\n", GetTotalRegistryMembers())

	activeProposals := registryDAO.ActiveProposals()
	if activeProposals.Size() > 0 {
		output += ufmt.Sprintf("**Active Proposals:** %d\n\n", activeProposals.Size())
		output += "[View all proposals](/r/pierre115/daoregistry:proposals)\n\n"
	} else {
		output += "**Active Proposals:** 0\n\n"
		output += "[View proposals](/r/pierre115/daoregistry:proposals) | "
	}

	finishedProposals := registryDAO.FinishedProposals()
	output += ufmt.Sprintf("[View archive (%d)](/r/pierre115/daoregistry:archive)\n\n", finishedProposals.Size())
	output += "---\n\n"

	// Vote Join button
	linkjoin := txlink.NewLink("JoinRegistryDAO").
		URL()
	output += svgbtn.SuccessButton(100, 30, "Join DAO", linkjoin) + "\n\n"
	output += "---\n\n"

	// Collections list
	output += "## Registered Collections\n\n"

	hasCollections := false
	collections.Iterate("", "", func(key string, value interface{}) bool {
		hasCollections = true
		info := value.(*CollectionInfo)
		output += renderCollectionPreview(info)
		return false
	})

	if !hasCollections {
		output += "No collections registered yet.\n\n"
	}

	return output
}

func renderCollectionPreview(info *CollectionInfo) string {
	output := ufmt.Sprintf("### %s (%s)\n\n", info.Name, info.Symbol)

	if info.Verified {
		output += "Status: VERIFIED\n\n"
	} else {
		output += "Status: Not verified\n\n"
	}

	output += ufmt.Sprintf("Category: %s\n\n", info.Category)
