func JudgeScore(cur realm, pkgpath string, score int) {
	if Pausable.IsPaused() {
		return
	}
	if score < judgeScoreMin || score > judgeScoreMax {
		panic("score out of range (0-10)")
	}

	rawItem, ok := exhibition.items.Get(pkgpath)
	if !ok {
		panic("no such submission")
	}
	item := rawItem.(*Item)

	judgeAddr := runtime.PreviousRealm().Address()
	if !exhibition.judges.Has(judgeAddr) {
		panic("not a judge")
	}

	oldFinal := finalScore(item)
	exhibition.itemsSortedByScore.Remove(getScoreSortKey(oldFinal, item.id))

	judgeKey := judgeAddr.String() // avl keys must be strings

	oldRaw, hadOld := item.judgeScores.Get(judgeKey)
	if hadOld {
		oldInt, _ := strconv.Atoi(oldRaw.(string))
		item.judgeTotal -= oldInt
	}

	item.judgeScores.Set(judgeKey, strconv.Itoa(score))
	item.judgeTotal += score

	exhibition.itemsSortedByScore.Set(getScoreSortKey(finalScore(item), item.id), item)

	chain.Emit("JudgeScored")
}

