// Render is the GnoWeb entrypoint. It supports routes like :hall and :about.
func Render(path string) string {
	switch path {
	case "", "hall":
		return renderHall()
	case "about":
		return renderAbout()
	default:
		return "<div><h3>Not found</h3><p>Unknown route: <code>" + htmlEscape(path) + "</code></p></div>"
	}
}

func renderAbout() string {
	out := ""
	out += "<div style='font-family: ui-sans-serif, system-ui; max-width: 980px;'>"
	out += "<h2>gnomas ‚Äî Design Competition</h2>"
	if exhibition.description != "" {
		out += "<p>" + htmlEscape(exhibition.description) + "</p>"
	}
	out += "<p><a href=':hall'>Hall</a></p>"
	out += "</div>"
	return out
}

func renderHall() string {
	out := ""
	out += "<div style='font-family: ui-sans-serif, system-ui; max-width: 980px;'>"
	out += "<h2>Design Competition ‚Äî Hall</h2>"
	out += "<p><a href=':hall'>Hall</a> | <a href=':about'>About</a></p>"

	if exhibition.description != "" {
		out += "<p>" + htmlEscape(exhibition.description) + "</p>"
	}

	out += "<p><small>Submissions: " + ufmt.Sprintf("%d", exhibition.items.Size()) + "</small></p>"

	out += "<table style='width:100%; border-collapse: collapse;'>"
	out += "<thead><tr>"
	out += th("Rank")
	out += th("Title")
	out += th("Category")
	out += th("Description")
	out += th("Score")
	out += th("Votes")
	out += th("Preview")
	out += th("PkgPath")
	out += th("Actions")
	out += "</tr></thead><tbody>"

	// itemsSortedByScore is lexicographically ascending by key; show highest first by iterating backwards.
	n := exhibition.itemsSortedByScore.Size()
	rank := 1
	if n > 0 {
		for i := n - 1; i >= 0; i-- {
			_, v := exhibition.itemsSortedByScore.GetByIndex(i)
			item := v.(*Item)

			up := item.upvote.Size()
			down := item.downvote.Size()
			score := finalScore(item)

			out += "<tr style='border-top: 1px solid #e5e7eb;'>"
			out += td(ufmt.Sprintf("%d", rank))
			out += td("<strong>" + htmlEscape(item.title) + "</strong>")
			out += td(htmlEscape(item.category))
			out += td(htmlEscape(item.description))
			out += td(ufmt.Sprintf("%d", score))
			out += td(ufmt.Sprintf("‚è∂ %d &nbsp; ‚è∑ %d", up, down))

			if item.previewURL != "" {
				out += td("<a href='" + htmlEscapeAttr(item.previewURL) + "' target='_blank'>link</a>")
			} else {
				out += td("<span style='color:#6b7280;'>‚Äî</span>")
			}

			out += td("<code>" + htmlEscape(item.pkgpath) + "</code>")

			// Match leon/hor pattern: link to help+execute page
			upHref := "$help&func=Upvote&pkgpath=" + urlEscapeLite(item.pkgpath)
			downHref := "$help&func=Downvote&pkgpath=" + urlEscapeLite(item.pkgpath)

			out += td("<a href='" + upHref + "'>Upvote</a> &nbsp; <a href='" + downHref + "'>Downvote</a>")
			out += "</tr>"

			rank++

			if i == 0 {
				break // prevent i-- underflow on ints
			}
		}
	}

	out += "</tbody></table>"
	out += "</div>"
	return out
}

func th(s string) string {
	return "<th style='text-align:left; padding:10px 8px; font-size:12px; color:#374151;'>" + htmlEscape(s) + "</th>"
}

func td(s string) string {
	return "<td style='vertical-align:top; padding:10px 8px; font-size:13px;'>" + s + "</td>"
}

// Minimal HTML escaping.
func htmlEscape(s string) string {
	s = strings.ReplaceAll(s, "&", "&amp;")
	s = strings.ReplaceAll(s, "<", "&lt;")
	s = strings.ReplaceAll(s, ">", "&gt;")
	s = strings.ReplaceAll(s, "\"", "&quot;")
	s = strings.ReplaceAll(s, "'", "&#39;")
	return s
}

func htmlEscapeAttr(s string) string {
	return htmlEscape(s)
}

// Lightweight URL escaping for pkgpath in query strings.
func urlEscapeLite(s string) string {
	s = strings.ReplaceAll(s, "%", "%25")
	s = strings.ReplaceAll(s, " ", "%20")
	s = strings.ReplaceAll(s, "?", "%3F")
	s = strings.ReplaceAll(s, "&", "%26")
	s = strings.ReplaceAll(s, "=", "%3D")
	s = strings.ReplaceAll(s, "#", "%23")
	s = strings.ReplaceAll(s, ":", "%3A")
	s = strings.ReplaceAll(s, "/", "%2F")
	return s
}

