		category    string
		previewURL  string

		// For this user-submission version, this is the submitter identity (address string).
		submitter string

		blockNum int64
		status   string // submitted | finalist | winner | disqualified

		upvote   *addrset.Set
		downvote *addrset.Set

		// Judge scores: key (string) = judge address string; value (string) = score int as string
		judgeScores *avl.Tree
		judgeTotal  int
	}
)

func init() {
	exhibition = &Exhibition{
		items:                  avl.NewTree(),
		itemsSortedByCreation:  avl.NewTree(),
		itemsSortedByScore:     avl.NewTree(),
		itemsSortedByUpvotes:   avl.NewTree(),
		itemsSortedByDownvotes: avl.NewTree(),
		judges:                 &addrset.Set{},
	}

	Ownable = ownable.NewWithAddress(config.OwnableMain.Owner())
	Pausable = pausable.NewFromOwnable(Ownable)
}

//
// Admin / configuration
//

func ConfigureCompetition(cur realm, submissionOpen, submissionClose, votingOpen, votingClose int64, description string) {
	if !Ownable.OwnedByPrevious() {
		panic(ownable.ErrUnauthorized)
	}
	if submissionOpen != 0 && submissionClose != 0 && submissionClose < submissionOpen {
		panic("invalid submission window")
	}
	if votingOpen != 0 && votingClose != 0 && votingClose < votingOpen {
		panic("invalid voting window")
	}

	exhibition.submissionOpenBlock = submissionOpen
	exhibition.submissionCloseBlock = submissionClose
	exhibition.votingOpenBlock = votingOpen
	exhibition.votingCloseBlock = votingClose
	exhibition.description = description

	chain.Emit("CompetitionConfigured")
}

func Pause(cur realm) {
	if !Ownable.OwnedByPrevious() {
		panic(ownable.ErrUnauthorized)
	}
	Pausable.Pause()
	chain.Emit("Paused")
}

func Unpause(cur realm) {
	if !Ownable.OwnedByPrevious() {
		panic(ownable.ErrUnauthorized)
	}
	Pausable.Unpause()
	chain.Emit("Unpaused")
}

// AddJudge adds the caller as a judge (owner-only).
func AddJudge(cur realm) {
	if !Ownable.OwnedByPrevious() {
		panic(ownable.ErrUnauthorized)
	}
	judge := runtime.PreviousRealm().Address()
	exhibition.judges.Add(judge)
	chain.Emit("JudgeAdded")
}

// RemoveJudge removes the caller from judges (owner-only).
func RemoveJudge(cur realm) {
	if !Ownable.OwnedByPrevious() {
		panic(ownable.ErrUnauthorized)
	}
	judge := runtime.PreviousRealm().Address()
	exhibition.judges.Remove(judge)
	chain.Emit("JudgeRemoved")
}

//
// Submission (USER-SUBMITTED)
//

// SubmitDesign stores one submission per user (keyed by address string).
func SubmitDesign(cur realm, title, description, category, previewURL string) {
	if Pausable.IsPaused() {
		return
	}

	height := runtime.ChainHeight()
	if exhibition.submissionOpenBlock != 0 && height < exhibition.submissionOpenBlock {
		return
	}
	if exhibition.submissionCloseBlock != 0 && height > exhibition.submissionCloseBlock {
		return
	}

	submitterKey := runtime.PreviousRealm().Address().String()

	// One submission per user; change to panic or allow overwrite if desired.
	if exhibition.items.Has(submitterKey) {
		return
	}

	if title == "" || len(title) > maxTitleLength {
		return
	}
	if len(description) > maxDescriptionLength {
		return
	}
	if category == "" || len(category) > maxCategoryLength {
		return
	}
	if previewURL != "" && len(previewURL) > maxURLLength {
		return
	}

	id := exhibition.itemCounter.Next()
	item := &Item{
		id:          id,
		title:       title,
		description: description,
		category:    category,
		previewURL:  previewURL,
		submitter:   submitterKey,
		blockNum:    height,
		status:      "submitted",
		upvote:      &addrset.Set{},
		downvote:    &addrset.Set{},
		judgeScores: avl.NewTree(),
		judgeTotal:  0,
	}

	exhibition.items.Set(submitterKey, item)

	exhibition.itemsSortedByCreation.Set(getCreationSortKey(item.blockNum, item.id), item)
	exhibition.itemsSortedByUpvotes.Set(getVoteSortKey(0, item.id), item)
	exhibition.itemsSortedByDownvotes.Set(getVoteSortKey(0, item.id), item)
	exhibition.itemsSortedByScore.Set(getScoreSortKey(0, item.id), item)

	chain.Emit("DesignSubmitted")
}

//
// Voting
//

func Upvote(cur realm, submitterKey string) {
	if Pausable.IsPaused() {
		return
	}

	height := runtime.ChainHeight()
	if exhibition.votingOpenBlock != 0 && height < exhibition.votingOpenBlock {
		return
	}
	if exhibition.votingCloseBlock != 0 && height > exhibition.votingCloseBlock {
		return
	}

	rawItem, ok := exhibition.items.Get(submitterKey)
	if !ok {
		panic("no such submission")
	}
	item := rawItem.(*Item)

	caller := runtime.PreviousRealm().Address()
	if item.upvote.Has(caller) {
		panic("double upvote")
	}
	if caller.String() == item.submitter {
		panic("cannot vote own submission")
	}

	oldFinal := finalScore(item)
	exhibition.itemsSortedByUpvotes.Remove(getVoteSortKey(item.upvote.Size(), item.id))
	exhibition.itemsSortedByScore.Remove(getScoreSortKey(oldFinal, item.id))

	item.upvote.Add(caller)

	exhibition.itemsSortedByUpvotes.Set(getVoteSortKey(item.upvote.Size(), item.id), item)
	exhibition.itemsSortedByScore.Set(getScoreSortKey(finalScore(item), item.id), item)

	chain.Emit("Upvoted")
}

func Downvote(cur realm, submitterKey string) {
	if Pausable.IsPaused() {
		return
	}

	height := runtime.ChainHeight()
	if exhibition.votingOpenBlock != 0 && height < exhibition.votingOpenBlock {
		return
	}
	if exhibition.votingCloseBlock != 0 && height > exhibition.votingCloseBlock {
		return
	}

	rawItem, ok := exhibition.items.Get(submitterKey)
	if !ok {
		panic("no such submission")
	}
	item := rawItem.(*Item)

	caller := runtime.PreviousRealm().Address()
	if item.downvote.Has(caller) {
		panic("double downvote")
	}
	if caller.String() == item.submitter {
		panic("cannot vote own submission")
	}

	oldFinal := finalScore(item)
	exhibition.itemsSortedByDownvotes.Remove(getVoteSortKey(item.downvote.Size(), item.id))
	exhibition.itemsSortedByScore.Remove(getScoreSortKey(oldFinal, item.id))

	item.downvote.Add(caller)

	exhibition.itemsSortedByDownvotes.Set(getVoteSortKey(item.downvote.Size(), item.id), item)
	exhibition.itemsSortedByScore.Set(getScoreSortKey(finalScore(item), item.id), item)

	chain.Emit("Downvoted")
}

//
// Judge scoring
//

func JudgeScore(cur realm, submitterKey string, score int) {
	if Pausable.IsPaused() {
		return
	}
	if score < judgeScoreMin || score > judgeScoreMax {
		panic("score out of range (0-10)")
	}

	rawItem, ok := exhibition.items.Get(submitterKey)
	if !ok {
		panic("no such submission")
	}
	item := rawItem.(*Item)

	judgeAddr := runtime.PreviousRealm().Address()
	if !exhibition.judges.Has(judgeAddr) {
		panic("not a judge")
	}

	oldFinal := finalScore(item)
	exhibition.itemsSortedByScore.Remove(getScoreSortKey(oldFinal, item.id))

	judgeKey := judgeAddr.String()

	oldRaw, hadOld := item.judgeScores.Get(judgeKey)
	if hadOld {
		oldInt, _ := strconv.Atoi(oldRaw.(string))
		item.judgeTotal -= oldInt
	}

	item.judgeScores.Set(judgeKey, strconv.Itoa(score))
	item.judgeTotal += score

	exhibition.itemsSortedByScore.Set(getScoreSortKey(finalScore(item), item.id), item)

	chain.Emit("JudgeScored")
}

//
// Status management (owner-only)
//

func SetStatus(cur realm, submitterKey, status string) {
	if !Ownable.OwnedByPrevious() {
		panic(ownable.ErrUnauthorized)
	}
	if status != "submitted" && status != "finalist" && status != "winner" && status != "disqualified" {
		panic("invalid status")
	}

	rawItem, ok := exhibition.items.Get(submitterKey)
	if !ok {
		panic("no such submission")
	}
	item := rawItem.(*Item)
	item.status = status

	chain.Emit("StatusUpdated")
}

//
// Admin delete (owner-only)
//

func Delete(cur realm, submitterKey string) {
	if !Ownable.OwnedByPrevious() {
		panic(ownable.ErrUnauthorized)
	}

	rawItem, ok := exhibition.items.Get(submitterKey)
	if !ok {
		panic("no such submission")
	}
	item := rawItem.(*Item)

	exhibition.itemsSortedByCreation.Remove(getCreationSortKey(item.blockNum, item.id))
	exhibition.itemsSortedByUpvotes.Remove(getVoteSortKey(item.upvote.Size(), item.id))
	exhibition.itemsSortedByDownvotes.Remove(getVoteSortKey(item.downvote.Size(), item.id))
	exhibition.itemsSortedByScore.Remove(getScoreSortKey(finalScore(item), item.id))

	exhibition.items.Remove(submitterKey)

	chain.Emit("Deleted")
}

//
// Helpers
//

func finalScore(item *Item) int {
	return (item.upvote.Size() - item.downvote.Size()) + item.judgeTotal
}

func getVoteSortKey(votes int, id seqid.ID) string {
	votesStr := strconv.Itoa(votes)
	paddedVotes := strings.Repeat("0", 10-len(votesStr)) + votesStr
	return paddedVotes + ":" + strconv.FormatUint(uint64(id), 10)
}

func getScoreSortKey(score int, id seqid.ID) string {
	normalized := score + scoreOffset
	if normalized < 0 {
		normalized = 0
	}
	scoreStr := strconv.Itoa(normalized)
	padded := strings.Repeat("0", 10-len(scoreStr)) + scoreStr
	return padded + ":" + strconv.FormatUint(uint64(id), 10)
}

func getCreationSortKey(blockNum int64, id seqid.ID) string {
	blockNumStr := strconv.Itoa(int(blockNum))
	paddedBlockNum := strings.Repeat("0", 10-len(blockNumStr)) + blockNumStr
	return paddedBlockNum + ":" + strconv.FormatUint(uint64(id), 10)
}

//
// Render (root-only)
//

func Render(path string) string {
	out := md.H1("gnomas15 ‚Äî Design Competition\n\n")

	if exhibition.description != "" {
		out += ufmt.Sprintf("%s\n\n", exhibition.description)
	}

	out += ufmt.Sprintf("Total submissions: **%d**\n\n", exhibition.items.Size())

	// Convenience "Submit" tx link (user edits values on the help page)
	out += md.Link("Submit a design", txlink.Call("SubmitDesign",
		"title", "Your title",
		"description", "Your description",
		"category", "poster",
		"previewURL", "https://...",
	))
	out += "\n\n"
	out += md.HorizontalRule()

	if exhibition.items.Size() == 0 {
		out += "No submissions yet.\n\n"
		return out
	}

	tree := exhibition.itemsSortedByScore
	n := tree.Size()
	rank := 1

	for i := n - 1; i >= 0; i-- {
		_, v := tree.GetByIndex(i)
		item := v.(*Item)

		out += renderItemCard(item, rank)
		rank++

		if i == 0 {
			break
		}
	}

	return out
}

func renderItemCard(item *Item, rank int) string {
	out := ""
	out += md.H3(ufmt.Sprintf("%d. %s\n\n", rank, item.title))

	if item.description == "" {
		out += "_No description provided._\n\n"
	} else {
		out += ufmt.Sprintf("%s\n\n", item.description)
	}

	out += ufmt.Sprintf("Category: **%s**\n\n", item.category)
	out += ufmt.Sprintf("Submitter: `%s`\n\n", item.submitter)

	score := finalScore(item)
	out += ufmt.Sprintf("Score: **%d** | Votes: **‚è∂ %d - ‚è∑ %d**\n\n",
		score, item.upvote.Size(), item.downvote.Size(),
	)

	if item.previewURL != "" {
		out += md.Link("Preview", item.previewURL) + "\n\n"
	}

	out += md.Bold(ufmt.Sprintf("%s  |  %s",
		md.Link("Upvote", txlink.Call("Upvote", "submitterKey", item.submitter)),
		md.Link("Downvote", txlink.Call("Downvote", "submitterKey", item.submitter)),
	))
	out += "\n\n"
	out += md.HorizontalRule()

	return out
}

