func Render(path string) string {
	if path == "" {
		return "Hello, world!"
	} else {
		return "Hello, " + path
	}
}

package querystring

import "strings"

// Break down path and querystring into a map[string]string.
// Since #2876, realm paths include the querystring,
// as in `/r/test:a?c=3` (question mark introduces the querystring).
//
// In case of a name defined several times, the last value wins.
//
// "a=1"             -> {a:"1"}, nil
// "?a=1"            -> {a:"1"}, nil
// "a=1&b=2?c=3&b=4" -> {a:"1", b:"4", c:"3"}, nil
func ToMap(querystring string) (map[string]string, error) {
	m := make(map[string]string)
	t := strings.Replace(querystring, "?", "&", 1)
	for _, s := range strings.Split(t, "&") {
		if len(strings.TrimSpace(s)) == 0 {
			continue
		}
		idx := strings.Index(s, "=")
		if idx > -1 {
			m[s[:idx]] = s[idx+1:]
		} else {
			m[s] = ""
		}
	}
	return m, nil
}

package querystring

import (
	"testing"

	"gno.land/p/nt/uassert"
)

func TestToMap(t *testing.T) {
	{
		m, e := ToMap("city=london&=&&&place=bigben&nuggets")
		uassert.Equal(t, e, "", "ToMap(city=london&=&&&place=bigben&nuggets) has no error")
		{
			city, has := m["city"]
			uassert.True(t, has)
			uassert.Equal(t, city, "london")
		}
		{
			place, has := m["place"]
			uassert.True(t, has)
			uassert.Equal(t, place, "bigben")
		}
		{
			nuggets, has := m["nuggets"]
			uassert.True(t, has)
			uassert.Equal(t, nuggets, "")
		}
	}
	{
		m, e := ToMap("a=b&=&&?c=d&e&a=f")
		uassert.Equal(t, e, "", "ToMap(a=b&=&&?c=d&e&a=f) has no error")
		{
			a, has := m["a"]
			uassert.True(t, has)
			uassert.Equal(t, a, "f")
		}
		{
			c, has := m["c"]
			uassert.True(t, has)
			uassert.Equal(t, c, "d")
		}
		{
			v, has := m["e"]
			uassert.True(t, has)
			uassert.Equal(t, v, "")
		}
	}
	{
		// only the first question mark is considered
		m, e := ToMap("a=b&=&&?c=d&e&a=f???")
		uassert.Nil(t, e, "ToMap(a=b&=&&?c=d&e&a=f???) has no error")
		{
			a, has := m["a"]
			uassert.True(t, has)
			uassert.Equal(t, a, "f???")
		}
		{
			c, has := m["c"]
			uassert.True(t, has)
			uassert.Equal(t, c, "d")
		}
		{
			v, has := m["e"]
			uassert.True(t, has)
			uassert.Equal(t, v, "")
		}
	}
	{
		m, e := ToMap("?a=b&a=c=d")
		uassert.Nil(t, e, "ToMap(?a=b&a=c=d) has no error")
		{
			a, has := m["a"]
			uassert.True(t, has)
			uassert.Equal(t, a, "c=d")
		}
	}
	{
		m, e := ToMap("?abc")
		uassert.Nil(t, e, "ToMap(?abc) has no error")
		{
			abc, has := m["abc"]
			uassert.True(t, has)
			uassert.Equal(t, abc, "")
		}
	}
}

