package tictactoe

import (
	"math/rand"
	"testing"

	"gno.land/p/nt/uassert"
)

var prngTest = rand.New(rand.NewPCG(uint64(3), uint64(7)))

func TestRand(t *testing.T) {
	n0 := prngTest.IntN(10)
	for i := 0; i < 128; i++ {
		if prngTest.IntN(10) != n0 {
			return
		}
	}
	t.Errorf("no randomness")
}

func TestGameFromRepr(t *testing.T) {
	for _, o := range []struct{ repr, expect string }{
		{repr: "", expect: "---------"},
		{repr: "-X--OX-O-", expect: "-X--OX-O-"},
		{repr: "_x__ox_o_", expect: "-X--OX-O-"},
		{repr: "O--------", expect: "O--------"},
		{repr: "O---X----", expect: "O---X----"},
		{repr: "-X--O--O-", expect: "-X--O--O-"},
		{repr: "OXOXOXOXO", expect: "OXOXOXOXO"},
		{repr: "X--------", expect: "error"}, // O always begin
		{repr: "OOOOOX---", expect: "error"},
	} {
		game, e := GameFromRepr(o.repr, cpuAddress, humanAddress, prngTest.IntN, nil)
		if o.expect == "error" {
			uassert.Error(t, e)
		} else {
			uassert.NoError(t, e)
			uassert.Equal(t, o.expect, game.ToRepr())
		}
	}
}

func TestPlayer1Alternates(t *testing.T) {
	// ensure CPU goes first or second, in different games
	var isCpuFirst0 bool
	for i := 0; i < 128; i++ {
		g, e := GameFromRepr("", cpuAddress, humanAddress, prngTest.IntN, nil)
		uassert.NoError(t, e)
		if i == 0 {
			isCpuFirst0 = g.IsCpuFirst()
		} else if isCpuFirst0 != g.IsCpuFirst() {
			return // ok
		}
	}
	t.FailNow()
}

func TestPickMove(t *testing.T) {
	// study expected VS statistical result
	// CPU sees own winning move ~80% of times
	//     is otherwise blind to other side
	for _, o := range []struct {
		board     string
		expectedX int
		expectedY int
	}{
		{"O-OX-X---", 1, 2},
		{"O-OX-XO--", 1, 1},
		{"X-O-XOO--", 2, 0},
		{"OX-XO----", 2, 0},
	} {
		// play over and over, see most frequent moves
		h := map[struct{ x, y int }]int{}
		for i := 0; i < 64; i++ {
			game, e := GameFromRepr(o.board, cpuAddress, humanAddress, prngTest.IntN, nil)
			if !uassert.NoError(t, e, "GameFromRepr") {
				t.FailNow()
			}
			x, y, e := game.pickMove(game.Game, game.Intn)
			h[struct{ x, y int }{x, y}] += 1
		}
		// assert most frequent move
		var hiTemperature int
		var hotMove struct{ x, y int }
		for coord, v := range h {
			if v > hiTemperature {
				hiTemperature = v
				hotMove = coord
			}
		}
		uassert.Equal(t, o.expectedX, hotMove.x, "for x of repr", o.board)
		uassert.Equal(t, o.expectedY, hotMove.y, "for y of repr", o.board)
	}
}

package tictactoe

// a 1P-vs-CPU tictactoe
// extending moul's tictactoe model

import (
	"errors"
	"strings"

	"gno.land/p/nt/ufmt"
)

// 1P-VS-CPU is a tictactoe game
type GameVsCPU struct {
	*Game
	cpu      address
	Intn     func(n int) int
	pickMove func(g *Game, Intn func(int) int) (x, y int, e error) // AI function
}

// "" is a whole new game, whether CPU goes first is random.
// "---------"` OTOH indicates a game where cpu /declined/ to go first.
// fRand is a user-supplied func returning a random number in [0, n(
// fPickMove is nil (uses averageDifficulty) or a func used for CPU to pick moves
func GameFromRepr(
	text string,
	cpu, human address,
	fRand func(int) int,
	fPickMove func(*Game, func(int) int,
	) (x, y int, e error),
) (*GameVsCPU, error) {
	addr1, addr2 := decideOrder(cpu, human, text, fRand)

	var g *Game
	var e error
	switch len(text) {
	case 0:
		g, e = NewGame(addr1, addr2)
		g.Start()
	case 9:
		r := []rune(text)
		g, e = RecoverGame(
			addr1,
			addr2,
			string([]rune{
				r[6], r[7], r[8],
				r[3], r[4], r[5],
				r[0], r[1], r[2],
			}),
		)
	default:
		return nil, errors.New("invalid board length")
	}
	if fPickMove == nil {
		fPickMove = averageDifficulty
	}
	return &GameVsCPU{g, cpu, fRand, fPickMove}, e
}

func (game GameVsCPU) ToRepr() string {
	return strings.ReplaceAll(game.Output(), "\n", "")
}

func (game GameVsCPU) IsCpuFirst() bool {
	return game.PlayerByIndex(0) == game.cpu
}

func (game *GameVsCPU) PlayCPU() (x, y int, e error) {
	switch {
	case game.Turn() != game.cpu:
		return -1, -1, ufmt.Errorf(
			"not my turn (%s), turn is %s's",
			game.cpu.String(), game.Turn().String(),
		)
	case game.IsOver():
		return -1, -1, errors.New("game is over")
	default:
		x, y, _ = game.pickMove(game.Game, game.Intn)
		e = game.Play(game.cpu, x, y)
		return x, y, e
	}
}

// Decide who go first, based on the count of markers.
// No error. A special and important case is the empty board,
// meaning player to go first is random.
// fRand is a user-supplied func returning a random number in [0, n(
func decideOrder(cpu, human address, board string, fRand func(int) int) (addr1, addr2 address) {
	var cpuFirst bool
	if board == "" {
		cpuFirst = fRand(2) == 0
	} else {
		numO := strings.Count(board, "O") + strings.Count(board, "o")
		numX := strings.Count(board, "X") + strings.Count(board, "x")
		cpuFirst = numX != numO
	}
	if cpuFirst {
		return cpu, human
	} else {
		return human, cpu
	}
}

package tictactoe

import (
	"errors"
)

// the default move picker.
func averageDifficulty(game *Game, Intn func(int) int) (x, y int, e error) {
	// candidates
	a := make([]struct{ x, y int }, 0)
	side := rune(1 + game.TurnNumber()%2)
	for y := 0; y <= 2; y++ {
		for x := 0; x <= 2; x++ {
			if game.At(x, y) != rune(0) {
				continue
			}
			// if can wins (and can see it), then win
			if game.WouldWin(side, x, y) && Intn(5) > 0 {
				return x, y, nil
			}
			a = append(a, struct{ x, y int }{x, y})
		}
	}
	if len(a) == 0 {
		return -1, -1, errors.New("no free tile left")
	} else {
		// random pick among candidates
		c := a[Intn(len(a))]
		return c.x, c.y, nil
	}
}

