package tictactoe

import (
	"errors"
	"gno.land/p/nt/ufmt"
)

// this file is @moul's work in #613
// a few changes and bugfixes have been made

type Game struct {
	player1, player2 address
	board            [9]rune // 0=empty, 1=player1, 2=player2
	turnCtr          int
	winnerIdx        int
}

func NewGame(player1, player2 address) (*Game, error) {
	if player1 == player2 {
		return nil, errors.New("cannot fight against self")
	}

	g := Game{
		player1:   player1,
		player2:   player2,
		winnerIdx: -1,
		turnCtr:   -1,
	}
	return &g, nil
}

// Partially recover a game
// The game is guaranteed to be legit in terms of number of tiles 1 and 2
// No winning detection is implemented here however
func RecoverGame(player1, player2 address, board string) (*Game, error) {
	g, e := NewGame(player1, player2)
	if e != nil {
		return nil, e
	}
	if len(board) != 9 {
		return nil, ufmt.Errorf("invalid board length: %d", len(board))
	}
	num1, num2 := 0, 0
	runes := [9]rune{}
	for i, c := range board {
		switch c {
		case rune(0), '_', '-':
			runes[i] = rune(0)
		case rune(1), 'O', 'o':
			num1 += 1
			runes[i] = rune(1)
		case rune(2), 'X', 'x':
			num2 += 1
			runes[i] = rune(2)
		default:
			return nil, errors.New("invalid rune")
		}
	}
	if num1 != num2 && num1 != num2+1 {
		return nil, errors.New("invalid number of x and o")
	}
	g.board = runes
	g.turnCtr = num1 + num2
	g.winnerIdx = -1
	return g, nil
}

// start sets turnCtr to 0.
func (g *Game) Start() {
	if g.turnCtr != -1 {
		panic("game already started")
	}
	g.turnCtr = 0
}

func (g *Game) Play(player address, posX, posY int) error {
	if !g.Started() {
		return errors.New("game not started")
	}

	if g.Turn() != player {
		return errors.New("invalid turn")
	}

	if g.IsOver() {
		return errors.New("game over")
	}

	// are posX and posY valid
	if posX < 0 || posY < 0 || posX > 2 || posY > 2 {
		return errors.New("posX and posY should be 0, 1 or 2")
	}

	// is slot already used?
	idx := xyToIdx(posX, posY)
	if g.board[idx] != 0 {
		return ufmt.Errorf("slot already used (%d, %d)", posX, posY)
	}

	// play
	playerVal := rune(g.turnCtr%2) + 1 // player1=1, player2=2
	g.board[idx] = playerVal

	// check if win
	if g.checkLastMoveWon(posX, posY) {
		g.winnerIdx = g.turnCtr
	}

	// change turn
	g.turnCtr++
	return nil
}

func (g Game) WouldWin(side rune, x, y int) bool {
	idx := xyToIdx(x, y)
	if g.board[idx] != rune(0) {
		panic("tile should be empty")
	}
	// place rune temporarily
	g.board[idx] = side
	b := g.checkLastMoveWon(x, y)
	g.board[idx] = rune(0)
	return b
}

func (g Game) checkLastMoveWon(posX, posY int) bool {
	// assumes the game wasn't won yet, and that the move was already applied.

	// check vertical line
	{
		a := g.At(posX, 0)
		b := g.At(posX, 1)
		c := g.At(posX, 2)
		if a == b && b == c {
			return true
		}
	}

	// check horizontal line
	{
		a := g.At(0, posY)
		b := g.At(1, posY)
		c := g.At(2, posY)
		if a == b && b == c {
			return true
		}
	}

	// diagonals
	{
		tl := g.At(0, 0)
		tr := g.At(0, 2)
		bl := g.At(2, 0)
		br := g.At(2, 2)
		c := g.At(1, 1)
		if posX == posY && tl == c && c == br {
			return true
		}
		if posX+posY == 2 && tr == c && c == bl {
			return true
		}
	}
	return false
}

func (g Game) At(posX, posY int) rune { return g.board[xyToIdx(posX, posY)] }
func (g Game) Winner() address    { return g.PlayerByIndex(g.winnerIdx) }
func (g Game) Turn() address      { return g.PlayerByIndex(g.turnCtr) }
func (g Game) TurnNumber() int        { return g.turnCtr }
func (g Game) IsDraw() bool           { return g.turnCtr > 8 && g.winnerIdx == -1 }
func (g Game) Started() bool          { return g.turnCtr >= 0 }

func (g Game) IsOver() bool {
	// draw
	if g.turnCtr > 8 {
		return true
	}

	// winner
	return g.Winner() != address("")
}

func (g Game) Output() string {
	output := ""

	for y := 2; y >= 0; y-- {
		for x := 0; x < 3; x++ {
			val := g.At(x, y)
			switch val {
			case 0:
				output += "-"
			case 1:
				output += "O"
			case 2:
				output += "X"
			}
		}
		output += "\n"
	}

	return output
}

func (g Game) PlayerByIndex(idx int) address {
	switch idx % 2 {
	case 0:
		return g.player1
	case 1:
		return g.player2
	default:
		return address("")
	}
}

func xyToIdx(x, y int) int { return y*3 + x }

package tictactoe

// Stateless human VS CPU Tic-tac-toe
// Markdown + HTML1.0 + gnolang
// no javascript, no wallet needed.

import (
	"chain/runtime"
	"math/rand"
	"strings"

	querystring "gno.land/p/grepsuzette001/querystring/v1"
	ternary "gno.land/p/grepsuzette001/ternary/v1"
	"gno.land/p/nt/ufmt"
)

const (
	version      = "v8"
	cpuAddress   = address("gCPU")
	humanAddress = address("gHUMAN")
	urlParrot    = "https://raw.githubusercontent.com/grepsuzette/gfx/master/parrot.png"
	statusWon    = "Looks like you've won!"
	statusLost   = "Sorry mate, you lost!"
	statusDraw   = "It's a draw..."
)

var prng *rand.Rand

func Intn(n int) int {
	if prng == nil {
		// Note: our PRNG is not stateful as calling Render is not going
		// to modify this stateless realm. We initialize it here when still nil
		// this creates the randomness we need (seeded from blockchain's height)
		prng = rand.New(rand.NewPCG(uint64(runtime.ChainHeight()), uint64(9)))
	}
	return prng.IntN(n)
}

func Render(path string) string {
	game, x, y, debug := parsePath(path)
	if x != -1 && y != -1 {
		game.Play(humanAddress, x, y)
	}
	cpuX, cpuY, e := game.PlayCPU()
	output := parrotTalk(*game)
	output += render(*game, cpuX, cpuY)
	if debug {
		output += ufmt.Sprintf(
			"--- played x=%d y=%d cpuX=%d cpuY=%d height=%d path=%s turn:%d error:%s",
			x, y, cpuX, cpuY, runtime.ChainHeight(), path, game.Game.TurnNumber(), e,
		)
	}
	return output
}

// Lower-case render is simply called by Render.
func render(game GameVsCPU, cpuX, cpuY int) string {
	status, class := statusAndClass(game)
	repr := game.ToRepr()
	output := css()
	output += `<form id="board" method="GET" class="` + class + `">` + "\n"
	output += "\t" + `<input type="hidden" name="state" value="` + repr + `">` + "\n"
	output += `<div id="left"><img src="` + urlParrot + `" id="parrot" width="120" height="120" align="left" /></div>` + "\n"
	output += `<div id="right">`

	for y := 2; y >= 0; y-- {
		output += "\t"
		for x := 0; x <= 2; x++ {
			runeAtXY := game.At(x, y)
			occupied := runeAtXY != rune(0)
			if occupied {
				highlighted := x == cpuX && y == cpuY
				output += button(x, y, ternary.String(runeAtXY == rune(1), "O", "X"), true, highlighted, repr)
			} else if game.IsOver() {
				output += button(x, y, "&nbsp;", true, false, repr)
			} else {
				// tile is free, but :hover must show
				cpu1st := game.Game.PlayerByIndex(0) == cpuAddress
				output += button(x, y, ternary.String(cpu1st, "X", "O"), false, false, repr)
			}
		}
		output += "<br />\n"
	}
	output += "</div>\n"
	output += "</form>\n"
	if game.IsOver() {
		output += ufmt.Sprintf(
			"\n%s [ %s | %s ]\n",
			status,
			"[New game](/r/grepsuzette001/games/tictactoe/"+version+")",
			"[Back to grepsuzette001/games](/r/grepsuzette001/games)",
		)
	} else {
		output += "\n[Okay country roads, take me home](/r/grepsuzette001/games)"
	}
	return output
}

func button(x, y int, char string, occupied, highlighted bool, repr string) string {
	return ufmt.Sprintf(
		`<button name="move" value="%c%d" %s class="button tile %s">%s</button>`,
		rune('a'+x), y+1,
		ternary.String(occupied, "disabled", ""),
		ternary.String(highlighted, "highlighted", ""),
		char,
	)
}

// return status and class(es).
// it's empty when the game is not over.
func statusAndClass(game GameVsCPU) (status string, classes string) {
	if game.IsOver() {
		switch {
		case game.Winner() == humanAddress:
			classes = "over won"
			status = statusWon
		case game.Winner() == cpuAddress:
			classes = "over lost"
			status = statusLost
		default:
			classes = "over draw"
			status = statusDraw
		}
	}
	return
}

// Decompose path, into valid game and played coordinates (or -1,-1)
// The path is like "move=c3?state=O--X-----"
//
//   - state is empty or 9 characters,
//     imagine the following but without the \n: "-OX\nO--\n---".
//     O always plays first.
//
//   - move coordinate ranges from "a1" to "c3" but can be empty.
//
//   - debug &debug enables printing of a debug line.
func parsePath(path string) (game *GameVsCPU, x, y int, debug bool) {
	h, e := querystring.ToMap(path)
	if e != nil {
		panic(e)
	}
	// nil: default AI
	game, e = GameFromRepr(h["state"], cpuAddress, humanAddress, Intn, nil)
	if e != nil {
		panic(e)
	}
	_, debug = h["debug"]
	x, y = -1, -1
	for i, xx := range h["move"] {
		switch i {
		case 0:
			x = int(xx) - int('a')
		case 1:
			y = int(xx) - int('1')
		default:
			panic("invalid move: " + h["move"])
		}
	}
	return game, x, y, debug
}

func parrotTalk(game GameVsCPU) string {
	var a []string
	switch {
	case !game.IsOver():
		a = []string{
			"Let's play Tic-tac-toe!",
			"Cap'n Cluck, am here and ready to rule the high seas of tic-tac-toe!",
			"Aarrr, we'll see who'll be top parrot on this jolly board.",
			"The stakes are higher than the mast of a sunken galleon!",
		}
	case game.IsDraw():
		a = []string{
			"Three cheers for Yers Truly, Cap'n! It's a draw.",
			"It's a draw, sailor...",
			"This match ends in neither victory nor defeat, but a draw!",
		}
	case game.Winner() == cpuAddress:
		a = []string{
			"Alrighty, mateys! Who's the bravest birdy of the seven seas!",
			"Avast, ye scallywags, for I won this game",
			"Remember, me hearties, even in victory, Cap'n Cluck remains a humble scallywag.",
			"Aarrr, parrot power prevails again!",
			"Cap'n Cluck claims the prize!",
			"The high seas crown me king o' the game!",
		}
	case game.Winner() != cpuAddress:
		a = []string{
			"Ye bested a bold parrot on this day!",
			"Cap'n Cluck, beaten by a buccaneer? Nay, 'tis unbirdable!",
			"Aarrr, this be the day I, Cap'n Cluck, ate me parroted pirate's words! But fear not, for I shall be back!",
			"Ye bested this parrot, but amongst feathery fiends, revenge is ripe!",
			"Despite this defeat, remember, I, Cap'n Cluck, am not a chicken when it comes to tic-tac-toe!",
		}
	}
	s := a[Intn(len(a))]
	return ufmt.Sprintf("<div><b>%s</b><br />"+strings.Repeat("&nbsp;", 17)+"/</div>", s)
}

package tictactoe

import (
	"testing"
)

func TestParsePath(t *testing.T) {
	for _, v := range []struct {
		path          string
		expectedRepr  string
		expectedX     int
		expectedY     int
		expectedDebug bool
	}{
		{"", "---------", -1, -1, false},
		{"move=a1", "---------", 0, 0, false},
		{"move=a2", "---------", 0, 1, false},
		{"move=a3", "---------", 0, 2, false},
		{"move=b1", "---------", 1, 0, false},
		{"move=b2", "---------", 1, 1, false},
		{"move=b3", "---------", 1, 2, false},
		{"move=c1", "---------", 2, 0, false},
		{"move=c2", "---------", 2, 1, false},
		{"move=c3", "---------", 2, 2, false},
		{"move=c1&debug", "---------", 2, 0, true},
		{"&", "---------", -1, -1, false},
		{"state=-X--OX-O-&move=b2", "-X--OX-O-", 1, 1, false},
		{"move=b2?state=-X--OX-O-", "-X--OX-O-", 1, 1, false},
		{"state=XOXX-O-O-&move=c1", "XOXX-O-O-", 2, 0, false},
		{"move=c1?state=XOXX-O-O-", "XOXX-O-O-", 2, 0, false},
		{"debug", "---------", -1, -1, true},
	} {
		g, x, y, _ := parsePath(v.path)
		repr := g.ToRepr()
		if repr != v.expectedRepr || x != v.expectedX || y != v.expectedY {
			t.Errorf(
				"failed to parsePath for '%s', expected x=%d y=%d repr=%s, got x=%d x=%d repr=%s",
				v.path,
				v.expectedX, v.expectedY, v.expectedRepr,
				x, y, repr,
			)
		}
	}
}

package minesweeper

import (
	"encoding/hex"
	"net/url"
	"strings"
)

const (
	Width  = 8
	Height = 8
	// Mines  = 4 They are random, from 4 (easy) to 10 (difficult) !
)

type Cell struct {
	IsMine        bool
	IsRevealed    bool
	AdjacentMines int
}

type Board struct {
	cells [][]Cell // height, width
	// todo probably add fields width and height
}

func NewBoard() *Board {
	var cells [][]Cell = make([][]Cell, Height)
	for i := range cells {
		cells[i] = make([]Cell, Width)
	}
	return &Board{
		cells: cells,
	}
}

// An empty hexstate will create a board and place mines.
// Otherwise a board is recovered from the state if possible (panic otherwise).
// input is a hex string where each hex-decoded byte 8 bits is 0b00ZYXXXX
//
//	XXXX: count of adjacent mines (4bits, valid ‚àà 0-8),
//	Y: discovered bit
//	Z: isMine bit
func BoardFromRepr(hexstate string) *Board {
	state := stateToBytes(hexstate)
	board := NewBoard()
	if len(state) == 0 {
		board.placeMines()
	} else if len(state) != Width*Height {
		panic("invalid state length")
	} else {
		for i, o := range state {
			board.cells[i/Width][i%Width] = Cell{
				IsMine:        ((o & 0b_0010_0000) >> 5) == 1,
				IsRevealed:    ((o & 0b_0001_0000) >> 4) == 1,
				AdjacentMines: int(o & 0b_0000_1111),
			}
		}
	}
	return board
}

// ToHexRepr is the opposite operation of BoardFromRepr
// it outputs an hexadecimal string
func (b Board) ToHexRepr() string {
	var builder strings.Builder
	for i := 0; i < Width*Height; i++ {
		cell := b.cells[i/Width][i%Width]
		var isMine uint8 = 0
		var isRevealed uint8 = 0
		if cell.IsMine {
			isMine = 1
		}
		if cell.IsRevealed {
			isRevealed = 1
		}
		builder.WriteByte(
			0b_0000_0000 | (isMine << 5) | (isRevealed << 4) | (uint8(cell.AdjacentMines)),
		)
	}
	return hex.EncodeToString([]byte(builder.String()))
}

// show the board as a string,
// respective of the revealed status of the squares unless `force` is true.
func (b *Board) show(force bool) string {
	var buf strings.Builder
	for y := range b.cells {
		for x := range b.cells[y] {
			cell := b.cells[y][x]
			if cell.IsRevealed || force {
				if cell.IsMine {
					buf.WriteRune('+')
				} else if cell.AdjacentMines == 0 {
					buf.WriteRune('.')
				} else {
					buf.WriteRune('0' + int32(cell.AdjacentMines))
				}
			} else {
				buf.WriteRune('?')
			}
		}
		buf.WriteRune('\n')
	}
	return buf.String()
}

// Reveal a square, using some kind of floodfill, and return
// whether we are still alive after that...
func (b *Board) Reveal(x, y int) bool {
	if x < 0 || x >= Width || y < 0 || y >= Height {
		panic("Invalid input. Enter valid coordinates.")
	}
	if b.cells[y][x].IsRevealed || b.cells[y][x].IsMine {
		return !b.cells[y][x].IsMine
	}
	b.cells[y][x].IsRevealed = true
	if b.cells[y][x].AdjacentMines == 0 {
		for i := -1; i <= 1; i++ {
			for j := -1; j <= 1; j++ {
				if i == 0 && j == 0 {
					continue
				}
				adjX, adjY := x+j, y+i
				if adjX >= 0 && adjX < Width && adjY >= 0 && adjY < Height {
					b.Reveal(adjX, adjY)
				}
			}
		}
	}
	return true
}

func (b *Board) placeMines() {
	// Mines vary between 4 (easy) to 10 (relatively difficult)
	mines := 4 + Intn(10-4)
	for i := 0; i < mines; i++ {
		x, y := Intn(Width), Intn(Height)
		if b.cells[y][x].IsMine {
			i-- // Retry if mine is already placed
			continue
		}
		b.cells[y][x].IsMine = true
		b.updateAdjacentMines(x, y)
	}
}

func (b *Board) updateAdjacentMines(x, y int) {
	for i := -1; i <= 1; i++ {
		for j := -1; j <= 1; j++ {
			if i == 0 && j == 0 {
				continue
			}
			adjX, adjY := x+j, y+i
			if adjX >= 0 && adjX < Width && adjY >= 0 && adjY < Height {
				b.cells[adjY][adjX].AdjacentMines++
			}
		}
	}
}

func stateToBytes(urlEncodedHex string) []byte {
	decoded, err := url.QueryUnescape(urlEncodedHex)
	if err != nil {
		panic("Error decoding URL:" + err.Error())
	}
	bytes, err := hex.DecodeString(decoded)
	if err != nil {
		panic("Error decoding hex:" + err.Error())
	}
	return bytes
}

package minesweeper

import (
	"testing"

	"gno.land/p/nt/urequire"
)

func TestBoard(t *testing.T) {
	urequire.Equal(t, 3, 3)
	{
		board := NewBoard()
		urequire.False(t, board == nil)
		urequire.Equal(t, len(board.cells), Height)
		for i := 0; i < Height; i++ {
			urequire.Equal(t, len(board.cells[i]), Width)
		}
	}
	{
		var board *Board
		urequire.NotPanics(t, func() {
			board = BoardFromRepr("")
		})
		// check all squares unrevealed
		// check some mines were placed
		foundMines := false
		for i := 0; i < Width*Height; i++ {
			urequire.False(t, board.cells[i/Width][i%Width].IsRevealed)
			if board.cells[i/Width][i%Width].IsMine {
				foundMines = true
			}
		}
		urequire.True(t, foundMines)

		// check BoardFromRepr(hex).ToHexRepr() == hex
		hex := board.ToHexRepr()
		urequire.Equal(t, len(hex), 2*Width*Height, "len(hex), 2*Width*Height")
		urequire.Equal(t, hex, BoardFromRepr(hex).ToHexRepr(), "hex, BoardFromRepr(hex).ToHexRepr()")
	}
	{
		urequire.PanicsWithMessage(t, "invalid state length", func() {
			BoardFromRepr("0FaaBBccDDee")
		})
		urequire.PanicsWithMessage(t, "Error decoding hex:encoding/hex: invalid byte: 120", func() {
			BoardFromRepr("xx")
		})
	}
}

