package clmm

import (
	"chain"
	"chain/banker"
	"chain/runtime"
	"strconv"
	"strings"
)

// =============================================================================
// CONSTANTS
// =============================================================================

const (
	MIN_TICK  = -1000
	MAX_TICK  = 1000
	PRECISION = 1000000
	MAX_INT64 = int64(9223372036854775807)
)

// =============================================================================
// SAFE MATH
// =============================================================================

func safeMul(a, b int64) int64 {
	if a == 0 || b == 0 {
		return 0
	}
	result := a * b
	if result/a != b {
		panic("multiplication overflow")
	}
	return result
}

func safeMulDiv(a, b, c int64) int64 {
	if c == 0 {
		panic("division by zero")
	}
	if a > MAX_INT64/b && b != 0 {
		quotient := a / c
		remainder := a % c
		return quotient*b + (remainder*b)/c
	}
	return (a * b) / c
}

// =============================================================================
// DATA STRUCTURES
// =============================================================================

type CLMMPool struct {
	ID          uint64
	DenomA      string
	DenomB      string
	FeeBPS      int64
	TickSpacing int64
	PriceX6     int64
	CurrentTick int64
	Liquidity   int64
	FeeGrowthA  int64
	FeeGrowthB  int64
}

type Position struct {
	ID            uint64
	PoolID        uint64
	Owner         string
	TickLower     int64
	TickUpper     int64
	Liquidity     int64
	FeeGrowthLastA int64
	FeeGrowthLastB int64
	TokensOwedA   int64
	TokensOwedB   int64
}

type TickInfo struct {
	LiquidityGross int64
	LiquidityNet   int64
	Initialized    bool
}

var (
	pools          []*CLMMPool
	nextPoolID     uint64
	positions      []*Position
	nextPositionID uint64
	tickData       map[uint64]map[int64]*TickInfo
)

func init() {
	tickData = make(map[uint64]map[int64]*TickInfo)
}

// =============================================================================
// TICK MATH
// =============================================================================

func getPriceAtTick(tick int64) int64 {
	if tick < MIN_TICK || tick > MAX_TICK {
		panic("tick out of range")
	}

	if tick == 0 {
		return PRECISION
	}

	absTick := tick
	if absTick < 0 {
		absTick = -absTick
	}

	// Use 1.01 per tick (1% price change per tick)
	// Calculate 1.01^absTick using simple iteration
	result := int64(PRECISION) // 1.0 in X6

	for i := int64(0); i < absTick; i++ {
		// result = result * 1.01 = result * 101 / 100
		// Check for overflow before multiplication
		if result > MAX_INT64/101 {
			panic("price calculation overflow")
		}
		result = (result * 101) / 100
	}

	if tick < 0 {
		// Invert: 1 / price = PRECISION^2 / result
		if result > 0 {
			return safeMulDiv(PRECISION, PRECISION, result)
		}
		return PRECISION
	}

	return result
}

func getTickAtPrice(priceX6 int64) int64 {
	if priceX6 <= 0 {
		panic("invalid price")
	}

	low := int64(MIN_TICK)
	high := int64(MAX_TICK)

	for low < high {
		mid := (low + high + 1) / 2
		priceAtMid := getPriceAtTick(mid)

		if priceAtMid <= priceX6 {
			low = mid
		} else {
			high = mid - 1
		}
	}

	return low
}

// =============================================================================
// LIQUIDITY MATH
// =============================================================================

func getAmountsForLiquidity(pL, pU, pC, liq int64) (int64, int64) {
	if pC <= pL {
		// Price below range: all token0
		denominator := safeMulDiv(pL, pU, PRECISION)
		if denominator == 0 {
			return 0, 0
		}
		amt0 := safeMulDiv(liq, pU-pL, denominator)
		return amt0, 0
	} else if pC >= pU {
		// Price above range: all token1
		amt1 := safeMulDiv(liq, pU-pL, PRECISION)
		return 0, amt1
	} else {
		// Price in range: mix of both tokens
		denominator := safeMulDiv(pC, pU, PRECISION)
		if denominator == 0 {
			return 0, 0
		}
		amt0 := safeMulDiv(liq, pU-pC, denominator)
		amt1 := safeMulDiv(liq, pC-pL, PRECISION)
		return amt0, amt1
	}
}

func getLiquidityForAmounts(pL, pU, pC, amt0, amt1 int64) int64 {
	if pC <= pL {
		if amt0 <= 0 {
			return 0
		}
		numerator := safeMulDiv(pL, pU, PRECISION)
		return safeMulDiv(amt0, numerator, pU-pL)
	} else if pC >= pU {
		if amt1 <= 0 {
			return 0
		}
		return safeMulDiv(amt1, PRECISION, pU-pL)
	} else {
		numerator0 := safeMulDiv(pC, pU, PRECISION)
		liq0 := safeMulDiv(amt0, numerator0, pU-pC)
		liq1 := safeMulDiv(amt1, PRECISION, pC-pL)
		if liq0 < liq1 {
			return liq0
		}
		return liq1
	}
}

// =============================================================================
// POOL MANAGEMENT
// =============================================================================

func CreateCLMMPool(_ realm, denomA, denomB string, feeBPS int64, initialPriceX6 int64) uint64 {
	if denomA == "" || denomB == "" {
		panic("invalid denom")
	}
	if denomA == denomB {
		panic("denoms must be different")
	}
	if denomA > denomB {
		denomA, denomB = denomB, denomA
		if initialPriceX6 > 0 {
			initialPriceX6 = (PRECISION * PRECISION) / initialPriceX6
		}
	}

	for _, p := range pools {
		if p.DenomA == denomA && p.DenomB == denomB && p.FeeBPS == feeBPS {
			panic("pool already exists")
		}
	}

	if feeBPS <= 0 || feeBPS > 10000 {
		panic("fee must be 1-10000 bps")
	}

	var tickSpacing int64 = 1
	if feeBPS >= 100 {
		tickSpacing = 10
	} else if feeBPS >= 30 {
		tickSpacing = 5
	}

	if initialPriceX6 <= 0 {
		initialPriceX6 = PRECISION
	}

	pool := &CLMMPool{
		ID:          nextPoolID,
		DenomA:      denomA,
		DenomB:      denomB,
		FeeBPS:      feeBPS,
		TickSpacing: tickSpacing,
		PriceX6:     initialPriceX6,
		CurrentTick: getTickAtPrice(initialPriceX6),
		Liquidity:   0,
	}

	pools = append(pools, pool)
	tickData[nextPoolID] = make(map[int64]*TickInfo)
	nextPoolID++

	return pool.ID
}

// =============================================================================
// POSITION MANAGEMENT
// =============================================================================

func MintPosition(_ realm, poolID uint64, tickLower, tickUpper int64) uint64 {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}

	pool := pools[poolID]

	if tickLower >= tickUpper {
		panic("tickLower must be < tickUpper")
	}
	if tickLower < MIN_TICK || tickUpper > MAX_TICK {
		panic("ticks out of range")
	}
	if tickLower%pool.TickSpacing != 0 || tickUpper%pool.TickSpacing != 0 {
		panic("ticks must align with spacing")
	}

	caller := string(runtime.OriginCaller())
	sentCoins := banker.OriginSend()

	amountA := sentCoins.AmountOf(pool.DenomA)
	amountB := sentCoins.AmountOf(pool.DenomB)

	if amountA <= 0 && amountB <= 0 {
		panic("must send tokens")
	}

	pL := getPriceAtTick(tickLower)
	pU := getPriceAtTick(tickUpper)

	liquidity := getLiquidityForAmounts(pL, pU, pool.PriceX6, amountA, amountB)
	if liquidity <= 0 {
		panic("insufficient liquidity")
	}

	// Update ticks
	updateTick(poolID, tickLower, liquidity, true)
	updateTick(poolID, tickUpper, liquidity, false)

	// Update pool liquidity if in range
	if pool.PriceX6 >= pL && pool.PriceX6 < pU {
		pool.Liquidity += liquidity
	}

	position := &Position{
		ID:            nextPositionID,
		PoolID:        poolID,
		Owner:         caller,
		TickLower:     tickLower,
		TickUpper:     tickUpper,
		Liquidity:     liquidity,
		FeeGrowthLastA: pool.FeeGrowthA,
		FeeGrowthLastB: pool.FeeGrowthB,
	}

	positions = append(positions, position)
	nextPositionID++

	return position.ID
}

func BurnPosition(_ realm, positionID uint64) {
	if positionID >= uint64(len(positions)) {
		panic("position not found")
	}

	pos := positions[positionID]
	caller := string(runtime.OriginCaller())

	if pos.Owner != caller {
		panic("not owner")
	}
	if pos.Liquidity == 0 {
		panic("already burned")
	}

	pool := pools[pos.PoolID]
	pL := getPriceAtTick(pos.TickLower)
	pU := getPriceAtTick(pos.TickUpper)

	amount0, amount1 := getAmountsForLiquidity(pL, pU, pool.PriceX6, pos.Liquidity)

	// Update ticks
	updateTick(pos.PoolID, pos.TickLower, -pos.Liquidity, true)
	updateTick(pos.PoolID, pos.TickUpper, -pos.Liquidity, false)

	// Update pool liquidity
	if pool.PriceX6 >= pL && pool.PriceX6 < pU {
		pool.Liquidity -= pos.Liquidity
	}

	pos.Liquidity = 0

	// Send tokens back
	if amount0 > 0 || amount1 > 0 {
		bnk := banker.NewBanker(banker.BankerTypeRealmSend)
		realmAddr := runtime.CurrentRealm().Address()
		callerAddr := runtime.OriginCaller()

		if amount0 > 0 && amount1 > 0 {
			coins := chain.NewCoins(
				chain.NewCoin(pool.DenomA, amount0),
				chain.NewCoin(pool.DenomB, amount1),
			)
			bnk.SendCoins(realmAddr, callerAddr, coins)
		} else if amount0 > 0 {
			bnk.SendCoins(realmAddr, callerAddr, chain.NewCoins(chain.NewCoin(pool.DenomA, amount0)))
		} else {
			bnk.SendCoins(realmAddr, callerAddr, chain.NewCoins(chain.NewCoin(pool.DenomB, amount1)))
		}
	}
}

// =============================================================================
// SWAP
// =============================================================================

func Swap(_ realm, poolID uint64, tokenIn string, minAmountOut int64) int64 {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}

	pool := pools[poolID]
	if pool.Liquidity == 0 {
		panic("no liquidity")
	}

	caller := runtime.OriginCaller()
	sentCoins := banker.OriginSend()

	var zeroForOne bool
	var amountIn int64

	if tokenIn == "A" {
		zeroForOne = true
		amountIn = sentCoins.AmountOf(pool.DenomA)
	} else if tokenIn == "B" {
		zeroForOne = false
		amountIn = sentCoins.AmountOf(pool.DenomB)
	} else {
		panic("tokenIn must be A or B")
	}

	if amountIn <= 0 {
		panic("must send input token")
	}

	// Calculate fee using safe math
	feeAmount := safeMulDiv(amountIn, pool.FeeBPS, 10000)
	amountRemaining := amountIn - feeAmount
	totalAmountOut := int64(0)

	// Process swap with tick crossing
	maxIterations := 100 // Safety limit to prevent infinite loops
	for i := 0; i < maxIterations && amountRemaining > 0; i++ {
		if pool.Liquidity == 0 {
			break // No more liquidity
		}

		// Find next initialized tick in the direction of swap
		nextTick := findNextInitializedTick(poolID, pool.CurrentTick, zeroForOne)

		// Calculate how much we can swap before hitting the next tick
		var amountInStep, amountOutStep int64
		var nextTickPrice int64

		if zeroForOne {
			// Swapping A for B (price decreases)
			nextTickPrice = getPriceAtTick(nextTick)
			if nextTickPrice < getPriceAtTick(MIN_TICK) {
				nextTickPrice = getPriceAtTick(MIN_TICK)
			}

			// Calculate max input to reach next tick
			priceDiff := pool.PriceX6 - nextTickPrice
			if priceDiff <= 0 {
				break
			}

			// Max amount that can be swapped at current liquidity to reach nextTickPrice
			maxAmountIn := safeMulDiv(pool.Liquidity, priceDiff, pool.PriceX6)

			if amountRemaining <= maxAmountIn {
				// Swap completes within this tick range
				amountInStep = amountRemaining
				amountOutStep = safeMulDiv(amountInStep, pool.PriceX6, PRECISION)
				// Update price proportionally
				priceChange := safeMulDiv(amountInStep, pool.PriceX6, pool.Liquidity)
				pool.PriceX6 -= priceChange
				if pool.PriceX6 < getPriceAtTick(MIN_TICK) {
					pool.PriceX6 = getPriceAtTick(MIN_TICK)
				}
			} else {
				// Swap crosses this tick
				amountInStep = maxAmountIn
				amountOutStep = safeMulDiv(amountInStep, pool.PriceX6, PRECISION)
				pool.PriceX6 = nextTickPrice

				// Cross the tick - update liquidity
				crossTick(poolID, nextTick, zeroForOne)
			}
		} else {
			// Swapping B for A (price increases)
			nextTickPrice = getPriceAtTick(nextTick)
			if nextTickPrice > getPriceAtTick(MAX_TICK) {
				nextTickPrice = getPriceAtTick(MAX_TICK)
			}

			priceDiff := nextTickPrice - pool.PriceX6
			if priceDiff <= 0 {
				break
			}

			maxAmountIn := safeMulDiv(pool.Liquidity, priceDiff, PRECISION)

			if amountRemaining <= maxAmountIn {
				amountInStep = amountRemaining
				amountOutStep = safeMulDiv(amountInStep, PRECISION, pool.PriceX6)
				priceChange := safeMulDiv(amountInStep, PRECISION, pool.Liquidity)
				pool.PriceX6 += priceChange
				if pool.PriceX6 > getPriceAtTick(MAX_TICK) {
					pool.PriceX6 = getPriceAtTick(MAX_TICK)
				}
			} else {
				amountInStep = maxAmountIn
				amountOutStep = safeMulDiv(amountInStep, PRECISION, pool.PriceX6)
				pool.PriceX6 = nextTickPrice

				crossTick(poolID, nextTick, zeroForOne)
			}
		}

		amountRemaining -= amountInStep
		totalAmountOut += amountOutStep

		// Accumulate fees for this step (proportional to amount used)
		if pool.Liquidity > 0 {
			stepFee := safeMulDiv(feeAmount, amountInStep, amountIn-feeAmount)
			if zeroForOne {
				pool.FeeGrowthA += safeMulDiv(stepFee, PRECISION, pool.Liquidity)
			} else {
				pool.FeeGrowthB += safeMulDiv(stepFee, PRECISION, pool.Liquidity)
			}
		}
	}

	pool.CurrentTick = getTickAtPrice(pool.PriceX6)

	if totalAmountOut < minAmountOut {
		panic("slippage exceeded")
	}

	// Send output
	bnk := banker.NewBanker(banker.BankerTypeRealmSend)
	realmAddr := runtime.CurrentRealm().Address()

	var denomOut string
	if zeroForOne {
		denomOut = pool.DenomB
	} else {
		denomOut = pool.DenomA
	}

	bnk.SendCoins(realmAddr, caller, chain.NewCoins(chain.NewCoin(denomOut, totalAmountOut)))

	return totalAmountOut
}

// findNextInitializedTick finds the next tick with liquidity in the swap direction
func findNextInitializedTick(poolID uint64, currentTick int64, zeroForOne bool) int64 {
	ticks := tickData[poolID]
	if ticks == nil {
		if zeroForOne {
			return MIN_TICK
		}
		return MAX_TICK
	}

	pool := pools[poolID]
	spacing := pool.TickSpacing

	if zeroForOne {
		// Going down (price decreasing), look for next lower initialized tick
		for tick := currentTick - spacing; tick >= MIN_TICK; tick -= spacing {
			if info := ticks[tick]; info != nil && info.Initialized {
				return tick
			}
		}
		return MIN_TICK
	} else {
		// Going up (price increasing), look for next higher initialized tick
		for tick := currentTick + spacing; tick <= MAX_TICK; tick += spacing {
			if info := ticks[tick]; info != nil && info.Initialized {
				return tick
			}
		}
		return MAX_TICK
	}
}

// crossTick updates liquidity when crossing a tick boundary
func crossTick(poolID uint64, tick int64, zeroForOne bool) {
	ticks := tickData[poolID]
	if ticks == nil {
		return
	}

	info := ticks[tick]
	if info == nil || !info.Initialized {
		return
	}

	pool := pools[poolID]

	// When crossing a tick from left to right (price increasing), add liquidityNet
	// When crossing from right to left (price decreasing), subtract liquidityNet
	if zeroForOne {
		// Price decreasing, crossing tick from above
		pool.Liquidity -= info.LiquidityNet
	} else {
		// Price increasing, crossing tick from below
		pool.Liquidity += info.LiquidityNet
	}

	// Ensure liquidity doesn't go negative
	if pool.Liquidity < 0 {
		pool.Liquidity = 0
	}
}

// =============================================================================
// COLLECT FEES
// =============================================================================

func CollectFees(_ realm, positionID uint64) (int64, int64) {
	if positionID >= uint64(len(positions)) {
		panic("position not found")
	}

	pos := positions[positionID]
	caller := string(runtime.OriginCaller())

	if pos.Owner != caller {
		panic("not owner")
	}

	pool := pools[pos.PoolID]

	// Calculate fees owed since last collection
	// Only collect fees if position is/was in range
	var feesA, feesB int64

	if pos.Liquidity > 0 {
		// Calculate accumulated fees based on fee growth difference
		feeGrowthDeltaA := pool.FeeGrowthA - pos.FeeGrowthLastA
		feeGrowthDeltaB := pool.FeeGrowthB - pos.FeeGrowthLastB

		// Use safe math to prevent overflow
		feesA = safeMulDiv(feeGrowthDeltaA, pos.Liquidity, PRECISION)
		feesB = safeMulDiv(feeGrowthDeltaB, pos.Liquidity, PRECISION)

		// Update position's last fee growth snapshot
		pos.FeeGrowthLastA = pool.FeeGrowthA
		pos.FeeGrowthLastB = pool.FeeGrowthB
	}

	// Add any previously uncollected tokens
	feesA += pos.TokensOwedA
	feesB += pos.TokensOwedB
	pos.TokensOwedA = 0
	pos.TokensOwedB = 0

	// Send fees to owner
	if feesA > 0 || feesB > 0 {
		bnk := banker.NewBanker(banker.BankerTypeRealmSend)
		realmAddr := runtime.CurrentRealm().Address()
		callerAddr := runtime.OriginCaller()

		if feesA > 0 && feesB > 0 {
			coins := chain.NewCoins(
				chain.NewCoin(pool.DenomA, feesA),
				chain.NewCoin(pool.DenomB, feesB),
			)
			bnk.SendCoins(realmAddr, callerAddr, coins)
		} else if feesA > 0 {
			bnk.SendCoins(realmAddr, callerAddr, chain.NewCoins(chain.NewCoin(pool.DenomA, feesA)))
		} else {
			bnk.SendCoins(realmAddr, callerAddr, chain.NewCoins(chain.NewCoin(pool.DenomB, feesB)))
		}
	}

	return feesA, feesB
}

// GetPositionFees returns uncollected fees for a position
func GetPositionFees(positionID uint64) (int64, int64) {
	if positionID >= uint64(len(positions)) {
		return 0, 0
	}

	pos := positions[positionID]
	pool := pools[pos.PoolID]

	var feesA, feesB int64

	if pos.Liquidity > 0 {
		feeGrowthDeltaA := pool.FeeGrowthA - pos.FeeGrowthLastA
		feeGrowthDeltaB := pool.FeeGrowthB - pos.FeeGrowthLastB

		// Use safe math to prevent overflow
		feesA = safeMulDiv(feeGrowthDeltaA, pos.Liquidity, PRECISION)
		feesB = safeMulDiv(feeGrowthDeltaB, pos.Liquidity, PRECISION)
	}

	feesA += pos.TokensOwedA
	feesB += pos.TokensOwedB

	return feesA, feesB
}

// =============================================================================
// TICK HELPERS
// =============================================================================

func updateTick(poolID uint64, tick int64, liquidityDelta int64, isLower bool) {
	ticks := tickData[poolID]
	if ticks == nil {
		ticks = make(map[int64]*TickInfo)
		tickData[poolID] = ticks
	}

	info := ticks[tick]
	if info == nil {
		info = &TickInfo{}
		ticks[tick] = info
	}

	info.LiquidityGross += liquidityDelta

	if isLower {
		info.LiquidityNet += liquidityDelta
	} else {
		info.LiquidityNet -= liquidityDelta
	}

	info.Initialized = info.LiquidityGross > 0
}

// =============================================================================
// QUERIES
// =============================================================================

func GetCLMMPool(poolID uint64) (string, string, int64, int64, int64, int64, int64) {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}
	p := pools[poolID]
	return p.DenomA, p.DenomB, p.PriceX6, p.CurrentTick, p.Liquidity, p.FeeBPS, p.TickSpacing
}

func GetCLMMPoolCount() uint64 {
	return uint64(len(pools))
}

func GetPosition(positionID uint64) (uint64, string, int64, int64, int64) {
	if positionID >= uint64(len(positions)) {
		panic("position not found")
	}
	p := positions[positionID]
	return p.PoolID, p.Owner, p.TickLower, p.TickUpper, p.Liquidity
}

func GetPositionCount() uint64 {
	return uint64(len(positions))
}

func GetPositionsByOwner(owner string) []uint64 {
	var result []uint64
	for _, p := range positions {
		if p.Owner == owner && p.Liquidity > 0 {
			result = append(result, p.ID)
		}
	}
	return result
}

func GetQuote(poolID uint64, tokenIn string, amountIn int64) int64 {
	if poolID >= uint64(len(pools)) {
		return 0
	}

	pool := pools[poolID]
	if pool.Liquidity == 0 {
		return 0
	}

	// Simulate the swap without modifying state
	// Copy pool state for simulation
	simPriceX6 := pool.PriceX6
	simLiquidity := pool.Liquidity
	simCurrentTick := pool.CurrentTick

	var zeroForOne bool
	if tokenIn == "A" {
		zeroForOne = true
	} else if tokenIn == "B" {
		zeroForOne = false
	} else {
		return 0
	}

	// Calculate fee
	feeAmount := safeMulDiv(amountIn, pool.FeeBPS, 10000)
	amountRemaining := amountIn - feeAmount
	totalAmountOut := int64(0)

	// Simulate swap with tick crossing (same logic as Swap but read-only)
	maxIterations := 100
	for i := 0; i < maxIterations && amountRemaining > 0; i++ {
		if simLiquidity == 0 {
			break // No more liquidity
		}

		// Find next tick (read-only)
		nextTick := findNextInitializedTick(poolID, simCurrentTick, zeroForOne)

		var amountInStep, amountOutStep int64

		if zeroForOne {
			// Swapping A for B (price decreases)
			nextTickPrice := getPriceAtTick(nextTick)
			if nextTickPrice < getPriceAtTick(MIN_TICK) {
				nextTickPrice = getPriceAtTick(MIN_TICK)
			}

			priceDiff := simPriceX6 - nextTickPrice
			if priceDiff <= 0 {
				break
			}

			maxAmountIn := safeMulDiv(simLiquidity, priceDiff, simPriceX6)

			if amountRemaining <= maxAmountIn {
				amountInStep = amountRemaining
				amountOutStep = safeMulDiv(amountInStep, simPriceX6, PRECISION)
				priceChange := safeMulDiv(amountInStep, simPriceX6, simLiquidity)
				simPriceX6 -= priceChange
				if simPriceX6 < getPriceAtTick(MIN_TICK) {
					simPriceX6 = getPriceAtTick(MIN_TICK)
				}
			} else {
				amountInStep = maxAmountIn
				amountOutStep = safeMulDiv(amountInStep, simPriceX6, PRECISION)
				simPriceX6 = nextTickPrice

				// Simulate tick crossing - get liquidity delta
				ticks := tickData[poolID]
				if ticks != nil {
					if tickInfo, ok := ticks[nextTick]; ok && tickInfo.Initialized {
						simLiquidity -= tickInfo.LiquidityNet
					}
				}
				simCurrentTick = nextTick
			}
		} else {
			// Swapping B for A (price increases)
			nextTickPrice := getPriceAtTick(nextTick)
			if nextTickPrice > getPriceAtTick(MAX_TICK) {
				nextTickPrice = getPriceAtTick(MAX_TICK)
			}

			priceDiff := nextTickPrice - simPriceX6
			if priceDiff <= 0 {
				break
			}

			maxAmountIn := safeMulDiv(simLiquidity, priceDiff, PRECISION)

			if amountRemaining <= maxAmountIn {
				amountInStep = amountRemaining
				amountOutStep = safeMulDiv(amountInStep, PRECISION, simPriceX6)
				priceChange := safeMulDiv(amountInStep, PRECISION, simLiquidity)
				simPriceX6 += priceChange
				if simPriceX6 > getPriceAtTick(MAX_TICK) {
					simPriceX6 = getPriceAtTick(MAX_TICK)
				}
			} else {
				amountInStep = maxAmountIn
				amountOutStep = safeMulDiv(amountInStep, PRECISION, simPriceX6)
				simPriceX6 = nextTickPrice

				// Simulate tick crossing
				ticks := tickData[poolID]
				if ticks != nil {
					if tickInfo, ok := ticks[nextTick]; ok && tickInfo.Initialized {
						simLiquidity += tickInfo.LiquidityNet
					}
				}
				simCurrentTick = nextTick
			}
		}

		amountRemaining -= amountInStep
		totalAmountOut += amountOutStep
	}

	return totalAmountOut
}

func GetPriceAtTick(tick int64) int64 {
	return getPriceAtTick(tick)
}

func GetTickAtPrice(priceX6 int64) int64 {
	return getTickAtPrice(priceX6)
}

func GetRealmAddress() string {
	return string(runtime.CurrentRealm().Address())
}

// =============================================================================
// RENDER
// =============================================================================

func Render(path string) string {
	var sb strings.Builder

	sb.WriteString("# Gnomo CLMM\n\n")
	sb.WriteString("**Address:** `" + string(runtime.CurrentRealm().Address()) + "`\n\n")

	if len(pools) == 0 {
		sb.WriteString("No pools yet.\n")
	} else {
		sb.WriteString("## Pools\n\n")
		for _, p := range pools {
			sb.WriteString("### Pool " + strconv.FormatUint(p.ID, 10) + "\n")
			sb.WriteString("- Pair: " + formatDenom(p.DenomA) + "/" + formatDenom(p.DenomB) + "\n")
			sb.WriteString("- Fee: " + strconv.FormatInt(p.FeeBPS, 10) + " bps\n")
			sb.WriteString("- Tick: " + strconv.FormatInt(p.CurrentTick, 10) + "\n")
			sb.WriteString("- Liquidity: " + strconv.FormatInt(p.Liquidity, 10) + "\n")
			priceFloat := float64(p.PriceX6) / float64(PRECISION)
			sb.WriteString("- Price: " + strconv.FormatFloat(priceFloat, 'f', 6, 64) + "\n\n")
		}
	}

	sb.WriteString("## Stats\n")
	sb.WriteString("- Pools: " + strconv.Itoa(len(pools)) + "\n")

	activePos := 0
	for _, p := range positions {
		if p.Liquidity > 0 {
			activePos++
		}
	}
	sb.WriteString("- Positions: " + strconv.Itoa(activePos) + "\n")

	return sb.String()
}

func formatDenom(denom string) string {
	if denom == "ugnot" {
		return "GNOT"
	}
	if strings.Contains(denom, ":") {
		parts := strings.Split(denom, ":")
		return strings.ToUpper(parts[len(parts)-1])
	}
	return denom
}

package gnomo

import (
	"chain"
	"chain/banker"
	"chain/runtime"
	"strconv"
	"strings"
)

// Pool represents a liquidity pool for two token denoms
type Pool struct {
	ID       uint64
	DenomA   string
	DenomB   string
	ReserveA int64
	ReserveB int64
	TotalLP  int64
	FeeBPS   int64
}

var (
	pools      []*Pool
	nextPoolID uint64
	lpBalances map[uint64]map[string]int64
)

func init() {
	lpBalances = make(map[uint64]map[string]int64)
}

// GetRealmTokenDenom returns the full denom for a realm-issued token
// e.g., "usdc" -> "/gno.land/r/dev/gnomo:usdc"
func GetRealmTokenDenom(baseName string) string {
	return runtime.CurrentRealm().CoinDenom(baseName)
}

// MintTestTokens mints test tokens to the caller (for testing only)
// This allows testing the DEX without needing external tokens
func MintTestTokens(_ realm, baseName string, amount int64) string {
	if amount <= 0 {
		panic("amount must be positive")
	}
	if baseName == "" {
		panic("baseName required")
	}

	caller := runtime.OriginCaller()
	bnk := banker.NewBanker(banker.BankerTypeRealmIssue)

	denom := runtime.CurrentRealm().CoinDenom(baseName)
	bnk.IssueCoin(caller, denom, amount)

	return denom
}

// CreatePool creates a new liquidity pool
func CreatePool(_ realm, denomA, denomB string, feeBPS int64) uint64 {
	if denomA == "" || denomB == "" {
		panic("invalid denom")
	}
	if denomA == denomB {
		panic("denoms must be different")
	}
	if denomA > denomB {
		denomA, denomB = denomB, denomA
	}
	for _, p := range pools {
		if p.DenomA == denomA && p.DenomB == denomB && p.FeeBPS == feeBPS {
			panic("pool already exists")
		}
	}
	if feeBPS == 0 {
		feeBPS = 30
	}
	if feeBPS > 1000 {
		panic("fee too high")
	}

	pool := &Pool{
		ID:     nextPoolID,
		DenomA: denomA,
		DenomB: denomB,
		FeeBPS: feeBPS,
	}
	pools = append(pools, pool)
	lpBalances[nextPoolID] = make(map[string]int64)
	nextPoolID++
	return pool.ID
}

// AddLiquidity adds liquidity to a pool
// User must send both tokens with the transaction
// minLPOut provides slippage protection - set to 0 to skip
func AddLiquidity(_ realm, poolID uint64, minLPOut int64) int64 {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}

	p := pools[poolID]
	caller := string(runtime.OriginCaller())

	sentCoins := banker.OriginSend()
	amountA := sentCoins.AmountOf(p.DenomA)
	amountB := sentCoins.AmountOf(p.DenomB)

	if amountA <= 0 || amountB <= 0 {
		panic("must send both tokens: " + p.DenomA + " and " + p.DenomB)
	}

	var lpMinted int64

	if p.TotalLP == 0 {
		// Use safe multiplication for initial liquidity
		lpMinted = sqrt64(safeMul(amountA, amountB))
		if lpMinted == 0 {
			panic("insufficient initial liquidity")
		}
	} else {
		// Use safeMulDiv to prevent overflow
		lpFromA := safeMulDiv(amountA, p.TotalLP, p.ReserveA)
		lpFromB := safeMulDiv(amountB, p.TotalLP, p.ReserveB)
		lpMinted = min64(lpFromA, lpFromB)
		if lpMinted == 0 {
			panic("insufficient liquidity minted")
		}
	}

	// Slippage protection
	if minLPOut > 0 && lpMinted < minLPOut {
		panic("slippage: LP minted " + strconv.FormatInt(lpMinted, 10) + " < min " + strconv.FormatInt(minLPOut, 10))
	}

	p.ReserveA += amountA
	p.ReserveB += amountB
	p.TotalLP += lpMinted
	lpBalances[poolID][caller] += lpMinted

	return lpMinted
}

// RemoveLiquidity burns LP tokens and returns proportional reserves
// minAmountA/minAmountB provide slippage protection - set to 0 to skip
func RemoveLiquidity(_ realm, poolID uint64, lpAmount int64, minAmountA int64, minAmountB int64) {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}
	if lpAmount <= 0 {
		panic("amount must be positive")
	}

	p := pools[poolID]
	caller := string(runtime.OriginCaller())
	callerLP := lpBalances[poolID][caller]

	if callerLP < lpAmount {
		panic("insufficient LP balance")
	}

	// Use safeMulDiv to prevent overflow
	amountA := safeMulDiv(lpAmount, p.ReserveA, p.TotalLP)
	amountB := safeMulDiv(lpAmount, p.ReserveB, p.TotalLP)

	if amountA <= 0 || amountB <= 0 {
		panic("insufficient output amounts")
	}

	// Slippage protection
	if minAmountA > 0 && amountA < minAmountA {
		panic("slippage: amountA " + strconv.FormatInt(amountA, 10) + " < min " + strconv.FormatInt(minAmountA, 10))
	}
	if minAmountB > 0 && amountB < minAmountB {
		panic("slippage: amountB " + strconv.FormatInt(amountB, 10) + " < min " + strconv.FormatInt(minAmountB, 10))
	}

	p.ReserveA -= amountA
	p.ReserveB -= amountB
	p.TotalLP -= lpAmount
	lpBalances[poolID][caller] -= lpAmount

	bnk := banker.NewBanker(banker.BankerTypeRealmSend)
	realmAddr := runtime.CurrentRealm().Address()
	callerAddr := runtime.OriginCaller()

	coins := chain.NewCoins(
		chain.NewCoin(p.DenomA, amountA),
		chain.NewCoin(p.DenomB, amountB),
	)
	bnk.SendCoins(realmAddr, callerAddr, coins)
}

// Swap swaps exact input amount for output
func Swap(_ realm, poolID uint64, tokenIn string, minAmountOut int64) int64 {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}
	if tokenIn != "A" && tokenIn != "B" {
		panic("tokenIn must be A or B")
	}

	p := pools[poolID]
	if p.ReserveA == 0 || p.ReserveB == 0 {
		panic("pool has no liquidity")
	}

	caller := runtime.OriginCaller()
	sentCoins := banker.OriginSend()

	var amountIn int64
	var denomOut string

	if tokenIn == "A" {
		denomOut = p.DenomB
		amountIn = sentCoins.AmountOf(p.DenomA)
	} else {
		denomOut = p.DenomA
		amountIn = sentCoins.AmountOf(p.DenomB)
	}

	if amountIn <= 0 {
		panic("must send input token")
	}

	var amountOut int64
	amountInWithFee := safeMulDiv(amountIn, 10000-p.FeeBPS, 10000)

	if tokenIn == "A" {
		// amountOut = (amountInWithFee * reserveB) / (reserveA + amountInWithFee)
		amountOut = safeMulDiv(amountInWithFee, p.ReserveB, p.ReserveA+amountInWithFee)
		p.ReserveA += amountIn
		p.ReserveB -= amountOut
	} else {
		amountOut = safeMulDiv(amountInWithFee, p.ReserveA, p.ReserveB+amountInWithFee)
		p.ReserveB += amountIn
		p.ReserveA -= amountOut
	}

	if amountOut <= 0 {
		panic("insufficient output amount")
	}
	if amountOut < minAmountOut {
		panic("slippage: output " + strconv.FormatInt(amountOut, 10) + " < min " + strconv.FormatInt(minAmountOut, 10))
	}

	bnk := banker.NewBanker(banker.BankerTypeRealmSend)
	realmAddr := runtime.CurrentRealm().Address()

	outCoins := chain.NewCoins(chain.NewCoin(denomOut, amountOut))
	bnk.SendCoins(realmAddr, caller, outCoins)

	return amountOut
}

// GetQuote returns expected output without executing swap
func GetQuote(poolID uint64, tokenIn string, amountIn int64) int64 {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}
	p := pools[poolID]
	if p.ReserveA == 0 || p.ReserveB == 0 {
		return 0
	}

	amountInWithFee := safeMulDiv(amountIn, 10000-p.FeeBPS, 10000)

	if tokenIn == "A" {
		return safeMulDiv(amountInWithFee, p.ReserveB, p.ReserveA+amountInWithFee)
	}
	return safeMulDiv(amountInWithFee, p.ReserveA, p.ReserveB+amountInWithFee)
}

// GetPool returns pool info
func GetPool(poolID uint64) (string, string, int64, int64, int64, int64) {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}
	p := pools[poolID]
	return p.DenomA, p.DenomB, p.ReserveA, p.ReserveB, p.TotalLP, p.FeeBPS
}

func GetPoolCount() uint64 {
	return uint64(len(pools))
}

func GetLPBalance(poolID uint64, addr string) int64 {
	if poolID >= uint64(len(pools)) {
		return 0
	}
	return lpBalances[poolID][addr]
}

func GetRealmAddress() string {
	return string(runtime.CurrentRealm().Address())
}

// Helper functions
func sqrt64(x int64) int64 {
	if x <= 0 {
		return 0
	}
	z := x
	y := (z + 1) / 2
	for y < z {
		z = y
		y = (z + x/z) / 2
	}
	return z
}

func min64(a, b int64) int64 {
	if a < b {
		return a
	}
	return b
}

// Safe math to prevent overflow
const MAX_INT64 = int64(9223372036854775807)

func safeMul(a, b int64) int64 {
	if a == 0 || b == 0 {
		return 0
	}
	result := a * b
	// Check for overflow: if result/a != b, overflow occurred
	if result/a != b {
		panic("multiplication overflow")
	}
	return result
}

func safeMulDiv(a, b, c int64) int64 {
	if c == 0 {
		panic("division by zero")
	}
	// For large numbers, divide first to avoid overflow
	// Use: (a * b) / c = (a / c) * b + (a % c) * b / c
	if a > MAX_INT64/b && b != 0 {
		quotient := a / c
		remainder := a % c
		return quotient*b + (remainder*b)/c
	}
	return (a * b) / c
}

// Render displays pool information
func Render(path string) string {
	var sb strings.Builder
	sb.WriteString("# Gnomo DEX\n\n")
	sb.WriteString("**Contract Address:** `" + string(runtime.CurrentRealm().Address()) + "`\n\n")

	// Show test token denom
	testDenom := runtime.CurrentRealm().CoinDenom("usdc")
	sb.WriteString("**Test Token (usdc):** `" + testDenom + "`\n\n")

	if len(pools) == 0 {
		sb.WriteString("No pools created yet.\n\n")
	} else {
		sb.WriteString("## Pools\n\n")
		for _, p := range pools {
			sb.WriteString("### Pool " + strconv.FormatUint(p.ID, 10) + ": " + p.DenomA + " / " + p.DenomB + "\n\n")
			sb.WriteString("| Metric | Value |\n")
			sb.WriteString("|--------|-------|\n")
			sb.WriteString("| Reserve A | " + strconv.FormatInt(p.ReserveA, 10) + " |\n")
			sb.WriteString("| Reserve B | " + strconv.FormatInt(p.ReserveB, 10) + " |\n")
			sb.WriteString("| Total LP | " + strconv.FormatInt(p.TotalLP, 10) + " |\n")
			sb.WriteString("| Fee | " + strconv.FormatInt(p.FeeBPS, 10) + " bps |\n")

			if p.ReserveA > 0 && p.ReserveB > 0 {
				priceAtoB := (p.ReserveB * 1000000) / p.ReserveA
				priceBtoA := (p.ReserveA * 1000000) / p.ReserveB
				sb.WriteString("| Price A→B | " + formatPrice(priceAtoB) + " |\n")
				sb.WriteString("| Price B→A | " + formatPrice(priceBtoA) + " |\n")
			}
			sb.WriteString("\n")
		}
	}

	sb.WriteString("## Quick Start\n\n")
	sb.WriteString("### 1. Get Test Tokens\n")
	sb.WriteString("```\n")
	sb.WriteString("gnokey maketx call -func MintTestTokens -args \"usdc\" -args \"10000000\" ...\n")
	sb.WriteString("```\n\n")
	sb.WriteString("### 2. Create Pool (ugnot + test usdc)\n")
	sb.WriteString("```\n")
	sb.WriteString("gnokey maketx call -func CreatePool -args \"ugnot\" -args \"" + testDenom + "\" -args \"30\" ...\n")
	sb.WriteString("```\n\n")
	sb.WriteString("### 3. Add Liquidity\n")
	sb.WriteString("```\n")
	sb.WriteString("gnokey maketx call -send \"1000000ugnot,2000000" + testDenom + "\" -func AddLiquidity -args \"0\" ...\n")
	sb.WriteString("```\n\n")
	sb.WriteString("### 4. Swap\n")
	sb.WriteString("```\n")
	sb.WriteString("gnokey maketx call -send \"100000ugnot\" -func Swap -args \"0\" -args \"A\" -args \"0\" ...\n")
	sb.WriteString("```\n\n")

	return sb.String()
}

func formatPrice(microPrice int64) string {
	whole := microPrice / 1000000
	frac := microPrice % 1000000
	if frac == 0 {
		return strconv.FormatInt(whole, 10)
	}
	fracStr := strconv.FormatInt(frac, 10)
	for len(fracStr) < 6 {
		fracStr = "0" + fracStr
	}
	fracStr = strings.TrimRight(fracStr, "0")
	return strconv.FormatInt(whole, 10) + "." + fracStr
}

package clmm

import (
	"chain"
	"chain/banker"
	"chain/runtime"
	"strconv"
	"strings"
)

// =============================================================================
// CONSTANTS
// =============================================================================

const (
	MIN_TICK  = -1000
	MAX_TICK  = 1000
	PRECISION = 1000000
	MAX_INT64 = int64(9223372036854775807)
)

// =============================================================================
// SAFE MATH
// =============================================================================

func safeMul(a, b int64) int64 {
	if a == 0 || b == 0 {
		return 0
	}
	result := a * b
	if result/a != b {
		panic("multiplication overflow")
	}
	return result
}

func safeMulDiv(a, b, c int64) int64 {
	if c == 0 {
		panic("division by zero")
	}
	if a > MAX_INT64/b && b != 0 {
		quotient := a / c
		remainder := a % c
		return quotient*b + (remainder*b)/c
	}
	return (a * b) / c
}

// Integer square root using Newton's method
func isqrt(n int64) int64 {
	if n < 0 {
		panic("sqrt of negative")
	}
	if n == 0 {
		return 0
	}
	if n < 2 {
		return n
	}

	// Initial guess
	x := n
	y := (x + 1) / 2

	for y < x {
		x = y
		y = (x + n/x) / 2
	}

	return x
}

// =============================================================================
// DATA STRUCTURES
// =============================================================================

type CLMMPool struct {
	ID          uint64
	DenomA      string
	DenomB      string
	FeeBPS      int64
	TickSpacing int64
	PriceX6     int64
	CurrentTick int64
	Liquidity   int64
	FeeGrowthA  int64
	FeeGrowthB  int64
}

type Position struct {
	ID            uint64
	PoolID        uint64
	Owner         string
	TickLower     int64
	TickUpper     int64
	Liquidity     int64
	FeeGrowthLastA int64
	FeeGrowthLastB int64
	TokensOwedA   int64
	TokensOwedB   int64
}

type TickInfo struct {
	LiquidityGross int64
	LiquidityNet   int64
	Initialized    bool
}

var (
	pools          []*CLMMPool
	nextPoolID     uint64
	positions      []*Position
	nextPositionID uint64
	tickData       map[uint64]map[int64]*TickInfo
)

func init() {
	tickData = make(map[uint64]map[int64]*TickInfo)
}

// =============================================================================
// TICK MATH
// =============================================================================

func getPriceAtTick(tick int64) int64 {
	if tick < MIN_TICK || tick > MAX_TICK {
		panic("tick out of range")
	}

	if tick == 0 {
		return PRECISION
	}

	absTick := tick
	if absTick < 0 {
		absTick = -absTick
	}

	// Use 1.01 per tick (1% price change per tick)
	// Calculate 1.01^absTick using simple iteration
	result := int64(PRECISION) // 1.0 in X6

	for i := int64(0); i < absTick; i++ {
		// result = result * 1.01 = result * 101 / 100
		// Check for overflow before multiplication
		if result > MAX_INT64/101 {
			panic("price calculation overflow")
		}
		result = (result * 101) / 100
	}

	if tick < 0 {
		// Invert: 1 / price = PRECISION^2 / result
		if result > 0 {
			return safeMulDiv(PRECISION, PRECISION, result)
		}
		return PRECISION
	}

	return result
}

func getTickAtPrice(priceX6 int64) int64 {
	if priceX6 <= 0 {
		panic("invalid price")
	}

	low := int64(MIN_TICK)
	high := int64(MAX_TICK)

	for low < high {
		mid := (low + high + 1) / 2
		priceAtMid := getPriceAtTick(mid)

		if priceAtMid <= priceX6 {
			low = mid
		} else {
			high = mid - 1
		}
	}

	return low
}

// =============================================================================
// LIQUIDITY MATH (using sqrt-based Uniswap v3 formulas)
// =============================================================================

// getAmountsForLiquidity calculates token amounts for a given liquidity
// Uses proper sqrt-based CLMM math:
// - amt0 = L * (1/sqrt(pC) - 1/sqrt(pU)) = L * (sqrt(pU) - sqrt(pC)) / (sqrt(pC) * sqrt(pU))
// - amt1 = L * (sqrt(pC) - sqrt(pL))
func getAmountsForLiquidity(pL, pU, pC, liq int64) (int64, int64) {
	if liq <= 0 {
		return 0, 0
	}

	sqrtPrecision := int64(1000) // sqrt(PRECISION)
	sqrtPL := isqrt(pL * PRECISION)
	sqrtPU := isqrt(pU * PRECISION)
	sqrtPC := isqrt(pC * PRECISION)

	if pC <= pL {
		// Price below range: all token0
		// amt0 = L * (sqrt(pU) - sqrt(pL)) / (sqrt(pL) * sqrt(pU) / sqrtPrecision)
		if sqrtPL == 0 || sqrtPU == 0 {
			return 0, 0
		}
		denom := safeMulDiv(sqrtPL, sqrtPU, sqrtPrecision*PRECISION)
		if denom == 0 {
			return 0, 0
		}
		amt0 := safeMulDiv(liq, sqrtPU-sqrtPL, denom)
		return amt0, 0
	} else if pC >= pU {
		// Price above range: all token1
		// amt1 = L * (sqrt(pU) - sqrt(pL)) / sqrtPrecision
		amt1 := safeMulDiv(liq, sqrtPU-sqrtPL, sqrtPrecision*PRECISION)
		return 0, amt1
	} else {
		// Price in range: mix of both tokens
		// amt0 = L * (sqrt(pU) - sqrt(pC)) / (sqrt(pC) * sqrt(pU) / sqrtPrecision)
		// amt1 = L * (sqrt(pC) - sqrt(pL)) / sqrtPrecision
		denom0 := safeMulDiv(sqrtPC, sqrtPU, sqrtPrecision*PRECISION)
		if denom0 == 0 {
			return 0, 0
		}
		amt0 := safeMulDiv(liq, sqrtPU-sqrtPC, denom0)
		amt1 := safeMulDiv(liq, sqrtPC-sqrtPL, sqrtPrecision*PRECISION)
		return amt0, amt1
	}
}

// getLiquidityForAmounts calculates liquidity from token amounts
// Uses proper sqrt-based CLMM math (inverse of getAmountsForLiquidity)
func getLiquidityForAmounts(pL, pU, pC, amt0, amt1 int64) int64 {
	sqrtPrecision := int64(1000)
	sqrtPL := isqrt(pL * PRECISION)
	sqrtPU := isqrt(pU * PRECISION)
	sqrtPC := isqrt(pC * PRECISION)

	if pC <= pL {
		// Price below range: use amt0 only
		// L = amt0 * sqrt(pL) * sqrt(pU) / (sqrt(pU) - sqrt(pL))
		if amt0 <= 0 || sqrtPU <= sqrtPL {
			return 0
		}
		numerator := safeMulDiv(sqrtPL, sqrtPU, sqrtPrecision*PRECISION)
		return safeMulDiv(amt0, numerator, sqrtPU-sqrtPL)
	} else if pC >= pU {
		// Price above range: use amt1 only
		// L = amt1 * sqrtPrecision / (sqrt(pU) - sqrt(pL))
		if amt1 <= 0 || sqrtPU <= sqrtPL {
			return 0
		}
		return safeMulDiv(amt1, sqrtPrecision*PRECISION, sqrtPU-sqrtPL)
	} else {
		// Price in range: use min of liquidity from both tokens
		// L0 = amt0 * sqrt(pC) * sqrt(pU) / (sqrt(pU) - sqrt(pC))
		// L1 = amt1 * sqrtPrecision / (sqrt(pC) - sqrt(pL))
		var liq0, liq1 int64

		if amt0 > 0 && sqrtPU > sqrtPC {
			numerator0 := safeMulDiv(sqrtPC, sqrtPU, sqrtPrecision*PRECISION)
			liq0 = safeMulDiv(amt0, numerator0, sqrtPU-sqrtPC)
		}

		if amt1 > 0 && sqrtPC > sqrtPL {
			liq1 = safeMulDiv(amt1, sqrtPrecision*PRECISION, sqrtPC-sqrtPL)
		}

		// Return minimum (or whichever is non-zero)
		if liq0 == 0 {
			return liq1
		}
		if liq1 == 0 {
			return liq0
		}
		if liq0 < liq1 {
			return liq0
		}
		return liq1
	}
}

// =============================================================================
// POOL MANAGEMENT
// =============================================================================

func CreateCLMMPool(_ realm, denomA, denomB string, feeBPS int64, initialPriceX6 int64) uint64 {
	if denomA == "" || denomB == "" {
		panic("invalid denom")
	}
	if denomA == denomB {
		panic("denoms must be different")
	}
	if denomA > denomB {
		denomA, denomB = denomB, denomA
		if initialPriceX6 > 0 {
			initialPriceX6 = (PRECISION * PRECISION) / initialPriceX6
		}
	}

	for _, p := range pools {
		if p.DenomA == denomA && p.DenomB == denomB && p.FeeBPS == feeBPS {
			panic("pool already exists")
		}
	}

	if feeBPS <= 0 || feeBPS > 10000 {
		panic("fee must be 1-10000 bps")
	}

	var tickSpacing int64 = 1
	if feeBPS >= 100 {
		tickSpacing = 10
	} else if feeBPS >= 30 {
		tickSpacing = 5
	}

	if initialPriceX6 <= 0 {
		initialPriceX6 = PRECISION
	}

	pool := &CLMMPool{
		ID:          nextPoolID,
		DenomA:      denomA,
		DenomB:      denomB,
		FeeBPS:      feeBPS,
		TickSpacing: tickSpacing,
		PriceX6:     initialPriceX6,
		CurrentTick: getTickAtPrice(initialPriceX6),
		Liquidity:   0,
	}

	pools = append(pools, pool)
	tickData[nextPoolID] = make(map[int64]*TickInfo)
	nextPoolID++

	return pool.ID
}

// =============================================================================
// POSITION MANAGEMENT
// =============================================================================

func MintPosition(_ realm, poolID uint64, tickLower, tickUpper int64) uint64 {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}

	pool := pools[poolID]

	if tickLower >= tickUpper {
		panic("tickLower must be < tickUpper")
	}
	if tickLower < MIN_TICK || tickUpper > MAX_TICK {
		panic("ticks out of range")
	}
	if tickLower%pool.TickSpacing != 0 || tickUpper%pool.TickSpacing != 0 {
		panic("ticks must align with spacing")
	}

	caller := string(runtime.OriginCaller())
	sentCoins := banker.OriginSend()

	amountA := sentCoins.AmountOf(pool.DenomA)
	amountB := sentCoins.AmountOf(pool.DenomB)

	if amountA <= 0 && amountB <= 0 {
		panic("must send tokens")
	}

	pL := getPriceAtTick(tickLower)
	pU := getPriceAtTick(tickUpper)

	liquidity := getLiquidityForAmounts(pL, pU, pool.PriceX6, amountA, amountB)
	if liquidity <= 0 {
		panic("insufficient liquidity")
	}

	// Update ticks
	updateTick(poolID, tickLower, liquidity, true)
	updateTick(poolID, tickUpper, liquidity, false)

	// Update pool liquidity if in range
	if pool.PriceX6 >= pL && pool.PriceX6 < pU {
		pool.Liquidity += liquidity
	}

	position := &Position{
		ID:            nextPositionID,
		PoolID:        poolID,
		Owner:         caller,
		TickLower:     tickLower,
		TickUpper:     tickUpper,
		Liquidity:     liquidity,
		FeeGrowthLastA: pool.FeeGrowthA,
		FeeGrowthLastB: pool.FeeGrowthB,
	}

	positions = append(positions, position)
	nextPositionID++

	return position.ID
}

func BurnPosition(_ realm, positionID uint64) {
	if positionID >= uint64(len(positions)) {
		panic("position not found")
	}

	pos := positions[positionID]
	caller := string(runtime.OriginCaller())

	if pos.Owner != caller {
		panic("not owner")
	}
	if pos.Liquidity == 0 {
		panic("already burned")
	}

	pool := pools[pos.PoolID]
	pL := getPriceAtTick(pos.TickLower)
	pU := getPriceAtTick(pos.TickUpper)

	amount0, amount1 := getAmountsForLiquidity(pL, pU, pool.PriceX6, pos.Liquidity)

	// Update ticks
	updateTick(pos.PoolID, pos.TickLower, -pos.Liquidity, true)
	updateTick(pos.PoolID, pos.TickUpper, -pos.Liquidity, false)

	// Update pool liquidity
	if pool.PriceX6 >= pL && pool.PriceX6 < pU {
		pool.Liquidity -= pos.Liquidity
	}

	pos.Liquidity = 0

	// Send tokens back
	if amount0 > 0 || amount1 > 0 {
		bnk := banker.NewBanker(banker.BankerTypeRealmSend)
		realmAddr := runtime.CurrentRealm().Address()
		callerAddr := runtime.OriginCaller()

		if amount0 > 0 && amount1 > 0 {
			coins := chain.NewCoins(
				chain.NewCoin(pool.DenomA, amount0),
				chain.NewCoin(pool.DenomB, amount1),
			)
			bnk.SendCoins(realmAddr, callerAddr, coins)
		} else if amount0 > 0 {
			bnk.SendCoins(realmAddr, callerAddr, chain.NewCoins(chain.NewCoin(pool.DenomA, amount0)))
		} else {
			bnk.SendCoins(realmAddr, callerAddr, chain.NewCoins(chain.NewCoin(pool.DenomB, amount1)))
		}
	}
}

// =============================================================================
// SWAP
// =============================================================================

func Swap(_ realm, poolID uint64, tokenIn string, minAmountOut int64) int64 {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}

	pool := pools[poolID]
	if pool.Liquidity == 0 {
		panic("no liquidity")
	}

	caller := runtime.OriginCaller()
	sentCoins := banker.OriginSend()

	var zeroForOne bool
	var amountIn int64

	if tokenIn == "A" {
		zeroForOne = true
		amountIn = sentCoins.AmountOf(pool.DenomA)
	} else if tokenIn == "B" {
		zeroForOne = false
		amountIn = sentCoins.AmountOf(pool.DenomB)
	} else {
		panic("tokenIn must be A or B")
	}

	if amountIn <= 0 {
		panic("must send input token")
	}

	// Calculate fee using safe math
	feeAmount := safeMulDiv(amountIn, pool.FeeBPS, 10000)
	amountRemaining := amountIn - feeAmount
	totalAmountOut := int64(0)

	// Process swap with tick crossing
	maxIterations := 100 // Safety limit to prevent infinite loops
	for i := 0; i < maxIterations && amountRemaining > 0; i++ {
		if pool.Liquidity == 0 {
			break // No more liquidity
		}

		// Find next initialized tick in the direction of swap
		nextTick := findNextInitializedTick(poolID, pool.CurrentTick, zeroForOne)

		// Calculate how much we can swap before hitting the next tick
		var amountInStep, amountOutStep int64
		var nextTickPrice int64

		if zeroForOne {
			// Swapping A for B (price decreases)
			// Using sqrt-based CLMM math for accurate output calculation
			nextTickPrice = getPriceAtTick(nextTick)
			if nextTickPrice < getPriceAtTick(MIN_TICK) {
				nextTickPrice = getPriceAtTick(MIN_TICK)
			}

			// Calculate using sqrt price (proper CLMM math)
			sqrtPriceCurrent := isqrt(pool.PriceX6 * PRECISION)
			sqrtPriceNext := isqrt(nextTickPrice * PRECISION)

			if sqrtPriceCurrent <= sqrtPriceNext {
				break
			}

			// Max amount in to reach next tick: dx = L * (1/sqrt(P_next) - 1/sqrt(P_current))
			// Simplified: dx = L * (sqrt(P_current) - sqrt(P_next)) / (sqrt(P_current) * sqrt(P_next) / SQRT_PRECISION)
			sqrtPrecision := int64(1000) // sqrt(PRECISION)
			maxAmountIn := safeMulDiv(pool.Liquidity, sqrtPriceCurrent-sqrtPriceNext, safeMulDiv(sqrtPriceCurrent, sqrtPriceNext, sqrtPrecision*PRECISION))

			if amountRemaining <= maxAmountIn || maxAmountIn <= 0 {
				// Swap completes within this tick range
				amountInStep = amountRemaining

				// Calculate new sqrt price: 1/sqrt(P_new) = 1/sqrt(P_old) + dx/L
				// sqrt(P_new) = sqrt(P_old) * L / (L + dx * sqrt(P_old) / sqrtPrecision)
				denom := pool.Liquidity + safeMulDiv(amountInStep, sqrtPriceCurrent, sqrtPrecision*PRECISION)
				if denom <= 0 {
					break
				}
				sqrtPriceNew := safeMulDiv(sqrtPriceCurrent, pool.Liquidity, denom)

				// Output: dy = L * (sqrt(P_old) - sqrt(P_new)) / sqrtPrecision
				amountOutStep = safeMulDiv(pool.Liquidity, sqrtPriceCurrent-sqrtPriceNew, sqrtPrecision*PRECISION)

				// Update price
				pool.PriceX6 = safeMulDiv(sqrtPriceNew, sqrtPriceNew, PRECISION)
				if pool.PriceX6 < getPriceAtTick(MIN_TICK) {
					pool.PriceX6 = getPriceAtTick(MIN_TICK)
				}
			} else {
				// Swap crosses this tick
				amountInStep = maxAmountIn

				// Output for crossing to next tick
				amountOutStep = safeMulDiv(pool.Liquidity, sqrtPriceCurrent-sqrtPriceNext, sqrtPrecision*PRECISION)
				pool.PriceX6 = nextTickPrice

				// Cross the tick - update liquidity
				crossTick(poolID, nextTick, zeroForOne)
			}
		} else {
			// Swapping B for A (price increases)
			nextTickPrice = getPriceAtTick(nextTick)
			if nextTickPrice > getPriceAtTick(MAX_TICK) {
				nextTickPrice = getPriceAtTick(MAX_TICK)
			}

			sqrtPriceCurrent := isqrt(pool.PriceX6 * PRECISION)
			sqrtPriceNext := isqrt(nextTickPrice * PRECISION)

			if sqrtPriceNext <= sqrtPriceCurrent {
				break
			}

			sqrtPrecision := int64(1000)
			// Max amount in to reach next tick: dy = L * (sqrt(P_next) - sqrt(P_current)) / sqrtPrecision
			maxAmountIn := safeMulDiv(pool.Liquidity, sqrtPriceNext-sqrtPriceCurrent, sqrtPrecision*PRECISION)

			if amountRemaining <= maxAmountIn || maxAmountIn <= 0 {
				amountInStep = amountRemaining

				// New sqrt price: sqrt(P_new) = sqrt(P_old) + dy * sqrtPrecision / L
				sqrtPriceNew := sqrtPriceCurrent + safeMulDiv(amountInStep, sqrtPrecision*PRECISION, pool.Liquidity)

				// Output: dx = L * (1/sqrt(P_old) - 1/sqrt(P_new))
				// = L * (sqrt(P_new) - sqrt(P_old)) / (sqrt(P_old) * sqrt(P_new) / sqrtPrecision)
				amountOutStep = safeMulDiv(pool.Liquidity, sqrtPriceNew-sqrtPriceCurrent, safeMulDiv(sqrtPriceCurrent, sqrtPriceNew, sqrtPrecision*PRECISION))

				pool.PriceX6 = safeMulDiv(sqrtPriceNew, sqrtPriceNew, PRECISION)
				if pool.PriceX6 > getPriceAtTick(MAX_TICK) {
					pool.PriceX6 = getPriceAtTick(MAX_TICK)
				}
			} else {
				amountInStep = maxAmountIn
				amountOutStep = safeMulDiv(pool.Liquidity, sqrtPriceNext-sqrtPriceCurrent, safeMulDiv(sqrtPriceCurrent, sqrtPriceNext, sqrtPrecision*PRECISION))
				pool.PriceX6 = nextTickPrice

				crossTick(poolID, nextTick, zeroForOne)
			}
		}

		amountRemaining -= amountInStep
		totalAmountOut += amountOutStep

		// Accumulate fees for this step (proportional to amount used)
		if pool.Liquidity > 0 {
			stepFee := safeMulDiv(feeAmount, amountInStep, amountIn-feeAmount)
			if zeroForOne {
				pool.FeeGrowthA += safeMulDiv(stepFee, PRECISION, pool.Liquidity)
			} else {
				pool.FeeGrowthB += safeMulDiv(stepFee, PRECISION, pool.Liquidity)
			}
		}
	}

	pool.CurrentTick = getTickAtPrice(pool.PriceX6)

	if totalAmountOut < minAmountOut {
		panic("slippage exceeded")
	}

	// Send output
	bnk := banker.NewBanker(banker.BankerTypeRealmSend)
	realmAddr := runtime.CurrentRealm().Address()

	var denomOut string
	if zeroForOne {
		denomOut = pool.DenomB
	} else {
		denomOut = pool.DenomA
	}

	bnk.SendCoins(realmAddr, caller, chain.NewCoins(chain.NewCoin(denomOut, totalAmountOut)))

	return totalAmountOut
}

// findNextInitializedTick finds the next tick with liquidity in the swap direction
func findNextInitializedTick(poolID uint64, currentTick int64, zeroForOne bool) int64 {
	ticks := tickData[poolID]
	if ticks == nil {
		if zeroForOne {
			return MIN_TICK
		}
		return MAX_TICK
	}

	pool := pools[poolID]
	spacing := pool.TickSpacing

	if zeroForOne {
		// Going down (price decreasing), look for next lower initialized tick
		for tick := currentTick - spacing; tick >= MIN_TICK; tick -= spacing {
			if info := ticks[tick]; info != nil && info.Initialized {
				return tick
			}
		}
		return MIN_TICK
	} else {
		// Going up (price increasing), look for next higher initialized tick
		for tick := currentTick + spacing; tick <= MAX_TICK; tick += spacing {
			if info := ticks[tick]; info != nil && info.Initialized {
				return tick
			}
		}
		return MAX_TICK
	}
}

// crossTick updates liquidity when crossing a tick boundary
func crossTick(poolID uint64, tick int64, zeroForOne bool) {
	ticks := tickData[poolID]
	if ticks == nil {
		return
	}

	info := ticks[tick]
	if info == nil || !info.Initialized {
		return
	}

	pool := pools[poolID]

	// When crossing a tick from left to right (price increasing), add liquidityNet
	// When crossing from right to left (price decreasing), subtract liquidityNet
	if zeroForOne {
		// Price decreasing, crossing tick from above
		pool.Liquidity -= info.LiquidityNet
	} else {
		// Price increasing, crossing tick from below
		pool.Liquidity += info.LiquidityNet
	}

	// Ensure liquidity doesn't go negative
	if pool.Liquidity < 0 {
		pool.Liquidity = 0
	}
}

// =============================================================================
// COLLECT FEES
// =============================================================================

func CollectFees(_ realm, positionID uint64) (int64, int64) {
	if positionID >= uint64(len(positions)) {
		panic("position not found")
	}

	pos := positions[positionID]
	caller := string(runtime.OriginCaller())

	if pos.Owner != caller {
		panic("not owner")
	}

	pool := pools[pos.PoolID]

	// Calculate fees owed since last collection
	// Only collect fees if position is/was in range
	var feesA, feesB int64

	if pos.Liquidity > 0 {
		// Calculate accumulated fees based on fee growth difference
		feeGrowthDeltaA := pool.FeeGrowthA - pos.FeeGrowthLastA
		feeGrowthDeltaB := pool.FeeGrowthB - pos.FeeGrowthLastB

		// Use safe math to prevent overflow
		feesA = safeMulDiv(feeGrowthDeltaA, pos.Liquidity, PRECISION)
		feesB = safeMulDiv(feeGrowthDeltaB, pos.Liquidity, PRECISION)

		// Update position's last fee growth snapshot
		pos.FeeGrowthLastA = pool.FeeGrowthA
		pos.FeeGrowthLastB = pool.FeeGrowthB
	}

	// Add any previously uncollected tokens
	feesA += pos.TokensOwedA
	feesB += pos.TokensOwedB
	pos.TokensOwedA = 0
	pos.TokensOwedB = 0

	// Send fees to owner
	if feesA > 0 || feesB > 0 {
		bnk := banker.NewBanker(banker.BankerTypeRealmSend)
		realmAddr := runtime.CurrentRealm().Address()
		callerAddr := runtime.OriginCaller()

		if feesA > 0 && feesB > 0 {
			coins := chain.NewCoins(
				chain.NewCoin(pool.DenomA, feesA),
				chain.NewCoin(pool.DenomB, feesB),
			)
			bnk.SendCoins(realmAddr, callerAddr, coins)
		} else if feesA > 0 {
			bnk.SendCoins(realmAddr, callerAddr, chain.NewCoins(chain.NewCoin(pool.DenomA, feesA)))
		} else {
			bnk.SendCoins(realmAddr, callerAddr, chain.NewCoins(chain.NewCoin(pool.DenomB, feesB)))
		}
	}

	return feesA, feesB
}

// GetPositionFees returns uncollected fees for a position
func GetPositionFees(positionID uint64) (int64, int64) {
	if positionID >= uint64(len(positions)) {
		return 0, 0
	}

	pos := positions[positionID]
	pool := pools[pos.PoolID]

	var feesA, feesB int64

	if pos.Liquidity > 0 {
		feeGrowthDeltaA := pool.FeeGrowthA - pos.FeeGrowthLastA
		feeGrowthDeltaB := pool.FeeGrowthB - pos.FeeGrowthLastB

		// Use safe math to prevent overflow
		feesA = safeMulDiv(feeGrowthDeltaA, pos.Liquidity, PRECISION)
		feesB = safeMulDiv(feeGrowthDeltaB, pos.Liquidity, PRECISION)
	}

	feesA += pos.TokensOwedA
	feesB += pos.TokensOwedB

	return feesA, feesB
}

// =============================================================================
// TICK HELPERS
// =============================================================================

func updateTick(poolID uint64, tick int64, liquidityDelta int64, isLower bool) {
	ticks := tickData[poolID]
	if ticks == nil {
		ticks = make(map[int64]*TickInfo)
		tickData[poolID] = ticks
	}

	info := ticks[tick]
	if info == nil {
		info = &TickInfo{}
		ticks[tick] = info
	}

	info.LiquidityGross += liquidityDelta

	if isLower {
		info.LiquidityNet += liquidityDelta
	} else {
		info.LiquidityNet -= liquidityDelta
	}

	info.Initialized = info.LiquidityGross > 0
}

