func GetCLMMPool(poolID uint64) (string, string, int64, int64, int64, int64, int64) {
	if poolID >= uint64(len(pools)) {
		panic("pool not found")
	}
	p := pools[poolID]
	return p.DenomA, p.DenomB, p.PriceX6, p.CurrentTick, p.Liquidity, p.FeeBPS, p.TickSpacing
}

func GetCLMMPoolCount() uint64 {
	return uint64(len(pools))
}

func GetPosition(positionID uint64) (uint64, string, int64, int64, int64) {
	if positionID >= uint64(len(positions)) {
		panic("position not found")
	}
	p := positions[positionID]
	return p.PoolID, p.Owner, p.TickLower, p.TickUpper, p.Liquidity
}

func GetPositionCount() uint64 {
	return uint64(len(positions))
}

func GetPositionsByOwner(owner string) []uint64 {
	var result []uint64
	for _, p := range positions {
		if p.Owner == owner && p.Liquidity > 0 {
			result = append(result, p.ID)
		}
	}
	return result
}

func GetQuote(poolID uint64, tokenIn string, amountIn int64) int64 {
	if poolID >= uint64(len(pools)) {
		return 0
	}

	pool := pools[poolID]
	if pool.Liquidity == 0 {
		return 0
	}

	// Simulate the swap without modifying state
	// Copy pool state for simulation
	simPriceX6 := pool.PriceX6
	simLiquidity := pool.Liquidity
	simCurrentTick := pool.CurrentTick

	var zeroForOne bool
	if tokenIn == "A" {
		zeroForOne = true
	} else if tokenIn == "B" {
		zeroForOne = false
	} else {
		return 0
	}

	// Calculate fee
	feeAmount := safeMulDiv(amountIn, pool.FeeBPS, 10000)
	amountRemaining := amountIn - feeAmount
	totalAmountOut := int64(0)

	// Simulate swap with tick crossing using sqrt-based CLMM math
	sqrtPrecision := int64(1000) // sqrt(PRECISION)
	maxIterations := 100
	for i := 0; i < maxIterations && amountRemaining > 0; i++ {
		if simLiquidity == 0 {
			break
		}

		nextTick := findNextInitializedTick(poolID, simCurrentTick, zeroForOne)

		var amountInStep, amountOutStep int64

		if zeroForOne {
			// Swapping A for B (price decreases)
			nextTickPrice := getPriceAtTick(nextTick)
			if nextTickPrice < getPriceAtTick(MIN_TICK) {
				nextTickPrice = getPriceAtTick(MIN_TICK)
			}

			sqrtPriceCurrent := isqrt(simPriceX6 * PRECISION)
			sqrtPriceNext := isqrt(nextTickPrice * PRECISION)

			if sqrtPriceCurrent <= sqrtPriceNext {
				break
			}

			maxAmountIn := safeMulDiv(simLiquidity, sqrtPriceCurrent-sqrtPriceNext, safeMulDiv(sqrtPriceCurrent, sqrtPriceNext, sqrtPrecision*PRECISION))

			if amountRemaining <= maxAmountIn || maxAmountIn <= 0 {
				amountInStep = amountRemaining
				denom := simLiquidity + safeMulDiv(amountInStep, sqrtPriceCurrent, sqrtPrecision*PRECISION)
				if denom <= 0 {
					break
				}
				sqrtPriceNew := safeMulDiv(sqrtPriceCurrent, simLiquidity, denom)
				amountOutStep = safeMulDiv(simLiquidity, sqrtPriceCurrent-sqrtPriceNew, sqrtPrecision*PRECISION)
				simPriceX6 = safeMulDiv(sqrtPriceNew, sqrtPriceNew, PRECISION)
				if simPriceX6 < getPriceAtTick(MIN_TICK) {
					simPriceX6 = getPriceAtTick(MIN_TICK)
				}
			} else {
				amountInStep = maxAmountIn
				amountOutStep = safeMulDiv(simLiquidity, sqrtPriceCurrent-sqrtPriceNext, sqrtPrecision*PRECISION)
				simPriceX6 = nextTickPrice

				ticks := tickData[poolID]
				if ticks != nil {
					if tickInfo, ok := ticks[nextTick]; ok && tickInfo.Initialized {
						simLiquidity -= tickInfo.LiquidityNet
					}
				}
				simCurrentTick = nextTick
			}
		} else {
			// Swapping B for A (price increases)
			nextTickPrice := getPriceAtTick(nextTick)
			if nextTickPrice > getPriceAtTick(MAX_TICK) {
				nextTickPrice = getPriceAtTick(MAX_TICK)
			}

			sqrtPriceCurrent := isqrt(simPriceX6 * PRECISION)
			sqrtPriceNext := isqrt(nextTickPrice * PRECISION)

			if sqrtPriceNext <= sqrtPriceCurrent {
				break
			}

			maxAmountIn := safeMulDiv(simLiquidity, sqrtPriceNext-sqrtPriceCurrent, sqrtPrecision*PRECISION)

			if amountRemaining <= maxAmountIn || maxAmountIn <= 0 {
				amountInStep = amountRemaining
				sqrtPriceNew := sqrtPriceCurrent + safeMulDiv(amountInStep, sqrtPrecision*PRECISION, simLiquidity)
				amountOutStep = safeMulDiv(simLiquidity, sqrtPriceNew-sqrtPriceCurrent, safeMulDiv(sqrtPriceCurrent, sqrtPriceNew, sqrtPrecision*PRECISION))
				simPriceX6 = safeMulDiv(sqrtPriceNew, sqrtPriceNew, PRECISION)
				if simPriceX6 > getPriceAtTick(MAX_TICK) {
					simPriceX6 = getPriceAtTick(MAX_TICK)
				}
			} else {
				amountInStep = maxAmountIn
				amountOutStep = safeMulDiv(simLiquidity, sqrtPriceNext-sqrtPriceCurrent, safeMulDiv(sqrtPriceCurrent, sqrtPriceNext, sqrtPrecision*PRECISION))
				simPriceX6 = nextTickPrice

				ticks := tickData[poolID]
				if ticks != nil {
					if tickInfo, ok := ticks[nextTick]; ok && tickInfo.Initialized {
						simLiquidity += tickInfo.LiquidityNet
					}
				}
				simCurrentTick = nextTick
			}
		}

		amountRemaining -= amountInStep
		totalAmountOut += amountOutStep
	}

	return totalAmountOut
}

func GetPriceAtTick(tick int64) int64 {
	return getPriceAtTick(tick)
}

func GetTickAtPrice(priceX6 int64) int64 {
	return getTickAtPrice(priceX6)
}

func GetRealmAddress() string {
	return string(runtime.CurrentRealm().Address())
}

// =============================================================================
// RENDER
// =============================================================================

func Render(path string) string {
	var sb strings.Builder

	sb.WriteString("# Gnomo CLMM\n\n")
	sb.WriteString("**Address:** `" + string(runtime.CurrentRealm().Address()) + "`\n\n")

	if len(pools) == 0 {
		sb.WriteString("No pools yet.\n")
	} else {
		sb.WriteString("## Pools\n\n")
		for _, p := range pools {
			sb.WriteString("### Pool " + strconv.FormatUint(p.ID, 10) + "\n")
			sb.WriteString("- Pair: " + formatDenom(p.DenomA) + "/" + formatDenom(p.DenomB) + "\n")
			sb.WriteString("- Fee: " + strconv.FormatInt(p.FeeBPS, 10) + " bps\n")
			sb.WriteString("- Tick: " + strconv.FormatInt(p.CurrentTick, 10) + "\n")
			sb.WriteString("- Liquidity: " + strconv.FormatInt(p.Liquidity, 10) + "\n")
			priceFloat := float64(p.PriceX6) / float64(PRECISION)
			sb.WriteString("- Price: " + strconv.FormatFloat(priceFloat, 'f', 6, 64) + "\n\n")
		}
	}

	sb.WriteString("## Stats\n")
	sb.WriteString("- Pools: " + strconv.Itoa(len(pools)) + "\n")

	activePos := 0
	for _, p := range positions {
		if p.Liquidity > 0 {
			activePos++
		}
	}
	sb.WriteString("- Positions: " + strconv.Itoa(activePos) + "\n")

	return sb.String()
}

func formatDenom(denom string) string {
	if denom == "ugnot" {
		return "GNOT"
	}
	if strings.Contains(denom, ":") {
		parts := strings.Split(denom, ":")
		return strings.ToUpper(parts[len(parts)-1])
	}
	return denom
}

package nameregistry

import (
	"errors"
	"regexp"
	"strconv"
	"strings"

	"gno.land/p/nt/avl/rotree"
)

// Checks that a string ends with with 3 numbers.
var reEndsWithThreeNumbers = regexp.MustCompile(`[0-9]{3}$`)

// Checker defines an interface for name checkers.
type Checker interface {
	// Description returns a short description of the name checker.
	Description() string

	// Check checks that a name is valid.
	Check(name string) error
}

// NewChecker creates a new name checker.
func NewChecker(description string, fn func(string) error) Checker {
	description = strings.TrimSpace(description)
	if description == "" {
		panic("checker description is required")
	}

	if fn == nil {
		panic("checker function is required")
	}

	return &checker{description, fn}
}

// NewMinLengthChecker creates a checker to checks  min length of names.
func NewMinLengthChecker(length int) Checker {
	if length == 0 {
		panic("min length checker requires a length > 0")
	}

	desc := "Minimum name length allowed is " + strconv.Itoa(length)
	return NewChecker(desc, func(name string) error {
		if len(name) < length {
			return errors.New("name is too short, min length is " + strconv.Itoa(length))
		}
		return nil
	})
}

// NewMaxLengthChecker creates a checker to checks max length of names.
func NewMaxLengthChecker(length int) Checker {
	if length < 1 {
		panic("max length checker requires a length > 1")
	}

	desc := "Maximum name length allowed is " + strconv.Itoa(length)
	return NewChecker(desc, func(name string) error {
		if len(name) > length {
			return errors.New("name is too long, max length is " + strconv.Itoa(length))
		}
		return nil
	})
}

// NewLowercaseChecker creates a checker to check that all alphanumeric
// chars are lowercase.
func NewLowercaseChecker() Checker {
	desc := "Alphanumeric chars must all have lowercase chars"
	return NewChecker(desc, func(name string) error {
		if strings.ToLower(name) != name {
			return errors.New("name characters must be in lowercase")
		}
		return nil
	})
}

// NewForbiddenPrefixChecker creates a checker to checks that name doesn't
// start with one or more prefixes.
func NewForbiddenPrefixChecker(prefixes ...string) Checker {
	if len(prefixes) == 0 {
		panic("name prefix checker requires at least one prefix")
	}

	forbidden := strings.Join(prefixes, ", ")
	desc := "Name must not start with: " + forbidden
	return NewChecker(desc, func(name string) error {
		for _, s := range prefixes {
			if strings.HasPrefix(name, s) {
				return errors.New("name must not start these prefixes: " + forbidden)
			}
		}
		return nil
	})
}

// NewAlphaNumChecker creates a checker to check that a name starts with
// a letter and ends with three numbers.
func NewAlphaNumChecker() Checker {
	desc := "Name must start with a letter and end with 3 numbers"
	return NewChecker(desc, func(name string) error {
		name = strings.ToLower(name)
		if name == "" || name[0] > 'z' || name[0] < 'a' {
			return errors.New(`name must start with a letter between "a" and "z"`)
		}

		if len(name) < 4 || !reEndsWithThreeNumbers.MatchString(name) {
			return errors.New("name must end with three numbers")
		}
		return nil
	})
}

// NewHomoglyphChecker creates a checker to check that a name is not reserved
// or blacklisted. Checker considers typosquatting with o=0, i=1 and i=l.
// Names must be given within one or more AVL trees where key values are names.
func NewHomoglyphChecker(names ...rotree.IReadOnlyTree) Checker {
	if len(names) == 0 {
		panic("homoglyph checker requires at least one names tree")
	}

	reNumbersSuffix := regexp.MustCompile(`[0-9]{1}[a-z0-9]*$`)
	desc := "Reserved or blacklisted names are not allowed"
	return NewChecker(desc, func(name string) error {
		variants := []string{name}

		// Remove all numbers from name prefix to avoid cases like "jesus000"
		prefix := reNumbersSuffix.ReplaceAllString(name, "")
		if prefix != name {
			variants = append(variants, prefix)
		}

		variants = append(variants, GenHomoglyphVariants(name)...)
		for i, n := range variants {
			for _, storage := range names {
				if !storage.Has(n) {
					continue
				}

				// First name in list is the one being checked
				if i == 0 {
					return errors.New("name is reserved")
				} else {
					return errors.New("similar name already exists: " + n)
				}
			}
		}
		return nil
	})
}

type checker struct {
	description string
	fn          func(string) error
}

func (c checker) Description() string     { return c.description }
func (c checker) Check(name string) error { return c.fn(name) }

