package nameregistry

import (
	"errors"
	"regexp"
	"strconv"
	"strings"
)

// Matches the last three suffix numbers.
var reEndsWithThreeNumbers = regexp.MustCompile(`[0-9]{3}$`)

// Checker defines an interface for name checkers.
type Checker interface {
	// Description returns a short description of the name checker.
	Description() string

	// Check checks that a name is valid.
	Check(name string) error
}

// NewChecker creates a new name checker.
func NewChecker(description string, fn func(string) error) Checker {
	description = strings.TrimSpace(description)
	if description == "" {
		panic("checker description is required")
	}

	if fn == nil {
		panic("checker function is required")
	}

	return &checker{description, fn}
}

// NewMinLengthChecker creates a checker to checks  min length of names.
func NewMinLengthChecker(length int) Checker {
	if length == 0 {
		panic("min length checker requires a length > 0")
	}

	desc := "Minimum name length allowed is " + strconv.Itoa(length)
	return NewChecker(desc, func(name string) error {
		if len(name) < length {
			return errors.New("name is too short, min length is " + strconv.Itoa(length))
		}
		return nil
	})
}

// NewMaxLengthChecker creates a checker to checks max length of names.
func NewMaxLengthChecker(length int) Checker {
	if length < 1 {
		panic("max length checker requires a length > 1")
	}

	desc := "Maximum name length allowed is " + strconv.Itoa(length)
	return NewChecker(desc, func(name string) error {
		if len(name) > length {
			return errors.New("name is too long, max length is " + strconv.Itoa(length))
		}
		return nil
	})
}

// NewLowercaseChecker creates a checker to check that all alphanumeric
// chars are lowercase.
func NewLowercaseChecker() Checker {
	desc := "Alphanumeric chars must all have lowercase chars"
	return NewChecker(desc, func(name string) error {
		if strings.ToLower(name) != name {
			return errors.New("name characters must be in lowercase")
		}
		return nil
	})
}

// NewForbiddenPrefixChecker creates a checker to checks that name doesn't
// start with one or more prefixes.
func NewForbiddenPrefixChecker(prefixes ...string) Checker {
	if len(prefixes) == 0 {
		panic("name prefix checker requires at least one prefix")
	}

	forbidden := strings.Join(prefixes, ", ")
	desc := "Name must not start with: " + forbidden
	return NewChecker(desc, func(name string) error {
		for _, s := range prefixes {
			if strings.HasPrefix(name, s) {
				return errors.New("name must not start these prefixes: " + forbidden)
			}
		}
		return nil
	})
}

// NewAlphaNumChecker creates a checker to check that a name starts with
// a letter and ends with three numbers.
func NewAlphaNumChecker() Checker {
	desc := "Name must start with a letter and end with 3 numbers"
	return NewChecker(desc, func(name string) error {
		name = strings.ToLower(name)
		if name == "" || name[0] > 'z' || name[0] < 'a' {
			return errors.New(`name must start with a letter between "a" and "z"`)
		}

		if len(name) < 4 || !reEndsWithThreeNumbers.MatchString(name) {
			return errors.New("name must end with three numbers")
		}
		return nil
	})
}

// NewSimilarityChecker creates a checker to check that a name is not similar
// or equal to a reserved or blacklisted name.
func NewSimilarityChecker() Checker {
	desc := "Reserved or blacklisted names are not allowed"
	return NewChecker(desc, func(name string) error {
		return CheckNameSimilarity(name, reserved, blacklisted, custom)
	})
}

type checker struct {
	description string
	fn          func(string) error
}

func (c checker) Description() string     { return c.description }
func (c checker) Check(name string) error { return c.fn(name) }

package nameregistry_test

import (
	"testing"

	"gno.land/p/nt/uassert"

	"gno.land/r/g1hy6zry03hg5d8le9s2w4fxme6236hkgd928dun/nameregistry"
)

func TestNewChecker(t *testing.T) {
	uassert.PanicsWithMessage(t, "checker description is required", func() {
		_ = nameregistry.NewChecker("", nil)
	}, "expect required panic")

	uassert.PanicsWithMessage(t, "checker function is required", func() {
		_ = nameregistry.NewChecker("description", nil)
	}, "expect required panic")

	checker := nameregistry.NewChecker("Foobar", func(string) error { return nil })
	uassert.Equal(t, "Foobar", checker.Description(), "expect description to match")
}

func TestNewCheckerCheck(t *testing.T) {
	tests := []struct {
		name         string
		registerName string
		err          error
	}{
		{
			name:         "ok",
			registerName: "xyz123",
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			// Arrange
			var validatedName string
			checker := nameregistry.NewChecker("Foobar", func(name string) error {
				validatedName = name
				return tt.err
			})

			// Act
			err := checker.Check(tt.registerName)

			// Assert
			if tt.err != nil {
				uassert.ErrorIs(t, err, tt.err, "expect an error")
			} else {
				uassert.Nil(t, err, "expect no error")
			}

			uassert.Equal(t, tt.registerName, validatedName, "expect validated name to match")
		})
	}
}

func TestCheckers(t *testing.T) {
	// Min length
	uassert.PanicsWithMessage(t, "min length checker requires a length > 0", func() {
		_ = nameregistry.NewMinLengthChecker(0)
	}, "expect invalid length panic")
	checker := nameregistry.NewMinLengthChecker(4)
	uassert.Equal(t, "Minimum name length allowed is 4", checker.Description(), "expect description to match")

	// Max length
	uassert.PanicsWithMessage(t, "max length checker requires a length > 1", func() {
		_ = nameregistry.NewMaxLengthChecker(0)
	}, "expect invalid length panic")
	checker = nameregistry.NewMaxLengthChecker(4)
	uassert.Equal(t, "Maximum name length allowed is 4", checker.Description(), "expect description to match")

	// Lowercase
	checker = nameregistry.NewLowercaseChecker()
	uassert.Equal(t, "Alphanumeric chars must all have lowercase chars", checker.Description(), "expect description to match")

	// Forbidden prefix
	uassert.PanicsWithMessage(t, "name prefix checker requires at least one prefix", func() {
		_ = nameregistry.NewForbiddenPrefixChecker()
	}, "expect panic when no prefix")
	checker = nameregistry.NewForbiddenPrefixChecker("a", "b")
	uassert.Equal(t, "Name must not start with: a, b", checker.Description(), "expect description to match")

	// Alphanum
	checker = nameregistry.NewAlphaNumChecker()
	uassert.Equal(t, "Name must start with a letter and end with 3 numbers", checker.Description(), "expect description to match")

	// Similarity
	checker = nameregistry.NewSimilarityChecker()
	uassert.Equal(t, "Reserved or blacklisted names are not allowed", checker.Description(), "expect description to match")
}

func TestCheckersCheck(t *testing.T) {
	tests := []struct {
		name         string
		registerName string
		setup        func() nameregistry.Checker
		errMsg       string
	}{
		{
			name: "min length valid",
			setup: func() nameregistry.Checker {
				return nameregistry.NewMinLengthChecker(4)
			},
			registerName: "x123",
		},
		{
			name: "min length invalid",
			setup: func() nameregistry.Checker {
				return nameregistry.NewMinLengthChecker(4)
			},
			registerName: "x",
			errMsg:       "name is too short, min length is 4",
		},
		{
			name: "max length valid",
			setup: func() nameregistry.Checker {
				return nameregistry.NewMaxLengthChecker(4)
			},
			registerName: "x123",
		},
		{
			name: "max length invalid",
			setup: func() nameregistry.Checker {
				return nameregistry.NewMaxLengthChecker(4)
			},
			registerName: "xy123",
			errMsg:       "name is too long, max length is 4",
		},
		{
			name: "lowercase valid",
			setup: func() nameregistry.Checker {
				return nameregistry.NewLowercaseChecker()
			},
			registerName: "xyz123",
		},
		{
			name: "lowercase invalid",
			setup: func() nameregistry.Checker {
				return nameregistry.NewLowercaseChecker()
			},
			registerName: "XYZ123",
			errMsg:       "name characters must be in lowercase",
		},
		{
			name: "prefix valid with one prefix",
			setup: func() nameregistry.Checker {
				return nameregistry.NewForbiddenPrefixChecker("a")
			},
			registerName: "xyz",
		},
		{
			name: "prefix valid with many prefixes",
			setup: func() nameregistry.Checker {
				return nameregistry.NewForbiddenPrefixChecker("a", "b", "c")
			},
			registerName: "xyz",
		},
		{
			name: "prefix invalid with one prefix",
			setup: func() nameregistry.Checker {
				return nameregistry.NewForbiddenPrefixChecker("a")
			},
			registerName: "aaa",
			errMsg:       "name must not start these prefixes: a",
		},
		{
			name: "prefix invalid with many prefixes",
			setup: func() nameregistry.Checker {
				return nameregistry.NewForbiddenPrefixChecker("a", "b", "c")
			},
			registerName: "bbb",
			errMsg:       "name must not start these prefixes: a, b, c",
		},
		{
			name: "alphanum valid",
			setup: func() nameregistry.Checker {
				return nameregistry.NewAlphaNumChecker()
			},
			registerName: "x123",
		},
		{
			name: "alphanum valid with uppercase",
			setup: func() nameregistry.Checker {
				return nameregistry.NewAlphaNumChecker()
			},
			registerName: "X123",
		},
		{
			name: "alphanum invalid without start letter",
			setup: func() nameregistry.Checker {
				return nameregistry.NewAlphaNumChecker()
			},
			registerName: "123x",
			errMsg:       `name must start with a letter between "a" and "z"`,
		},
		{
			name: "alphanum invalid without numbers suffix",
			setup: func() nameregistry.Checker {
				return nameregistry.NewAlphaNumChecker()
			},
			registerName: "x",
			errMsg:       "name must end with three numbers",
		},
		{
			name: "alphanum invalid with few numbers suffix",
			setup: func() nameregistry.Checker {
				return nameregistry.NewAlphaNumChecker()
			},
			registerName: "x12",
			errMsg:       "name must end with three numbers",
