package grade_rules_registry

// ==============================
// 1) 데이터 구조
// ==============================

// 문서(지침 PDF) 단위 기록
type DocRevision struct {
	DocVer   string // 예: "2025-v1"
	PdfHash  string // SHA-256(pdf) 식별값
	Issuer   string // 예: "경기도교육청" / "연구자"
	Ts       int64  // 기록 시각(실험에서는 입력값으로 관리)
	Summary  string // 요약
}

// 조항 단위 기록(핵심 조항만)
type ArticleRevision struct {
	ArticleKey string // 예: "perf_inclass_principle"
	Rev        int    // 조항 리비전 번호
	TextHash   string // SHA-256(정규화 텍스트)
	RefDocVer  string // 연결된 문서 버전
	Editor     string // 기록 주체
	Ts         int64  // 기록 시각(입력값)
	Summary    string // 변경 요약
}

// ==============================
// 2) 상태(State)
// ==============================

var DocRevisions []DocRevision
var ArticleRevisions map[string][]ArticleRevision

// ==============================
// 3) 초기화
// ==============================

func init() {
	if ArticleRevisions == nil {
		ArticleRevisions = make(map[string][]ArticleRevision)
	}
}

// ==============================
// 4) 권장 ArticleKey 목록
// ==============================

const (
	AK_PerfInClassPrinciple = "perf_inclass_principle"
	AK_NoHomeworkTypePerf   = "no_homework_type_perf"
	AK_CommitteeProcess     = "committee_process"
	AK_AvoidConcentration   = "avoid_concentration"
	AK_EvidenceRetention    = "evidence_retention"
)

// ==============================
// 5) 문서(지침 PDF) 기록 함수
// ==============================

func AddDocRevision(docVer, pdfHash, issuer, summary string, ts int64) {
	DocRevisions = append(DocRevisions, DocRevision{
		DocVer:  docVer,
		PdfHash: pdfHash,
		Issuer:  issuer,
		Ts:      ts,
		Summary: summary,
	})
}

func ListDocRevisions() []DocRevision {
	return DocRevisions
}

func FindDocRevision(docVer string) (DocRevision, bool) {
	for i := 0; i < len(DocRevisions); i++ {
		if DocRevisions[i].DocVer == docVer {
			return DocRevisions[i], true
		}
	}
	return DocRevision{}, false
}

func VerifyDocHash(docVer, pdfHash string) bool {
	d, ok := FindDocRevision(docVer)
	if !ok {
		return false
	}
	return d.PdfHash == pdfHash
}

// ==============================
// 6) 조항(핵심) 기록 함수 (append-only)
// ==============================

func AddArticleRevision(articleKey, textHash, refDocVer, editor, summary string, ts int64) {
	prev := ArticleRevisions[articleKey]
	nextRev := len(prev) + 1

	ArticleRevisions[articleKey] = append(prev, ArticleRevision{
		ArticleKey: articleKey,
		Rev:        nextRev,
		TextHash:   textHash,
		RefDocVer:  refDocVer,
		Editor:     editor,
		Ts:         ts,
		Summary:    summary,
	})
}

func ListArticleRevisions(articleKey string) []ArticleRevision {
	return ArticleRevisions[articleKey]
}

func GetLatestArticleRevision(articleKey string) (ArticleRevision, bool) {
	h := ListArticleRevisions(articleKey)
	if len(h) == 0 {
		return ArticleRevision{}, false
	}
	return h[len(h)-1], true
}

func VerifyLatestArticleHash(articleKey, textHash string) bool {
	latest, ok := GetLatestArticleRevision(articleKey)
	if !ok {
		return false
	}
	return latest.TextHash == textHash
}

// ==============================
// 7) 실험용 헬퍼
// ==============================

func ListArticleKeys() []string {
	return []string{
		AK_PerfInClassPrinciple,
		AK_NoHomeworkTypePerf,
		AK_CommitteeProcess,
		AK_AvoidConcentration,
		AK_EvidenceRetention,
	}
}

