	RefDocVer  string // 연결된 문서 버전: 예) "2026-02-05_v1"
	Editor     string // 기록자: 예) "황민우"
	EditDate   string // 예: "2026-02-06" (사람이 읽는 날짜, 캡처용)
	Ts         int64  // 등록 시각(Unix time)
	Summary    string // 변경 요약(무엇이 바뀌었는지)
}

// ==============================
// 2) 상태(State)
