		commits.Iterate(prefix, stop, func(key string, val any) bool {
			c := val.(*Commit)
			if c.Revealed {
				addr := address(key[len(prefix):])
				bnk.SendCoins(realmAddr, addr, chain.Coins{chain.NewCoin("ugnot", stake)})
			}
