package gnocial12

import (
	"gno.land/p/demo/ufmt"
	"std"
	"strings"
	"time"
)

type Post struct {
	Author  std.Address
	Content string
	Time    time.Time
}

type DirectMessage struct {
	Sender   std.Address
	Receiver std.Address
	Content  string
	Time     time.Time
}

var posts []Post
var directMessages []DirectMessage

// Post your thoughts.
//
// Enter a post below, and then press the `Call` button.
//
// The post will be recorded along with your address for everyone else to read.
func CreatePost(content string) {
	newPost := Post{
		Author:  std.GetOrigCaller(),
		Content: content,
		Time:    time.Now(),
	}
	posts = append(posts, newPost)
}

// Talk with someone else! Enter their address, and the message that you want
// to send to them below, and then press the `Call` button.
func SendDirectMessage(receiver std.Address, content string) {
	newDM := DirectMessage{
		Sender:   std.GetOrigCaller(),
		Receiver: receiver,
		Content:  content,
		Time:     time.Now(),
	}
	directMessages = append(directMessages, newDM)
}

// Call this to see all of the current posts.
func RenderPosts() string {
	var output strings.Builder
	output.WriteString("<h1>All Posts</h1>")
	for _, post := range posts {
		output.WriteString("<div>")
		output.WriteString(ufmt.Sprintf("<p><a href='/user/%s'>%s</a>: %s</p>", post.Author, post.Author, post.Content))
		output.WriteString(ufmt.Sprintf("<small>Posted at: %s</small>", post.Time.String()))
		output.WriteString("</div><hr>")
	}
	return output.String()
}

// Call this to see your messages.
func RenderDMs() string {
	var output strings.Builder
	caller := std.GetOrigCaller()

	if caller == "" {
		output.WriteString("<h1>All Direct Messages</h1>")
	} else {
		output.WriteString("<h1>Your Direct Messages</h1>")
	}

	for _, dm := range directMessages {
		if caller == "" || dm.Sender == caller || dm.Receiver == caller {
			output.WriteString("<div>")
			output.WriteString(ufmt.Sprintf("<p>From: <a href='/user/%s'>%s</a> To: <a href='/user/%s'>%s</a></p>", dm.Sender, dm.Sender, dm.Receiver, dm.Receiver))
			output.WriteString(ufmt.Sprintf("<p>Message: %s</p>", dm.Content))
			output.WriteString(ufmt.Sprintf("<small>Sent at: %s</small>", dm.Time.String()))
			output.WriteString("</div><hr>")
		}
	}
	return output.String()
}

// Calling Render() with `/posts` will return an HTML string with all of the current posts.
// Calling Render() with `/dms` will return an HTML string with all of the current direct messages.
func Render(path string) string {
	command := ""
	if idx := strings.Index(path, "/"); idx >= 0 {
		command = path[idx+1:]
	}

	switch command {
	case "posts":
		return RenderPosts()
	case "dms":
		return RenderDMs()
	default:
		return "<p>Welcome to GnoSocial! Use /posts to view all posts or /dms to view your messages.</p>"
	}
}
