package gnoyourdate_v1

import (
	"std"
	"strconv"
	"strings"
	"time"

	"gno.land/p/demo/ufmt"
)

const (
	ChoiceNone             Choice = 0x0
	ChoiceSinceInception   Choice = 0x1
	ChoiceOneOrTwoYearsAgo Choice = 0x2
	ChoiceThisYear         Choice = 0x4
	ChoiceThisMonth        Choice = 0x8
)

const (
	dateFmt = "2006-01-02 15:04 MST"
)

var (
	poll = Poll{
		Question: "When did you first hear about Gno.land?",
		Answers: []Answer{
			{Choice: ChoiceThisMonth},
			{Choice: ChoiceThisYear},
			{Choice: ChoiceOneOrTwoYearsAgo},
			{Choice: ChoiceSinceInception},
		},
	}
	admin         = std.Address("g1rdld2ay4c3r3eghk563sz6ne79mmplcl8zatuu")
	isRealmLocked bool
	nextRealmPath string
)

type (
	// Choice defines a type for poll choices.
	Choice byte

	// Poll tracks the number of votes for a set of pre defined answers.
	Poll struct {
		Question string
		Answers  []Answer
	}

	// Answer contains the number of votes for a poll choice.
	Answer struct {
		Choice       Choice
		Participants []Participant
	}
)

type Participant struct {
	Address std.Address
	Date    time.Time
}

// String returns the choice as a string.
func (c Choice) String() string {
	return strconv.Itoa(int(c))
}

// GetCount returns the number of votes submitted for a poll choice.
func (p Poll) GetCount(c Choice) int {
	for _, a := range p.Answers {
		if a.Choice == c {
			return len(a.Participants)
		}
	}
	return 0
}

// GetTotalCount returns the total number of votes.
func (p Poll) GetTotalCount() int {
	var count int
	for _, a := range p.Answers {
		count += len(a.Participants)
	}
	return count
}

// HasVoted checks if an address already submitted a vote.
func (p Poll) HasVoted(addr std.Address) bool {
	for _, answer := range p.Answers {
		for _, p := range answer.Participants {
			if p.Address == addr {
				return true
			}
		}
	}
	return false
}

// 👋 Welcome to the GnoYourDate (Know-Your-Gno-Date) Poll! 🎈🎈🎈
//
// You are now viewing the Vote function.
// This function allows you to submit your choice for the poll.
//
// ❔ Question: When did you first hear about Gno.land?
// 👇 Options:
//   - This month
//   - This year
//   - 1-2 years ago
//   - Since its inception
//
// 📍 Please make sure to mark the option you choose as 'True (Yes)' and mark all other options as 'False (No)' for your vote to be valid.
// 📍 If you select multiple options as 'True (Yes)', your vote will be considered invalid.
//
// 📢 Now, please cast your vote using the form below, then click the "Call" button to submit it.
//
// 💬 After casting your vote, click the "Copy result link" button to copy the link to your vote, then share it by tagging @_gnostudio and using the #gnoyourdate hashtag.
func Vote(thisMonth, thisYear, oneOrTwoYearsAgo, sinceInception bool) string {
	if isRealmLocked {
		if nextRealmPath == "" {
			panic("Realm is locked")
		}

		// Allow TX to succeed but return an "error" message with the link to vote on the next realm version.
		// Panic and error results are not used to favor GnoStudio Connect usability to making links clickable.
		return "This realm is deprecated. To vote, please navigate to the new version, which can be found at " + nextRealmPath
	}

	choice := mustCreateChoiceFromVoteArgs(thisMonth, thisYear, oneOrTwoYearsAgo, sinceInception)
	participant := Participant{
		Address: std.GetOrigCaller(),
		Date:    time.Now(),
	}
	assertFirstVote(participant.Address)

	for i, a := range poll.Answers {
		if a.Choice == choice {
			poll.Answers[i].Participants = append(poll.Answers[i].Participants, participant)
			break
		}
	}

	return ufmt.Sprintf(
		`You have successfully voted Yes on '%s'. You can check out the results at https://%s.%s`,
		getAnswerLabel(choice),
		std.GetChainID(),
		std.CurrentRealm().PkgPath(),
	)
}

// Render returns a Markdown string with the poll details.
func Render(_ string) string {
	var buf strings.Builder

	if isRealmLocked {
		var msg string
		if nextRealmPath == "" {
			msg = "Realm is locked."
		} else {
			msg += ufmt.Sprintf(
				"This realm is deprecated. </br>Please navigate to the new version, which can be found at <a href=\"%s\">%s</a>",
				nextRealmPath,
				nextRealmPath,
			)
		}

		buf.WriteString(
			`<p style="padding: .75rem 1.25rem; border: 1px solid #FFEEBA; background-color: #FFF3CD; color: #856404; border-radius: .25rem">` +
				msg +
				"</p>\n\n",
		)
	}

	buf.WriteString("# GnoYourDate Poll\n👋 Welcome to the GnoYourDate (Know-Your-Gno-Date) Poll! 🎈🎈🎈\n\n")
	buf.WriteString(ufmt.Sprintf("## Question: %s\n", poll.Question))

	if !isRealmLocked {
		url := getGnoStudioConnectVoteURL(std.CurrentRealm().PkgPath())
		buf.WriteString("👉 You can participate in the poll by [submitting your vote from here](" + url + ") 👈\n")
	}

	buf.WriteString("### Choices:\n")
	for _, c := range [4]Choice{ChoiceThisMonth, ChoiceThisYear, ChoiceOneOrTwoYearsAgo, ChoiceSinceInception} {
		buf.WriteString(
			ufmt.Sprintf("- %s: **%d** vote(s)\n", getAnswerLabel(c), poll.GetCount(c)),
		)
	}

	buf.WriteString(
		ufmt.Sprintf("\nThe total number of received votes is **%d**\n", poll.GetTotalCount()),
	)

	// Render participant addresses when there are votes
	if poll.GetTotalCount() > 0 {
		buf.WriteString("### Participants:\nList of participants grouped by their vote\n")
		for _, a := range poll.Answers {
			// Skip anwers without votes
			if len(a.Participants) == 0 {
				continue
			}

			buf.WriteString("\n- **" + getAnswerLabel(a.Choice) + "**:\n")
			for _, p := range a.Participants {
				date := p.Date.UTC().Format(dateFmt)
				buf.WriteString("  - " + p.Address.String() + " <small><i>(voted at " + date + ")</i></small>\n")
			}
		}
	}

	return buf.String()
}

// GetAdmin returns the address of the Realm admin account.
func GetAdmin() string {
	return admin.String()
}

// DeprecateRealm deprecates the realm in favor of another realm.
// Depecation locks this realm to disallow new votes.
func DeprecateRealm(realmPath string) string {
	assertCallerIsAdmin()

	realmPath = strings.TrimSpace(realmPath)
	if realmPath == "" {
		panic("Realm path is empty")
	}

	isRealmLocked = true
	nextRealmPath = realmPath
	return "The poll has been moved to " + realmPath
}

// LockRealm locks the realm disallowing new vote submission.
func LockRealm() string {
	assertCallerIsAdmin()

	isRealmLocked = true
	return "Realm locked successfully"
}

// UnlockRealm unlocks the realm allowing new votes to be submitted.
func UnlockRealm() string {
	assertCallerIsAdmin()

	isRealmLocked = false
	nextRealmPath = ""
	return "Realm unlocked successfully"
}

func getAnswerLabel(c Choice) string {
	switch c {
	case ChoiceThisMonth:
		return "This month"
	case ChoiceThisYear:
		return "This year"
	case ChoiceOneOrTwoYearsAgo:
		return "1-2 years ago"
	case ChoiceSinceInception:
		return "Since its inception"
	default:
		return "None"
	}
}

func getGnoStudioConnectVoteURL(realmPath string) string {
	return ufmt.Sprintf(
		"https://gno.studio/connect/view/%s?network=%s#Vote",
		realmPath,
		std.GetChainID(),
	)
}

func mustCreateChoiceFromVoteArgs(voteArgs ...bool) Choice {
	var c Choice
	for _, arg := range voteArgs {
		c <<= 1 // shift left
		if arg {
			c |= 1 // last bit on
		}
	}

	switch c {
	case ChoiceThisMonth, ChoiceThisYear, ChoiceOneOrTwoYearsAgo, ChoiceSinceInception:
		return c
	case ChoiceNone:
		panic(
			"You haven't selected an option. Please submit your vote by selecting one of the options as 'True (Yes)'.",
		)
	default:
		panic(
			"Multiple options have been selected. Please submit your vote by selecting only one of the options as 'True (Yes)'.\n" +
				"The option you choose must be marked as 'True (Yes)', while all other options are marked as 'False (No)'.",
		)
	}
}

func assertFirstVote(addr std.Address) {
	if poll.HasVoted(addr) {
		panic("This address already voted")
	}
}

func assertCallerIsAdmin() {
	if admin != std.GetOrigCaller() {
		panic("Caller is not admin")
	}
}

package gnoyourdate_v1

import (
	"std"
	"strings"
	"testing"

	"gno.land/p/demo/testutils"

	realm "gno.land/r/gnoyourdate/poll_v1"
)

func TestVote(t *testing.T) {
	cases := []struct {
		name        string
		args        [4]bool
		addr        std.Address
		msg, errMsg string
		hasVoted    bool
		totalVotes  int
		choiceVotes map[realm.Choice]int
		setup       func(*realm.Poll)
	}{
		{
			name:       "ok",
			args:       [4]bool{false, true, false, false},
			addr:       testutils.TestAddress("test"),
			hasVoted:   true,
			totalVotes: 1,
			msg:        `You have successfully voted yes on "This year" you can check out the result at https://dev.gno.land/r/gnoyourdate/poll_v1`,
			choiceVotes: map[realm.Choice]int{
				realm.ChoiceThisMonth:        0,
				realm.ChoiceThisYear:         1,
				realm.ChoiceOneOrTwoYearsAgo: 0,
				realm.ChoiceSinceInception:   0,
			},
		},
		{
			name:       "ok with other votes",
			args:       [4]bool{false, false, true, false},
			addr:       testutils.TestAddress("test"),
			hasVoted:   true,
			totalVotes: 3,
			msg:        `You have successfully voted yes on "1-2 years ago" you can check out the result at https://dev.gno.land/r/gnoyourdate/poll_v1`,
			choiceVotes: map[realm.Choice]int{
				realm.ChoiceThisMonth:        1,
				realm.ChoiceThisYear:         0,
				realm.ChoiceOneOrTwoYearsAgo: 2,
				realm.ChoiceSinceInception:   0,
			},
			setup: func(p *realm.Poll) {
				p.Answers[0].Participants = append(p.Answers[0].Participants, testutils.TestAddress("test2"))
				p.Answers[2].Participants = append(p.Answers[2].Participants, testutils.TestAddress("test3"))
			},
		},
		{
			name:       "already voted",
			args:       [4]bool{true, false, false, false},
			addr:       testutils.TestAddress("test"),
			hasVoted:   true,
			totalVotes: 1,
			errMsg:     "This address already voted",
			choiceVotes: map[realm.Choice]int{
				realm.ChoiceThisMonth:        1,
				realm.ChoiceThisYear:         0,
				realm.ChoiceOneOrTwoYearsAgo: 0,
				realm.ChoiceSinceInception:   0,
			},
			setup: func(p *realm.Poll) {
				p.Answers[0].Participants = append(p.Answers[0].Participants, testutils.TestAddress("test"))
			},
		},
		{
			name: "multiple vote choices",
			args: [4]bool{true, true, false, false},
			addr: testutils.TestAddress("test"),
			errMsg: "Multiple choices has been selected, you should submit a vote for only one answer.\\n" +
				"The answer you choose should be selected as True (Yes) making sure that all other options are selected as False (No).",
			choiceVotes: map[realm.Choice]int{
				realm.ChoiceThisMonth:        0,
				realm.ChoiceThisYear:         0,
				realm.ChoiceOneOrTwoYearsAgo: 0,
				realm.ChoiceSinceInception:   0,
			},
		},
		{
			name: "no vote choice",
			addr: testutils.TestAddress("test"),
			errMsg: "No choice has been selected, you should submit a vote for one of the answers.\\n" +
				"Only one of the choices should be selected to consider the vote as valid.",
			choiceVotes: map[realm.Choice]int{
				realm.ChoiceThisMonth:        0,
				realm.ChoiceThisYear:         0,
				realm.ChoiceOneOrTwoYearsAgo: 0,
				realm.ChoiceSinceInception:   0,
			},
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			defer teardown(t)()

			std.TestSetOrigCaller(tc.addr)

			if tc.setup != nil {
				tc.setup(&realm.poll)
			}

			var msg string

			// Act
			errMsg := catchPanic(t, func() {
				msg = realm.Vote(tc.args[0], tc.args[1], tc.args[2], tc.args[3])
			})

			// Assert
			if msg != tc.msg {
				t.Fatalf("expected vote success message: '%s', got: '%s'", tc.msg, msg)
			}

			if got := strings.ReplaceAll(errMsg, "\n", "\\n"); got != tc.errMsg {
				t.Fatalf("expected vote panic: '%s', got: '%s'", tc.errMsg, got)
			}

			if got := realm.poll.HasVoted(tc.addr); got != tc.hasVoted {
				t.Fatalf("expected has voted check: %v, got: %v", tc.hasVoted, got)
			}

			if got := realm.poll.GetTotalCount(); got != tc.totalVotes {
				t.Fatalf("expected total vote count: %d, got: %d", tc.totalVotes, got)
			}

			for choice, count := range tc.choiceVotes {
				if got := realm.poll.GetCount(choice); got != count {
					t.Fatalf("expected vote count for choice %s: %d, got: %d", choice.String(), count, got)
				}
			}
		})
	}
}

func TestRender(t *testing.T) {
	// Arrange
	wantMarkdown := "## Poll: When did you first learn about Gno.land?\\n" +
		"👉 You can participate in the poll by [submitting your vote]" +
		"(https://gno.studio/connect/view/gno.land/r/gnoyourdate/poll_v1?network=dev&tab=functions#Vote) 👈\\n" +
		"### Answers:\\n" +
		"- This month: 0 vote(s)\\n" +
		"- This year: 0 vote(s)\\n" +
		"- 1-2 years ago: 0 vote(s)\\n" +
		"- Since its inception: 0 vote(s)\\n\\n" +
		"The total number of answers is **0**\\n"

	// Act
	markdown := realm.Render("")

	// Assert
	if got := strings.ReplaceAll(markdown, "\n", "\\n"); got != wantMarkdown {
		t.Fatalf("expected render output: '%s', got: '%s'", wantMarkdown, got)
	}
}

func teardown(t *testing.T) func() {
	t.Helper()

	// Make a copy of the current Poll instance
	poll := realm.poll

	return func() {
		// Cleanup votes
		for i := range poll.Answers {
			poll.Answers[i].Participants = nil
		}

		// Restore the global Poll instance
		realm.poll = poll
	}
}

func catchPanic(t *testing.T, fn func()) (reason string) {
	t.Helper()

	defer func() {
		if r := recover(); r != nil {
			if err, _ := r.(error); err != nil {
				reason = err.Error()
			} else {
				reason = r.(string)
			}
		}
	}()

	fn()
	return
}

