	0x80:    u256.MustFromDecimal("338111622100601834656805679988414885971"), // 0xfe5dee046a99a2a811c461f1969c3053,
	0x100:   u256.MustFromDecimal("335954724994790223023589805789778977700"), // 0xfcbe86c7900a88aedcffc83b479aa3a4,
	0x200:   u256.MustFromDecimal("331682121138379247127172139078559817300"), // 0xf987a7253ac413176f2b074cf7815e54,
	0x400:   u256.MustFromDecimal("323299236684853023288211250268160618739"), // 0xf3392b0822b70005940c7a398e4b70f3,
	0x800:   u256.MustFromDecimal("307163716377032989948697243942600083929"), // 0xe7159475a2c29b7443b29c7fa6e889d9,
	0x1000:  u256.MustFromDecimal("277268403626896220162999269216087595045"), // 0xd097f3bdfd2022b8845ad8f792aa5825,
	0x2000:  u256.MustFromDecimal("225923453940442621947126027127485391333"), // 0xa9f746462d870fdf8a65dc1f90e061e5,
	0x4000:  u256.MustFromDecimal("149997214084966997727330242082538205943"), // 0x70d869a156d2a1b890bb3df62baf32f7,
	0x8000:  u256.MustFromDecimal("66119101136024775622716233608466517926"),  // 0x31be135f97d08fd981231505542fcfa6,
	0x10000: u256.MustFromDecimal("12847376061809297530290974190478138313"),  // 0x9aa508b5b7a84e1c677de54f3e99bc9,
	0x20000: u256.MustFromDecimal("485053260817066172746253684029974020"),    // 0x5d6af8dedb81196699c329225ee604,
	0x40000: u256.MustFromDecimal("691415978906521570653435304214168"),       // 0x2216e584f5fa1ea926041bedfe98,
	0x80000: u256.MustFromDecimal("1404880482679654955896180642"),            // 0x48a170391f7dc42444e8fa2,
}

var binaryLogConsts = [8]*u256.Uint{
	u256.MustFromDecimal("0"),                                       // 0x0,
	u256.MustFromDecimal("3"),                                       // 0x3,
	u256.MustFromDecimal("15"),                                      // 0xF,
	u256.MustFromDecimal("255"),                                     // 0xFF,
	u256.MustFromDecimal("65535"),                                   // 0xFFFF,
	u256.MustFromDecimal("4294967295"),                              // 0xFFFFFFFF,
	u256.MustFromDecimal("18446744073709551615"),                    // 0xFFFFFFFFFFFFFFFF,
	u256.MustFromDecimal("340282366920938463463374607431768211455"), // 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF,
}

var (
	shift1By32Left = u256.MustFromDecimal("4294967296") // (1 << 32)
)

func TickMathGetSqrtRatioAtTick(tick int32) *u256.Uint { // uint160 sqrtPriceX96
	absTick := abs(tick)
	ratio := u256.MustFromDecimal("340282366920938463463374607431768211456") // consts.Q128

	for mask, value := range tickRatioMap {
		if absTick&mask != 0 {
			// ratio = (ratio * value) >> 128
			ratio = ratio.Mul(ratio, value)
			ratio = ratio.Rsh(ratio, 128)
		}
	}

	if tick > 0 {
		_maxUint256 := u256.MustFromDecimal("115792089237316195423570985008687907853269984665640564039457584007913129639935") // consts.MAX_UINT256
		_tmp := new(u256.Uint).Div(_maxUint256, ratio)
		ratio = _tmp.Clone()
	}

	shifted := ratio.Rsh(ratio, 32).Clone()

	remainder := ratio.Mod(ratio, shift1By32Left)

	if new(u256.Uint).Add(shifted.Clone(), remainder.Clone()).IsZero() {
		return shifted
	}

	return new(u256.Uint).Add(shifted, u256.One())
}

func TickMathGetTickAtSqrtRatio(sqrtPriceX96 *u256.Uint) int32 {

	ratio := new(u256.Uint).Lsh(sqrtPriceX96, 32)

	msb, adjustedRatio := findMSB(ratio)
	adjustedRatio = adjustRatio(ratio, msb)

	log2 := calculateLog2(msb, adjustedRatio)
	tick := getTickValue(log2, sqrtPriceX96)

	return tick
}

// findMSB computes the MSB (most significant bit) of the given ratio.
func findMSB(ratio *u256.Uint) (*u256.Uint, *u256.Uint) {
	msb := u256.Zero()

	for i := 7; i >= 1; i-- {
		f := new(u256.Uint).Lsh(gt(ratio, binaryLogConsts[i]), uint(i))
		msb = new(u256.Uint).Or(msb, f)
		ratio = new(u256.Uint).Rsh(ratio, uint(f.Uint64()))
	}

	// handle the remaining bits
	{
		f := gt(ratio, u256.One()) // 0x1
		// msb = msb | f
		msb = new(u256.Uint).Or(msb, f)
	}

	return msb, ratio
}

// adjustRatio adjusts the given ratio based on the MSB found.
//
// This adjustment ensures that the ratio falls within the specific range.
func adjustRatio(ratio, msb *u256.Uint) *u256.Uint {
	if msb.Gte(u256.NewUint(128)) {
		return new(u256.Uint).Rsh(ratio, uint(msb.Uint64()-127))
	}

	return new(u256.Uint).Lsh(ratio, uint(127-msb.Uint64()))
}

// calculateLog2 calculates the binary logarith, of the adjusted ratio using a fixed-point arithmetic.
//
// This function iteratively squares the ratio and adjusts the result to compute the log base 2, which will determine the tick value.
func calculateLog2(msb, ratio *u256.Uint) *i256.Int {
	_msb := i256.FromUint256(msb)
	_128 := i256.NewInt(128)

	log_2 := i256.Zero().Sub(_msb, _128)
	log_2 = log_2.Lsh(log_2, 64)

	for i := 63; i >= 51; i-- {
		ratio = new(u256.Uint).Mul(ratio, ratio)
		ratio = ratio.Rsh(ratio, 127)

		f := i256.FromUint256(new(u256.Uint).Rsh(ratio, 128))

		// log_2 = log_2 | (f << i)
		log_2 = i256.Zero().Or(log_2, i256.Zero().Lsh(f, uint(i)))

		// ratio = ratio >> uint64(f)
		ratio = ratio.Rsh(ratio, uint(f.Uint64()))
	}

	// handle the remaining bits
	{
		// ratio = ratio * ratio >> 127
		ratio = new(u256.Uint).Mul(ratio, ratio)
		ratio = new(u256.Uint).Rsh(ratio, 127)

		f := i256.FromUint256(new(u256.Uint).Rsh(ratio, 128))

		log_2 = i256.Zero().Or(log_2, i256.Zero().Lsh(f, 50))
	}

	return log_2
}

// getTickValue determines the tick value corresponding to a given sqrtPriveX96.
//
// It calculates the upper and lower bounds for each tick, and selects the appropriate tock value
// based on the given sqrtPriceX96.
func getTickValue(log2 *i256.Int, sqrtPriceX96 *u256.Uint) int32 {
	// ref: https://github.com/Uniswap/v3-core/issues/500
	// 2^64 / log2 (1.0001) = 255738958999603826347141
	log_sqrt10001 := i256.Zero().Mul(log2, i256.MustFromDecimal("255738958999603826347141"))

	// ref: https://ethereum.stackexchange.com/questions/113844/how-does-uniswap-v3s-logarithm-library-tickmath-sol-work/113912#113912
	// 0.010000497 x 2^128 = 3402992956809132418596140100660247210
	tickLow256 := i256.Zero().Sub(log_sqrt10001, i256.MustFromDecimal("3402992956809132418596140100660247210"))
	tickLow256 = tickLow256.Rsh(tickLow256, 128)
	tickLow := int32(tickLow256.Int64())

	// ref: https://ethereum.stackexchange.com/questions/113844/how-does-uniswap-v3s-logarithm-library-tickmath-sol-work/113912#113912
	// 0.856 x 2^128 = 291339464771989622907027621153398088495
	tickHi256 := i256.Zero().Add(log_sqrt10001, i256.MustFromDecimal("291339464771989622907027621153398088495"))
	tickHi256 = tickHi256.Rsh(tickHi256, 128)
	tickHi := int32(tickHi256.Int64())

	var tick int32
	if tickLow == tickHi {
		tick = tickLow
	} else if TickMathGetSqrtRatioAtTick(tickHi).Lte(sqrtPriceX96) {
		tick = tickHi
	} else {
		tick = tickLow
	}

	return tick
}

func gt(x, y *u256.Uint) *u256.Uint {
	if x.Gt(y) {
		return u256.One()
	}

	return u256.Zero()
}

func abs(x int32) int32 {
	if x < 0 {
		return -x
	}

	return x
}

package pool

import (
	u256 "gno.land/p/gnoswap/uint256"
)

type bitShift struct {
	bitPattern *u256.Uint
	shift      uint
}

func BitMathMostSignificantBit(x *u256.Uint) uint8 {
	shifts := []bitShift{
		{u256.MustFromDecimal(Q128), 128}, // 2^128
		{u256.MustFromDecimal(Q64), 64},   // 2^64
		{u256.NewUint(0x100000000), 32},
		{u256.NewUint(0x10000), 16},
		{u256.NewUint(0x100), 8},
		{u256.NewUint(0x10), 4},
		{u256.NewUint(0x4), 2},
		{u256.NewUint(0x2), 1},
	}

	r := uint8(0)
	for _, s := range shifts {
		if x.Gte(s.bitPattern) {
			x = new(u256.Uint).Rsh(x, s.shift)
			r += uint8(s.shift)
		}
	}

	return r
}

func BitMathLeastSignificantBit(x *u256.Uint) uint8 {
	shifts := []bitShift{
		{u256.MustFromDecimal(MAX_UINT128), 128},
		{u256.MustFromDecimal(MAX_UINT64), 64},
		{u256.MustFromDecimal(MAX_UINT32), 32},
		{u256.MustFromDecimal(MAX_UINT16), 16},
		{u256.MustFromDecimal(MAX_UINT8), 8},
		{u256.NewUint(0xf), 4},
		{u256.NewUint(0x3), 2},
		{u256.NewUint(0x1), 1},
	}

	r := uint8(255)
	for _, s := range shifts {
		if new(u256.Uint).And(x, s.bitPattern).Gt(u256.Zero()) {
			r -= uint8(s.shift)
		} else {
			x = new(u256.Uint).Rsh(x, s.shift)
		}
	}

	return r
}

package pool

import (
	i256 "gno.land/p/gnoswap/int256"
	u256 "gno.land/p/gnoswap/uint256"

	"gno.land/r/gnoswap/consts"
)

func sqrtPriceMathGetNextSqrtPriceFromAmount0RoundingUp(
	sqrtPX96 *u256.Uint, // uint160
	liquidity *u256.Uint, // uint128
	amount *u256.Uint, // uint256
	add bool,
) *u256.Uint { // uint160
	if amount.IsZero() {
		return sqrtPX96
	}

	numerator1 := new(u256.Uint).Lsh(liquidity, 96)
	product := new(u256.Uint).Mul(amount, sqrtPX96)

	if add {
		if new(u256.Uint).Div(product, amount).Eq(sqrtPX96) {
			denominator := new(u256.Uint).Add(numerator1, product)

			if denominator.Gte(numerator1) {
				return u256.MulDivRoundingUp(numerator1, sqrtPX96, denominator)
			}
		}

		div := new(u256.Uint).Div(numerator1, sqrtPX96)
		add := new(u256.Uint).Add(div, amount)
		return u256.DivRoundingUp(numerator1, add)
	} else {
		cond1 := new(u256.Uint).Div(product, amount).Eq(sqrtPX96)
		cond2 := numerator1.Gt(product)

		if !(cond1 && cond2) {
			panic("pool_sqrt price math #1")
		}

		denominator := new(u256.Uint).Sub(numerator1, product)
		return u256.MulDivRoundingUp(numerator1, sqrtPX96, denominator)
	}
}

func sqrtPriceMathGetNextSqrtPriceFromAmount1RoundingDown(
	sqrtPX96 *u256.Uint, // uint160
	liquidity *u256.Uint, // uint1288
	amount *u256.Uint, // uint256
	add bool,
) *u256.Uint { // uint160
	quotient := u256.Zero()

	if add {
		if amount.Lte(u256.MustFromDecimal(MAX_UINT160)) {
			value1 := new(u256.Uint).Lsh(amount, 96)
			quotient = new(u256.Uint).Div(value1, liquidity)
		} else {
			quotient = u256.MulDiv(amount, u256.MustFromDecimal(consts.Q96), liquidity)
		}

		return new(u256.Uint).Add(sqrtPX96, quotient)
	} else {
		if amount.Lte(u256.MustFromDecimal(MAX_UINT160)) {
			value1 := new(u256.Uint).Lsh(amount, 96)
			quotient = u256.DivRoundingUp(value1, liquidity)
		} else {
			quotient = u256.MulDivRoundingUp(amount, u256.MustFromDecimal(consts.Q96), liquidity)
		}

		if !(sqrtPX96.Gt(quotient)) {
			panic("pool_sqrt price math #2")
		}

		return new(u256.Uint).Sub(sqrtPX96, quotient)
	}
}

func sqrtPriceMathGetNextSqrtPriceFromInput(
	sqrtPX96 *u256.Uint, // uint160
	liquidity *u256.Uint, // uint128
	amountIn *u256.Uint, // uint256
	zeroForOne bool, // bool
) *u256.Uint { // uint160
	if zeroForOne {
		return sqrtPriceMathGetNextSqrtPriceFromAmount0RoundingUp(sqrtPX96, liquidity, amountIn, true)
	}
	return sqrtPriceMathGetNextSqrtPriceFromAmount1RoundingDown(sqrtPX96, liquidity, amountIn, true)
}

func sqrtPriceMathGetNextSqrtPriceFromOutput(
	sqrtPX96 *u256.Uint, // uint160
	liquidity *u256.Uint, // uint128
	amountOut *u256.Uint, // uint256
	zeroForOne bool, // bool
) *u256.Uint { // uint160
	if zeroForOne {
		return sqrtPriceMathGetNextSqrtPriceFromAmount1RoundingDown(sqrtPX96, liquidity, amountOut, false)
	}

	return sqrtPriceMathGetNextSqrtPriceFromAmount0RoundingUp(sqrtPX96, liquidity, amountOut, false)
}

func sqrtPriceMathGetAmount0DeltaHelper(
	sqrtRatioAX96 *u256.Uint, // uint160
	sqrtRatioBX96 *u256.Uint, // uint160
	liquidity *u256.Uint, // uint160
	roundUp bool,
) *u256.Uint { // uint256
	if sqrtRatioAX96.Gt(sqrtRatioBX96) {
		sqrtRatioAX96, sqrtRatioBX96 = sqrtRatioBX96, sqrtRatioAX96
	}

	numerator1 := new(u256.Uint).Lsh(liquidity, 96)
	numerator2 := new(u256.Uint).Sub(sqrtRatioBX96, sqrtRatioAX96)

	if !(sqrtRatioAX96.Gt(u256.Zero())) {
		panic("pool_sqrt price math #3")
	}

	if roundUp {
		value1 := u256.MulDivRoundingUp(numerator1, numerator2, sqrtRatioBX96)
		return u256.DivRoundingUp(value1, sqrtRatioAX96)
	} else {
		value1 := u256.MulDiv(numerator1, numerator2, sqrtRatioBX96)
		return new(u256.Uint).Div(value1, sqrtRatioAX96)
	}
}

func sqrtPriceMathGetAmount1DeltaHelper(
	sqrtRatioAX96 *u256.Uint, // uint160
	sqrtRatioBX96 *u256.Uint, // uint160
	liquidity *u256.Uint, // uint160
	roundUp bool,
) *u256.Uint { // uint256
	if sqrtRatioAX96.Gt(sqrtRatioBX96) {
		sqrtRatioAX96, sqrtRatioBX96 = sqrtRatioBX96, sqrtRatioAX96
	}

	if roundUp {
		diff := new(u256.Uint).Sub(sqrtRatioBX96, sqrtRatioAX96)
		return u256.MulDivRoundingUp(liquidity, diff, u256.MustFromDecimal(consts.Q96))
	} else {
		diff := new(u256.Uint).Sub(sqrtRatioBX96, sqrtRatioAX96)
		return u256.MulDiv(liquidity, diff, u256.MustFromDecimal(consts.Q96))
	}
}

func SqrtPriceMathGetAmount0DeltaStr(
	sqrtRatioAX96 *u256.Uint, // uint160
	sqrtRatioBX96 *u256.Uint, // uint160
	liquidity *i256.Int, // int128
) string { // int256
	if liquidity.IsNeg() {
		u := sqrtPriceMathGetAmount0DeltaHelper(sqrtRatioAX96, sqrtRatioBX96, liquidity.Abs(), false)
		i := i256.FromUint256(u)
		return i256.Zero().Neg(i).ToString()
	}

	u := sqrtPriceMathGetAmount0DeltaHelper(sqrtRatioAX96, sqrtRatioBX96, liquidity.Abs(), true)
	return i256.FromUint256(u).ToString()
}

func SqrtPriceMathGetAmount1DeltaStr(
	sqrtRatioAX96 *u256.Uint, // uint160
	sqrtRatioBX96 *u256.Uint, // uint160
	liquidity *i256.Int, // int128
) string { // int256
	if liquidity.IsNeg() {
		u := sqrtPriceMathGetAmount1DeltaHelper(sqrtRatioAX96, sqrtRatioBX96, liquidity.Abs(), false)
		i := i256.FromUint256(u)
		return i256.Zero().Neg(i).ToString()
	}

	u := sqrtPriceMathGetAmount1DeltaHelper(sqrtRatioAX96, sqrtRatioBX96, liquidity.Abs(), true)
	return i256.FromUint256(u).ToString()
}

package pool

import (
	i256 "gno.land/p/gnoswap/int256"
	u256 "gno.land/p/gnoswap/uint256"
)

func SwapMathComputeSwapStepStr(
	sqrtRatioCurrentX96 *u256.Uint, // uint160
	sqrtRatioTargetX96 *u256.Uint, // uint160
	liquidity *u256.Uint, // uint128
	amountRemaining *i256.Int, // int256
	feePips uint64,
) (string, string, string, string) { // (sqrtRatioNextX96, amountIn, amountOut, feeAmount *u256.Uint)
	isToken1Expensive := sqrtRatioCurrentX96.Gte(sqrtRatioTargetX96)

	// POSTIVIE == EXACT_IN => Estimated AmountOut
	// NEGATIVE == EXACT_OUT => Estimated AmountIn
	exactIn := !(amountRemaining.IsNeg()) // amountRemaining >= 0

	sqrtRatioNextX96 := u256.Zero()
	amountIn := u256.Zero()
	amountOut := u256.Zero()
	feeAmount := u256.Zero()

	if exactIn {
		amountRemainingLessFee := u256.MulDiv(amountRemaining.Abs(), u256.NewUint(1000000-feePips), u256.NewUint(1000000))

		if isToken1Expensive {
			amountIn = sqrtPriceMathGetAmount0DeltaHelper(sqrtRatioTargetX96, sqrtRatioCurrentX96, liquidity, true)
		} else {
			amountIn = sqrtPriceMathGetAmount1DeltaHelper(sqrtRatioCurrentX96, sqrtRatioTargetX96, liquidity, true)
		}

		if amountRemainingLessFee.Gte(amountIn) {
			sqrtRatioNextX96 = sqrtRatioTargetX96
		} else {
			sqrtRatioNextX96 = sqrtPriceMathGetNextSqrtPriceFromInput(
				sqrtRatioCurrentX96,
				liquidity,
				amountRemainingLessFee,
				isToken1Expensive,
			)
		}

	} else {
		if isToken1Expensive {
			amountOut = sqrtPriceMathGetAmount1DeltaHelper(sqrtRatioTargetX96, sqrtRatioCurrentX96, liquidity, false)
		} else {
			amountOut = sqrtPriceMathGetAmount0DeltaHelper(sqrtRatioCurrentX96, sqrtRatioTargetX96, liquidity, false)
		}

		if amountRemaining.Abs().Gte(amountOut) {
			sqrtRatioNextX96 = sqrtRatioTargetX96
		} else {
			sqrtRatioNextX96 = sqrtPriceMathGetNextSqrtPriceFromOutput(
				sqrtRatioCurrentX96,
				liquidity,
				amountRemaining.Abs(),
				isToken1Expensive,
			)
		}
	}

	max := sqrtRatioTargetX96.Eq(sqrtRatioNextX96)

	if isToken1Expensive {
		if max && exactIn {
			amountIn = amountIn
		} else {
			amountIn = sqrtPriceMathGetAmount0DeltaHelper(sqrtRatioNextX96, sqrtRatioCurrentX96, liquidity, true)
		}

		if max && !exactIn {
			amountOut = amountOut
		} else {
			amountOut = sqrtPriceMathGetAmount1DeltaHelper(sqrtRatioNextX96, sqrtRatioCurrentX96, liquidity, false)
		}
	} else {
		if max && exactIn {
			amountIn = amountIn
		} else {
			amountIn = sqrtPriceMathGetAmount1DeltaHelper(sqrtRatioCurrentX96, sqrtRatioNextX96, liquidity, true)
		}

		if max && !exactIn {
			amountOut = amountOut
		} else {
			amountOut = sqrtPriceMathGetAmount0DeltaHelper(sqrtRatioCurrentX96, sqrtRatioNextX96, liquidity, false)
		}
	}

	if !exactIn && amountOut.Gt(amountRemaining.Abs()) {
		amountOut = amountRemaining.Abs()
	}

	if exactIn && !(sqrtRatioNextX96.Eq(sqrtRatioTargetX96)) {
		feeAmount = new(u256.Uint).Sub(amountRemaining.Abs(), amountIn)
	} else {
		feeAmount = u256.MulDivRoundingUp(amountIn, u256.NewUint(feePips), new(u256.Uint).Sub(u256.NewUint(1000000), u256.NewUint(feePips)))
	}

	return sqrtRatioNextX96.ToString(), amountIn.ToString(), amountOut.ToString(), feeAmount.ToString()
}

package gns

import (
	"std"
	"strings"
	"time"

	"gno.land/p/demo/grc/grc20"
	"gno.land/p/demo/ownable"
	"gno.land/p/demo/ufmt"
	pusers "gno.land/p/demo/users"

	"gno.land/r/demo/users"

	"gno.land/r/gnoswap/consts"
)

const MAXIMUM_SUPPLY = uint64(1_000_000_000_000_000) // 1B

var (
	banker *grc20.Banker
	admin  *ownable.Ownable
	token  grc20.Token
)

var (
	deployedHeight   int64
	deployedAt       time.Time
	lastMintedHeight int64
	amountToEmission uint64
)

func init() {
	admin = ownable.NewWithAddress("g1lmvrrrr4er2us84h2732sru76c9zl2nvknha8c") // GNOSWAP_ADMIN
	banker = grc20.NewBanker("Gnoswap", "GNS", 6)

	banker.Mint(admin.Owner(), 100_000_000_000_000) // 100_000_000 GNS  0.1B
	token = banker.Token()

	amountToEmission = MAXIMUM_SUPPLY - uint64(100_000_000_000_000)

	height := std.GetHeight()
	deployedHeight = height
	deployedAt = time.Now()
	lastMintedHeight = 0
}

func TotalSupply() uint64 { return token.TotalSupply() }

func BalanceOf(owner pusers.AddressOrName) uint64 {
	ownerAddr := users.Resolve(owner)
	return token.BalanceOf(ownerAddr)
}

func Allowance(owner, spender pusers.AddressOrName) uint64 {
	ownerAddr := users.Resolve(owner)
	spenderAddr := users.Resolve(spender)
	return token.Allowance(ownerAddr, spenderAddr)
}

func Transfer(to pusers.AddressOrName, amount uint64) {
	toAddr := users.Resolve(to)
	checkErr(token.Transfer(toAddr, amount))
}

func Approve(spender pusers.AddressOrName, amount uint64) {
	spenderAddr := users.Resolve(spender)
	checkErr(token.Approve(spenderAddr, amount))
}

func TransferFrom(from, to pusers.AddressOrName, amount uint64) {
	fromAddr := users.Resolve(from)
	toAddr := users.Resolve(to)
	checkErr(token.TransferFrom(fromAddr, toAddr, amount))
}

func Render(path string) string {
	parts := strings.Split(path, "/")
	c := len(parts)

	switch {
	case path == "":
		return banker.RenderHome()
	case c == 2 && parts[0] == "balance":
		owner := pusers.AddressOrName(parts[1])
		ownerAddr := users.Resolve(owner)
		balance := banker.BalanceOf(ownerAddr)
		return ufmt.Sprintf("%d\n", balance)
	default:
		return "404\n"
	}
}

// Mint
func Mint(address pusers.AddressOrName) {
	// only emission contract can call Mint
	caller := std.PrevRealm().Addr()
	if caller != consts.EMISSION_ADDR {
		panic("only emission contract can call Mint")
	}

	// calculate gns emission amount for every block, and send by single call
	// for this case, we assume that inside of block range gnoswap state hasn't changed.
	nowHeight := std.GetHeight()
	amountToMint := uint64(0)
	for i := lastMintedHeight + 1; i <= nowHeight; i++ {
		amount := getAmountByHeight(i)
		amountToMint += amount
	}
	err := banker.Mint(users.Resolve(address), amountToMint)
	if err != nil {
		panic(err.Error())
	}
	lastMintedHeight = nowHeight
}

func GetLastMintedHeight() int64 {
	return lastMintedHeight
}

func checkErr(err error) {
	if err != nil {
		panic(err.Error())
	}
}

package gns

import (
	"std"

	"gno.land/r/gnoswap/consts"

	"gno.land/p/demo/ufmt"
)

/*
	NOTE: assume block will be created every 2 second
	2 second = 1 block
	1 minute = 30 block
	1 hour = 1800 block
	1 day = 43200 block
	(365 days) 1 year = 15768000 block

	2 year = +31536000 block
	4 year = +63072000 block
	6 year = +94608000 block
	8 year = +126144000 block
	12 year = +189216000 block
*/

type halvingTierAmountType map[int64]int64     // map[tier]mintAmount
var halvingTierAmount = halvingTierAmountType{ // u amount per year
	1:  18_750_000_000_000 * 12, // 225_000_000_000_000
	2:  18_750_000_000_000 * 12, // 225_000_000_000_000
	3:  9_375_000_000_000 * 12,  // 112_500_000_000_000
	4:  9_375_000_000_000 * 12,  // 112_500_000_000_000
	5:  4_687_500_000_000 * 12,  // 56_250_000_000_000
	6:  4_687_500_000_000 * 12,  // 56_250_000_000_000
	7:  2_343_750_000_000 * 12,  // 28_125_000_000_000
	8:  2_343_750_000_000 * 12,  // 28_125_000_000_000
	9:  1_171_875_000_000 * 12,  // 14_062_500_000_000
	10: 1_171_875_000_000 * 12,  // 14_062_500_000_000
	11: 1_171_875_000_000 * 12,  // 14_062_500_000_000
	12: 1_171_875_000_000 * 12,  // 14_062_500_000_000
}

type halvingTierBlockType map[int64]int64 // map[block[tier]mintAmount
var halvingTierBlock = make(halvingTierBlockType, 5)

func init() {
	// init 12 years halving tier block
	height := std.GetHeight()
	for i := int64(1); i < 13; i++ {
		halvingTierBlock[i] = height + 15768000*i
	}
}

func SetHalvingTierBlock(tier int64, block int64) {
	// admin or governance only
	caller := std.PrevRealm().Addr()

	if caller != consts.GNOSWAP_ADMIN && caller != consts.GOV_ADDR {
		panic(
			ufmt.Sprintf(
				"only admin(%s) or governance(%s) can set halving tier block, called from %s",
				consts.GNOSWAP_ADMIN,
				consts.GOV_ADDR,
				caller,
			),
		)
	}

	// cannot set non exist tier
	if tier < 1 || tier > 12 {
		panic(ufmt.Sprintf("invalid tier %d", tier))
	}

	// cannot set block less than current block
	if block < std.GetHeight() {
		panic(ufmt.Sprintf("cannot set block less than current block %d", std.GetHeight()))
	}

	halvingTierBlock[tier] = block
}

func getAmountByHeight(height int64) uint64 {
	halvingTier := getHalvingTierByBlock(height)
	halvingAmountYear := halvingTierAmount[halvingTier] // amount per year
	halvingAmountDay := halvingAmountYear / 365
	halvingAmountBlock := halvingAmountDay / 43200 // 1 day = 43200 block

	return uint64(halvingAmountBlock)
}

func getHalvingTierByBlock(height int64) int64 {
	// determine which halving tier block is in
	for tier, block := range halvingTierBlock {
		if height <= block {
			return tier
		}
	}

	return 0
}

