import (
	"encoding/base64"
	"std"

	"gno.land/p/demo/ufmt"
	"gno.land/r/gnoswap/consts"

	i256 "gno.land/p/gnoswap/int256"
	u256 "gno.land/p/gnoswap/uint256"
)

func positionGetKey(
	owner std.Address,
	tickLower int32,
	tickUpper int32,
) string {
	positionKey := ufmt.Sprintf("%s__%d__%d", owner.String(), tickLower, tickUpper)

	encoded := base64.StdEncoding.EncodeToString([]byte(positionKey))
	return encoded
}

func (pool *Pool) positionUpdateWithKey(
	positionKey string,
	liquidityDelta *i256.Int, // int128
	feeGrowthInside0X128 *u256.Uint, // uint256
	feeGrowthInside1X128 *u256.Uint, // uint256
) PositionInfo {
	// PARAM INIT
	liquidityDelta = liquidityDelta.NilToZero()
	feeGrowthInside0X128 = feeGrowthInside0X128.NilToZero()
	feeGrowthInside1X128 = feeGrowthInside1X128.NilToZero()

	positionToUpdate := pool.positions[positionKey]
	positionAfterUpdate := positionUpdate(positionToUpdate, liquidityDelta, feeGrowthInside0X128, feeGrowthInside1X128)
	pool.positions[positionKey] = positionAfterUpdate

	return positionAfterUpdate
}

func positionUpdate(
	self PositionInfo,
	liquidityDelta *i256.Int, // int128
	feeGrowthInside0X128 *u256.Uint, // uint256
	feeGrowthInside1X128 *u256.Uint, // uint256
) PositionInfo {
	self.init()

	var liquidityNext *u256.Uint
	if liquidityDelta.IsZero() {
		liquidityNext = self.liquidity
	} else {
		liquidityNext = liquidityMathAddDelta(self.liquidity, liquidityDelta)
	}

	tokensOwed0 := u256.Zero()
	{
		diff := new(u256.Uint).Sub(feeGrowthInside0X128, self.feeGrowthInside0LastX128)
		tokensOwed0 = u256.MulDiv(diff, self.liquidity, u256.MustFromDecimal(consts.Q128))
	}

	tokensOwed1 := u256.Zero()
	{
		diff := new(u256.Uint).Sub(feeGrowthInside1X128, self.feeGrowthInside1LastX128)
		tokensOwed1 = u256.MulDiv(diff, self.liquidity, u256.MustFromDecimal(consts.Q128))
	}

	if !(liquidityDelta.IsZero()) {
		self.liquidity = liquidityNext
	}

	self.feeGrowthInside0LastX128 = feeGrowthInside0X128
	self.feeGrowthInside1LastX128 = feeGrowthInside1X128
	if tokensOwed0.Gt(u256.Zero()) || tokensOwed1.Gt(u256.Zero()) {
		self.tokensOwed0 = self.tokensOwed0.Add(self.tokensOwed0, tokensOwed0)
		self.tokensOwed1 = self.tokensOwed1.Add(self.tokensOwed1, tokensOwed1)
	}

	return self
}

package pool

import (
	"gno.land/r/gnoswap/common"

	i256 "gno.land/p/gnoswap/int256"

	plp "gno.land/p/gnoswap/pool" // pool package
)

func (pool *Pool) modifyPosition(params ModifyPositionParams) (PositionInfo, *i256.Int, *i256.Int) {
	position := pool.updatePosition(
		params.owner,
		params.tickLower,
		params.tickUpper,
		params.liquidityDelta,
		pool.slot0.tick,
	)

	var amount0, amount1 *i256.Int

	if !(params.liquidityDelta.IsZero()) {
		if pool.slot0.tick < params.tickLower {
			_amount0Str := plp.SqrtPriceMathGetAmount0DeltaStr(
				common.TickMathGetSqrtRatioAtTick(params.tickLower),
				common.TickMathGetSqrtRatioAtTick(params.tickUpper),
				params.liquidityDelta,
			)
			amount0 = i256.MustFromDecimal(_amount0Str)
		} else if pool.slot0.tick < params.tickUpper {
			liquidityBefore := pool.liquidity

			_amount0Str := plp.SqrtPriceMathGetAmount0DeltaStr(
				pool.slot0.sqrtPriceX96,
				common.TickMathGetSqrtRatioAtTick(params.tickUpper),
				params.liquidityDelta,
			)
			amount0 = i256.MustFromDecimal(_amount0Str)

			_amount1Str := plp.SqrtPriceMathGetAmount1DeltaStr(
				common.TickMathGetSqrtRatioAtTick(params.tickLower),
				pool.slot0.sqrtPriceX96,
				params.liquidityDelta,
			)
			amount1 = i256.MustFromDecimal(_amount1Str)

			pool.liquidity = liquidityMathAddDelta(liquidityBefore, params.liquidityDelta)

		} else {
			_amount1Str := plp.SqrtPriceMathGetAmount1DeltaStr(
				common.TickMathGetSqrtRatioAtTick(params.tickLower),
				common.TickMathGetSqrtRatioAtTick(params.tickUpper),
				params.liquidityDelta,
			)
			amount1 = i256.MustFromDecimal(_amount1Str)
		}
	}

	// if position is out of range, one of amount0 or amount1 can be nil
	// > handle as 0
	amount0 = amount0.NilToZero()
	amount1 = amount1.NilToZero()

	return position, amount0, amount1
}

package pool

import (
	"std"

	i256 "gno.land/p/gnoswap/int256"
	u256 "gno.land/p/gnoswap/uint256"
)

func (pool *Pool) updatePosition(
	owner std.Address,
	tickLower int32,
	tickUpper int32,
	liquidityDelta *i256.Int, // int128
	tick int32,
) PositionInfo {
	_feeGrowthGlobal0X128 := u256.MustFromDecimal(pool.feeGrowthGlobal0X128.ToString())
	_feeGrowthGlobal1X128 := u256.MustFromDecimal(pool.feeGrowthGlobal1X128.ToString())

	var flippedLower, flippedUpper bool
	if !(liquidityDelta.IsZero()) {
		flippedLower = pool.tickUpdate(
			tickLower,
			tick,
			liquidityDelta,
			_feeGrowthGlobal0X128,
			_feeGrowthGlobal1X128,
			false,
			pool.maxLiquidityPerTick,
		)

		flippedUpper = pool.tickUpdate(
			tickUpper,
			tick,
			liquidityDelta,
			_feeGrowthGlobal0X128,
			_feeGrowthGlobal1X128,
			true,
			pool.maxLiquidityPerTick,
		)

		if flippedLower {
			pool.tickBitmapFlipTick(tickLower, pool.tickSpacing)
		}

		if flippedUpper {
			pool.tickBitmapFlipTick(tickUpper, pool.tickSpacing)
		}
	}

	feeGrowthInside0X128, feeGrowthInside1X128 := pool.tickGetFeeGrowthInside(
		tickLower,
		tickUpper,
		tick,
		_feeGrowthGlobal0X128,
		_feeGrowthGlobal1X128,
	)

	positionKey := positionGetKey(owner, tickLower, tickUpper)

	position := pool.positionUpdateWithKey(
		positionKey,
		liquidityDelta,
		u256.MustFromDecimal(feeGrowthInside0X128.ToString()),
		u256.MustFromDecimal(feeGrowthInside1X128.ToString()),
	)

	if liquidityDelta.IsNeg() {
		if flippedLower {
			delete(pool.ticks, tickLower)
		}

		if flippedUpper {
			delete(pool.ticks, tickUpper)
		}
	}

	return position
}

