}

const (
	JSONTokenKindUnknown JSONTokenKind = iota
	JSONTokenKindString
	JSONTokenKindNumber
	JSONTokenKindTrue
	JSONTokenKindFalse
	JSONTokenKindSpaces
	JSONTokenKindComma
	JSONTokenKindColon
	JSONTokenKindOpenArray
	JSONTokenKindCloseArray
	JSONTokenKindOpenObject
	JSONTokenKindCloseObject
	JSONTokenKindNull
)

func (k JSONTokenKind) String() string {
	switch k {
	case JSONTokenKindString:
		return "string"
	case JSONTokenKindNumber:
		return "number"
	case JSONTokenKindTrue:
		return "true"
	case JSONTokenKindFalse:
		return "false"
	case JSONTokenKindSpaces:
		return "spaces"
	case JSONTokenKindComma:
		return "comma"
	case JSONTokenKindColon:
		return "colon"
	case JSONTokenKindOpenArray:
		return "open-array"
	case JSONTokenKindCloseArray:
		return "close-array"
	case JSONTokenKindOpenObject:
		return "open-object"
	case JSONTokenKindCloseObject:
		return "close-object"
	case JSONTokenKindNull:
		return "null"
	default:
		return "unknown"
	}
}

type JSONToken struct {
	Kind JSONTokenKind
	Raw  string
}

package ujson

import (
	"unicode/utf8"

	"gno.land/p/teritori/utf16"
)

const (
	ReplacementChar = '\uFFFD' // Represents invalid code points.
)

// Ported from https://cs.opensource.google/go/go/+/refs/tags/go1.20.6:src/encoding/json/encode.go
func FormatString(s string) string {
	const escapeHTML = true
	e := `"` // e.WriteByte('"')
	start := 0
	for i := 0; i < len(s); {
		if b := s[i]; b < utf8.RuneSelf {
			if htmlSafeSet[b] || (!escapeHTML && safeSet[b]) {
				i++
				continue
			}
			if start < i {
				e += s[start:i] // e.WriteString(s[start:i])
			}
			e += "\\" // e.WriteByte('\\')
			switch b {
			case '\\', '"':
				e += string(b) // e.WriteByte(b)
			case '\n':
				e += "n" // e.WriteByte('n')
			case '\r':
				e += "r" // e.WriteByte('r')
			case '\t':
				e += "t" // e.WriteByte('t')
			default:
				// This encodes bytes < 0x20 except for \t, \n and \r.
				// If escapeHTML is set, it also escapes <, >, and &
				// because they can lead to security holes when
				// user-controlled strings are rendered into JSON
				// and served to some browsers.
				e += `u00`              // e.WriteString(`u00`)
				e += string(hex[b>>4])  // e.WriteByte(hex[b>>4])
				e += string(hex[b&0xF]) // e.WriteByte(hex[b&0xF])
			}
			i++
			start = i
			continue
		}
		c, size := utf8.DecodeRuneInString(s[i:])
		if c == utf8.RuneError && size == 1 {
			if start < i {
				e += s[start:i] // e.WriteString(s[start:i])
			}
			e += `\ufffd` // e.WriteString(`\ufffd`)
			i += size
			start = i
			continue
		}
		// U+2028 is LINE SEPARATOR.
		// U+2029 is PARAGRAPH SEPARATOR.
		// They are both technically valid characters in JSON strings,
		// but don't work in JSONP, which has to be evaluated as JavaScript,
		// and can lead to security holes there. It is valid JSON to
		// escape them, so we do so unconditionally.
		// See http://timelessrepo.com/json-isnt-a-javascript-subset for discussion.
		if c == '\u2028' || c == '\u2029' {
			if start < i {
				e += s[start:i] // e.WriteString(s[start:i])
			}
			e += `\u202`            // e.WriteString(`\u202`)
			e += string(hex[c&0xF]) // e.WriteByte(hex[c&0xF])
			i += size
			start = i
			continue
		}
		i += size
	}
	if start < len(s) {
		e += s[start:] // e.WriteString(s[start:])
	}
	e += `"` // e.WriteByte('"')
	return e
}

// Ported from https://cs.opensource.google/go/go/+/refs/tags/go1.20.6:src/encoding/json/decode.go
// unquote converts a quoted JSON string literal s into an actual string t.
// The rules are different than for Go, so cannot use strconv.Unquote.
func ParseString(s string) string {
	o, ok := unquoteBytes([]byte(s))
	if !ok {
		panic("invalid string")
	}
	return string(o)
}

func unquoteBytes(s []byte) (t []byte, ok bool) {
	if len(s) < 2 || s[0] != '"' || s[len(s)-1] != '"' {
		return
	}
	s = s[1 : len(s)-1]

	// Check for unusual characters. If there are none,
	// then no unquoting is needed, so return a slice of the
	// original bytes.
	r := 0
	for r < len(s) {
		c := s[r]
		if c == '\\' || c == '"' || c < ' ' {
			break
		}
		if c < utf8.RuneSelf {
			r++
			continue
		}
		rr, size := utf8.DecodeRune(s[r:])
		if rr == utf8.RuneError && size == 1 {
			break
		}
		r += size
	}
	if r == len(s) {
		return s, true
	}

	b := make([]byte, len(s)+2*utf8.UTFMax)
	w := copy(b, s[0:r])
	for r < len(s) {
		// Out of room? Can only happen if s is full of
		// malformed UTF-8 and we're replacing each
		// byte with RuneError.
		if w >= len(b)-2*utf8.UTFMax {
			nb := make([]byte, (len(b)+utf8.UTFMax)*2)
			copy(nb, b[0:w])
			b = nb
		}
		switch c := s[r]; {
		case c == '\\':
			r++
			if r >= len(s) {
				return
			}
			switch s[r] {
			default:
				return
			case '"', '\\', '/', '\'':
				b[w] = s[r]
				r++
				w++
			case 'b':
				b[w] = '\b'
				r++
				w++
			case 'f':
				b[w] = '\f'
				r++
				w++
			case 'n':
				b[w] = '\n'
				r++
				w++
			case 'r':
				b[w] = '\r'
				r++
				w++
			case 't':
				b[w] = '\t'
				r++
				w++
			case 'u':
				r--
				rr := getu4(s[r:])
				if rr < 0 {
					return
				}
				r += 6
				if utf16.IsSurrogate(rr) {
					rr1 := getu4(s[r:])
					if dec := utf16.DecodeRune(rr, rr1); dec != ReplacementChar {
						// A valid pair; consume.
						r += 6
						w += utf8.EncodeRune(b[w:], dec)
						break
					}
					// Invalid surrogate; fall back to replacement rune.
					rr = ReplacementChar
				}

			}

		// Quote, control characters are invalid.
		case c == '"', c < ' ':
			return

		// ASCII
		case c < utf8.RuneSelf:
			b[w] = c
			r++
			w++

		// Coerce to well-formed UTF-8.
		default:
			rr, size := utf8.DecodeRune(s[r:])
			r += size
			w += utf8.EncodeRune(b[w:], rr)
		}
	}
	return b[0:w], true
}

// getu4 decodes \uXXXX from the beginning of s, returning the hex value,
// or it returns -1.
func getu4(s []byte) rune {
	if len(s) < 6 || s[0] != '\\' || s[1] != 'u' {
		return -1
	}
	var r rune
	for _, c := range s[2:6] {
		switch {
		case '0' <= c && c <= '9':
			c = c - '0'
		case 'a' <= c && c <= 'f':
			c = c - 'a' + 10
		case 'A' <= c && c <= 'F':
			c = c - 'A' + 10
		default:
			return -1
		}
		r = r*16 + rune(c)
	}
	return r
}

package ujson

import (
	"strings"
	"testing"
	"time"

	"gno.land/p/demo/avl"
)

func TestAST(t *testing.T) {
	json := `{"a":[42, null, true, false, "hello\n\t\r"],"b":-3,"c":{"ia":{}, "ib":{     "foo"   :    "bar"}},"d":4,"e":5}`
	tokens := tokenize(json)
	expected := 44
	if len(tokens) != expected {
		t.Errorf("Expected %d tokens, got %d", expected, len(tokens))
	}
	remainingTokens, ast := parseAST(tokens)
	if len(remainingTokens) != 0 {
		t.Errorf("Expected 0 remaining tokens, got %d", len(remainingTokens))
	}
	if ast.Kind != JSONKindObject {
		t.Errorf("Expected root node to be an object, got %s", ast.Kind)
	}
	expectedTree := `{"a":[42,null,true,false,"hello\n\t\r"],"b":-3,"c":{"ia":{},"ib":{"foo":"bar"}},"d":4,"e":5}`
	if ast.String() != expectedTree {
		t.Errorf("Expected root node to be `%s`, got `%s`", expectedTree, ast.String())
	}
}

type TestType struct {
	A []string `json:"a"`
	B int      `json:"b"`
	C SubTestType
	D uint `json:"d"`
	E int  `json:"e"`
	F bool `json:"f"`
	// G       *EmptyType `json:"g"`
	AVLTree *avl.Tree `json:"avlTree"`
}

func (tt *TestType) FromJSON(ast *JSONASTNode) {
	ast.ParseObject([]*ParseKV{
		{Key: "a", ArrayParser: func(children []*JSONASTNode) {
			tt.A = make([]string, len(children))
			for i, child := range children {
				child.ParseAny(&tt.A[i])
			}
		}},
		{Key: "b", Value: &tt.B},
		{Key: "c", Value: &tt.C},
		{Key: "d", Value: &tt.D},
		{Key: "e", Value: &tt.E},
		{Key: "f", Value: &tt.F},
		// {Key: "g", Value: &tt.G},
		{Key: "avlTree", CustomParser: func(node *JSONASTNode) {
			if node.Kind != JSONKindObject {
				panic("Expected avlTree to be an object")
			}
			tt.AVLTree = avl.NewTree()
			for _, child := range node.ObjectChildren {
				var t string
				child.Value.ParseAny(&t)
				tt.AVLTree.Set(child.Key, t)
			}
		}},
	})
}

func (tt TestType) ToJSON() string {
	iSlice := make([]interface{}, len(tt.A))
	for i, v := range tt.A {
		iSlice[i] = v
	}
	return FormatObject([]FormatKV{
		{Key: "a", Value: FormatSlice(iSlice), Raw: true},
		{Key: "b", Value: tt.B},
		{Key: "c", Value: tt.C},
		{Key: "d", Value: tt.D},
		{Key: "e", Value: tt.E},
		{Key: "f", Value: tt.F},
		// {Key: "g", Value: tt.G},
		{Key: "avlTree", Value: tt.AVLTree},
	})
}

