package social_feeds

import (
	"strconv"
	"strings"
)

func renderFeed(parts []string) string {
	// /r/demo/social_feeds_v4:FEED_NAME
	name := parts[0]
	feedI, exists := gFeedsByName.Get(name)
	if !exists {
		return "feed does not exist: " + name
	}
	return feedI.(*Feed).Render()
}

func renderPost(parts []string) string {
	// /r/demo/boards:FEED_NAME/POST_ID
	name := parts[0]
	feedI, exists := gFeedsByName.Get(name)
	if !exists {
		return "feed does not exist: " + name
	}
	pid, err := strconv.Atoi(parts[1])
	if err != nil {
		return "invalid thread id: " + parts[1]
	}
	feed := feedI.(*Feed)
	post := feed.MustGetPost(PostID(pid))
	return post.Render()
}

func renderFeedsList() string {
	str := "There are " + intToString(gFeeds.Size()) + " available feeds:\n\n"
	gFeeds.Iterate("", "", func(key string, value interface{}) bool {
		feed := value.(*Feed)
		str += " * [" + feed.url + " (FeedID: " + feed.id.String() + ")](" + feed.url + ")\n"
		return false
	})
	return str
}

func Render(path string) string {
	if path == "" {
		return renderFeedsList()
	}

	parts := strings.Split(path, "/")

	if len(parts) == 1 {
		// /r/demo/social_feeds_v4:FEED_NAME
		return renderFeed(parts)
	} else if len(parts) == 2 {
		// /r/demo/social_feeds_v4:FEED_NAME/POST_ID
		return renderPost(parts)
	}

	return "Not found"
}

package simplefeedback

import (
    "gno.land/p/demo/ufmt"
    "std"
    "strings"
)

type Feedback struct {
    ID       int
    User     std.Address
    Content  string
    Response string
}

var feedbackList []Feedback
var nextID = 1

// SubmitFeedback allows users to submit feedback.
func SubmitFeedback(content string) string {
    user := std.GetOrigCaller()
    feedback := Feedback{ID: nextID, User: user, Content: content}
    feedbackList = append(feedbackList, feedback)
    nextID++
    return ufmt.Sprintf("Feedback submitted successfully with ID: %d", feedback.ID)
}

// RespondToFeedback allows any user to respond to a feedback.
func RespondToFeedback(feedbackID int, response string) string {
    for i, fb := range feedbackList {
        if fb.ID == feedbackID {
            feedbackList[i].Response = response
            return "Response added successfully"
        }
    }
    return "Feedback ID not found"
}

// Render displays all feedback and responses.
func Render(path string) string {
    var output strings.Builder
    output.WriteString("# Feedback Collection\n")
    for _, fb := range feedbackList {
        output.WriteString(ufmt.Sprintf("## Feedback ID: %d\n", fb.ID))
        output.WriteString(ufmt.Sprintf("- User: %s\n", fb.User))
        output.WriteString(ufmt.Sprintf("- Content: %s\n", fb.Content))
        if fb.Response != "" {
            output.WriteString(ufmt.Sprintf("- Response: %s\n", fb.Response))
        } else {
            output.WriteString("- Response: Pending\n")
        }
        output.WriteString("\n")
    }
    return output.String()
}
