)

func TickMathGetSqrtRatioAtTick(tick int32) *u256.Uint { // uint160 sqrtPriceX96
	absTick := abs(tick)
	ratio := u256.MustFromDecimal("340282366920938463463374607431768211456") // consts.Q128

	for mask, value := range tickRatioMap {
		if absTick&mask != 0 {
			// ratio = (ratio * value) >> 128
			ratio = ratio.Mul(ratio, value)
			ratio = ratio.Rsh(ratio, 128)
		}
	}

	if tick > 0 {
		_maxUint256 := u256.MustFromDecimal("115792089237316195423570985008687907853269984665640564039457584007913129639935") // consts.MAX_UINT256
		_tmp := new(u256.Uint).Div(_maxUint256, ratio)
		ratio = _tmp.Clone()
	}

	shifted := ratio.Rsh(ratio, 32).Clone()

	remainder := ratio.Mod(ratio, shift1By32Left)

	if new(u256.Uint).Add(shifted.Clone(), remainder.Clone()).IsZero() {
		return shifted
	}

	return new(u256.Uint).Add(shifted, u256.One())
}

func TickMathGetTickAtSqrtRatio(sqrtPriceX96 *u256.Uint) int32 {

	ratio := new(u256.Uint).Lsh(sqrtPriceX96, 32)

	msb, adjustedRatio := findMSB(ratio)
	adjustedRatio = adjustRatio(ratio, msb)

	log2 := calculateLog2(msb, adjustedRatio)
	tick := getTickValue(log2, sqrtPriceX96)

	return tick
}

// findMSB computes the MSB (most significant bit) of the given ratio.
func findMSB(ratio *u256.Uint) (*u256.Uint, *u256.Uint) {
	msb := u256.Zero()

	for i := 7; i >= 1; i-- {
		f := new(u256.Uint).Lsh(gt(ratio, binaryLogConsts[i]), uint(i))
		msb = new(u256.Uint).Or(msb, f)
		ratio = new(u256.Uint).Rsh(ratio, uint(f.Uint64()))
	}

	// handle the remaining bits
	{
		f := gt(ratio, u256.One()) // 0x1
		// msb = msb | f
		msb = new(u256.Uint).Or(msb, f)
	}

	return msb, ratio
}

// adjustRatio adjusts the given ratio based on the MSB found.
//
// This adjustment ensures that the ratio falls within the specific range.
func adjustRatio(ratio, msb *u256.Uint) *u256.Uint {
	if msb.Gte(u256.NewUint(128)) {
		return new(u256.Uint).Rsh(ratio, uint(msb.Uint64()-127))
	}

	return new(u256.Uint).Lsh(ratio, uint(127-msb.Uint64()))
}

// calculateLog2 calculates the binary logarith, of the adjusted ratio using a fixed-point arithmetic.
//
// This function iteratively squares the ratio and adjusts the result to compute the log base 2, which will determine the tick value.
func calculateLog2(msb, ratio *u256.Uint) *i256.Int {
	_msb := i256.FromUint256(msb)
	_128 := i256.NewInt(128)

	log_2 := i256.Zero().Sub(_msb, _128)
	log_2 = log_2.Lsh(log_2, 64)

	for i := 63; i >= 51; i-- {
		ratio = new(u256.Uint).Mul(ratio, ratio)
		ratio = ratio.Rsh(ratio, 127)

		f := i256.FromUint256(new(u256.Uint).Rsh(ratio, 128))

		// log_2 = log_2 | (f << i)
		log_2 = i256.Zero().Or(log_2, i256.Zero().Lsh(f, uint(i)))

		// ratio = ratio >> uint64(f)
		ratio = ratio.Rsh(ratio, uint(f.Uint64()))
	}

	// handle the remaining bits
	{
		// ratio = ratio * ratio >> 127
		ratio = new(u256.Uint).Mul(ratio, ratio)
		ratio = new(u256.Uint).Rsh(ratio, 127)

		f := i256.FromUint256(new(u256.Uint).Rsh(ratio, 128))

		log_2 = i256.Zero().Or(log_2, i256.Zero().Lsh(f, 50))
	}

	return log_2
}

// getTickValue determines the tick value corresponding to a given sqrtPriveX96.
//
// It calculates the upper and lower bounds for each tick, and selects the appropriate tock value
// based on the given sqrtPriceX96.
func getTickValue(log2 *i256.Int, sqrtPriceX96 *u256.Uint) int32 {
	// ref: https://github.com/Uniswap/v3-core/issues/500
	// 2^64 / log2 (1.0001) = 255738958999603826347141
	log_sqrt10001 := i256.Zero().Mul(log2, i256.MustFromDecimal("255738958999603826347141"))

	// ref: https://ethereum.stackexchange.com/questions/113844/how-does-uniswap-v3s-logarithm-library-tickmath-sol-work/113912#113912
	// 0.010000497 x 2^128 = 3402992956809132418596140100660247210
	tickLow256 := i256.Zero().Sub(log_sqrt10001, i256.MustFromDecimal("3402992956809132418596140100660247210"))
	tickLow256 = tickLow256.Rsh(tickLow256, 128)
	tickLow := int32(tickLow256.Int64())

	// ref: https://ethereum.stackexchange.com/questions/113844/how-does-uniswap-v3s-logarithm-library-tickmath-sol-work/113912#113912
	// 0.856 x 2^128 = 291339464771989622907027621153398088495
	tickHi256 := i256.Zero().Add(log_sqrt10001, i256.MustFromDecimal("291339464771989622907027621153398088495"))
	tickHi256 = tickHi256.Rsh(tickHi256, 128)
	tickHi := int32(tickHi256.Int64())

	var tick int32
	if tickLow == tickHi {
		tick = tickLow
	} else if TickMathGetSqrtRatioAtTick(tickHi).Lte(sqrtPriceX96) {
		tick = tickHi
	} else {
		tick = tickLow
	}

	return tick
}

func gt(x, y *u256.Uint) *u256.Uint {
	if x.Gt(y) {
		return u256.One()
	}

	return u256.Zero()
}

func abs(x int32) int32 {
	if x < 0 {
		return -x
	}

	return x
}

package gns

import (
	"std"
	"strings"

	"gno.land/p/demo/grc/grc20"
	"gno.land/p/demo/ownable"
	"gno.land/p/demo/ufmt"
	pusers "gno.land/p/demo/users"

	"gno.land/r/demo/users"

	"gno.land/r/gnoswap/v2/consts"
)

const MAXIMUM_SUPPLY = uint64(1_000_000_000_000_000) // 1B

var (
	banker *grc20.Banker
	admin  *ownable.Ownable
	token  grc20.Token
)

var (
	lastMintedHeight int64
	amountToEmission uint64
)

func init() {
	admin = ownable.NewWithAddress("g1lmvrrrr4er2us84h2732sru76c9zl2nvknha8c") // GNOSWAP_ADMIN
	banker = grc20.NewBanker("Gnoswap", "GNS", 6)

	banker.Mint(admin.Owner(), 100_000_000_000_000) // 100_000_000 GNS  0.1B
	token = banker.Token()

	amountToEmission = MAXIMUM_SUPPLY - uint64(100_000_000_000_000)

	lastMintedHeight = std.GetHeight()
}

func TotalSupply() uint64 { return token.TotalSupply() }

func TotalMinted() uint64 { return token.TotalSupply() - uint64(100_000_000_000_000) }

func BalanceOf(owner pusers.AddressOrName) uint64 {
	ownerAddr := users.Resolve(owner)
	return token.BalanceOf(ownerAddr)
}

func Allowance(owner, spender pusers.AddressOrName) uint64 {
	ownerAddr := users.Resolve(owner)
	spenderAddr := users.Resolve(spender)
	return token.Allowance(ownerAddr, spenderAddr)
}

func Transfer(to pusers.AddressOrName, amount uint64) {
	toAddr := users.Resolve(to)
	checkErr(token.Transfer(toAddr, amount))
}

func Approve(spender pusers.AddressOrName, amount uint64) {
	spenderAddr := users.Resolve(spender)
	checkErr(token.Approve(spenderAddr, amount))
}

func TransferFrom(from, to pusers.AddressOrName, amount uint64) {
	fromAddr := users.Resolve(from)
	toAddr := users.Resolve(to)
	checkErr(token.TransferFrom(fromAddr, toAddr, amount))
}

func Render(path string) string {
	parts := strings.Split(path, "/")
	c := len(parts)

	switch {
	case path == "":
		return banker.RenderHome()
	case c == 2 && parts[0] == "balance":
		owner := pusers.AddressOrName(parts[1])
		ownerAddr := users.Resolve(owner)
		balance := banker.BalanceOf(ownerAddr)
		return ufmt.Sprintf("%d\n", balance)
	default:
		return "404\n"
	}
}

// Mint
func Mint(address pusers.AddressOrName) {
	// only emission contract can call Mint
	caller := std.PrevRealm().Addr()
	if caller != consts.EMISSION_ADDR {
		panic("only emission contract can call Mint")
	}

	// if not yet initialized, mint 0 amount
	if initialized == false {
		println("GNS_not yet initialized")
		return
	}

	// calculate gns emission amount for every block, and send by single call
	// for this case, we assume that inside of block range gnoswap state hasn't changed.
	nowHeight := std.GetHeight()
	amountToMint := uint64(0)

	println("GNS_lastMintedHeight", lastMintedHeight)
	println("GNS_lastMintedHeight + 1", lastMintedHeight+1)
	println("GNS_nowHeight", nowHeight)
	println("GNS_now-lastMint", nowHeight-lastMintedHeight)

	if lastMintedHeight >= nowHeight {
		println("GNS_only one mint for each block")
		return
	}

	for i := lastMintedHeight + 1; i <= nowHeight; i++ {
		amount := GetAmountByHeight(i)
		amountToMint += amount

		year := GetHalvingYearByHeight(i)
		halvingYearMintAmount[year] += amount

		lastBlock := halvingYearBlock[year]
		if i == lastBlock {
			// halvingYearAmount[year] = amountToMint
			// halvingYearMintAmount[year] = amountToMint
			println(" > GNS_LAST_year:", year)
			println(" > GNS_LAST_processing height:", i)
			println(" > GNS_LAST_halvingYearAmount[1]:\t", halvingYearAmount[1])
			println(" > GNS_LAST_halvingYearMintAmount[1]:\t", halvingYearMintAmount[1])
			leftForThisYear := halvingYearAmount[year] - halvingYearMintAmount[year]
			println(" > GNS_LAST_leftForThisYear:", leftForThisYear)
			println()

			amountToMint += leftForThisYear
			halvingYearMintAmount[year] += leftForThisYear
		}
	}
	err := banker.Mint(users.Resolve(address), amountToMint)
	if err != nil {
		panic(err.Error())
	}

	lastMintedHeight = nowHeight
	println("GNS_lastMintedHeight:", lastMintedHeight)
	println("GNS_amountToMint:", amountToMint)
}

func checkErr(err error) {
	if err != nil {
		panic(err.Error())
	}
}

package gns

import (
	"std"
	"time"

	"gno.land/r/gnoswap/v2/consts"

	"gno.land/p/demo/ufmt"
)

// init 12 years halving tier block
/*
	NOTE: assume block will be created every 2 second by default
	2 second = 1 block
	1 minute = 30 block
	1 hour = 1800 block
	1 day = 43200 block
	(365 days) 1 year = 15768000 block

	2 year = +31536000 block
	4 year = +63072000 block
	6 year = +94608000 block
	8 year = +126144000 block
	12 year = +189216000 block
*/
const YEAR_BLOCK int64 = 15768000

const TIMESTAMP_365DAYS int64 = 31536000

var avgBlockTimeMs int64 = 2000 // 2000s = 2s for default

var (
	initialized    bool
	startHeight    int64
	startTimestamp int64
)

var halvingYearBlock = make(map[int64]int64)     // year => block
var halvingYearTimestamp = make(map[int64]int64) // year => timestamp

var halvingYearAmount = make(map[int64]uint64)     // year => mintAmount
var halvingYearMintAmount = make(map[int64]uint64) // year => (actual) mintAmount
var halvingYearAccuAmount = make(map[int64]uint64) // year => accuAmount
func init() {
	// yearly mint amount
	halvingYearAmount[1] = 18_750_000_000_000 * 12 // 225000000000000
	halvingYearAmount[2] = 18_750_000_000_000 * 12 // 225000000000000
	halvingYearAmount[3] = 9_375_000_000_000 * 12  // 112500000000000
	halvingYearAmount[4] = 9_375_000_000_000 * 12  // 112500000000000
	halvingYearAmount[5] = 4_687_500_000_000 * 12  // 56250000000000
	halvingYearAmount[6] = 4_687_500_000_000 * 12  // 56250000000000
	halvingYearAmount[7] = 2_343_750_000_000 * 12  // 28125000000000
	halvingYearAmount[8] = 2_343_750_000_000 * 12  // 28125000000000
	halvingYearAmount[9] = 1_171_875_000_000 * 12  // 14062500000000
	halvingYearAmount[10] = 1_171_875_000_000 * 12 // 14062500000000
	halvingYearAmount[11] = 1_171_875_000_000 * 12 // 14062500000000
	halvingYearAmount[12] = 1_171_875_000_000 * 12 // 14062500000000

	// yearly accumulated mint amount
	halvingYearAccuAmount[1] = halvingYearAmount[1]
	halvingYearAccuAmount[2] = halvingYearAccuAmount[1] + halvingYearAmount[2]
	halvingYearAccuAmount[3] = halvingYearAccuAmount[2] + halvingYearAmount[3]
	halvingYearAccuAmount[4] = halvingYearAccuAmount[3] + halvingYearAmount[4]
	halvingYearAccuAmount[5] = halvingYearAccuAmount[4] + halvingYearAmount[5]
	halvingYearAccuAmount[6] = halvingYearAccuAmount[5] + halvingYearAmount[6]
	halvingYearAccuAmount[7] = halvingYearAccuAmount[6] + halvingYearAmount[7]
	halvingYearAccuAmount[8] = halvingYearAccuAmount[7] + halvingYearAmount[8]
	halvingYearAccuAmount[9] = halvingYearAccuAmount[8] + halvingYearAmount[9]
	halvingYearAccuAmount[10] = halvingYearAccuAmount[9] + halvingYearAmount[10]
	halvingYearAccuAmount[11] = halvingYearAccuAmount[10] + halvingYearAmount[11]
	halvingYearAccuAmount[12] = halvingYearAccuAmount[11] + halvingYearAmount[12]
}

var amountPerBlockPerHalvingYear = make(map[int64]uint64) // year => reward per block

func init() {
	// caller := std.PrevRealm().Addr()
	// if caller != consts.STAKER_ADDR {
	// 	panic(
	// 		ufmt.Sprintf(
	// 			"only staker(%s) can init start height, start timestamp, called from %s",
	// 			consts.STAKER_ADDR,
	// 			caller,
	// 		),
	// 	)
	// }

	height := std.GetHeight()

	startHeight = height
	startTimestamp = time.Now().Unix()

	initialized = true

	for i := int64(1); i < 13; i++ {
		halvingYearBlock[i] = height + YEAR_BLOCK*i
		halvingYearTimestamp[i] = startTimestamp + (TIMESTAMP_365DAYS * i)

		amountPerYear := halvingYearAmount[i]  // amount per year
		amountPerDay := amountPerYear / 365    // amount per day
		amountPerBlock := amountPerDay / 43200 // amount per block ( 1 day = 43200 block (2s/block) )

		amountPerBlockPerHalvingYear[i] = uint64(amountPerBlock)
	}
}

func SetAvgBlockTimeInMs(ms int64) {
	// admin or governance or staker only
	caller := std.PrevRealm().Addr()

	if caller != consts.GNOSWAP_ADMIN && caller != consts.GOV_ADDR && caller != consts.STAKER_ADDR {
		panic(
			ufmt.Sprintf(
				"only admin(%s) or governance(%s) or staker(%s) can set avg block time, called from %s",
				consts.GNOSWAP_ADMIN,
				consts.GOV_ADDR,
				consts.STAKER_ADDR,
				caller,
			),
		)
	}

	avgBlockTimeMs = ms

	// which year current time is in
	now := time.Now().Unix()
	println("GNS_HALVING__now_time:", now)
	println("GNS_HALVING__now_height:", std.GetHeight())
	year, endTimestamp := GetHalvingYearByTimestamp(now)
	println("GNS_HALVING__year:", year, "endTimestamp:", endTimestamp)

	// how much time left to next halving
	timeLeft := endTimestamp - now
	println("GNS_HALVING__timeLeft:", timeLeft)

	// how many block left to next halving
	timeLeftMs := timeLeft * 1000
	println("GNS_HALVING__timeLeftMs:", timeLeftMs)
	blockLeft := timeLeftMs / avgBlockTimeMs
	println("GNS_HALVING__blockLeft:", blockLeft)

	// how many reward left to next halving
	minted := TotalMinted()
	println("GNS_HALVING__minted:", minted)
	amountLeft := halvingYearAccuAmount[year] - minted
	println("GNS_HALVING__amountLeft:", amountLeft)

	// how much reward per block
	adjustedAmountPerBlock := amountLeft / uint64(blockLeft)
	println("GNS_HALVING__adjustedAmountPerBlock:", adjustedAmountPerBlock)

	// update it
	amountPerBlockPerHalvingYear[year] = adjustedAmountPerBlock
	println("GNS_HALVING__amountPerBlockPerHalvingYear[year]:", amountPerBlockPerHalvingYear[year])

	// adjust halving block
	for year, block := range halvingYearBlock {
		println("GNS_HALVING__ > year:\t\t", year)

		yearEnd := halvingYearTimestamp[year]
		println("GNS_HALVING__ > endTime:\t", yearEnd)

		diff := yearEnd - now
		println("GNS_HALVING__ > diff:\t\t", diff)

		numBlock := diff * 1000 / avgBlockTimeMs
		println("GNS_HALVING__ > numBlock:\t", numBlock)

		halvingYearBlock[year] = std.GetHeight() + numBlock
		println("GNS_HALVING__ > origBlock:\t", block)
		println("GNS_HALVING__ > newBlock:\t", halvingYearBlock[year])

		println()
	}

	println()

}

func GetAmountByHeight(height int64) uint64 {
	halvingYear := GetHalvingYearByHeight(height)

	return amountPerBlockPerHalvingYear[halvingYear]
}

func GetHalvingYearByHeight(height int64) int64 {
	// determine which halving year block is in
	for year, block := range halvingYearBlock {
		if height <= block {
			return year
		}
	}

	return 0
}

func GetHalvingYearByTimestamp(timestamp int64) (int64, int64) {
	// determine which halving tier block is in
	for tier, ts := range halvingYearTimestamp {
		if timestamp <= ts {
			return tier, ts
		}
	}

	return 0, 0
}

func getYearlyBlockForTimestampMs(ms int64) int64 {
	yearMs := TIMESTAMP_365DAYS * 1000 // how much millisecond in a year
	return yearMs / ms                 // how many block in a year
}

package gnft

import (
