)

func TestCreateGameBoard(t *testing.T) {
	tests := []struct {
		name       string
		tileType   string
		boardSize  int
		seed       int64
		maxTileVal int
	}{
		{"SquareGrid", "squareGrid", 4, 12345, 16},
		{"Hexagrams", "hexagrams", 4, 12345, 4},
		{"Dice", "dice", 4, 12345, 6},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			board, gameTiles, size := CreateGameBoard(tt.tileType, tt.boardSize, tt.seed)

			// Test the length of the board
			if len(board) != tt.boardSize {
				t.Errorf("Expected board length of %d, got %d", tt.boardSize, len(board))
			}

			// Test the length of gameTiles
			expectedGameTilesLength := int(sqrt(float64(tt.boardSize)))
			if len(gameTiles) != expectedGameTilesLength {
				t.Errorf("Expected gameTiles length of %d, got %d", expectedGameTilesLength, len(gameTiles))
			}

			// Check if gameTiles values are within the expected range
			for _, tile := range gameTiles {
				if tile < 1 || tile > int64(tt.maxTileVal) {
					t.Errorf("gameTiles contains a value out of expected range (1 to %d): got %d", tt.maxTileVal, tile)
				}
			}

			// Check if the gameTiles values are unique
			uniqueTiles := make(map[int64]bool)
			for _, tile := range gameTiles {
				if uniqueTiles[tile] {
					t.Errorf("Duplicate tile found in gameTiles: %d", tile)
				}
				uniqueTiles[tile] = true
			}

			// Test the returned board size
			if size != tt.boardSize {
				t.Errorf("Expected returned board size to be %d, got %d", tt.boardSize, size)
			}
		})
	}
}

func TestGenerateRandomNumbers(t *testing.T) {
	tests := []struct {
		howMany int
		min     int
		max     int
		seed    int64
		expect  int
	}{
		{5, 1, 10, 123, 5},
		{3, 5, 7, 456, 3},
		{10, 1, 5, 789, 0},
		{0, 1, 10, 101, 0},
		{-2, 1, 10, 112, 0},
		{5, 10, 5, 123, 0},
	}

	for _, tt := range tests {
		got := GenerateRandomNumbers(tt.howMany, tt.min, tt.max, tt.seed)

		if len(got) != tt.expect {
			t.Errorf("expected %d numbers but got %d", tt.expect, len(got))
		}

		// Check for uniqueness
		numSet := make(map[int]bool)
		for _, num := range got {
			if numSet[num] {
				t.Errorf("duplicate number found: %d", num)
			}
			numSet[num] = true
		}

		// Check if numbers are within range
		for _, num := range got {
			if num < tt.min || num > tt.max {
				t.Errorf("number %d out of range [%d, %d]", num, tt.min, tt.max)
			}
		}
	}
}


// Helper function to compare two slices for equality
func sliceEqual(a, b []int64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

package flippandoserver

// this is a refactoring of the math/rand implementation with the goal of
// generating "good-enough" randomness, while eliminating the dependency
// from the actual math/rand package.
//
// once we have access to a reliable verifiable randmness source, this 
// package should be replaced / refactored

import (
	"std"
	"time"
)

type Instance struct {
	seed int64
}

// In the rand package:

func New(seed int64) *Instance {
	r := Instance{seed: seed}
	r.addEntropy()
	return &r
}

func FromSeed(seed int64) *Instance {
	r := Instance{seed: seed}
	r.addEntropy()
	return &r
}

func (i *Instance) Seed() int64 {
	return i.seed
}

func (i *Instance) djb2String(input string) {
	for _, c := range input {
		i.djb2Int64(int64(c))
	}
}

// super fast random algorithm.
// http://www.cse.yorku.ca/~oz/hash.html
func (i *Instance) djb2Int64(input int64) {
	i.seed = (i.seed << 5) + i.seed + input
}

// AddEntropy uses various runtime variables to add entropy to the existing seed.
func (i *Instance) addEntropy() {
	// FIXME: reapply the 5381 initial value?

	// inherit previous entropy
	// nothing to do

	// handle callers
	{
		caller1 := std.GetCallerAt(1).String()
		i.djb2String(caller1)
		caller2 := std.GetCallerAt(2).String()
		i.djb2String(caller2)
	}

	// height
	{
		height := std.GetHeight()
		i.djb2Int64(height)
	}

	// time
	{
		secs := time.Now().Second()
		i.djb2Int64(int64(secs))
		nsecs := time.Now().Nanosecond()
		i.djb2Int64(int64(nsecs))
	}

	// FIXME: compute other hard-to-guess but deterministic variables, like real gas?
}

func (i *Instance) Float32() float32 {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Float32()
}

func (i *Instance) Float64() float64 {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Float64()
}

func (i *Instance) Int() int {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Int()
}

func (i *Instance) Intn(n int) int {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Intn(n)
}

func (i *Instance) Int63() int64 {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Int63()
}

func (i *Instance) Int63n(n int64) int64 {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Int63n(n)
}

func (i *Instance) Int31() int32 {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Int31()
}

func (i *Instance) Int31n(n int32) int32 {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Int31n(n)
}

func (i *Instance) Uint32() uint32 {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Uint32()
}

func (i *Instance) Uint64() uint64 {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Uint64()
}

func (i *Instance) Read(p []byte) (n int, err error) {
	i.addEntropy()
	return NewRand(NewSource(i.seed)).Read(p)
}

func (i *Instance) Shuffle(n int, swap func(i, j int)) {
	i.addEntropy()
	NewRand(NewSource(i.seed)).Shuffle(n, swap)
}


// below is the relevant part of the code from the math/rand implementation
// Copyright retained

// Copyright 2009 The Go Authors. All rights reserved.
// Use of this source code is governed by a BSD-style
// license that can be found in the LICENSE file.

// Package rand implements pseudo-random number generators unsuitable for
// security-sensitive work.
//
// Random numbers are generated by a [Source], usually wrapped in a [Rand].
// Both types should be used by a single goroutine at a time: sharing among
// multiple goroutines requires some kind of synchronization.
//
// Top-level functions, such as [Float64] and [Int],
// are safe for concurrent use by multiple goroutines.
//
// This package's outputs might be easily predictable regardless of how it's
// seeded. For random numbers suitable for security-sensitive work, see the
// crypto/rand package.

// A Source represents a source of uniformly-distributed
// pseudo-random int64 values in the range [0, 1<<63).
//
// A Source is not safe for concurrent use by multiple goroutines.
type Source interface {
	Int63() int64
	Seed(seed int64)
}

// A Source64 is a Source that can also generate
// uniformly-distributed pseudo-random uint64 values in
// the range [0, 1<<64) directly.
// If a Rand r's underlying Source s implements Source64,
// then r.Uint64 returns the result of one call to s.Uint64
// instead of making two calls to s.Int63.
type Source64 interface {
	Source
	Uint64() uint64
}

// NewSource returns a new pseudo-random Source seeded with the given value.
// Unlike the default Source used by top-level functions, this source is not
// safe for concurrent use by multiple goroutines.
// The returned Source implements Source64.
func NewSource(seed int64) Source {
	return newSource(seed)
}

func newSource(seed int64) *rngSource {
	var rng rngSource
	rng.Seed(seed)
	return &rng
}

// A Rand is a source of random numbers.
type Rand struct {
	src Source
	s64 Source64 // non-nil if src is source64

	// readVal contains remainder of 63-bit integer used for bytes
	// generation during most recent Read call.
	// It is saved so next Read call can start where the previous
	// one finished.
	readVal int64
	// readPos indicates the number of low-order bytes of readVal
	// that are still valid.
	readPos int8
}

// New returns a new Rand that uses random values from src
// to generate other random values.
func NewRand(src Source) *Rand {
	s64, _ := src.(Source64)
	return &Rand{src: src, s64: s64}
}

// Seed uses the provided seed value to initialize the generator to a deterministic state.
// Seed should not be called concurrently with any other Rand method.
func (r *Rand) Seed(seed int64) {
	/*
	if lk, ok := r.src.(*lockedSource); ok {
		lk.seedPos(seed, &r.readPos)
		return
	}*/

	r.src.Seed(seed)
	r.readPos = 0
}

// Int63 returns a non-negative pseudo-random 63-bit integer as an int64.
func (r *Rand) Int63() int64 { return r.src.Int63() }

// Uint32 returns a pseudo-random 32-bit value as a uint32.
func (r *Rand) Uint32() uint32 { return uint32(r.Int63() >> 31) }

// Uint64 returns a pseudo-random 64-bit value as a uint64.
func (r *Rand) Uint64() uint64 {
	if r.s64 != nil {
		return r.s64.Uint64()
	}
	return uint64(r.Int63())>>31 | uint64(r.Int63())<<32
}

// Int31 returns a non-negative pseudo-random 31-bit integer as an int32.
func (r *Rand) Int31() int32 { return int32(r.Int63() >> 32) }

// Int returns a non-negative pseudo-random int.
func (r *Rand) Int() int {
	u := uint(r.Int63())
	return int(u << 1 >> 1) // clear sign bit if int == int32
}

// Int63n returns, as an int64, a non-negative pseudo-random number in the half-open interval [0,n).
// It panics if n <= 0.
func (r *Rand) Int63n(n int64) int64 {
	if n <= 0 {
		panic("invalid argument to Int63n")
	}
	if n&(n-1) == 0 { // n is power of two, can mask
		return r.Int63() & (n - 1)
	}
	max := int64((1 << 63) - 1 - (1<<63)%uint64(n))
	v := r.Int63()
	for v > max {
		v = r.Int63()
	}
	return v % n
}

// Int31n returns, as an int32, a non-negative pseudo-random number in the half-open interval [0,n).
// It panics if n <= 0.
func (r *Rand) Int31n(n int32) int32 {
	if n <= 0 {
		panic("invalid argument to Int31n")
	}
	if n&(n-1) == 0 { // n is power of two, can mask
		return r.Int31() & (n - 1)
	}
	max := int32((1 << 31) - 1 - (1<<31)%uint32(n))
	v := r.Int31()
	for v > max {
		v = r.Int31()
	}
	return v % n
}

// int31n returns, as an int32, a non-negative pseudo-random number in the half-open interval [0,n).
// n must be > 0, but int31n does not check this; the caller must ensure it.
// int31n exists because Int31n is inefficient, but Go 1 compatibility
// requires that the stream of values produced by math/rand remain unchanged.
// int31n can thus only be used internally, by newly introduced APIs.
//
// For implementation details, see:
// https://lemire.me/blog/2016/06/27/a-fast-alternative-to-the-modulo-reduction
// https://lemire.me/blog/2016/06/30/fast-random-shuffling
func (r *Rand) int31n(n int32) int32 {
	v := r.Uint32()
	prod := uint64(v) * uint64(n)
	low := uint32(prod)
	if low < uint32(n) {
		thresh := uint32(-n) % uint32(n)
		for low < thresh {
			v = r.Uint32()
			prod = uint64(v) * uint64(n)
			low = uint32(prod)
		}
	}
	return int32(prod >> 32)
}

// Intn returns, as an int, a non-negative pseudo-random number in the half-open interval [0,n).
// It panics if n <= 0.
func (r *Rand) Intn(n int) int {
	if n <= 0 {
		panic("invalid argument to Intn")
	}
	if n <= 1<<31-1 {
		return int(r.Int31n(int32(n)))
	}
	return int(r.Int63n(int64(n)))
}

// Float64 returns, as a float64, a pseudo-random number in the half-open interval [0.0,1.0).
func (r *Rand) Float64() float64 {
	// A clearer, simpler implementation would be:
	//	return float64(r.Int63n(1<<53)) / (1<<53)
	// However, Go 1 shipped with
	//	return float64(r.Int63()) / (1 << 63)
	// and we want to preserve that value stream.
	//
	// There is one bug in the value stream: r.Int63() may be so close
	// to 1<<63 that the division rounds up to 1.0, and we've guaranteed
	// that the result is always less than 1.0.
	//
	// We tried to fix this by mapping 1.0 back to 0.0, but since float64
	// values near 0 are much denser than near 1, mapping 1 to 0 caused
	// a theoretically significant overshoot in the probability of returning 0.
	// Instead of that, if we round up to 1, just try again.
	// Getting 1 only happens 1/2âµÂ³ of the time, so most clients
	// will not observe it anyway.
again:
	f := float64(r.Int63()) / (1 << 63)
	if f == 1 {
		goto again // resample; this branch is taken O(never)
	}
	return f
}

// Float32 returns, as a float32, a pseudo-random number in the half-open interval [0.0,1.0).
func (r *Rand) Float32() float32 {
	// Same rationale as in Float64: we want to preserve the Go 1 value
	// stream except we want to fix it not to return 1.0
	// This only happens 1/2Â²â´ of the time (plus the 1/2âµÂ³ of the time in Float64).
again:
	f := float32(r.Float64())
	if f == 1 {
		goto again // resample; this branch is taken O(very rarely)
	}
	return f
}

// Perm returns, as a slice of n ints, a pseudo-random permutation of the integers
// in the half-open interval [0,n).
func (r *Rand) Perm(n int) []int {
	m := make([]int, n)
	// In the following loop, the iteration when i=0 always swaps m[0] with m[0].
	// A change to remove this useless iteration is to assign 1 to i in the init
	// statement. But Perm also effects r. Making this change will affect
	// the final state of r. So this change can't be made for compatibility
	// reasons for Go 1.
	for i := 0; i < n; i++ {
		j := r.Intn(i + 1)
		m[i] = m[j]
		m[j] = i
	}
	return m
}

// Shuffle pseudo-randomizes the order of elements.
// n is the number of elements. Shuffle panics if n < 0.
// swap swaps the elements with indexes i and j.
func (r *Rand) Shuffle(n int, swap func(i, j int)) {
	if n < 0 {
		panic("invalid argument to Shuffle")
	}

	// Fisher-Yates shuffle: https://en.wikipedia.org/wiki/Fisher%E2%80%93Yates_shuffle
	// Shuffle really ought not be called with n that doesn't fit in 32 bits.
	// Not only will it take a very long time, but with 2Â³Â¹! possible permutations,
	// there's no way that any PRNG can have a big enough internal state to
	// generate even a minuscule percentage of the possible permutations.
	// Nevertheless, the right API signature accepts an int n, so handle it as best we can.
	i := n - 1
	for ; i > 1<<31-1-1; i-- {
		j := int(r.Int63n(int64(i + 1)))
		swap(i, j)
	}
	for ; i > 0; i-- {
		j := int(r.int31n(int32(i + 1)))
		swap(i, j)
	}
}

// Read generates len(p) random bytes and writes them into p. It
// always returns len(p) and a nil error.
// Read should not be called concurrently with any other Rand method.
func (r *Rand) Read(p []byte) (n int, err error) {
	/*
	if lk, ok := r.src.(*lockedSource); ok {
		return lk.read(p, &r.readVal, &r.readPos)
	}*/
	return read(p, r.src, &r.readVal, &r.readPos)
}

func read(p []byte, src Source, readVal *int64, readPos *int8) (n int, err error) {
	pos := *readPos
	val := *readVal
	rng, _ := src.(*rngSource)
	for n = 0; n < len(p); n++ {
		if pos == 0 {
			if rng != nil {
				val = rng.Int63()
			} else {
				val = src.Int63()
			}
			pos = 7
		}
		p[n] = byte(val)
		val >>= 8
		pos--
	}
	*readPos = pos
	*readVal = val
	return
}

/*
 * Top-level convenience functions
 */

var globalRand = NewRand(new(rngSource))

// Seed uses the provided seed value to initialize the default Source to a
// deterministic state. Seed values that have the same remainder when
// divided by 2Â³Â¹-1 generate the same pseudo-random sequence.
// Seed, unlike the Rand.Seed method, is safe for concurrent use.
//
// If Seed is not called, the generator is seeded randomly at program startup.
//
// Prior to Go 1.20, the generator was seeded like Seed(1) at program startup.
// To force the old behavior, call Seed(1) at program startup.
// Alternately, set GODEBUG=randautoseed=0 in the environment
// before making any calls to functions in this package.
//
// Deprecated: Programs that call Seed and then expect a specific sequence
// of results from the global random source (using functions such as Int)
// can be broken when a dependency changes how much it consumes
// from the global random source. To avoid such breakages, programs
// that need a specific result sequence should use NewRand(NewSource(seed))
// to obtain a random generator that other packages cannot access.
func Seed(seed int64) { globalRand.Seed(seed) }

// Int63 returns a non-negative pseudo-random 63-bit integer as an int64
// from the default Source.
func Int63() int64 { return globalRand.Int63() }

// Uint32 returns a pseudo-random 32-bit value as a uint32
// from the default Source.
func Uint32() uint32 { return globalRand.Uint32() }

// Uint64 returns a pseudo-random 64-bit value as a uint64
// from the default Source.
func Uint64() uint64 { return globalRand.Uint64() }

// Int31 returns a non-negative pseudo-random 31-bit integer as an int32
// from the default Source.
func Int31() int32 { return globalRand.Int31() }

// Int returns a non-negative pseudo-random int from the default Source.
func Int() int { return globalRand.Int() }

// Int63n returns, as an int64, a non-negative pseudo-random number in the half-open interval [0,n)
// from the default Source.
// It panics if n <= 0.
func Int63n(n int64) int64 { return globalRand.Int63n(n) }

// Int31n returns, as an int32, a non-negative pseudo-random number in the half-open interval [0,n)
// from the default Source.
// It panics if n <= 0.
func Int31n(n int32) int32 { return globalRand.Int31n(n) }

// Intn returns, as an int, a non-negative pseudo-random number in the half-open interval [0,n)
// from the default Source.
// It panics if n <= 0.
func Intn(n int) int { return globalRand.Intn(n) }

// Float64 returns, as a float64, a pseudo-random number in the half-open interval [0.0,1.0)
// from the default Source.
func Float64() float64 { return globalRand.Float64() }

// Float32 returns, as a float32, a pseudo-random number in the half-open interval [0.0,1.0)
// from the default Source.
func Float32() float32 { return globalRand.Float32() }

// Perm returns, as a slice of n ints, a pseudo-random permutation of the integers
// in the half-open interval [0,n) from the default Source.
func Perm(n int) []int { return globalRand.Perm(n) }

// Shuffle pseudo-randomizes the order of elements using the default Source.
// n is the number of elements. Shuffle panics if n < 0.
// swap swaps the elements with indexes i and j.
func Shuffle(n int, swap func(i, j int)) { globalRand.Shuffle(n, swap) }

// Read generates len(p) random bytes from the default Source and
// writes them into p. It always returns len(p) and a nil error.
// Read, unlike the Rand.Read method, is safe for concurrent use.
//
// Deprecated: For almost all use cases, crypto/rand.Read is more appropriate.
func Read(p []byte) (n int, err error) { return globalRand.Read(p) }

// NormFloat64 returns a normally distributed float64 in the range
// [-math.MaxFloat64, +math.MaxFloat64] with
// standard normal distribution (mean = 0, stddev = 1)
// from the default Source.
// To produce a different normal distribution, callers can
// adjust the output using:
//
//	sample = NormFloat64() * desiredStdDev + desiredMean
func NormFloat64() float64 { return globalRand.NormFloat64() }

func (r *Rand) NormFloat64() float64 {
	z := r.Float64()*2 - 1 + r.Float64()*2 - 1
	radius := z*z
	for radius >= 1 || radius == 0 {
		z = r.Float64()*2 - 1 + r.Float64()*2 - 1
		radius = z*z
	}
	return z * (-2 * ln(radius) / radius)
}

// while not having access to math package at all
func ln(x float64) float64 {
    const negativeInfinity = -1e308
    const NaN = -99999.0

    if x == 0 {
        return negativeInfinity // Return a very large negative value for ln(0)
    }
    if x < 0 {
        return NaN // Return a special value for negative values
    }

    // Adjust large values into a suitable range for Maclaurin series
    if x > 2 {
        return -ln(1/x)
    }

    // Maclaurin series for ln(1 + x) around 0
    x -= 1 // Adjust x for the expansion around 1
    result := 0.0
    term := x
    for i := 1; i < 100; i++ { // Use 100 terms. You can adjust this for more accuracy.
        if i%2 == 1 {
            result += term / float64(i)
        } else {
            result -= term / float64(i)
        }
        term *= x
    }

    return result
}


// ExpFloat64 returns an exponentially distributed float64 in the range
// (0, +math.MaxFloat64] with an exponential distribution whose rate parameter
// (lambda) is 1 and whose mean is 1/lambda (1) from the default Source.
// To produce a distribution with a different rate parameter,
// callers can adjust the output using:
//
//	sample = ExpFloat64() / desiredRateParameter
//func ExpFloat64() float64 { return globalRand.ExpFloat64() }

func (r *Rand) ExpFloat64() float64 {
	return globalRand.ExpFloat64()
}

package flippando

import (
	"strconv"
	"std"
	"bytes"
	"strings"

	"gno.land/p/demo/grc/grc721"
	"gno.land/p/demo/avl"
	"gno.land/p/demo/ufmt"
)

var (
	// hardcoding the airdrop account and restricting minting to it
	validAirdropAccount std.Address = std.Address("g1mphljw4duhzwqs72hv9grm4k6ufkvts3g7t3t4")
	bNFTtokenIDCounter uint64
	mintedNFTs avl.Tree // owner -> []string
)


// - generates a GRC721 token with the airdropped board SVG as a tokenURI
// - returns token metadata as string, to be used in client

func AirdropBasicFlipNFT( 
	playerRecipient string,
	airdropName string, 
	airdropParentID string,
	airdropXPos string,
	airdropYPos string,
	gameType string, 
	gameLevel string,
	svgData string) (string, string) {

	// - toPlayer is the user receiving the NFT, and the locked FLIP
	toPlayer := std.Address(playerRecipient)

	result := ""
	nftError := ""

	// Check if the function is called by the valid airdrop account
	if validAirdropAccount.String() != std.GetOrigCaller().String() {
		return "", "airdrop account not correct"
	}

	// Check if an NFT with the same airdropParentID, airdropXPos, and airdropYPos has already been minted
	mintedNFTs.Iterate("", "", func(key string, value interface{}) bool {
		mintedList := value.([]string)
		for _, mintedEntry := range mintedList {
			parts := strings.Split(mintedEntry, ":")
			if len(parts) == 4 && parts[1] == airdropParentID && parts[2] == airdropXPos && parts[3] == airdropYPos {
				nftError = "an NFT with this airdropParentID, airdropXPos, and airdropYPos has already been minted"
				return true // Stop the iteration
			}
		}
		return false // Continue the iteration
	})
	if nftError != "" {
		return "", nftError
	}

	// Check if the user has already minted the maximum number of NFTs
	const maxNFTs = 64
	userMintedNFTs, exists := mintedNFTs.Get(toPlayer.String())
	var mintedList []string
	if exists {
		mintedList = userMintedNFTs.([]string)
		if len(mintedList) >= maxNFTs {
			return "", "user has already minted the maximum number of NFTs"
		}
	} else {
		mintedList = []string{}
		mintedNFTs.Set(toPlayer.String(), mintedList)
	}

	// Mint the NFT
	count := FlipBasicNFT.TokenCount()
	bNFTtokenIDCounter = count + 1
	tidString := strconv.FormatUint(bNFTtokenIDCounter, 10)
	tid := grc721.TokenID(tidString)
	// we first mint to airdrop account, to be able to set TokenURI
	// then we transfer
	mintResultError := FlipBasicNFT.Mint(validAirdropAccount, tid)

	if mintResultError == nil {
		gameId := "airdrop/" + airdropName + "/1"

		flipBasicNFTMetaData := &BasicNFTMetaData{
			tokenID: tidString,
			name: "Flippando - the Game",
			version: "1.0.0",
			gameId: gameId, 
			gameType: gameType,
			gameLevel: gameLevel,
			svgData: svgData,
			airdropName: airdropName,
			airdropParentID: airdropParentID,
			airdropXPos: airdropXPos,
			airdropYPos: airdropYPos,
		}
		flipBasicNFTMetaDataJSON, err := flipBasicNFTMetaData.MarshalJSON()
		if err != nil {
			panic("error in marshalling flipBasicNFTMetaData")
		}
		_, terr := FlipBasicNFT.SetTokenURI(tid, grc721.TokenURI(flipBasicNFTMetaDataJSON))
		if terr != nil {
			return "", ufmt.Sprintf("error in SetTokenUR in airdrop, %s", terr.Error())
		}
		// transfer the NFT to player
		FlipBasicNFT.TransferFrom(validAirdropAccount, toPlayer, tid)
		FlipBasicNFT.Approve(GetBasicNFTRealmAddress(), tid)

		// Add NFT to the LockedNFTs avl.Tree
		var flipAmount int64
		if gameLevel == "16" {
			flipAmount = 1000
		} else if gameLevel == "64" {
			flipAmount = 4000
		}
		MintAndLockFLIP(toPlayer, tidString, std.NewCoin("FLIP", flipAmount))
		LockedNFTs.Set(tidString, flipAmount)

		// Mint FLIP for the account airdropping, to create liquidity
		merr := Mint(airdropAccount, std.NewCoin("FLIP", flipAmount))
		if merr != nil {
			return "", ufmt.Sprintf("error in calling Mint in airdrop function, %s", merr.Error())
		}

		// Add minted NFT to the user's minted NFTs list with the format "basicTokenId:airdropParentId:airdropXPos:airdropYPos"
		mintedEntry := tidString + ":" + airdropParentID + ":" + airdropXPos + ":" + airdropYPos
		if exists {
			mintedList = append(mintedList, mintedEntry)
			mintedNFTs.Set(toPlayer.String(), mintedList)
		} else {
			mintedNFTs.Set(toPlayer.String(), []string{mintedEntry})
		}

		result = string(flipBasicNFTMetaDataJSON)
	} else {
		nftError = ufmt.Sprintf("error in calling Mint in airdrop function, %s", mintResultError.Error())
	}

	res := &nftResult{
		result: result,
		nftError: nftError,
	}

	createNFTResult, err := res.MarshalJSON()
	if err != nil {
		panic("error in marshaling result")
	}
	return string(createNFTResult), nftError
}

// getters, utils

func GetAllMintedAirdropNFTs() string {
	var allTokenIDs []string

	mintedNFTs.Iterate("", "", func(key string, value interface{}) bool {

		if tId, ok := value.([]string); ok {
			allTokenIDs = append(allTokenIDs, tId...)
		}
		return false // Continue iteration until all nodes have been visited.
	})

	res := &mintedAirdropNFTs{
		TokenIDs: allTokenIDs,
	}

	mintedNFTResult, err := res.MarshalJSON()
	if err != nil {
		panic("error in marshalling result")
	}
	return string(mintedNFTResult)
}

func (man mintedAirdropNFTs) MarshalJSON() ([]byte, error) {
	var b bytes.Buffer

	b.WriteByte('{')

	b.WriteString(`"tokenIDs":[`)
	for i, s := range man.TokenIDs {
		if i > 0 {
			b.WriteString(",")
		}
		b.WriteString(`"` + s + `"`)
	}
	b.WriteString(`]`)

	b.WriteByte('}')

	return b.Bytes(), nil
}

type mintedAirdropNFTs struct {
	TokenIDs []string `json:"tokenIDs"`
}

package flippando

import (
	"strconv"
	"std"
	"errors"
	"strings"

	"gno.land/p/demo/grc/grc721"
	"gno.land/p/demo/avl"
	"gno.land/p/demo/ufmt"
)

var (
	FlipBasicNFT	= grc721.NewBasicNFT("Flippando Basic NFT", "BFLP")
	basicNFTtokenIDCounter	uint64
	LockedNFTs avl.Tree // tokenId -> flipAmount
	childOf avl.Tree // tokenId -> parent (composite) tokenId
	mintingLock bool
)

func CreateBasicFlipNFT(player std.Address, gameId string) (string, string) {
	// - generates a GRC721 token with the solved board SVG as a tokenURI
	// - returns token metadata as string, to be used in client
	result := ""
	nftError := ""

	games := GetUserGames(player)
	// safety checks
	if(len(games) == 0){
		result = "{failedSafetyCheck: userHasGames}"
		nftError = "{error: user has zero games}"
	}
	currentGame := games[0]
	hasZeroValues := func(s []int64) bool { for _, v := range s { if v == 0 { return true } }; return false }(currentGame.SolvedGameBoard)
	if(hasZeroValues){
		result = "{failedSafetyCheck: gameCompletion}"
		nftError = "{error: game is not finished}"
	}
	// Check and acquire the lock
    if mintingLock {
        return "", "minting in progress"
    }
    mintingLock = true
    defer func() { mintingLock = false }() // Release lock at the end

	count := FlipBasicNFT.TokenCount()
	basicNFTtokenIDCounter = count + 1
	tidString := strconv.FormatUint(basicNFTtokenIDCounter, 10)
	tid := grc721.TokenID(tidString)
	mintResultError := FlipBasicNFT.Mint(player, tid)
	if mintResultError == nil {
		gameSVGData, errSVG := GenerateGrid(currentGame.SolvedGameBoard, currentGame.TileType)
		if errSVG != nil {
			result = "{failedSafetyCheck: svgDataCorrupted}"
			nftError = "SVG data not generated"
		}
		flipBasicNFTMetaData := &BasicNFTMetaData{
			tokenID: tidString,
			name: "Flippando - the Game",
			version: "1.0.0",
			gameId: currentGame.ID,
			gameType: currentGame.TileType,
			gameLevel: strconv.Itoa(len(currentGame.SolvedGameBoard)),
			svgData: gameSVGData,
			airdropName: "",
			airdropParentID: "",
			airdropXPos: "",
			airdropYPos: "",
		}
		flipBasicNFTMetaDataJSON, err := flipBasicNFTMetaData.MarshalJSON()
		if err != nil {
			panic("error in marhasling flipBasicNFTMetaData");
		}
		FlipBasicNFT.SetTokenURI(tid, grc721.TokenURI(flipBasicNFTMetaDataJSON))
		// set approval for the realm owner so we can later transfer the NFT
		FlipBasicNFT.Approve(std.CurrentRealm().Addr(), tid)

		// add NFT to the LockedNFTs avl.Tree
		var flipAmount int64
		if len(currentGame.SolvedGameBoard) == 16 {
			flipAmount = 1000 // 1000 uflip = 1 FLIP
		} else if len(currentGame.SolvedGameBoard) == 64 {
			flipAmount = 4000 // 4000 uflip = 4 FLIP
		}
		// mint fungible FLIP token
		MintAndLockFLIP(player, tidString, std.NewCoin("FLIP", flipAmount))
		LockedNFTs.Set(tidString, flipAmount)

		result = string(flipBasicNFTMetaDataJSON)
	}
	res := &nftResult{
		result: result,
		nftError: nftError,
	}

	createNFTResult, err := res.MarshalJSON()
	if(err != nil){
		panic("error in minting")
	}
	return string(createNFTResult), nftError
}

// returns a string version of the TokenURI
func GetBasicTokenURI(tokenID string) string {
	tid := grc721.TokenID(tokenID)
	tokenURI, err := FlipBasicNFT.TokenURI(tid)
	if err != nil {
		return ufmt.Sprintf("error in GetBasicTokenURI in basicNFT, %s", err.Error())
	}
	return string(tokenURI);
}

// basically identical with the other one, but returns BasicNFTMetaData type
func GetTokenMetadata(tokenId string) (BasicNFTMetaData, error) {
	uri, gerr := FlipBasicNFT.TokenURI(grc721.TokenID(tokenId))

	var metadata BasicNFTMetaData
	if gerr != nil {
		return metadata, gerr
	}
	metadata, err := ParseBasicNFTMetaData(uri)
	if err != nil {
		return metadata, err
	}
	return metadata, nil
}

func GetParentdOf(tokenId string) string {
	parentTokenId, exists := childOf.Get(tokenId)
	if exists {
		return parentTokenId.(string);
	} 
	return "" 
}

func UnlockAndTransferNFT(from, to std.Address, tid string, parentTid string) error {
	tokenId := grc721.TokenID(tid)

	terr := FlipBasicNFT.TransferFrom(from, to, tokenId)
	if terr != nil {
		return terr
	}
	// remove from locked NFTs
	LockedNFTs.Remove(tid)
	childOf.Set(tid, parentTid)
	return nil
}

// it is used in marketplace
func TransferBasicNFTToBuyer(from, to std.Address, tid grc721.TokenID) error {
	var approveErr error
	var transferErr error

    // Check current approval status
    approvedAddress, err := FlipBasicNFT.GetApproved(tid)
    if err != nil {
        approveErr = errors.New(ufmt.Sprintf("Failed to get approval status: %s", err.Error()))
    } 

    if approvedAddress != std.CurrentRealm().Addr() {
        // Approve the marketplace to transfer the token on behalf of the owner
        err = FlipBasicNFT.Approve(std.CurrentRealm().Addr(), tid)
        if err != nil {
            approveErr = errors.New(ufmt.Sprintf("Failed to approve NFT transfer: %s, approvedAddress: %s", err.Error(), approvedAddress))
		}
    }

    // Perform the transfer
    err = FlipBasicNFT.TransferFrom(from, to, tid)
    if err != nil {
        transferErr = errors.New(ufmt.Sprintf("Failed to transfer NFT: %s", err.Error()))
    } 

    // Combine errors if any
    if approveErr != nil && transferErr != nil {
        return errors.New(ufmt.Sprintf("%s; %s", approveErr.Error(), transferErr.Error()))
    } else if approveErr != nil {
        return approveErr
    } else if transferErr != nil {
        return transferErr
    }

    return nil
}

// it is used after minting a composite NFT
func BurnBasicNFTsAfterMinting(tid grc721.TokenID) error {
	err := FlipBasicNFT.Burn(tid)
	if err != nil {
		return err
	}
	return nil
}

func GetOwnerOfBasicNFT(tid string) std.Address {
	tokenId := grc721.TokenID(tid)
	owner, err := FlipBasicNFT.OwnerOf(tokenId)
	if err != nil {
		return "can't get owner"
	}
	return owner
}

func GetUserBasicFlipNFTs(addr std.Address, readyToUse string) string {
	tokenCount := FlipBasicNFT.TokenCount()
	tokenURIs := []string{}
	userNFTsError := ""

	for i := uint64(1); i < uint64(tokenCount+1); i++ {		
		tidString := strconv.FormatUint(i, 10)
		owner, err := FlipBasicNFT.OwnerOf(grc721.TokenID(tidString))
		// debug
		if err != nil {
			userNFTsError += err.Error()
		}
		if err != nil {
			if err == ErrInvalidTokenId {
				// do we need to do smth else here?
				return "invalid token"
			} 
		}

		// check the owner is the addr
		if owner == addr {
			// if readyToUSe == "yes" we're returning only locked NFTs
			_, exists := LockedNFTs.Get(tidString)
			if readyToUse == "yes" {
				if exists {
					tokenURI := GetBasicTokenURI(tidString)
					tokenURIs = append(tokenURIs, tokenURI)
				}
			} else {
				if !exists {
					tokenURI := GetBasicTokenURI(tidString)
					tokenURIs = append(tokenURIs, tokenURI)
				}
			}
		}
	}

	fetchedUserNFTs := &userNFTsResult{
		UserNFTs: tokenURIs,
	}

	userNFTs, err := fetchedUserNFTs.MarshalJSON() 
	if(err != nil){
		panic("error in fetching user NFTs")
	}

	return string(userNFTs)
}

func GetBasicNFTsByTokenId(bTokenIDs string) string {

	tokenURIs := []string{}
	bTokenIDsArray, _ := ParseString(bTokenIDs)

	for i := int64(0); i < int64(len(bTokenIDsArray)); i++ {		
		tidString := strconv.FormatUint(uint64(bTokenIDsArray[i]), 10)

		tokenURI := GetBasicTokenURI(tidString)
		tokenURIs = append(tokenURIs, tokenURI)		
	}

	fetchedUserNFTs := &userNFTsResult{
		UserNFTs: tokenURIs,
	}

	userNFTs, err := fetchedUserNFTs.MarshalJSON() 
	if(err != nil){
		panic("error in fetching basic NFTs by tokenIDs")
	}

	return string(userNFTs)
}

func GetAllBasicFlipNFTs(forPlayer std.Address) string {
	tokenCount := FlipBasicNFT.TokenCount()
	tokenURIs := []string{}
	userNFTsError := ""

	for i := uint64(1); i < uint64(tokenCount+1); i++ {		
		tidString := strconv.FormatUint(i, 10)
		owner, err := FlipBasicNFT.OwnerOf(grc721.TokenID(tidString))

		if err != nil {
			userNFTsError += err.Error()
			if err == ErrInvalidTokenId {
				// do we need to do smth else here?
				return "invalid token"
			} 
		}

		// return nfts not belonging to forPlayer
		if owner != forPlayer {
			// only locked nfts, unlocked can't be reused
			_, exists := LockedNFTs.Get(tidString)

			if exists {
				tokenURI := GetBasicTokenURI(tidString)
				tokenURIs = append(tokenURIs, tokenURI)
			}
		}
	}

	fetchedUserNFTs := &userNFTsResult{
		UserNFTs: tokenURIs,
	}

	userNFTs, err := fetchedUserNFTs.MarshalJSON() 
	if(err != nil){
		panic("error in fetching user NFTs")
	}

	return string(userNFTs)
}

func GetTotalAvailableBasicNFTs() int {
	return LockedNFTs.Size()
}

func GetAllUnlockedNFTs() int {
	tokenCount := FlipBasicNFT.TokenCount()
	LockedNFTs := LockedNFTs.Size()
	totalUnlocked := int(tokenCount) - LockedNFTs
	return int(totalUnlocked)
}

// util

func GetBasicNFTRealmAddress() std.Address {
	return std.CurrentRealm().Addr()
}


// util
func ParseBasicNFTMetaData(jsonStr string) (BasicNFTMetaData, error) {
	var metadata BasicNFTMetaData

	// Helper function to extract a value from the JSON string by key
	extractValue := func(key string) string {
		startIndex := strings.Index(jsonStr, `"`+key+`":"`)
		if startIndex == -1 {
			return ""
		}
		startIndex += len(key) + 4 // Move to the start of the value

		endIndex := strings.Index(jsonStr[startIndex:], `"`)
		if endIndex == -1 {
			return ""
		}

		return jsonStr[startIndex : startIndex+endIndex]
	}

	metadata.tokenID = extractValue("tokenID")
	metadata.name = extractValue("name")
	metadata.version = extractValue("version")
	metadata.gameId = extractValue("gameId")
	metadata.gameType = extractValue("gameType")
	metadata.gameLevel = extractValue("gameLevel")
	metadata.svgData = extractValue("svgData")
	metadata.airdropName = extractValue("airdropName")
	metadata.airdropParentID = extractValue("airdropParentID")
	metadata.airdropXPos = extractValue("airdropXPos")
	metadata.airdropYPos = extractValue("airdropYPos")

	return metadata, nil
}

