package flippando

import (
	"std"
	"testing"
	"strings"
	"encoding/base64"

	"gno.land/p/demo/testutils"
)

func TestGenerateGrid(t *testing.T) {
    solvedBoard := generateSolvedBoard()

    tests := []struct {
        name         string
        solvedBoard  []int64
        gridType     string
        expectError  bool
    }{
        {"ValidSquareGrid", solvedBoard, "squareGrid", false},
        {"ValidDiceGrid", solvedBoard, "dice", false},
        {"ValidHexagramGrid", solvedBoard, "hexagrams", false},
        {"InvalidGridType", solvedBoard, "invalidType", true},
        {"InvalidBoardSize", make([]int64, 10), "squareGrid", true},
    }

    for _, tt := range tests {
        t.Run(tt.name, func(t *testing.T) {
            _, err := GenerateGrid(tt.solvedBoard, tt.gridType)
            if (err != nil) != tt.expectError {
                t.Errorf("GenerateGrid() error = %v, expectError %v", err, tt.expectError)
            }
        })
    }
}

func TestGenerateColorSquareGrid(t *testing.T) {
    // Example solved board
    solvedBoard := generateSolvedBoard()

    // Call the function with the test board
    base64Svg, err := generateColorSquareGrid(solvedBoard, "squareGrid", 4) // gridType squareGrid, size 4 for a 4x4 grid
    if err != nil {
        t.Fatalf("generateColorSquareGrid returned an unexpected error: %v", err)
    }

    // Decode the Base64-encoded SVG string
    svgBytes, err := base64.StdEncoding.DecodeString(base64Svg)
    if err != nil {
        t.Fatalf("Error decoding Base64 SVG string: %v", err)
    }
    svg := string(svgBytes)

    // Check if the SVG string starts with <svg and ends with </svg>
    if !strings.HasPrefix(svg, "<svg") || !strings.HasSuffix(svg, "</svg>") {
        t.Errorf("SVG does not start with '<svg' or end with '</svg>'")
    }

    // Count the number of <rect> elements
    rectCount := strings.Count(svg, "<rect")
    expectedRectCount := len(solvedBoard) // There should be a rect for each board element
    if rectCount != expectedRectCount {
        t.Errorf("Expected %d <rect> elements, found %d", expectedRectCount, rectCount)
    }
}


func generateSolvedBoard() []int64 {
    board := make([]int64, 16)
    for i := 0; i < 4; i++ {
        for j := 1; j <= 4; j++ {
            board[i*4+j-1] = int64(j)
        }
    }
    return board
}

package flippando

import (
	"errors"
	"strconv"
	"strings"
	"std"
	"gno.land/p/demo/ufmt"
	"gno.land/p/demo/grc/grc721"
)

type Marketplace struct {
	listings map[string]Listing // CompositeTokenID -> Listing
	basicListings map[string]BasicListing // BasicTokenID -> Listing
}

type Listing struct {
	CompositeTokenID grc721.TokenID // TokenID of the composite NFT
	BasicTokenIDs    []int64        // Slice of TokenIDs for the basic NFTs contained within the composite NFT
	Seller           std.Address    // Address of the seller
	Price            uint64         // Sale price
}

type BasicListing struct {
	BasicTokenID grc721.TokenID // TokenID of the basic NFT
	Seller           std.Address    // Address of the seller
	Price            uint64         // Sale price
}

func NewMarketplace() *Marketplace {
	return &Marketplace{
		listings: make(map[string]Listing),
		basicListings: make(map[string]BasicListing),
	}
}

func (m *Marketplace) ListCompositeNFT(compositeTokenID grc721.TokenID, seller std.Address, price string) error {
    // approve it for buying
    FlipCompositeNFT.Approve(std.CurrentRealm().Addr(), compositeTokenID)
	// get tokenURI of the composite NFT
    compositeTokenIDstr := string(compositeTokenID)
	basicTokenIDArray, err := GetChildrenOf(compositeTokenIDstr)
    if err != nil {
        return err
    }
    basicTokenIDs, _ := ParseString(basicTokenIDArray)
    // approve all basic NFTs for transferring
    for _, t := range basicTokenIDs {
        basicTokenId := grc721.TokenID(strconv.FormatInt(t, 10))
        FlipBasicNFT.Approve(std.CurrentRealm().Addr(), basicTokenId)
    }
    // convert price into uflips, multiply by 1000
    intPrice, ferr := convertPriceStringToUint64(price)
	if ferr != nil {
		return ferr
	}
	// Create a new listing
	newListing := Listing{
		CompositeTokenID: compositeTokenID,
		BasicTokenIDs:    basicTokenIDs,
		Seller:           seller,
		Price:            intPrice,
	}

	// Add the new listing to the marketplace
	m.listings[compositeTokenIDstr] = newListing
	return nil
}

func (m *Marketplace) ListBasicNFT(basicTokenID grc721.TokenID, seller std.Address, price string) error {
	// approve it for buying
    FlipBasicNFT.Approve(std.CurrentRealm().Addr(), basicTokenID)
    // convert price into uflips, multiply by 1000
    intPrice, ferr := convertPriceStringToUint64(price)
	if ferr != nil {
		return ferr
	}
	// Create a new listing
	newBasicListing := BasicListing{
		BasicTokenID: basicTokenID,
		Seller:           seller,
		Price:            intPrice,
	}

	// Add the new listing to the marketplace
	m.basicListings[string(basicTokenID)] = newBasicListing
	return nil
}

func (m *Marketplace) RemoveCompositeNFTListing(compositeTokenID grc721.TokenID, seller std.Address) error {
	// Retrieve the listing
	rawListing, exists := m.listings[string(compositeTokenID)]
	if !exists {
		return errors.New("listing does not exist")
	}

	listing := rawListing

	// Verify that the caller is the seller
	if listing.Seller != seller {
		return errors.New("unauthorized - caller is not the seller")
	}

	// Remove the listing from the marketplace
	delete(m.listings, string(compositeTokenID))
	return nil
}

func (m *Marketplace) RemoveBasicNFTListing(basicTokenID grc721.TokenID, seller std.Address) error {
	// Retrieve the listing
	rawListing, exists := m.basicListings[string(basicTokenID)]
	if !exists {
		return errors.New("basic listing does not exist")
	}

	listing := rawListing

	// Verify that the caller is the seller
	if listing.Seller != seller {
		return errors.New("unauthorized - caller is not the seller")
	}

	// Remove the listing from the marketplace
	delete(m.basicListings, string(basicTokenID))
	// move approval to Basic NFT in case the basic NFT is to be used
	// in the assembly of a composite NFT later on
	FlipBasicNFT.Approve(GetBasicNFTRealmAddress(), basicTokenID)
	return nil
}

func (m *Marketplace) GetCompositeNFTListings() string {
	var allListings []string
	for _, value := range m.listings {
		// Type assert the value to a Listing
		listing := value
		compositeTokenURI := GetCompositeTokenURI(string(listing.CompositeTokenID))
		compositeTokenURI = strings.Trim(compositeTokenURI, "\"")

		listingResult := &listingCompositeResult{
			compositeTokenID: string(listing.CompositeTokenID),
			tokenURI:         compositeTokenURI,
			seller:           string(listing.Seller),
			price:            strconv.FormatUint(listing.Price, 10),
		}
		listingResultJSON, _ := listingResult.MarshalJSON()
		allListings = append(allListings, string(listingResultJSON))
	}

	marketplaceListings := &marketplaceListings{
		marketplaceListings: allListings,
	}
	marketplaceJSON, _ := marketplaceListings.MarshalJSON()

	return string(marketplaceJSON)
}

func (m *Marketplace) GetBasicNFTListings() string {
	var allListings []string
	for _, value := range m.basicListings {
		// Type assert the value to a Listing
		listing := value
		basicTokenURI := GetBasicTokenURI(string(listing.BasicTokenID))

		listingBasicResult := &listingBasicResult{
			basicTokenID: string(listing.BasicTokenID),
			tokenURI:         basicTokenURI,
			seller:           string(listing.Seller),
			price:            strconv.FormatUint(listing.Price, 10),
		}
		listingResultJSON, _ := listingBasicResult.MarshalJSON()
		allListings = append(allListings, string(listingResultJSON))
	}

	marketplaceListings := &marketplaceListings{
		marketplaceListings: allListings,
	}
	marketplaceJSON, _ := marketplaceListings.MarshalJSON()

	return string(marketplaceJSON)
}

func (m *Marketplace) IsBasicTokenIdListed(tokenId string) error {
	if _, exists := m.basicListings[tokenId]; exists {
		return errors.New("tokenId is already listed")
	}
	return nil
}

func (m *Marketplace) BuyCompositeNFT(buyer std.Address, compositeTokenID grc721.TokenID) (buyResult string) {
    var buyError error

    flipPaid := ""
    flipBurned := ""
    listingRaw, exists := m.listings[string(compositeTokenID)]
    if !exists {
        buyError = ErrMarketplaceListingDoesNotExist
    }
    listing := listingRaw


    compositeNFTOwner := GetOwnerOfCompositeTokenId(string(compositeTokenID))
    tcerr := TransferCompositeNFT(std.Address(listing.Seller), buyer, compositeTokenID)
    if tcerr != nil {
        buyError = errors.New(ufmt.Sprintf("TransferCompositeNFT error: seller=%s, buyer=%s, tokenID=%s, owner=%s, error: %s", listing.Seller, buyer, string(compositeTokenID), compositeNFTOwner, tcerr.Error()))
    }

    transferredBasicNFTs := []grc721.TokenID{}

    if buyError == nil {
        for _, t := range listing.BasicTokenIDs {
            basicTokenId := grc721.TokenID(strconv.FormatInt(t, 10))
            ownerBasicNFT := GetOwnerOfBasicNFT(string(basicTokenId))
			//FlipBasicNFT.Approve(std.CurrentRealm().Addr(), basicTokenId)
            berr := FlipBasicNFT.TransferFrom(std.Address(listing.Seller), buyer, basicTokenId)
            if berr != nil {
                buyError = errors.New(ufmt.Sprintf("FlipBasicNFT.TransferFrom error: seller=%s, buyer=%s, basicTokenId=%s, owner=%s, error: %s", listing.Seller, buyer, string(basicTokenId), string(ownerBasicNFT), berr.Error()))
                break
            }
            transferredBasicNFTs = append(transferredBasicNFTs, basicTokenId)
        }

        if buyError != nil {
            reverr := TransferCompositeNFT(buyer, std.Address(listing.Seller), compositeTokenID)
            if reverr != nil {
                buyError = errors.New(ufmt.Sprintf("revert TransferCompositeNFT error: seller=%s, buyer=%s, tokenID=%s, owner=%s, error: %s", listing.Seller, buyer, string(compositeTokenID), compositeNFTOwner, tcerr.Error()))
            }
            // Revert any successfully transferred basic NFTs
            for _, basicTokenId := range transferredBasicNFTs {
				//FlipBasicNFT.Approve(std.CurrentRealm().Addr(), basicTokenId)
                _ = FlipBasicNFT.TransferFrom(buyer, std.Address(listing.Seller), basicTokenId)
            }
        }
    }

    if buyError == nil {
        fPricePaid, fPriceBurned, terr := PayFLIPToSeller(buyer, listing.Seller, std.NewCoin("FLIP", int64(listing.Price)))
        if terr != nil {
            buyError = terr
            //revert the sale on FLIP transfer error, most likely not enough tokens
            reverr := TransferCompositeNFT(buyer, std.Address(listing.Seller), compositeTokenID)
            if reverr != nil {
                buyError = errors.New(ufmt.Sprintf("revert TransferCompositeNFT on FLIP error: seller=%s, buyer=%s, tokenID=%s, owner=%s, error: %s", listing.Seller, buyer, string(compositeTokenID), compositeNFTOwner, tcerr.Error()))
            }
            // Revert any successfully transferred basic NFTs
            for _, basicTokenId := range transferredBasicNFTs {
				//FlipBasicNFT.Approve(std.CurrentRealm().Addr(), basicTokenId)
                _ = FlipBasicNFT.TransferFrom(buyer, std.Address(listing.Seller), basicTokenId)
            }
        }
        flipPaid = fPricePaid
        flipBurned = fPriceBurned
    }

    if buyError == nil {
        delete(m.listings, string(compositeTokenID))
    }

    var buyErrorString string
    if buyError != nil {
        buyErrorString = buyError.Error()
    } else {
        buyErrorString = ""
    }

    buyNFTResultObject := &buyNFTResult{
        flipPaid: flipPaid,
        flipBurned: flipBurned,
        buyerror: buyErrorString,
    }
    buyResultJSON, merr := buyNFTResultObject.MarshalJSON() 

    if merr != nil {
        buyError = errors.New("Error in marshaling buyNFTResult")
    }
    return string(buyResultJSON)
}

func (m *Marketplace) BuyBasicNFT(buyer std.Address, basicTokenID grc721.TokenID) (buyResult string) {
	var buyError error

	flipPaid := ""
	flipBurned := ""
	listingRaw, exists := m.basicListings[string(basicTokenID)]
	if !exists {
		buyError = ErrMarketplaceListingDoesNotExist
	}
	listing := listingRaw

	basicNFTOwner := GetOwnerOfBasicNFT(string(basicTokenID))

	// Pay the price, including random burning
	if buyError == nil {
		fPricePaid, fPriceBurned, terr := PayFLIPToSeller(buyer, listing.Seller, std.NewCoin("FLIP", int64(listing.Price)))
		if terr != nil {
			buyError = terr
		}
		flipPaid = fPricePaid
		flipBurned = fPriceBurned
	}

	// Transfer the basic NFT
	if buyError == nil {
		berr := FlipBasicNFT.TransferFrom(std.Address(listing.Seller), buyer, basicTokenID)
		if berr != nil {
			buyError = errors.New(ufmt.Sprintf("TransferFrom inside BuyBsicNFT error: seller=%s, buyer=%s, basicTokenId=%s, owner=%s, error: %s", listing.Seller, buyer, string(basicTokenID), string(basicNFTOwner), berr.Error()))
		}
	}

	// Transfer locked FLIP to buyer
	if buyError == nil {
		lerr := TransferLockedFLIP(std.Address(listing.Seller), buyer, string(basicTokenID))
		if lerr != nil {
			buyError = errors.New(ufmt.Sprintf("TransferLockedFLIP error: seller=%s, buyer=%s, basicTokenId=%s, owner=%s, error: %s", listing.Seller, buyer, string(basicTokenID), string(basicNFTOwner), lerr.Error()))
		}
	}

	// If no errors, remove the listing
	if buyError == nil {
		delete(m.basicListings, string(basicTokenID))
		// move approval to Basic NFT in case the basic NFT is to be used
		// in the assembly of a composite NFT later on
		FlipBasicNFT.Approve(GetBasicNFTRealmAddress(), basicTokenID)
	}



	return prepareBuyResult(flipPaid, flipBurned, buyError)
}

// util

func convertPriceStringToUint64(price string) (uint64, error) {
	// Split the string at the decimal point
	parts := strings.Split(price, ".")

	// Parse the integer part
	integerPart, err := strconv.Atoi(parts[0])
	if err != nil {
		return 0, err
	}

	// Parse the fractional part and ensure it has at least three digits
	var fractionalPart int
	if len(parts) > 1 {
		fractionalPartStr := parts[1]
		if len(fractionalPartStr) > 3 {
			fractionalPartStr = fractionalPartStr[:3]
		}
		fractionalPart, err = strconv.Atoi(fractionalPartStr)
		if err != nil {
			return 0, err
		}
		for len(fractionalPartStr) < 3 {
			fractionalPart *= 10
			fractionalPartStr += "0"
		}
	}

	// Combine the integer and fractional parts
	combined := uint64(integerPart * 1000 + fractionalPart)
	return combined, nil
}

func prepareBuyResult(flipPaid, flipBurned string, buyError error) string {
	var buyErrorString string
	if buyError != nil {
		buyErrorString = buyError.Error()
	} else {
		buyErrorString = ""
	}

	buyNFTResultObject := &buyNFTResult{
		flipPaid:    flipPaid,
		flipBurned:  flipBurned,
		buyerror:    buyErrorString,
	}
	buyResultJSON, merr := buyNFTResultObject.MarshalJSON()

	if merr != nil {
		return errors.New("Error in marshaling buyNFTResult").Error()
	}
	return string(buyResultJSON)
}

package flippando

import (
	"errors"
	"std"
	"strconv"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/flippando/flippandoserver"
	"gno.land/p/demo/grc/grc20"
	"gno.land/p/demo/ufmt"
)

type lockedVault struct {
	tokenId   string
	lockedFlip std.Coin
}

var (
	flipToken          = grc20.NewBanker("Flippando", "FLIP", 12)
    // 1000 uFLIP = 1 FLIP
    // max denom 1,000,000,000 FLIP
	totalLockedSupply  std.Coin = std.NewCoin("FLIP", 0)
	// hardcoding the airdrop account and restricting minting to it
	airdropAccount std.Address = std.Address("g1mphljw4duhzwqs72hv9grm4k6ufkvts3g7t3t4")
	userFlipNFTs       avl.Tree // onwer -> avl.Tree // []*lockedVault
)

// overrides the Mint function in the interface, restricts only to airdropAccount calls
func Mint(recipient std.Address, flipAmount std.Coin) error {
	if airdropAccount.String() != std.GetOrigCaller().String() {
		return errors.New("invalid account calling Mint")
	}

	if airdropAccount.String() != recipient.String() {
		return errors.New("invalid recipient for Mint")
	}

	if err := flipToken.Mint(recipient, uint64(flipAmount.Amount)); err != nil {
		return errors.New(ufmt.Sprintf("Error minting FLIP to airdrop account: %s", err.Error()))
	}
	return nil
}

// mint tokens and locks them, making them unavaialble for spending
func MintAndLockFLIP(recipient std.Address, tokenId string, flipAmount std.Coin) error {
	if err := flipToken.Mint(recipient, uint64(flipAmount.Amount)); err != nil {
		return err
	}

	var userLockedBalances []*lockedVault

	v, exists := userFlipNFTs.Get(recipient.String())
	if exists {
		// Assert the type of v to []*lockedVault
		var ok bool
		userLockedBalances, ok = v.([]*lockedVault)
		if !ok {
			return errors.New("invalid type for user locked balances")
		}
	}

	lockingBalance := &lockedVault{
		tokenId:   tokenId,
		lockedFlip: flipAmount,
	}

	userLockedBalances = append([]*lockedVault{lockingBalance}, userLockedBalances...)
	userFlipNFTs.Set(recipient.String(), userLockedBalances)

	totalLockedSupply = totalLockedSupply.Add(flipAmount)

	return nil
}

// unlocks the balance, making it avaialble for spending for recipient
// no actual transfer is performed
func ReleaseLockedFLIP(recipient std.Address, tokenId string) error {
	var userLockedBalances []*lockedVault
	var flipAmount std.Coin
	tokenFound := false

	v, found := userFlipNFTs.Get(recipient.String())
	if found {
		// Assert the type of v to []*lockedVault
		var ok bool
		userLockedBalances, ok = v.([]*lockedVault)
		if !ok {
			return errors.New("invalid type for user locked balances")
		}

		// Create a new slice for storing balances without the unlocked token
		var newLockedBalances []*lockedVault
		for _, lv := range userLockedBalances {
			if lv.tokenId == tokenId {
				// Record the amount and indicate the token was found
				flipAmount = lv.lockedFlip
				tokenFound = true
			} else {
				// Add to the new slice if it's not the token to unlock
				newLockedBalances = append(newLockedBalances, lv)
			}
		}

		// Use the new slice if the token was found and removed
		if tokenFound {
			userLockedBalances = newLockedBalances
		}
	}

	if !tokenFound {
		return ErrInvalidTokenId // Ensure this error is defined
	}

	// Update the user's locked balances in userFlipNFTs
	userFlipNFTs.Set(recipient.String(), userLockedBalances)

	// Update the total locked supply
	totalLockedSupply = totalLockedSupply.Sub(flipAmount)

	return nil
}

// transfers fungible tokens, taking into account the locked balances
// i.e. only avaialble tokens can be spent
func Transfer(from, recipient std.Address, fAmount std.Coin) error {
	totalFLIPBalance := flipToken.BalanceOf(from)

	lockedFLIPBalance := getUserLockedBalances(from)

	if int64(totalFLIPBalance) >= fAmount.Amount && int64(totalFLIPBalance)-int64(lockedFLIPBalance.Amount) >= fAmount.Amount {
		if err := flipToken.Transfer(from, recipient, uint64(fAmount.Amount)); err != nil {
			return err
		}
	} else {
		return errors.New("not enough available balance")
	}

	return nil
}

// pays the FLIP price in a marketplace transaction
// burning a percentage between 1% anf 50%
func PayFLIPToSeller(from, recipient std.Address, fPrice std.Coin) (flipPaid string, flipBurned string, err error) {
	flipToken.Approve(from, std.CurrentRealm().Addr(), uint64(fPrice.Amount))
	totalFLIPBalance := flipToken.BalanceOf(from)

	lockedFLIPBalance := getUserLockedBalances(from)

	if int64(totalFLIPBalance) >= fPrice.Amount && int64(totalFLIPBalance)-lockedFLIPBalance.Amount >= fPrice.Amount {
		randomPercentage := flippandoserver.GenerateRandomNumbers(2, 1, 50, time.Now().Unix())
		randomBurnAmount := fPrice.Amount * int64(randomPercentage[0]) / 100
		fPaidPrice := fPrice.Amount - randomBurnAmount
		flipToBurn := fPrice.Amount - fPaidPrice 

		if err := flipToken.Transfer(from, recipient, uint64(fPaidPrice)); err != nil {
			return "", "", err
		}
		if err := flipToken.Burn(from, uint64(flipToBurn)); err != nil {
			return "", "", err
		}

		return strconv.FormatInt(fPaidPrice, 10), strconv.FormatInt(flipToBurn, 10), nil
	} else {
		return "", "", ErrFlipTokenNotEnoughBalance
	}
}

// transfers the locked tokens to a new recipient (toBuyer)
// used when a basic NFT is sold in the marketplace
func TransferLockedFLIP(fromSeller, toBuyer std.Address, tokenId string) error {
    var sellerLockedBalances []*lockedVault
    var flipAmount std.Coin
    tokenFound := false

    // Step 1: Unlock the tokens associated with the tokenId for the seller
    v, found := userFlipNFTs.Get(fromSeller.String())
    if found {
        // Assert the type of v to []*lockedVault
        var ok bool
        sellerLockedBalances, ok = v.([]*lockedVault)
        if !ok {
            return errors.New("invalid type for user locked balances")
        }

        // Create a new slice for storing balances without the unlocked token
        var newLockedBalances []*lockedVault
        for _, lv := range sellerLockedBalances {
            if lv.tokenId == tokenId {
                // Record the amount and indicate the token was found
                flipAmount = lv.lockedFlip
                tokenFound = true
            } else {
                // Add to the new slice if it's not the token to unlock
                newLockedBalances = append(newLockedBalances, lv)
            }
        }

        // Use the new slice if the token was found and removed
        if tokenFound {
            sellerLockedBalances = newLockedBalances
        }
    }

    if !tokenFound {
        return ErrInvalidTokenId // Ensure this error is defined
    }

    // Update the seller's locked balances in userFlipNFTs
    userFlipNFTs.Set(fromSeller.String(), sellerLockedBalances)

    // Update the total locked supply
    totalLockedSupply = totalLockedSupply.Sub(flipAmount)

    // Step 2: Transfer the unlocked tokens to the buyer
    if err := flipToken.Transfer(fromSeller, toBuyer, uint64(flipAmount.Amount)); err != nil {
        return err
    }

    // Step 3: Lock the received tokens in the buyer's userFlipNFTs
    var buyerLockedBalances []*lockedVault

    v, exists := userFlipNFTs.Get(toBuyer.String())
    if exists {
        // Assert the type of v to []*lockedVault
        var ok bool
        buyerLockedBalances, ok = v.([]*lockedVault)
        if !ok {
            return errors.New("invalid type for user locked balances")
        }
    }

    lockingBalance := &lockedVault{
        tokenId:   tokenId,
        lockedFlip: flipAmount,
    }

    buyerLockedBalances = append([]*lockedVault{lockingBalance}, buyerLockedBalances...)
    userFlipNFTs.Set(toBuyer.String(), buyerLockedBalances)

    totalLockedSupply = totalLockedSupply.Add(flipAmount)

    return nil
}

func GetFLIPBalance(user std.Address) string {
	totalFLIPBalance := flipToken.BalanceOf(user)

	lockedFLIPBalance := getUserLockedBalances(user)
	availableFLIPBalance := totalFLIPBalance - uint64(lockedFLIPBalance.Amount)

	userBalances := &userFLIPBalances{
		lockedBalance:   lockedFLIPBalance.String(),
		availableBalance: strconv.FormatUint(availableFLIPBalance, 10),
	}

	userBalancesJSON, err := userBalances.MarshalJSON()
	if err != nil {
		panic("error in marshalling user FLIP balances")
	}

	return string(userBalancesJSON)
}

func getUserLockedBalances(user std.Address) std.Coin {
	lockedFLIPBalance := std.NewCoin("FLIP", 0)

	v, exists := userFlipNFTs.Get(user.String())
	if exists {
		userLockedBalances, ok := v.([]*lockedVault)
		if !ok {
			return lockedFLIPBalance
		}

		for _, lv := range userLockedBalances {
			lockedFLIPBalance = lockedFLIPBalance.Add(lv.lockedFlip)
		}
	}

	return lockedFLIPBalance
}

