}

func (f *FilterByContractor) FromJSON(ast *json.Node) {
	obj := ast.MustObject()
	f.Contractor = jsonutil.MustAddress(obj["contractor"])
}

var _ Filter = &FilterByContractor{}

type FilterByContractorAndFunder struct {
