	Funder     std.Address
}

func (f *FilterByContractorAndFunder) FromJSON(ast *json.Node) {
	obj := ast.MustObject()
	f.Contractor = jsonutil.MustAddress(obj["contractor"])
	f.Funder = jsonutil.MustAddress(obj["funder"])
}

var _ Filter = &FilterByContractorAndFunder{}

